u8102\u5236\u6210\u7684\u751f\u7269\u67f4\u6cb9\u7ec8\u4e8e\u6446\u8131\u4e86\u201c\u5b9e\u9a8c\u5ba4\u201d\u9636\u6bb5\uff0c\u6253\u901a\u4e86\u5e02\u573a\u5316\u7684\u201c\u6700\u540e\u4e00\u516c\u91cc\u201d\uff0c\u5927\u9762\u79ef\u590d\u5236\u63a8\u5e7f\u5e94\u7528\u6307\u65e5\u53ef\u5f85\u3002\n\u3000\u3000\u548c\u666e\u901a\u67f4\u6cb9\u54c1\u8d28\u51e0\u4e4e\u6ca1\u533a\u522b\n\u3000\u3000\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5386\u7ecf\u53bb\u9664\u6742\u8d28\u3001\u5316\u5b66\u53cd\u5e94\u3001\u84b8\u998f\u7b49\u591a\u9053\u5de5\u5e8f\uff0c\u53ef\u5f97\u5230\u7cbe\u5236\u7684\u8102\u80aa\u9178\u7532\u916f\uff0c\u4e5f\u5c31\u662f\u4fd7\u79f0\u7684\u751f\u7269\u67f4\u6cb9\u3002\u6839\u636e\u6211\u56fd\u56fd\u5bb6\u6807\u51c6\u300a\u751f\u7269\u67f4\u6cb9\u8c03\u548c\u71c3\u6599\u300b\uff0c\u80fd\u591f\u8f66\u7528\u7684\u751f\u7269\u67f4\u6cb9\u5fc5\u987b\u662f\u201c\u8c03\u548c\u6cb9\u201d\u3002\u6b64\u6b21\u4e24\u4e2a\u8bd5\u70b9\u52a0\u6cb9\u7ad9\u63d0\u4f9b\u7684B5\u751f\u7269\u67f4\u6cb9\uff0c\u75315%\u7684\u751f\u7269\u67f4\u6cb9\u4e0e95%\u7684\u77ff\u7269\u67f4\u6cb9\u8c03\u548c\u800c\u6210\u3002\u540c\u65f6\uff0c\u8fd8\u8981\u901a\u8fc7\u786b\u542b\u91cf\u3001\u9178\u503c\u3001\u6c34\u542b\u91cf\u3001\u51dd\u70b9\u3001\u591a\u73af\u82b3\u70c3\u7b49\u51e0\u5341\u9879\u8d28\u91cf\u6307\u6807\u7684\u8003\u6838\u3002\n\u3000\u3000\u8bb0\u8005\u5728\u5357\u4ead\u516c\u8def\u3001\u6d66\u536b\u516c\u8def\u8def\u53e3\u7684\u4e00\u5ea7\u52a0\u6cb9\u7ad9\u5185\u770b\u5230\uff0c\u4e24\u4e2a\u52a0\u6cb9\u5668\u7ecf\u8fc7\u6539\u9020\uff0c\u67094\u67c4\u52a0\u6cb9\u67aa\u53ef\u4ee5\u63d0\u4f9bB5\u751f\u7269\u67f4\u6cb9\u7684\u52a0\u6ce8\u670d\u52a1\uff0c\u76f8\u5173\u52a0\u6cb9\u4f4d\u7684\u5730\u9762\u4e0a\u8d34\u6709\u7eff\u5e95\u767d\u5b57\u7684\u201c\u751f\u7269\u67f4\u6cb9\u201d\u6807\u8bb0\u3002\u52a0\u6cb9\u7ad9\u9ad8\u9ad8\u7ad6\u8d77\u7684\u7ad9\u724c\u4e0a\uff0c\u4e5f\u589e\u52a0\u4e86\u201c\u751f\u7269\u67f4\u6cb9\u201d\u56db\u4e2a\u5927\u5b57\uff0c\u63d0\u9192\u7740\u8fc7\u5f80\u7684\u8f66\u8f86\u3002\u8be5\u7ad9\u7684\u4e00\u822c\u67f4\u6cb9\u552e\u4ef7\u662f6.02\u5143/\u5347\uff0c\u800c\u52a0\u6ce8B5\u751f\u7269\u67f4\u6cb9\u53ef\u62539.5\u6298\uff0c\u552e\u4ef75.72\u5143/\u5347\u3002\u76ee\u524d\uff0c\u5949\u8d24\u8fd9\u5ea7\u52a0\u6cb9\u7ad9\u50a8\u5907\u4e86\u7ea610\u5428B5\u751f\u7269\u67f4\u6cb9\uff0c\u6309\u6bcf\u8f86\u8f66\u4e00\u6b21\u52a0\u6ce860\u5347\u8ba1\u7b97\uff0c\u9884\u8ba1\u56db\u4e94\u5929\u5c31\u53ef\u4ee5\u52a0\u5b8c\u3002\u670d\u52a1\u5458\u8868\u793a\uff0c\u53d1\u7968\u4e0a\u5c06\u6ce8\u660e\u662f\u201c\u751f\u7269\u67f4\u6cb9\u201d\u3002\u4ed6\u4eec\u7ed9\u8f66\u8f86\u52a0\u6ce8\u524d\uff0c\u4f1a\u4e3b\u52a8\u660e\u793a\u6cb9\u54c1\u5e76\u8be2\u95ee\u9a7e\u9a76\u5458\u610f\u613f\u3002\n\u3000\u3000\u8bb0\u8005\u968f\u673a\u91c7\u8bbf\u4e86\u51e0\u4f4d\u8fdb\u7ad9\u52a0\u6cb9\u7684\u8f66\u4e3b\uff0c\u4ed6\u4eec\u666e\u904d\u5bf9\u751f\u7269\u67f4\u6cb9\u8868\u793a\u6b22\u8fce\uff0c\u4f46\u90fd\u4ee5\u201c\u5403\u4e0d\u51c6\u54c1\u8d28\u201d\u3001\u201c\u5355\u4f4d\u62a5\u9500\u65f6\u53ef\u80fd\u4e0d\u63a5\u53d7\u201d\u7b49\u4e3a\u7531\uff0c\u6ca1\u6709\u9009\u62e9\u751f\u7269\u67f4\u6cb9\u3002\n\u3000\u3000\u5bf9\u6b64\uff0c\u540c\u6d4e\u5927\u5b66\u6559\u6388\u697c\u72c4\u660e\u8868\u793a\uff0c\u80fd\u591f\u8fdb\u5165\u4e2d\u77f3\u5316\u52a0\u6cb9\u7ad9\u8bd5\u70b9\u7684\u751f\u7269\u67f4\u6cb9\uff0c\u5176\u54c1\u8d28\u5df2\u7ecf\u8fc7\u53cd\u590d\u8003\u9a8c\uff0c\u7528\u8d77\u6765\u548c\u666e\u901a\u67f4\u6cb9\u51e0\u4e4e\u6ca1\u6709\u533a\u522b\u3002\u636e\u4ed6\u4ecb\u7ecd\uff0c\u81ea2013\u5e749\u670829\u65e5\u4e0a\u6d77\u7b2c\u4e00\u8f86\u4f7f\u7528\u201c\u5730\u6c9f\u6cb9\u201d\u768470\u8def\u516c\u4ea4\u8f66\u4e0a\u7ebf\u8fd0\u8425\uff0c\u81f32017\u5e749\u670830\u65e5\uff0c\u5171\u6709104\u8f86\u516c\u4ea4\u8f66\u53c2\u52a0\u4e86\u300a\u9ad8\u6bd4\u4f8b\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5236\u751f\u7269\u67f4\u6cb9\u4e0a\u6d77\u5e02\u5e94\u7528\u4f53\u7cfb\u7814\u7a76\u300b\u8bfe\u9898\u3002\u4f7f\u7528\u4e09\u79cd\u89c4\u683c\uff08B5\u3001B10\u3001B20\uff09\u751f\u7269\u67f4\u6cb9\u6df7\u5408\u71c3\u6599\u7684\u8fd9\u4e9b\u516c\u4ea4\u8f66\u7d2f\u8ba1\u8fd0\u884c1560.72\u4e07\u516c\u91cc\uff0c\u6d88\u8017\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5236\u751f\u7269\u67f4\u6cb945.63\u4e07\u5347\u3002\u8fd110\u4e2a\u6708\u6765\uff0c\u53c8\u670932\u8f86\u73af\u536b\u8f66\u53c2\u52a0\u4e86\u4e0a\u8ff0\u8bd5\u9a8c\uff0c\u7d2f\u8ba1\u8fd0\u884c8.31\u4e07\u516c\u91cc\uff0c\u6d88\u8017\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5236\u751f\u7269\u67f4\u6cb92405.6\u5347\u3002\n\u3000\u3000\u7ed3\u679c\u663e\u793a\uff0c\u4f7f\u7528B5\u751f\u7269\u67f4\u6cb9\u540e\uff0c\u8bd5\u9a8c\u8f66\u8f86\u53d1\u52a8\u673a\u7684\u6d3b\u585e\u9876\u90e8\u3001\u6c14\u95e8\u3001\u55b7\u6cb9\u5668\u7b49\u5173\u952e\u96f6\u90e8\u4ef6\u8868\u9762\u90fd\u6ca1\u6709\u79ef\u78b3\uff0c\u4e5f\u672a\u51fa\u73b0\u4e0e\u6cb9\u8def\u76f8\u5173\u7684\u6545\u969c\uff1b\u90e8\u5206\u8f66\u8f86\u8fd8\u51fa\u73b0\u4e86\u5c3e\u6c14\u6392\u653e\u7684\u6539\u5584\uff0c\u76f8\u6bd4\u7eaf\u67f4\u6cb9\uff0c\u4e00\u6c27\u5316\u78b3\u3001\u78b3\u6c22\u5316\u5408\u7269\u3001\u8d85\u7ec6\u9897\u7c92\u7269\u7b49\u6392\u653e\u5747\u6709\u5c0f\u5e45\u964d\u4f4e\u3002\n\u3000\u3000\u7834\u89e3\u201c\u5403\u4e0d\u9971\u201d\u548c\u201c\u6ca1\u94b1\u8d5a\u201d\n\u3000\u3000\u697c\u72c4\u660e\u5766\u8a00\uff0c\u201c\u5730\u6c9f\u6cb9\u201d\u6210\u4e3a\u8f66\u7528\u71c3\u6599\u7684\u6280\u672f\uff0c\u65e9\u5728\u5341\u591a\u5e74\u524d\u5c31\u5df2\u653b\u514b\uff0c\u5176\u53c2\u4e0e\u7684\u8bfe\u9898\u6210\u679c\u201c\u6df7\u5408\u67f4\u6cb9\u71c3\u6599\u8f66\u7528\u5173\u952e\u6280\u672f\u53ca\u5e94\u7528\u201d\u593a\u5f97\u4e86\u4e0a\u6d77\u5e022012\u5e74\u5ea6\u79d1\u6280\u8fdb\u6b65\u4e00\u7b49\u5956\uff0c\u4f46\u6b64\u540e\uff0c\u8981\u8ba9\u201c\u5730\u6c9f\u6cb9\u201d\u5f7b\u5e95\u8131\u79bb\u5b9e\u9a8c\u5ba4\u800c\u8d70\u5411\u5e02\u573a\uff0c\u987b\u8fc7\u4e24\u9053\u5173\uff1a\u201c\u5403\u4e0d\u9971\u201d\u548c\u201c\u6ca1\u94b1\u8d5a\u201d\u3002\n\u3000\u3000\u6240\u8c13\u201c\u5403\u4e0d\u9971\u201d\uff0c\u5c31\u662f\u539f\u672c\u6ca1\u6709\u7a33\u5b9a\u7684\u201c\u5730\u6c9f\u6cb9\u201d\u6765\u6e90\uff0c\u65e0\u6cd5\u4fdd\u8bc1\u751f\u7269\u67f4\u6cb9\u7684\u7a33\u5b9a\u91cf\u4ea7\u3002\u8fd9\u4e00\u95ee\u9898\u57282013\u5e74\u88ab\u653b\u7834\uff0c\u5728\u5e02\u98df\u5b89\u529e\u7275\u5934\u4e0b\uff0c\u5e02\u98df\u5b89\u529e\u3001\u5e02\u7eff\u5316\u5e02\u5bb9\u5c40\u3001\u5e02\u98df\u5b89\u8054\u5408\u4f1a\u3001\u540c\u6d4e\u5927\u5b66\u3001\u4e0a\u6d77\u534e\u8c0a\u96c6\u56e2\u548c\u4e0a\u6d77\u4e2d\u5668\u73af\u4fdd\u79d1\u6280\u6709\u9650\u516c\u53f8\u7b7e\u7f72\u4e86\u201c\u4e0a\u6d77\u5e02\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5faa\u73af\u5229\u7528\u516d\u65b9\u5408\u4f5c\u534f\u8bae\u201d\uff0c\u660e\u786e\u5168\u5e02\u6536\u96c6\u7684\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u4f18\u5148\u4f9b\u5e94\u516c\u4ea4\u8bd5\u70b9\u9879\u76ee\u3002\u53ea\u8981\u6761\u4ef6\u5141\u8bb8\uff0c\u4e0a\u6d77\u6bcf\u5e74\u4ea7\u751f\u7684\u7ea6\u4e09\u56db\u4e07\u5428\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u90fd\u53ef\u4ee5\u8fd0\u5230\u6307\u5b9a\u5355\u4f4d\uff0c\u5236\u51fa\u751f\u7269\u67f4\u6cb9\u3002\n\u3000\u3000\u6240\u8c13\u201c\u6ca1\u94b1\u8d5a\u201d\uff0c\u662f\u53d7\u5236\u4e8e\u8f83\u9ad8\u7684\u6536\u8fd0\u548c\u5904\u7f6e\u6210\u672c\uff0c\u4e0a\u6d77\u751f\u7269\u67f4\u6cb9\u751f\u4ea7\u4f01\u4e1a\u957f\u671f\u5904\u4e8e\u6210\u672c\u5012\u6302\u7684\u7a98\u5883\u3002\u4ece2010\u5e74\u5f00\u59cb\uff0c\u4e0a\u6d77\u5904\u7f6e\u4f01\u4e1a\u7684\u201c\u5730\u6c9f\u6cb9\u201d\u6536\u8d2d\u4ef7\u8981\u6309\u56fd\u9645\u539f\u6cb9\u3001\u68d5\u6988\u6cb9\u3001\u7532\u916f\u4e09\u8005\u56fd\u9645\u4ef7\u683c\u4e4b\u548c\u7684\u4e00\u5b9a\u6bd4\u4f8b\u6765\u8ba1\uff0c\u4e00\u65e6\u9047\u5230\u56fd\u9645\u6cb9\u4ef7\u8d70\u4f4e\uff0c\u751f\u4ea7\u7ebf\u5f00\u5f97\u8d8a\u4e45\u5c31\u8d8a\u4e8f\u3002\u8fd9\u4e00\u95ee\u9898\u57282016\u5e74\u89e3\u51b3\uff0c\u300a\u4e0a\u6d77\u5e02\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5236\u751f\u7269\u67f4\u6cb9\u6536\u8fd0\u5904\u7f6e\u5e94\u6025\u6276\u6301\u529e\u6cd5\u300b\u9881\u5e03\uff0c\u5c06\u5728\u5e02\u573a\u5931\u7075\u7684\u60c5\u51b5\u4e0b\uff0c\u5bf9\u76f8\u5173\u5904\u7f6e\u4f01\u4e1a\u7684\u4e8f\u635f\u8fdb\u884c\u8865\u8d34\uff0c\u786e\u4fdd\u5176\u80fd\u7ef4\u6301\u6b63\u5e38\u8fd0\u8f6c\u3002\u6b64\u5916\uff0c\u4e0a\u6d77\u8bbe\u7f6e\u4e86\u63a8\u5e7f\u5e94\u7528\u751f\u7269\u67f4\u6cb9\u9884\u7b97\uff0c\u76ee\u524d\u548c\u4e24\u4e2a\u8bd5\u70b9\u52a0\u6cb9\u7ad9\u6709\u5173\u7684B5\u751f\u7269\u67f4\u6cb9\u751f\u4ea7\u53ca\u914d\u9001\u8fc7\u7a0b\u4e2d\u4ea7\u751f\u7684\u6210\u672c\u548c\u8d39\u7528\u8ba1\u5165\u8be5\u9884\u7b97\u5185\uff0c\u8fd9\u4e5f\u5c06\u51cf\u8f7b\u76f8\u5173\u4f01\u4e1a\u8d1f\u62c5\u3002\n\u3000\u3000\u4e0a\u6d77\u5e02\u98df\u54c1\u5b89\u5168\u5de5\u4f5c\u8054\u5408\u4f1a\u526f\u79d8\u4e66\u957f\u90d1\u6811\u677e\u8868\u793a\uff0c\u4f9d\u9760\u8d22\u653f\u652f\u6301\uff0c\u5e76\u975e\u957f\u4e45\u4e4b\u8ba1\uff0c\u5173\u952e\u8fd8\u662f\u8981\u4ece\u5e02\u573a\u91cc\u627e\u51fa\u8def\uff0c\u8fd9\u6b21\u751f\u7269\u67f4\u6cb9\u8fdb\u5165\u793e\u4f1a\u52a0\u6cb9\u7ad9\uff0c\u5c31\u662f\u8fc8\u51fa\u4e86\u5de8\u5927\u7684\u4e00\u6b65\u3002\u201c\u6709\u52a0\u6cb9\u7ad9\u80fd\u63a5\u53d7\u751f\u7269\u67f4\u6cb9\uff0c\u5c31\u80fd\u8ba9\u516c\u4f17\u771f\u6b63\u63a5\u89e6\u5230\u751f\u7269\u67f4\u6cb9\uff0c\u53ea\u6709\u4e86\u89e3\uff0c\u6574\u4e2a\u793e\u4f1a\u624d\u4f1a\u8fdb\u4e00\u6b65\u63a5\u53d7\u3001\u8ba4\u53ef\u8fd9\u79cd\u65b0\u578b\u7684\u80fd\u6e90\u6d88\u8d39\u65b9\u5f0f\u3002\u201d\n\u3000\u3000\u4e0a\u6d77\u5e02\u98df\u836f\u76d1\u5c40\u5c40\u957f\u6768\u52b2\u677e\u8868\u793a\uff0c\u201c\u901a\u8fc7\u751f\u7269\u67f4\u6cb9\u8fd9\u6761\u8def\u5f84\uff0c\u4e0a\u6d77\u5df2\u5b9e\u73b0\u4e86\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u7684\u95ed\u73af\u5f0f\u7ba1\u7406\u3002\u201d\u672a\u6765\u4e0a\u6d77\u8fd8\u5c06\u7ee7\u7eed\u63d0\u5347\u6280\u672f\u6807\u51c6\uff0c\u8fd0\u7528\u5e02\u573a\u5316\u8fd0\u4f5c\u7684\u6a21\u5f0f\uff0c\u63a8\u8fdb\u9910\u53a8\u5e9f\u5f03\u6cb9\u8102\u5236\u751f\u7269\u67f4\u6cb9\u7684\u591a\u5143\u5316\u5e94\u7528\uff0c\u4ece\u516c\u4ea4\u8f66\u3001\u73af\u536b\u8f66\u6269\u5927\u5230\u7269\u6d41\u8f66\u548c\u957f\u6c5f\u8d27\u8fd0\u8239\u7b49\uff0c\u5e76\u63a2\u7d22\u5efa\u7acb\u9002\u5408\u672c\u5e02\u5b9e\u9645\u5e76\u4e14\u5b89\u5168\u53ef\u9760\u3001\u80fd\u6709\u6548\u964d\u4f4e\u6210\u672c\u7684\u6df7\u5408\u67f4\u6cb9\u71c3\u6599\u50a8\u5b58\u53ca\u52a0\u6ce8\u4f53\u7cfb\uff0c\u66f4\u52a0\u5b8c\u5584\u5168\u4ea7\u4e1a\u94fe\u76d1\u7ba1\u4f53\u7cfb\u53ca\u5e02\u573a\u8425\u8fd0\u6a21\u5f0f\u3002\n{IMG:1}\n[\u8d23\u4efb\u7f16\u8f91:\u767d\u4e3d\u514b\u5b5c\u00b7\u5e15\u54c8\u4e01]"}, {"emotion": "neg", "id": "01a7b69b8c944349d72fdcffa1ea282c57", "c_t": "\u7684\u54e5\u201c\u62fe\u91d1\u7d22\u916c\u201d\u66b4\u9732\u201c\u8fd8\u7269\u8def\u5f84\u201d\u72ed\u7a84\u3000\u3000\u591a\u65e5\u6765\uff0c\u897f\u5b89\u705e\u6865\u533a\u7684\u5b5f\u5148\u751f\u6709\u70b9\u70e6\uff1a\u5750\u51fa\u79df\u8f66\u65f6\u4e0d\u614e\u843d\u4e0b\u4e86\u88c5\u6709\u9a7e\u7167\u548c\u51e0\u5f20\u94f6\u884c\u5361\u7684\u5305\uff0c\u51fa\u79df\u8f66\u53f8\u673a\u591a\u65b9\u5bfb\u627e\u5230\u4ed6\uff0c\u4f46\u5728\u5f52\u8fd8\u7269\u54c1\u65f6\u5411\u4ed6\u7d22\u8981\u4e86200\u5143\u3002\u5b5f\u5148\u751f\u8ba4\u4e3a\uff0c\u53f8\u673a\u4e3b\u52a8\u5f52\u8fd8\u4e58\u5ba2\u9057\u5931\u7269\u503c\u5f97\u79f0\u8d5e\uff0c\u4f46\u8981\u94b1\u7684\u884c\u4e3a\u5b9e\u5728\u4e0d\u53ef\u53d6\u3002(10\u670830\u65e5\u300a\u534e\u5546\u62a5\u300b)\n\u3000\u3000\u7684\u54e5\u201c\u62fe\u91d1\u4e0d\u6627\u201d\uff0c\u5f53\u7136\u8ba9\u4e58\u5ba2\u6b22\u559c\u3002\u4f46\u73b0\u5728\u7684\u54e5\u5374\u662f\u201c\u62fe\u91d1\u7d22\u916c\u201d\uff0c\u8fd9\u4e0d\u662f\u4e00\u822c\u4eba\u6240\u80fd\u591f\u63a5\u53d7\u7684\uff0c\u51b5\u4e14\u5f00\u53e3\u5c31\u662f200\u5143\uff0c\u8fd9\u5bf9\u4e8e\u4e00\u4e9b\u4eba\u6765\u8bf4\uff0c\u4e5f\u4e0d\u662f\u5c0f\u6570\u76ee\u3002\u7684\u54e5\u201c\u62fe\u91d1\u7d22\u916c\u201d\u5176\u5b9e\u662f\u53ef\u4ee5\u907f\u514d\u7684\uff0c\u8fd9\u66b4\u9732\u51fa\u51fa\u79df\u8f66\u201c\u8fd8\u7269\u8def\u5f84\u201d\u72ed\u7a84\u3002\n\u3000\u3000\u4ece\u6cd5\u7406\u800c\u8a00\uff0c\u62fe\u5230\u4e1c\u897f\u7d22\u8981\u62a5\u916c\u662f\u5141\u8bb8\u7684\u3002\u6c11\u6cd5\u901a\u5219\u7b2c\u4e03\u5341\u4e5d\u6761\u89c4\u5b9a\uff0c\u62fe\u5f97\u9057\u5931\u7269\u3001\u6f02\u6d41\u7269\u6216\u5931\u6563\u7684\u9972\u517b\u52a8\u7269\uff0c\u5e94\u5f53\u5f52\u8fd8\u5931\u4e3b\uff0c\u56e0\u6b64\u800c\u652f\u51fa\u7684\u8d39\u7528\u7531\u5931\u4e3b\u507f\u8fd8\u3002\u53e6\u5916\uff0c\u6c11\u6cd5\u901a\u5219\u7b2c\u4e5d\u5341\u4e09\u6761\u518d\u6b21\u89c4\u5b9a\uff0c\u6ca1\u6709\u6cd5\u5b9a\u7684\u6216\u8005\u7ea6\u5b9a\u7684\u4e49\u52a1\uff0c\u4e3a\u907f\u514d\u4ed6\u4eba\u5229\u76ca\u53d7\u635f\u5931\u8fdb\u884c\u7ba1\u7406\u6216\u8005\u670d\u52a1\u7684\uff0c\u6709\u6743\u8981\u6c42\u53d7\u76ca\u4eba\u507f\u4ed8\u7531\u6b64\u800c\u652f\u4ed8\u7684\u5fc5\u8981\u8d39\u7528\u3002\u7269\u6743\u6cd5\u4e5f\u89c4\u5b9a\uff0c\u6743\u5229\u4eba\u9886\u53d6\u9057\u5931\u7269\u65f6\uff0c\u5e94\u5f53\u5411\u62fe\u5f97\u4eba\u6216\u8005\u6709\u5173\u90e8\u95e8\u652f\u4ed8\u4fdd\u7ba1\u9057\u5931\u7269\u7b49\u652f\u51fa\u7684\u5fc5\u8981\u8d39\u7528\uff0c\u4f46\u6ca1\u6709\u89c4\u5b9a\u916c\u8c22\u6570\u989d\u6216\u8005\u6bd4\u4f8b\u3002\u800c\u6709\u4e9b\u5730\u65b9\u89c4\u5b9a\u62a5\u916c\u7684\u6bd4\u4f8b\u5219\u662f10%\u3002\u4e0d\u8fc7\uff0c\u5177\u4f53\u5230\u7684\u54e5\uff0c\u5f88\u591a\u4eba\u8ba4\u4e3a\u5e94\u8be5\u65e0\u507f\u5f52\u8fd8\u7684\u3002\n\u3000\u3000\u4ece\u60c5\u7406\u800c\u8a00\uff0c\u7684\u54e5\u62fe\u5230\u4e1c\u897f\u5e94\u8be5\u65e0\u507f\u5f52\u8fd8\u3002\u516c\u4ea4\u8f66\u53f8\u673a\u4e5f\u8be5\u662f\u5982\u6b64\u3002\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u8fd9\u4e5f\u662f\u4e00\u79cd\u8d23\u4efb\u548c\u804c\u8d23\u3002\u800c\u6700\u91cd\u8981\u7684\u4e00\u70b9\u5219\u662f\uff0c\u7684\u54e5\u62fe\u5230\u4e1c\u897f\uff0c\u6ca1\u6709\u5fc5\u8981\u4e00\u5b9a\u8981\u4eb2\u81ea\u9001\u5230\u4e58\u5ba2\u624b\u4e2d\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u81ea\u5df1\u4eb2\u81ea\u53bb\u627e\uff0c\u53ea\u8981\u5c06\u4e1c\u897f\u4ea4\u5230\u516c\u53f8\uff0c\u800c\u516c\u53f8\u5e94\u8be5\u6709\u81ea\u5df1\u7684\u8def\u5f84\u53bb\u627e\u5931\u4e3b\uff0c\u5c06\u5931\u7269\u4ea4\u8fd8\u7ed9\u5931\u4e3b\u3002\u8fd9\u6837\u5c31\u80fd\u591f\u907f\u514d\u7684\u54e5\u56e0\u4e3a\u8bef\u5de5\u800c\u7d22\u8981\u62a5\u916c\u7684\u505a\u6cd5\u3002\u4e0d\u8fc7\uff0c\u5177\u4f53\u5230\u8fd9\u4e00\u6b21\uff0c\u7684\u54e5\u4e4b\u6240\u4ee5\u7d22\u8981\u62a5\u916c\uff0c\u4e00\u65b9\u9762\u662f\u62ff\u56de\u5305\u65f6\u4ed8\u4e8650\u5143\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u5bfb\u627e\u5931\u4e3b\u8bef\u4e86\u81ea\u5df1\u51fa\u8f66\u7684\u65f6\u95f4\uff0c\u5e0c\u671b\u501f\u6b64\u5f25\u8865\u81ea\u5df1\u7684\u635f\u5931\u3002\u52a8\u673a\u548c\u5fc3\u60c5\u53ef\u4ee5\u7406\u89e3\uff0c\u800c\u62ff\u56de\u5931\u7269\u7684\u4e58\u5ba2\u4ed8\u51fa\u94b1\uff0c\u4e0d\u5e94\u8be5\u89c9\u5f97\u4e0d\u5e73\uff0c\u6bd5\u7adf\u5bf9\u65b9\u4ed8\u51fa\u4e86\u8bb8\u591a\u3002\n\u3000\u3000\u666e\u901a\u4eba\u62fe\u5230\u4e1c\u897f\u5f52\u8fd8\u7ed9\u5931\u4e3b\uff0c\u7d22\u8981\u4e00\u5b9a\u7684\u62a5\u916c\u662f\u53ef\u4ee5\u7406\u89e3\u7684\uff0c\u6211\u4eec\u73b0\u5728\u4e5f\u5df2\u7ecf\u9010\u6e10\u63a5\u53d7\u4e86\u3002\u4f46\u5bf9\u4e8e\u4e00\u4e9b\u7279\u6b8a\u5c97\u4f4d\u7684\u4eba\u5458\u5728\u81ea\u5df1\u7684\u804c\u8d23\u8303\u56f4\u5185\uff0c\u5728\u81ea\u5df1\u7684\u5c97\u4f4d\u4e0a\u62fe\u5230\u4e1c\u897f\u518d\u7d22\u8981\u62a5\u916c\uff0c\u6050\u6015\u8fd8\u662f\u96be\u4ee5\u63a5\u53d7\u7684\u3002\u4e3a\u4e86\u907f\u514d\u51fa\u73b0\u6ca1\u6709\u5fc5\u8981\u7684\u77db\u76fe\u3002\u5e94\u8be5\u58ee\u5927\u51fa\u79df\u8f66\u516c\u53f8\u5bfb\u627e\u5f52\u8fd8\u5931\u4e3b\u4e22\u5931\u4e4b\u7269\u7684\u8def\u5f84\uff0c\u5e76\u4e14\u80fd\u591f\u548c\u8b66\u65b9\u3001\u5a92\u4f53\u5408\u4f5c\uff0c\u5f62\u6210\u4e00\u79cd\u591a\u5143\u5316\u7684\u5bfb\u627e\u5931\u4e3b\u7684\u6e20\u9053\uff0c\u8fd9\u6837\u5c31\u53ef\u4ee5\u907f\u514d\u7684\u54e5\u4ee5\u53ca\u516c\u4ea4\u8f66\u53f8\u673a\u62fe\u5230\u4e1c\u897f\u7d22\u8981\u62a5\u916c\u7684\u8bef\u533a\u3002\n\u3000\u3000\u201c\u62fe\u91d1\u7d22\u916c\u201d\u53ef\u4ee5\u63a5\u53d7\uff0c\u4f46\u7684\u54e5\u201c\u62fe\u91d1\u7d22\u916c\u201d\u5219\u5e94\u8be5\u4e3b\u52a8\u907f\u514d\u3002"}, {"emotion": "neg", "id": "018c070830519262160ad5e5b33a3f12da", "c_t": "\u670d\u52a1\u5929\u7b49 \u6211\u5fc3\u65e0\u6094 \u2014\u2014\u8bbf\u897f\u90e8\u8ba1\u5212\u5927\u5b66\u751f\u5fd7\u613f\u8005\u848b\u5168\u5229 \n\u25a1 \u672c\u62a5\u8bb0\u8005 \u738b\u5cb3\u741b \u901a\u8baf\u5458 \u5f20\u4f1f\u660e \n\u201c\u6628\u5929\uff0c\u6211\u8fd8\u662f\u82b8\u82b8\u4f17\u751f\u4e2d\u7684\u4e00\u540d\u5b66\u5b50\uff1b\u800c\u4eca\u5929\uff0c\u5df2\u7ecf\u6210\u4e3a\u4e86\u4e00\u540d\u9752\u5e74\u5fd7\u613f\u8005\uff0c\u6211\u611f\u5230\u65e0\u6bd4\u7684\u5149\u8363\uff0c\u4e5f\u611f\u5230\u65e0\u6bd4\u7684\u81ea\u8c6a\uff01\u670d\u52a1\u897f\u90e8\u662f\u6211\u4e00\u751f\u7684\u8d22\u5bcc\u3002\u5929\u7b49\u662f\u6211\u7684\u7b2c\u4e8c\u6545\u4e61\uff0c\u6211\u5728\u8fd9\u91cc\u670d\u52a1\uff0c\u4e5f\u5728\u8fd9\u91cc\u6210\u957f\uff0c\u5728\u8fd9\u91cc\u4ed8\u51fa\uff0c\u4e5f\u5728\u8fd9\u91cc\u6536\u83b7\uff1b\u5728\u8fd9\u91cc\u6709\u6211\u9752\u6625\u7684\u8eab\u5f71\uff0c\u4e5f\u6709\u6211\u65e0\u6cd5\u78e8\u706d\u7684\u8bb0\u5fc6\u2026\u2026\u201d\uff13\u6708\uff17\u65e5\uff0c\u897f\u90e8\u8ba1\u5212\u5927\u5b66\u751f\u5fd7\u613f\u8005\u848b\u5168\u5229\u5728\u5411\u8bb0\u8005\u8bb2\u8ff0\u90a3\u4e00\u5e74\u6765\u670d\u52a1\u897f\u90e8\u7684\u96be\u5fd8\u7ecf\u5386\u65f6\uff0c\u4e00\u8138\u7684\u795e\u91c7\u98de\u626c\u3002\n\u5efa\u7acb\u201c\uff11\uff0b\uff11\u75c5\u5386\u6a21\u5f0f\u201d \u60e0\u6cfd\u5c71\u6751\u8001\u767e\u59d3\n\uff12\uff10\uff10\uff13\u5e74\u590f\u5929\uff0c\u5168\u56fd\u201c\u5927\u5b66\u751f\u5fd7\u613f\u670d\u52a1\u897f\u90e8\u8ba1\u5212\u201d\u9996\u6b21\u542f\u52a8\uff0c\u848b\u5168\u5229\u79ef\u6781\u54cd\u5e94\uff0c\u5411\u5b66\u6821\u9012\u4ea4\u4e86\u7533\u8bf7\u4e66\u3002\u5f53\u5e74\uff18\u6708\uff11\uff10\u65e5\uff0c\u4ed6\u5e78\u8fd0\u5165\u9009\uff0c\u6210\u4e3a\u5168\u56fd\u7b2c\u4e00\u6279\u670d\u52a1\u897f\u90e8\u7684\u5927\u5b66\u751f\u5fd7\u613f\u8005\u3002\u7ecf\u8fc7\u5341\u51e0\u5929\u57f9\u8bad\u540e\uff0c\uff12\uff10\uff10\uff13\u5e74\uff18\u6708\uff13\uff11\u65e5\uff0c\u848b\u5168\u5229\u548c\u6765\u81ea\u5404\u5730\u7684\u5fd7\u613f\u8005\u4e00\u6837\u8fdc\u79bb\u7236\u6bcd\uff0c\u79bb\u5f00\u4e86\u540c\u5b66\u548c\u55a7\u56a3\u7684\u57ce\u5e02\uff0c\u6765\u5230\u4e86\u897f\u90e8\u5730\u533a\u56fd\u5b9a\u8d2b\u56f0\u53bf\u2014\u2014\u5929\u7b49\u53bf\uff0c\u5e76\u88ab\u5206\u5230\u5929\u7b49\u53bf\u5929\u7b49\u9547\u536b\u751f\u9662\uff0c\u5f53\u4e00\u540d\u7259\u79d1\u533b\u751f\u3002\n\u848b\u5168\u5229\u6765\u5230\u5929\u7b49\u9547\u536b\u751f\u9662\u540e\uff0c\u5c31\u79ef\u6781\u6295\u5165\u5de5\u4f5c\uff0c\u767d\u5929\u4e3a\u75c5\u4eba\u770b\u75c5\u7684\u540c\u65f6\uff0c\u5411\u75c5\u4eba\u4e86\u89e3\u4ed6\u4eec\u7684\u751f\u6d3b\u4e60\u60ef\u3002\u5f53\u4ed6\u4e86\u89e3\u5230\u5929\u7b49\u53bf\u519c\u6751\u7fa4\u4f17\u60a3\u7259\u75c5\u6709\u201c\u4e00\u75db\u5c31\u62d4\u7259\uff0c\u4e00\u4e86\u767e\u4e86\u201d\u7684\u9648\u65e7\u89c2\u5ff5\u540e\uff0c\u5c31\u5229\u7528\u5729\u65e5\u548c\u540c\u6765\u7684\u5fd7\u613f\u8005\u4e0a\u8857\u5f00\u5c55\u53e3\u8154\u75be\u75c5\u9884\u9632\u5ba3\u4f20\u6d3b\u52a8\uff0c\u5411\u7fa4\u4f17\u5ba3\u4f20\u5982\u4f55\u9884\u9632\u53e3\u8154\u75be\u75c5\u7b49\u77e5\u8bc6\uff0c\u540c\u65f6\u8fd8\u5411\u7fa4\u4f17\u53d1\u653e\u53e3\u8154\u75be\u75c5\u9884\u9632\u4fdd\u5065\u77e5\u8bc6\u5ba3\u4f20\u5355\uff0c\u5f97\u5230\u4e86\u7fa4\u4f17\u7684\u597d\u8bc4\u3002\u4ed6\u590d\u5370\u7684\u51e0\u5343\u4efd\u5ba3\u4f20\u5355\u5728\u77ed\u77ed\u7684\uff12\u4e2a\u5c0f\u65f6\u4fbf\u88ab\u62a2\u62ff\u4e00\u7a7a\u3002\u5728\u91c7\u8bbf\u4e2d\uff0c\u848b\u5168\u5229\u8bf4\uff1a\u201c\u4f5c\u4e3a\u4e00\u540d\u533b\u751f\uff0c\u4ec5\u4ec5\u89e3\u9664\u8001\u767e\u59d3\u7684\u75c5\u75db\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u5173\u952e\u8fd8\u662f\u6539\u53d8\u4ed6\u4eec\u7684\u601d\u60f3\u89c2\u5ff5\uff0c\u7531\u8981\u6c42\u6cbb\u7597\u75be\u75c5\u5230\u81ea\u53d1\u5730\u9884\u9632\u75be\u75c5\u624d\u662f\u6700\u91cd\u8981\u7684\u3002\u201d\n\u4e00\u6b21\u7684\u5ba3\u4f20\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u4e3a\u5de9\u56fa\u5ba3\u4f20\u6548\u679c\uff0c\u4e3a\u4e86\u4f7f\u4eba\u4eec\u61c2\u5f97\u66f4\u591a\u7684\u53e3\u8154\u9884\u9632\u4fdd\u5065\u77e5\u8bc6\uff0c\u848b\u5168\u5229\u8054\u7cfb\u5b9e\u9645\uff0c\u521b\u5efa\u4e86\u5929\u7b49\u9547\u536b\u751f\u9662\u201c\uff11\uff0b\uff11\u75c5\u5386\u201d\u6a21\u5f0f\uff0c\u5373\u5728\u4e3a\u75c5\u4eba\u5199\u75c5\u5386\u7684\u540c\u65f6\uff0c\u5728\u75c5\u5386\u672c\u4e0a\u9644\u4e0a\u4e00\u4efd\u53e3\u8154\u75be\u75c5\u9884\u9632\u4fdd\u5065\u77e5\u8bc6\u7684\u8d44\u6599\u3002\u8fd9\u79cd\u5ba3\u4f20\u65b9\u6cd5\u9010\u6b65\u6539\u53d8\u4e86\u5f53\u5730\u7fa4\u4f17\u201c\u4e00\u75db\u5c31\u62d4\u7259\uff0c\u4e00\u4e86\u767e\u4e86\u201d\u7684\u9648\u65e7\u89c2\u5ff5\u3002\n\u201c\u51ed\u8bc1\u51cf\u514d\u533b\u7597\u8d39\u7528\u771f\u597d\uff01\u201d \n\uff13\u6708\uff17\u65e5\uff0c\u8bb0\u8005\u5728\u5929\u7b49\u9547\u536b\u751f\u9662\u53e3\u8154\u95e8\u8bca\u90e8\u770b\u5230\u4e00\u4f4d\u8001\u5927\u7237\u624b\u91cc\u62ff\u7740\u4e00\u672c\u8bc1\u5728\u6392\u961f\u770b\u75c5\u3002\u7b14\u8005\u4e8e\u662f\u4e0a\u524d\u548c\u8fd9\u4f4d\u8001\u5927\u7237\u6500\u8c08\u8d77\u6765\u3002\u8001\u5927\u7237\u544a\u8bc9\u8bb0\u8005\uff0c\u51ed\u8fd9\u672c\u8bc1\u53ef\u4ee5\u51cf\u514d\u90e8\u5206\u7684\u533b\u7597\u8d39\u7528\uff0c\u8fd9\u5168\u9760\u5c0f\u848b\u7684\u5e2e\u52a9\u3002\n\u539f\u6765\uff0c\uff12\uff10\uff10\uff14\u5e74\uff17\u6708\u7684\u4e00\u5929\uff0c\u848b\u5168\u5229\u6b63\u5728\u503c\u73ed\u7684\u65f6\u5019\uff0c\u4e00\u4f4d\u4e61\u4e0b\u8001\u4eba\u6342\u7740\u7ea2\u80bf\u7684\u5de6\u8138\uff0c\u5728\u95e8\u8bca\u5916\u5f98\u5f8a\u3002\u848b\u5168\u5229\u770b\u5230\u540e\uff0c\u5c31\u8d70\u5411\u90a3\u4f4d\u8001\u5927\u7237\uff0c\u95ee\u4ed6\u6709\u4ec0\u4e48\u9700\u8981\u5e2e\u52a9\u3002\u8001\u4eba\u770b\u4e86\u770b\u848b\u5168\u5229\uff0c\u5c0f\u58f0\u5730\u95ee\u9053\uff1a\u201c\u62d4\u7259\u8981\u591a\u5c11\u94b1\uff1f\u201d\u848b\u5168\u5229\u68c0\u67e5\u540e\u544a\u8bc9\u8001\u4eba\uff0c\u62d4\u7259\u9700\u8981\uff15\u5143\u94b1\uff0c\u4f46\u4ed6\u8fd9\u7259\u4e0d\u7528\u62d4\uff0c\u53ef\u4ee5\u6cbb\u7597\u540e\u518d\u8865\u3002\u7136\u800c\u90a3\u4f4d\u8001\u4eba\u8bf4\uff0c\u4f60\u4eec\u7684\u6536\u8d39\u592a\u9ad8\u4e86\uff0c\u6ca1\u94b1\u770b\u7259\uff01\u848b\u5168\u5229\u514d\u6536\u4e86\u4ed6\u7684\u533b\u7597\u8d39\u7528\uff0c\u624d\u4f7f\u5f97\u8fd9\u4f4d\u8001\u4eba\u628a\u7259\u75c5\u6cbb\u597d\u3002\u7259\u6cbb\u597d\u540e\uff0c\u8001\u4eba\u6d41\u7740\u6cea\u8bf4\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\u554a\uff01\u201d\n\u201c\u8fd9\u4ef6\u4e8b\u8ba9\u6211\u611f\u89e6\u9887\u6df1\u3002\u662f\u554a\uff0c\u8fd8\u6709\u5f88\u591a\u7684\u4eba\uff0c\u7279\u522b\u662f\u5b64\u5be1\u8001\u4eba\uff0c\u8fde\uff15\u5143\u94b1\u7684\u6cbb\u7259\u8d39\u90fd\u6ca1\u6709\uff0c\u4ed6\u4eec\u4ecd\u5728\u5fcd\u53d7\u7740\u75c5\u75db\u7684\u6298\u78e8\u3002\u6211\u5728\u5fc3\u5e95\u4e0d\u65ad\u5730\u95ee\u81ea\u5df1\uff0c\u6211\u80fd\u4e3a\u4ed6\u4eec\u5b9e\u5b9e\u5728\u5728\u5730\u505a\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d\u5728\u91c7\u8bbf\u4e2d\uff0c\u848b\u5168\u5229\u8fd9\u6837\u5bf9\u8bb0\u8005\u8bf4\u3002\u540e\u6765\u5728\u848b\u5168\u5229\u7684\u52aa\u529b\u4e0b\uff0c\u5929\u7b49\u9547\u536b\u751f\u9662\u7387\u5148\u5b9e\u65bd\u4e86\u90e8\u5206\u4eba\u201c\u51ed\u8bc1\u51cf\u514d\u533b\u7597\u8d39\u201d\u7684\u65b9\u6848\uff0c\u5373\u5b64\u5be1\u8001\u4eba\u3001\u6b8b\u75be\u4eba\u51ed\u8bc1\u6216\u8005\u7279\u56f0\u5bb6\u5ead\u7ecf\u6751\u59d4\u4f1a\u8bc1\u660e\uff0c\u53ef\u4eab\u53d7\u51cf\u3001\u514d\u6cbb\u7597\u7259\u75c5\u7684\u533b\u7597\u8d39\u7528\uff0c\u6b64\u7c7b\u75c5\u4eba\u7531\u848b\u5168\u5229\u63a5\u6cbb\uff0c\u51cf\u514d\u7684\u8fd9\u90e8\u5206\u533b\u7597\u8d39\u7528\u7531\u848b\u5168\u5229\u4ece\u81ea\u5df1\u7684\u751f\u6d3b\u8865\u8d34\u4e2d\u62bd\u51fa\u90e8\u5206\u652f\u4ed8\u3002\u8fd9\u4e00\u4e3e\u63aa\u6df1\u53d7\u8001\u767e\u59d3\u7684\u6b22\u8fce\uff0c\u4e5f\u4f7f\u66f4\u591a\u7684\u4eba\u8131\u79bb\u4e86\u7259\u75c5\u7684\u75db\u82e6\u3002\n\u5e2e\u52a9\u8d2b\u56f0\u5b66\u751f\u5706\u6c42\u5b66\u68a6 \n\u534f\u52a9\u201c\u5fc3\u5b88\u5bb6\u56ed\u201d\u5e2e\u52a9\u5931\u5b66\u513f\u7ae5\u53ca\u9752\u5c11\u5e74\uff0c\u662f\u848b\u5168\u5229\u5728\u5929\u7b49\u53bf\u670d\u52a1\u5de5\u4f5c\u4e2d\u7684\u53e6\u4e00\u4e3b\u9898\u3002\u5728\u670d\u52a1\u671f\u95f4\uff0c\u848b\u5168\u5229\u9664\u4e86\u79ef\u6781\u5730\u53c2\u52a0\u533b\u9662\u4e0e\u53bf\u9632\u75ab\u7ad9\u8054\u5408\u5b9e\u65bd\u7684\u63a5\u79cd\uff21\uff0b\uff23\u6d41\u8111\u75ab\u82d7\u548c\u5c0f\u513f\u9ebb\u75f9\u7cd6\u4e38\u53d1\u653e\u7b49\u9632\u75ab\u5de5\u4f5c\u5916\uff0c\u8fd8\u4e0e\u540c\u6765\u7684\u5fd7\u613f\u8005\u4eec\u653e\u5f03\u4f11\u606f\u65e5\uff0c\u9a91\u7740\u81ea\u884c\u8f66\uff0c\u6df1\u5165\u6751\u5c6f\u8c03\u67e5\u4e86\u89e3\u5f53\u5730\u7684\u6559\u80b2\u5de5\u4f5c\u60c5\u51b5\uff0c\u8d70\u8bbf\u4e86\u6570\u5341\u4f4d\u7279\u56f0\u5b66\u751f\u5bb6\u5ead\u3002\u4ed6\u4eec\u90a3\u7834\u7816\u70c2\u74e6\u6784\u6210\u7684\u5c0f\u5c4b\uff0c\u4ed6\u4eec\u90a3\u4e00\u53cc\u53cc\u6e34\u671b\u77e5\u8bc6\u7684\u53cc\u773c\uff0c\u8fd8\u6709\u4ed6\u4eec\u53cc\u4eb2\u90a3\u7126\u6025\u800c\u53c8\u65e0\u5948\u7684\u795e\u60c5\uff0c\u6df1\u6df1\u9707\u64bc\u4e86\u848b\u5168\u5229\u3002\u4e8e\u662f\uff0c\u848b\u5168\u5229\u4ece\u81ea\u5df1\u5fae\u8584\u7684\u751f\u6d3b\u8865\u8d34\u4e2d\u7701\u5403\u4fed\u7528\u62ff\u51fa\u90e8\u5206\u6765\uff0c\u8d44\u52a9\u4e00\u540d\u5bb6\u5728\u8fdb\u7ed3\u9547\u8fdb\u7ed3\u6751\u5bff\u5c6f\u7684\u521d\u4e00\u5b66\u751f\u519c\u7389\u6885\u3002\n\u848b\u5168\u5229\u77e5\u9053\u4ec5\u4ec5\u4e00\u4e2a\u4eba\u6216\u51e0\u4e2a\u4eba\u7684\u529b\u91cf\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\u3002\u968f\u540e\u4ed6\u548c\u5fd7\u613f\u8005\u4eec\u4ed4\u7ec6\u5730\u6574\u7406\u4e86\u7279\u56f0\u5b66\u751f\u5bb6\u5ead\u72b6\u51b5\u8d44\u6599\uff0c\u5e76\u4e0a\u4f20\u201c\u5fc3\u5b88\u5bb6\u56ed\u201d\u7f51\u7ad9\u3002\u848b\u5168\u5229\u7b49\u5fd7\u613f\u8005\u4e0a\u4f20\u7684\u7279\u56f0\u751f\u7684\u8d44\u6599\u5f15\u8d77\u4e86\u7f51\u4e0a\u8d44\u52a9\u8005\u7684\u6ce8\u610f\uff0c\u4ed6\u4eec\u7eb7\u7eb7\u62ff\u51fa\u8d44\u91d1\u8d44\u52a9\u8fd9\u4e9b\u7279\u56f0\u751f\uff0c\u4f7f\u8d44\u6599\u4e0a\u4f20\u81f3\u201c\u5fc3\u5b88\u5bb6\u56ed\u201d\u7f51\u7ad9\u7684\u5927\u90e8\u5206\u7279\u56f0\u5bb6\u5ead\u7684\u5b69\u5b50\u5f97\u5230\u4e86\u8d44\u52a9\uff0c\u89e3\u51b3\u4e86\u5b66\u6742\u8d39\u53ca\u5728\u6821\u7684\u57fa\u672c\u751f\u6d3b\u95ee\u9898\u3002\u5728\u91c7\u8bbf\u4e2d\uff0c\u848b\u5168\u5229\u6df1\u6709\u611f\u89e6\u5730\u8bf4\uff0c\u201c\u6388\u4eba\u73ab\u7470\uff0c\u624b\u6709\u4f59\u9999\u201d\u3002\u6bcf\u548c\u4e00\u4e2a\u8d44\u52a9\u8005\u901a\u8bdd\uff0c\u6bcf\u89e3\u51b3\u4e00\u4e2a\u5931\u5b66\u5b69\u5b50\u7684\u5b66\u8d39\uff0c\u4ed6\u90fd\u4f1a\u6fc0\u52a8\u4e07\u5206\u3002\u4ed6\u771f\u5fc3\u5e0c\u671b\uff0c\u80fd\u6709\u66f4\u591a\u7684\u4eba\u4e3a\u5931\u5b66\u7684\u5b69\u5b50\u91cd\u8fd4\u6821\u56ed\u800c\u4f38\u51fa\u5145\u6ee1\u7231\u5fc3\u7684\u53cc\u624b\u3002\n\u8c08\u5230\u8fd9\u4e00\u5e74\u6765\u7684\u6536\u83b7\u65f6\uff0c\u848b\u5168\u5229\u52a8\u60c5\u5730\u8bf4\uff1a\u201c\u5fd7\u613f\u670d\u52a1\u662f\u6211\u4e00\u751f\u4e2d\u6700\u96be\u5f97\u7684\u4eba\u751f\u7ecf\u5386\uff0c\u4e5f\u662f\u4e00\u6bb5\u5e73\u51e1\u800c\u6fc0\u60c5\u71c3\u70e7\u3001\u4ee4\u6211\u7ec8\u8eab\u96be\u5fd8\u7684\u5c81\u6708\u3002\u8fd9\u91cc\u6709\u6211\u7684\u4ed8\u51fa\uff0c\u6709\u6211\u9752\u6625\uff0c\u4e5f\u6709\u7740\u6211\u65e0\u6570\u7684\u6536\u83b7\uff0c\u662f\u6211\u4eba\u751f\u6210\u957f\u8f68\u8ff9\u4e2d\u6700\u5149\u8f89\u707f\u70c2\u7684\u4e00\u6bb5\u3002\u505a\u4e00\u540d\u5fd7\u613f\u8005\uff0c\u670d\u52a1\u4e8e\u897f\u90e8\uff0c\u670d\u52a1\u4e8e\u5929\u7b49\uff0c\u8fd9\u662f\u6211\u65e0\u6094\u7684\u9009\u62e9\uff0c\u65e0\u6094\u7684\u9752\u6625\u3002\u201d"}, {"emotion": "neg", "id": "0117ab4c3dbf0cc585a28894ad60409358", "c_t": "\u4e91\u5357\u6c11\u65cf\u5927\u5b66\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u63a8\u52a8\u6c11\u65cf\u5730\u533a\u53d1\u5c55\u4e91\u5357\u7f51\u8baf\uff08\u8bb0\u8005 \u6768\u4e4b\u8f89\uff094\u670828\u65e5\uff0c\u4e91\u5357\u6c11\u65cf\u5927\u5b66\u9080\u8bf7\u5341\u4f59\u5bb6\u5730\u5dde\u534f\u8bae\u653f\u5e9c\u5355\u4f4d\u4e8e\u96e8\u82b1\u6821\u533a\u5f18\u9053\u697cB\u533a203\u5ba4\u5171\u540c\u53ec\u5f00\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u8ba2\u5355\u5f0f\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u7814\u8ba8\u4f1a\uff0c\u4ee5\u8fdb\u4e00\u6b65\u63a8\u52a8\u9ad8\u7b49\u6559\u80b2\u4e0e\u6c11\u65cf\u5730\u533a\u53d1\u5c55\u7684\u6709\u673a\u7ed3\u5408\u3002\n\u6587\u5c71\u5dde\u3001\u4e3d\u6c5f\u5e02\u3001\u6f9c\u6ca7\u53bf\u3001\u7984\u529d\u53bf\u3001\u77f3\u6797\u53bf\u3001\u53cc\u6c5f\u53bf\u7b49\u5341\u4f59\u5bb6\u5dde\u3001\u5e02\u3001\u53bf\u534f\u8bae\u5355\u4f4d\u9886\u5bfc\u51fa\u5e2d\u672c\u6b21\u7814\u8ba8\u4f1a\u3002\u4f1a\u8bae\u7531\u6821\u515a\u59d4\u59d4\u5458\u6bb5\u4e91\u5b66\u4e3b\u6301\u3002\n\u4e0e\u4f1a\u4ee3\u8868\u5c31\u5982\u4f55\u5b8c\u5584\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u8ba2\u5355\u5f0f\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u3001\u56fd\u5bb6\u6c11\u59d4\u201c\u53cc\u8bed\u4eba\u624d\u57f9\u8bad\u57fa\u5730\u201d\u5efa\u8bbe\u548c\u62d3\u5c55\u8bed\u8a00\u6587\u5316\u5408\u4f5c\u7a7a\u95f4\u3001\u4eba\u624d\u7684\u5c31\u4e1a\u7b49\u95ee\u9898\u8fdb\u884c\u4e86\u70ed\u70c8\u7684\u8ba8\u8bba\uff0c\u7eb7\u7eb7\u652f\u62db\uff0c\u4ee5\u5730\u65b9\u7ecf\u6d4e\u3001\u6587\u5316\u53d1\u5c55\u548c\u5355\u4f4d\u7684\u5207\u8eab\u9700\u8981\u63d0\u51fa\u4e86\u5b9d\u8d35\u5efa\u8bae\u548c\u5efa\u8bbe\u6027\u610f\u89c1\u3002\n\u4e91\u5357\u6c11\u65cf\u5927\u5b66\u6821\u515a\u59d4\u59d4\u5458\u674e\u5149\u660e\u8868\u793a\uff0c\u4e91\u5357\u6c11\u65cf\u5927\u5b66\u5728\u53cc\u8bed\u4eba\u624d\u57f9\u517b\u65b9\u9762\u5177\u6709\u5f97\u5929\u72ec\u539a\u7684\u4f18\u52bf\uff0c\u5bf9\u5c11\u6570\u6c11\u65cf\u8bed\u8a00\u7684\u7814\u7a76\u7279\u8272\u4f18\u52bf\u7a81\u51fa\u3002\u5982\u4f55\u5728\u53d1\u5c55\u4e2d\u6c42\u7a81\u7834\uff0c\u63a2\u8ba8\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u8ba2\u5355\u5f0f\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u5728\u65b0\u5f62\u52bf\u4e0b\u9762\u4e34\u7684\u56f0\u96be\u3001\u673a\u9047\u548c\u6311\u6218\uff0c\u5171\u8c0b\u5b8c\u5584\u548c\u521b\u65b0\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u7684\u65b0\u673a\u5236\u548c\u65b0\u505a\u6cd5\uff0c\u5bf9\u4e8e\u53cc\u8bed\u4eba\u624d\u7684\u57f9\u517b\u3001\u9ad8\u6c34\u5e73\u5927\u5b66\u5efa\u8bbe\u3001\u6c11\u65cf\u56e2\u7ed3\u8fdb\u6b65\u8fb9\u7586\u7e41\u8363\u7a33\u5b9a\u793a\u8303\u6821\u5efa\u8bbe\u610f\u4e49\u91cd\u5927\u3002\n\u4e91\u5357\u6c11\u65cf\u5927\u5b66\u6c11\u65cf\u6587\u5316\u5b66\u9662\u9662\u957f\u5218\u52b2\u8363\u4ecb\u7ecd\uff0c\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u8ba2\u5355\u5f0f\u53cc\u8bed\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u7684\u6210\u529f\uff0c\u79bb\u4e0d\u5f00\u5404\u5730\u5dde\u653f\u5e9c\u90e8\u95e8\u7684\u503e\u529b\u652f\u6301\u3001\u6821\u9886\u5bfc\u7684\u8fdc\u89c1\u5353\u8bc6\u3001\u4e00\u7ebf\u6559\u5e08\u5728\u6559\u5b66\u5de5\u4f5c\u4e2d\u7684\u5168\u529b\u4ee5\u8d74\u3002\u4f46\u8be5\u6a21\u5f0f\u4e2d\u4ecd\u5b58\u5728\u7684\u95ee\u9898\u548c\u65b0\u5f62\u52bf\u4e0b\u9762\u4e34\u7684\u65b0\u6311\u6218\uff0c\u5e0c\u671b\u901a\u8fc7\u6b64\u6b21\u7814\u8ba8\u4f1a\uff0c\u53cc\u65b9\u96c6\u601d\u5e7f\u76ca\uff0c\u5171\u540c\u52aa\u529b\uff0c\u4e0d\u65ad\u6df1\u5165\u5b8c\u5584\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u8ba2\u5355\u5f0f\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\uff0c\u521b\u65b0\u673a\u5236\uff0c\u52a0\u5f3a\u5408\u4f5c\uff0c\u4e3a\u5c11\u6570\u6c11\u65cf\u6587\u5316\u4f20\u627f\u548c\u533a\u57df\u7ecf\u6d4e\u53d1\u5c55\u505a\u51fa\u66f4\u5927\u8d21\u732e\u3002\n\u4e0e\u4f1a\u4ee3\u8868\u8ba4\u4e3a\uff0c\u672c\u6b21\u7814\u8ba8\u4f1a\u901a\u8fc7\u5bf9\u201c\u5b66\u6821+\u653f\u5e9c\u201d\u8ba2\u5355\u5f0f\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\u7684\u63a2\u8ba8\uff0c\u5bf9\u5b8c\u5584\u3001\u6539\u9769\u548c\u521b\u65b0\u8ba2\u5355\u5f0f\u4eba\u624d\u57f9\u517b\u6a21\u5f0f\uff0c\u62d3\u5c55\u5408\u4f5c\u9886\u57df\u548c\u5e73\u53f0\uff0c\u63d0\u9ad8\u4eba\u624d\u57f9\u517b\u8d28\u91cf\uff0c\u52a0\u5f3a\u62db\u751f\u548c\u5c31\u4e1a\u5de5\u4f5c\uff0c\u5c06\u8d77\u5230\u79ef\u6781\u4f5c\u7528\u3002"}, {"emotion": "neg", "id": "019a68809357099a3048e1e02d1e254c4b", "c_t": "\u201c\u7559\u5b88\u5973\u5b69\u201d\u8c2d\u6d77\u7f8e\u611f\u52a8\u4ebf\u4e07\u9752\u5c11\u5e74\u82e6\u96be\u7684\u7ae5\u5e74 \n\u8c2d\u6d77\u7f8e\u4eca\u5e7412\u5c81\uff0c\u51fa\u751f\u5728\u80a5\u4e1c\u53bf\u516d\u5bb6\u7548\u9547\uff0c\u8be5\u9547\u4f4d\u4e8e\u5168\u56fd\u4e94\u5927\u6de1\u6c34\u6e56\u4e4b\u2014\u7684\u5de2\u6e56\u4e4b\u6ee8\uff0c\u662f\u4e2a\u6c11\u98ce\u6df3\u6734\u3001\u4eba\u6770\u5730\u7075\u7684\u597d\u5730\u65b9\u3002 \n\u8c2d\u6d77\u7f8e\u542c\u7237\u7237\u8bf4\uff0c\u5728\u5979\u51fa\u751f\u516d\u4e2a\u6708\u7684\u65f6\u5019\uff0c\u5916\u51fa\u6253\u5de5\u7684\u7236\u4eb2\u8c2d\u56fd\u5e73\u5c31\u5728\u4e00\u6b21\u4e8b\u6545\u4e2d\uff0c\u4e3a\u4e86\u6551\u5de5\u53cb\uff0c\u4e0d\u5e78\u88ab\u574d\u584c\u7684\u5e7f\u544a\u724c\u94c1\u67b6\u7838\u65ad\u4e86\u4e00\u53ea\u624b\u3002\u7531\u4e8e\u662f\u7ed9\u79c1\u4eba\u8001\u677f\u6253\u5de5\uff0c\u53c8\u6ca1\u6709\u7b7e\u8ba2\u52b3\u52a8\u7528\u5de5\u5408\u540c\uff0c\u5931\u53bb\u4e86\u4e00\u53ea\u624b\u7684\u7236\u4eb2\u4ec5\u5f97\u5230\u4e86\u4e03\u767e\u591a\u5143\u7684\u8d54\u507f\u6b3e\u3002\u770b\u7740\u5931\u53bb\u52b3\u52a8\u80fd\u529b\u7684\u4e08\u592b\uff0c\u8c2d\u6d77\u7f8e\u7684\u6bcd\u4eb2\u5fcd\u53d7\u4e0d\u4e86\u6c89\u91cd\u7684\u751f\u6d3b\u538b\u529b\uff0c\u79bb\u5bb6\u51fa\u8d70\uff0c\u81f3\u4eca\u97f3\u4fe1\u5168\u65e0\uff0c\u5979\u7684\u8111\u6d77\u91cc\u6839\u672c\u5c31\u6ca1\u6709\u6bcd\u4eb2\u7684\u5370\u8c61\u3002\u6bcd\u4eb2\u51fa\u8d70\u540e\uff0c\u5df2\u7ecf\u6b8b\u75be\u3001\u751f\u6d3b\u96be\u4ee5\u81ea\u7406\u7684\u7236\u4eb2\u53ea\u5f97\u5c06\u55f7\u55f7\u5f85\u54fa\u7684\u8c2d\u6d77\u7f8e\u9001\u7ed9\u7237\u7237\u3001\u5976\u5976\u629a\u517b\uff0c\u81ea\u5df1\u53ea\u8eab\u5916\u51fa\u6253\u5de5\u8c0b\u751f\u3002\u800c\u751f\u6d3b\u5728\u5408\u80a5\u5e02\u7684\u7237\u7237\u3001\u5976\u5976\u672c\u5c31\u6ca1\u6709\u56fa\u5b9a\u6536\u5165\uff0c\u7237\u7237\u9760\u2014\u8f86\u7834\u65e7\u7684\u6469\u6258\u8f66\u62c9\u5ba2\u6bcf\u5929\u8d5a\u4e2a\u5341\u5143\u516b\u5143\u7ef4\u6301\u751f\u6d3b\uff0c\u800c\u5976\u5976\u662f\u9760\u6361\u7834\u70c2\u5ea6\u65e5\u7684\u3002\u8c2d\u6d77\u7f8e\u7684\u5230\u6765\uff0c\u65e5\u5b50\u5c31\u66f4\u96be\u4e86\u3002 \n\u5728\u521a\u521a\u5b66\u4f1a\u8d70\u8def\u7684\u65f6\u5019\uff0c\u8c2d\u6d77\u7f8e\u5c31\u8ddf\u7740\u5976\u5976\u62fe\u7834\u70c2\uff0c\u662f\u5728\u5783\u573e\u4e0e\u7834\u70c2\u4e2d\u6e10\u6e10\u957f\u5927\u7684\u3002\u770b\u5230\u540c\u9f84\u7684\u5b69\u5b50\u4eec\u5728\u6d25\u6d25\u6709\u5473\u5730\u559d\u7740\u5404\u79cd\u6613\u62c9\u7f50\u996e\u6599\u65f6\uff0c\u5979\u53ea\u80fd\u6084\u6084\u5730\u7ad9\u5728\u4e00\u65c1\uff0c\u7b49\u4eba\u5bb6\u628a\u996e\u6599\u559d\u5b8c\uff0c\u5c06\u4e22\u5728\u4e00\u65c1\u7684\u6613\u62c9\u7f50\u6361\u8d70\u3002\u590f\u5929\uff0c\u5373\u4f7f\u5929\u6c14\u518d\u70ed\uff0c\u5976\u5976\u4e5f\u820d\u4e0d\u5f97\u4e70\u4e00\u7f50\u996e\u6599\u8ba9\u5979\u559d\uff0c\u6e34\u4e86\uff0c\u5c31\u53bb\u627e\u70b9\u81ea\u6765\u6c34\u559d\u3002\u201c\u6211\u8ba4\u8bc6\u5f88\u591a\u79cd\u6613\u62c9\u7f50\uff0c\u867d\u7136\u6ca1\u6709\u559d\u8fc7\uff0c\u4f46\u6211\u77e5\u9053\u5851\u6599\u7684\u3001\u94dd\u8d28\u7684\u3001\u94c1\u8d28\u7684\u6613\u62c9\u7f50\u5356\u51fa\u7684\u4ef7\u94b1\u4e0d\u4e00\u6837\u3002\u201d \n\u52e4\u594b\u800c\u597d\u5b66 \n\u6240\u6709\u6559\u8fc7\u8c2d\u6d77\u7f8e\u7684\u6559\u5e08\u90fd\u6709\u4e00\u4e2a\u5171\u540c\u7684\u611f\u89c9\uff0c\u90a3\u5c31\u662f\u5979\u52e4\u594b\u597d\u5b66\u597d\u95ee\u80af\u94bb\u3002\u5979\u7684\u8bed\u6587\u6559\u5e08\u5434\u7ea2\u519b\u5bf9\u8bb0\u8005\u8bf4\uff1a\u201c\u8c2d\u6d77\u7f8e\u662f\u4e2a\u5b66\u4e60\u523b\u82e6\u3001\u81ea\u89c9\u6027\u5f88\u5f3a\u7684\u597d\u5b66\u751f\uff0c\u9047\u5230\u4e0d\u61c2\u7684\u95ee\u9898\u4ece\u4e0d\u653e\u8fc7\uff0c\u6562\u4e8e\u8d28\u7591\uff0c\u56e0\u6b64\uff0c\u6211\u4e0a\u8bfe\u524d\u603b\u8981\u7ec6\u81f4\u5730\u5907\u8bfe\uff0c\u5426\u5219\uff0c\u4e0a\u8bfe\u65f6\u9762\u5bf9\u5979\u7684\u63d0\u95ee\u4f1a\u611f\u5230\u5f88\u68d8\u624b\u3002\u5728\u5979\u7684\u5e26\u52a8\u4e0b\uff0c\u73ed\u4e0a\u4e0d\u5c11\u5b66\u751f\u90fd\u559c\u6b22\u63d0\u95ee\u3002\u73b0\u5728\uff0c\u6211\u4e0a\u8bfe\u7684\u538b\u529b\u662f\u6bd4\u8f83\u5927\u7684\uff0c\u4f46\u4f5c\u4e3a\u6559\u5e08\u6559\u5230\u8fd9\u6837\u7684\u5b66\u751f\u4ece\u5185\u5fc3\u6765\u8bf4\u611f\u5230\u975e\u5e38\u6b23\u6170\u3002\u201d \n\u5c3d\u7ba1\u5bb6\u91cc\u7a77\uff0c\u53ef\u5230\u4e86\u8bfb\u4e66\u7684\u5e74\u9f84\uff0c\u5bb6\u91cc\u4eba\u8fd8\u662f\u5c06\u8c2d\u6d77\u7f8e\u9001\u8fdb\u4e86\u5b66\u6821\u3002\u8c2d\u6d77\u7f8e\u559c\u6b22\u8bfb\u4e66\uff0c\u51e0\u4e4e\u5230\u4e86\u75f4\u8ff7\u7a0b\u5ea6\u3002\u7531\u4e8e\u5bb6\u5883\u8d2b\u5bd2\uff0c\u5c11\u513f\u56fe\u4e66\u4ef7\u683c\u53c8\u4e0d\u83f2\uff0c\u4e70\u4e0d\u8d77\u56fe\u4e66\uff0c\u5979\u5c31\u4ece\u540c\u5b66\u624b\u4e2d\u501f\u6216\u4ece\u5b66\u6821\u56fe\u4e66\u5ba4\u501f\uff0c\u5b66\u6821\u56fe\u4e66\u5ba4\u56e0\u6b64\u6210\u4e86\u5979\u7684\u7b2c\u4e8c\u6559\u5ba4\u3002\u6bcf\u5468\u4e09\u4e0b\u5348\u662f\u5b66\u6821\u56fe\u4e66\u5ba4\u8c03\u6362\u4e66\u65f6\u95f4\uff0c\u8c2d\u6d77\u7f8e\u5fc5\u5230\u56fe\u4e66\u5ba4\u6362\u4e66\u3002\u8d1f\u8d23\u7ba1\u7406\u56fe\u4e66\u7684\u5de5\u4f5c\u4eba\u5458\u975e\u5e38\u719f\u6089\u5979\u3002\u6709\u4e00\u6b21\uff0c\u8d1f\u8d23\u7ba1\u7406\u56fe\u4e66\u7684\u5de5\u4f5c\u4eba\u5458\u4e00\u4e0b\u5348\u6ca1\u6709\u770b\u5230\u8c2d\u6d77\u7f8e\uff0c\u611f\u5230\u5f88\u5947\u602a\uff0c\u95ee\u4e86\u5176\u4ed6\u540c\u5b66\uff0c\u624d\u77e5\u9053\u5979\u751f\u75c5\u672a\u4e0a\u5b66\u3002\u6b21\u65e5\u65e9\u6668\uff0c\u8c2d\u6d77\u7f8e\u4e00\u5230\u5b66\u6821\uff0c\u5c31\u8dd1\u53bb\u627e\u738b\u4e3b\u4efb\u8981\u6c42\u6362\u4e66\uff0c\u738b\u4e3b\u4efb\u7b11\u7740\u8bf4\uff1a\u201c\u771f\u662f\u4e2a\u5c0f\u4e66\u75f4\u3002\u201d \n\u5b66\u6821\u56fe\u4e66\u5ba4\u7684\u4e66\u6bd5\u7adf\u6709\u9650\uff0c\u4ece\u540c\u5b66\u624b\u4e2d\u501f\u4e66\u5c31\u6210\u4e86\u8c2d\u6d77\u7f8e\u8bfb\u4e66\u7684\u53c8\u4e00\u9014\u5f84\uff0c\u9047\u5230\u540c\u5b66\u6709\u597d\u4e66\uff0c\u5979\u603b\u662f\u60f3\u65b9\u8bbe\u6cd5\u501f\u6765\u9605\u8bfb\u3002\u6709\u4e00\u5929\u653e\u5b66\u6bd4\u8f83\u65e9\uff0c\u8c2d\u6d77\u7f8e\u4e0e\u51e0\u4e2a\u540c\u5b66\u5230\u540c\u5b66\u5434\u5a1f\u5bb6\u53bb\u73a9\u3002\u5434\u5a1f\u5bb6\u6761\u4ef6\u6bd4\u8f83\u597d\uff0c\u7238\u7238\u3001\u5988\u5988\u7ed9\u5434\u5a1f\u4e70\u4e86\u8bb8\u591a\u73a9\u5177\uff0c\u51e0\u4e2a\u5c0f\u59d1\u5a18\u5230\u5bb6\u540e\u4fbf\u73a9\u8d77\u4e86\u6d0b\u5a03\u5a03\u3001\u79ef\u6728\uff0c\u53ef\u73a9\u7740\u73a9\u7740\uff0c\u4e0d\u89c1\u4e86\u8c2d\u6d77\u7f8e\uff0c\u540e\u6765\u5728\u5434\u5a1f\u7684\u4e66\u623f\u91cc\u627e\u5230\u4e86\u5979\uff0c\u53ea\u89c1\u5979\u6b63\u62ff\u7740\u300a\u5b89\u5f92\u751f\u7ae5\u8bdd\u96c6\u300b\u6d25\u6d25\u6709\u5473\u5730\u770b\u7740\uff0c\u8fde\u540c\u5b66\u4eec\u627e\u5979\u90fd\u6ca1\u6709\u53d1\u89c9\u3002\u540c\u5b66\u4eec\u7b11\u7740\u8bf4\uff1a\u201c\u8c2d\u6d77\u7f8e\uff0c\u738b\u4e3b\u4efb\u4e00\u70b9\u90fd\u6ca1\u8bf4\u9519\uff0c\u4f60\u771f\u662f\u4e2a\u5c0f\u4e66\u75f4\u3002\u201d\u4e34\u8d70\u65f6\uff0c\u8c2d\u6d77\u7f8e\u501f\u8d70\u4e86\u300a\u5b89\u5f92\u751f\u7ae5\u8bdd\u96c6\u300b\uff0c\u800c\u201c\u5c0f\u4e66\u75f4\u201d\u7684\u7ef0\u53f7\u4e5f\u968f\u4e4b\u5728\u540c\u5b66\u4eec\u4e2d\u53eb\u5f00\u4e86\u3002\n{IMG:1}\n\u56fe\u4e3a\u8c2d\u6d77\u7f8e\u5728\u5c55\u793a\u5979\u83b7\u5f97\u7684\u5168\u56fd\u201c\u5341\u4f73\u5c11\u5148\u961f\u5458\u201d\u5956\u724c\n\u7559\u5b88\u5c0f\u961f\u957f \n\u81ea\u5df1\u7684\u4eb2\u8eab\u7ecf\u5386\uff0c\u4f7f\u8c2d\u6d77\u7f8e\u771f\u6b63\u611f\u53d7\u5230\u4e86\u7236\u6bcd\u90fd\u5728\u5916\u6253\u5de5\u7684\u5b69\u5b50\u662f\u591a\u4e48\u7684\u5b64\u72ec\u3002\u770b\u7740\u8eab\u8fb9\u8bb8\u591a\u548c\u81ea\u5df1\u4e00\u6837\u7684\u7559\u5b88\u5b69\u5b50\uff0c\u5979\u51b3\u5b9a\u5728\u73ed\u4e0a\u6210\u7acb\u4e00\u4e2a\u201c\u7559\u5b88\u5b69\u5b50\u5c0f\u961f\u201d\u3002\u5728\u5c11\u5148\u961f\u8f85\u5bfc\u5458\u7684\u652f\u6301\u4e0b\uff0c\u5b66\u6821\u7b2c\u4e00\u4e2a\u201c\u7559\u5b88\u5b69\u5b50\u5c0f\u961f\u201d\u4fbf\u6210\u7acb\u4e86\uff0c\u5979\u88ab\u961f\u5458\u4eec\u9009\u4e3a\u5c0f\u961f\u957f\u3002\u5728\u8f85\u5bfc\u5458\u548c\u8c2d\u6d77\u7f8e\u7684\u5e26\u9886\u4e0b\uff0c\u201c\u7559\u5b88\u5b69\u5b50\u5c0f\u961f\u201d\u5f00\u5c55\u4e86\u5e2e\u52a9\u540e\u8fdb\u751f\u63d0\u9ad8\u6210\u7ee9\u3001\u5e2e\u52a9\u751f\u6d3b\u4e0a\u6709\u56f0\u96be\u7684\u540c\u5b66\u89e3\u51b3\u56f0\u96be\u3001\u5e2e\u52a9\u7167\u987e\u5b64\u5be1\u8001\u4eba\u7b49\u4e00\u7cfb\u5217\u7684\u6d3b\u52a8\u3002\u961f\u5458\u4e4b\u95f4\u4e5f\u4e92\u76f8\u5e2e\u52a9\uff0c\u5e2e\u5bb6\u91cc\u5e72\u519c\u6d3b\u3001\u505a\u5bb6\u52a1\uff0c\u81ea\u5f3a\u81ea\u7acb\uff0c\u76f8\u4e92\u5b66\u4e60\uff0c\u5171\u540c\u8fdb\u6b65\u3002\u5728\u5c0f\u961f\u91cc\uff0c\u5c3d\u7ba1\u751f\u6d3b\u5f88\u5e73\u6de1\uff0c\u4f46\u5927\u5bb6\u90fd\u611f\u5230\u5f88\u5feb\u4e50\u3001\u5f88\u5145\u5b9e\u3002 \n\u80e1\u6708\u662f\u8c2d\u6d77\u7f8e\u7684\u540c\u684c\uff0c\u4e5f\u662f\u2014\u4f4d\u7559\u5b88\u5b69\u5b50\uff0c\u7236\u6bcd\u90fd\u5728\u5317\u4eac\u5e02\u6253\u5de5\uff0c\u5bb6\u5ead\u73af\u5883\u6bd4\u8f83\u8270\u96be\uff0c\u5b66\u4e60\u6210\u7ee9\u6bd4\u8f83\u5dee\u3002\u80e1\u6708\u548c\u8c2d\u6d77\u7f8e\u662f\u90bb\u6751\u5c45\u4f4f\uff0c\u4e0a\u5b66\u3001\u653e\u5b66\u7ecf\u5e38\u540c\u8def\u3002\u4e8e\u662f\uff0c\u8c2d\u6d77\u7f8e\u5c31\u5229\u7528\u4e0a\u5b66\u548c\u653e\u5b66\u7684\u65f6\u95f4\u4e0e\u80e1\u6708\u4ea4\u6d41\uff0c\u5e76\u8f85\u5bfc\u5979\u5b66\u4e60\u6570\u5b66\u3002\u7ecf\u8fc7\u4e00\u6bb5\u65f6\u95f4\u7684\u5e2e\u6276\uff0c\u80e1\u6708\u7684\u6570\u5b66\u6210\u7ee9\u6709\u4e86\u660e\u663e\u7684\u63d0\u9ad8\u3002\u5728\u4ea4\u5f80\u4e2d\uff0c\u4e24\u4e2a\u5c0f\u59d1\u5a18\u4e5f\u6210\u4e86\u77e5\u5fc3\u670b\u53cb\u3002 \n\u7559\u5b88\u5b69\u5b50\u8521\u9752\u9752\u7684\u5976\u5976\u4f53\u5f31\u591a\u75c5\uff0c\u56e0\u800c\u65f6\u5e38\u4e0a\u5b66\u8fdf\u5230\uff0c\u5f71\u54cd\u4e86\u5b66\u4e60\uff0c\u201c\u7559\u5b88\u5b69\u5b50\u5c0f\u961f\u201d\u5f97\u77e5\u540e\u5c31\u7ecf\u5e38\u5229\u7528\u8bfe\u4f59\u65f6\u95f4\u5e2e\u52a9\u8521\u9752\u9752\u7684\u5976\u5976\u5e72\u5bb6\u52a1\u3001\u626b\u5c4b\u5b50\u3001\u6d17\u8863\u670d\u3001\u6574\u7406\u83dc\u56ed\u7b49\uff0c\u961f\u5458\u4eec\u5728\u8f9b\u52b3\u4e2d\u65e2\u589e\u8fdb\u4e86\u53cb\u8c0a\uff0c\u4e5f\u4f11\u4f1a\u5230\u4e86\u5c0f\u96c6\u4f53\u7684\u6e29\u6696\u3002 \n\u5b5d\u987a\u7684\u5b59\u5973 \n\u8c2d\u6d77\u7f8e\u672c\u5728\u5408\u80a5\u5e02\u4e0a\u5b66\uff0c\u540e\u6765\u56e0\u4e3a\u96be\u4ee5\u7ef4\u6301\u751f\u8ba1\uff0c\u5976\u5976\u7559\u5728\u5e02\u5185\u6361\u7834\u70c2\uff0c\u5979\u8ddf\u7740\u7237\u7237\u56de\u4e61\u4e0b\u751f\u6d3b\u3002\u7237\u7237\u5df2 70\u591a\u5c81\u4e86\uff0c\u4f46\u4e3a\u4e86\u6f6d\u6d77\u7f8e\u80fd\u5728\u79bb\u5bb63.5\u516c\u91cc\u7684\u9547\u4e2d\u5fc3\u5c0f\u5b66\u8bfb\u4e66\uff0c\u7237\u7237\u628a\u62bd\u4e86\u4e00\u8f88\u5b50\u7684\u70df\u6212\u4e86\uff0c\u7701\u4e0b\u94b1\u4f5c\u8c2d\u6d77\u7f8e\u4e0a\u5b66\u7684\u8f66\u8d39\u3002 \n\u7237\u7237\u5173\u5fc3\u5b59\u5973\uff0c\u5b59\u5973\u4e5f\u5341\u5206\u5b5d\u656c\u7237\u7237\u3002\u7236\u6bcd\u4e0d\u5728\u8eab\u8fb9\uff0c\u7237\u7237\u5e74\u7eaa\u53c8\u5f88\u5927\u4e86\uff0c\u5f53\u522b\u7684\u5b69\u5b50\u8fd8\u5728\u7236\u6bcd\u6000\u91cc\u6492\u5a07\u7684\u65f6\u5019\uff0c\u8c2d\u6d77\u7f8e\u5df2\u7ecf\u77e5\u9053\u4e00\u5207\u8981\u4f9d\u9760\u81ea\u5df1\uff0c\u8981\u5e2e\u7237\u7237\u51cf\u8f7b\u8d1f\u62c5\u3002\u6bcf\u5929\u6e05\u6668\uff0c\u5979\u65e9\u65e9\u5730\u8d77\u5e8a\uff0c\u6253\u626b\u5c4b\u5b50\u548c\u5ead\u9662\uff0c\u653e\u517b\u5bb6\u79bd\uff0c\u6709\u65f6\u8fd8\u628a\u8863\u670d\u6d17\u5e72\u51c0\u3001\u505a\u597d\u65e9\u996d\uff0c\u5403\u8fc7\u65e9\u996d\u4fbf\u81ea\u5df1\u4e0a\u5b66\u4e86\u3002\u6bcf\u5929\u653e\u5b66\uff0c\u5979\u4ece\u4e0d\u5728\u5916\u9762\u9017\u7559\uff0c\u6293\u7d27\u65f6\u95f4\u56de\u5bb6\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u7237\u7237\u8fd8\u5728\u7530\u91cc\u5e72\u519c\u6d3b\uff0c\u81ea\u5df1\u5feb\u70b9\u56de\u5bb6\u591a\u5c11\u4e5f\u80fd\u5e2e\u7237\u7237\u505a\u70b9\u4e8b\u3002\u5e73\u65f6\uff0c\u8c2d\u6d77\u7f8e\u2014\u505a\u5b8c\u4f5c\u4e1a\uff0c\u4fbf\u5e2e\u7237\u7237\u68b3\u68b3\u5934\u3001\u6376\u6376\u80cc\uff0c\u6709\u7a7a\u8fd8\u5e2e\u7237\u7237\u5288\u67f4\u3001\u505a\u519c\u6d3b\u3002 \n\u6709\u4e00\u6b21\uff0c\u7237\u7237\u7a81\u7136\u751f\u75c5\u4e86\uff0c\u8c2d\u6d77\u7f8e\u6025\u5f97\u6ca1\u529e\u6cd5\uff0c\u8bf7\u90bb\u5c45\u5bb6\u7684\u53d4\u53d4\u5e2e\u5fd9\u628a\u7237\u7237\u9001\u8fdb\u4e86\u533b\u9662\u3002\u7237\u7237\u7684\u75c5\u60c5\u7a33\u5b9a\u540e\u56de\u5230\u5bb6\u91cc\uff0c\u8c2d\u6d77\u7f8e\u5fd9\u524d\u5fd9\u540e\u6089\u5fc3\u7167\u6599\u7237\u7237\u3002\u4e0a\u5b66\u4e86\uff0c\u8c2d\u6d77\u7f8e\u5c31\u6258\u4ed8\u90bb\u5c45\u4eec\u5e2e\u52a9\u7167\u6599\u7237\u7237\uff0c\u4e00\u653e\u5b66\u5c31\u8d76\u56de\u5bb6\u770b\u62a4\u7237\u7237\uff0c\u7ecf\u8fc7\u51e0\u5929\u7684\u62a4\u7406\uff0c\u7237\u7237\u7ec8\u4e8e\u6062\u590d\u4e86\u5065\u5eb7\uff0c\u800c\u8c2d\u6d77\u7f8e\u5374\u7626\u4e86\u4e00\u5708\u3002\u2014\u63d0\u8d77\u8fd9\u4e9b\uff0c\u7237\u7237\u5c31\u8bf4\uff1a\u201c\u6211\u73b0\u5728\u6700\u6015\u7684\u5c31\u662f\u751f\u75c5\uff0c\u81ea\u5df1\u53d7\u7f6a\u4e0d\u8bf4\uff0c\u8fd8\u8fde\u7d2f\u5b59\u5973\u3002\u5979\u8fd8\u592a\u5c0f\u4e86\uff0c\u627f\u53d7\u4e0d\u8d77\u8fd9\u6837\u7684\u5bb6\u5ead\u91cd\u62c5\uff0c\u5979\u5e94\u8be5\u5b89\u5fc3\u5b66\u4e60\u624d\u5bf9\u5440\u3002\u751f\u5728\u8fd9\u6837\u7684\u5bb6\u5ead\uff0c\u771f\u5bf9\u4e0d\u8d77\u5979\u3002\u201d \n\u5728\u9881\u5956\u5927\u4f1a\u4e0a\uff0c\u8c2d\u6d77\u7f8e\u5bf9\u5168\u56fd\u7684\u7559\u5b88\u5c0f\u4f19\u4f34\u8bf4\uff0c\u53ef\u601c\u5929\u4e0b\u7236\u6bcd\u5fc3\u3002\u4f5c\u4e3a\u7559\u5b88\u5b69\u5b50\uff0c\u6211\u4eec\u5e94\u8be5\u7406\u89e3\u7236\u6bcd\u7684\u65e0\u5948\u3001\u4f53\u8c05\u7236\u6bcd\u7684\u8270\u96be\u3001\u4e86\u89e3\u7236\u6bcd\u7684\u5fc3\u58f0\u3002\u867d\u7136\u7236\u6bcd\u5728\u5916\u6253\u5de5\uff0c\u4f46\u6700\u5173\u5fc3\u6211\u4eec\u7684\u4ecd\u7136\u662f\u4ed6\u4eec\uff0c\u6211\u4eec\u5e94\u8be5\u628a\u7559\u5b88\u4f5c\u4e3a\u4e00\u79cd\u78e8\u7ec3\u81ea\u5df1\u7684\u673a\u4f1a\uff0c\u81ea\u5f3a\u81ea\u7acb\uff0c\u5065\u5eb7\u6210\u957f\uff0c\u8ba9\u7236\u6bcd\u5728\u5916\u5b89\u5fc3\u6253\u5de5\u3002 \n\uff08\u636e\u300a\u5b89\u5fbd\u9752\u5e74\u62a5\u300b\uff09"}, {"emotion": "neg", "id": "016ff200ae4f1c22d458730f1f561f5d36", "c_t": "9\u6708\u4efd74\u57ce\u7a7a\u6c14\u8d28\u91cf\u6392\u540d\uff1a\u53a6\u95e8\u6392\u540d\u7b2c\u4e03 \u798f\u5dde\u6392\u7b2c\u4e5d\u3000\u3000\u6628\u65e5\uff0c\u73af\u4fdd\u90e8\u53d1\u5e03\u4e86\u4eca\u5e749\u6708\u548c1~9\u6708\u5168\u56fd\u548c\u4eac\u6d25\u5180\u3001\u957f\u4e09\u89d2\u3001\u73e0\u4e09\u89d2\u533a\u57df\u53ca\u76f4\u8f96\u5e02\u3001\u7701\u4f1a\u57ce\u5e02\u3001\u8ba1\u5212\u5355\u5217\u5e02\u7a7a\u6c14\u8d28\u91cf\u72b6\u51b5\uff0c\u798f\u5dde\u5747\u5c45\u524d\u5341\u540d\u3002\n\u3000\u30009\u6708\uff0c74\u4e2a\u57ce\u5e02\u7a7a\u6c14\u8d28\