201d\u7cfb\u5217\u62a5\u9053\uff0c\u56f4\u7ed5\u76f8\u5173\u95ee\u9898\u7ea6\u8bf7\u4e13\u5bb6\u5c55\u5f00\u8ba8\u8bba\uff0c\u656c\u8bf7\u5173\u6ce8\u3002\n\u3000\u3000\u521a\u521a\u95ed\u5e55\u7684\u515a\u7684\u5341\u4e5d\u5927\uff0c\u505a\u51fa\u4e86\u201c\u6211\u56fd\u7ecf\u6d4e\u5df2\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\u8fd9\u4e00\u5386\u53f2\u6027\u8bba\u65ad\u3002\n\u3000\u3000\u4e3a\u4ec0\u4e48\u8bf4\u4e2d\u56fd\u7ecf\u6d4e\u8f6c\u5411\u4e86\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff1f\u4e0e\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u76f8\u6bd4\uff0c\u65b0\u9636\u6bb5\u4f1a\u5448\u73b0\u51fa\u54ea\u4e9b\u65b0\u53d8\u5316\u3001\u65b0\u8d8b\u52bf\uff1f\u6211\u4eec\u5728\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u8fc7\u7a0b\u4e2d\u5982\u4f55\u6293\u4f4f\u673a\u9047\uff0c\u653b\u5173\u593a\u9698\uff0c\u51b3\u80dc\u5168\u9762\u5efa\u6210\u5c0f\u5eb7\u793e\u4f1a\uff0c\u5411\u201c\u4e24\u4e2a\u4e00\u767e\u5e74\u201d\u76ee\u6807\u633a\u8fdb\uff1f\n\u3000\u3000\u5982\u4f55\u7406\u89e3\u201c\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\uff1f\n\u3000\u3000\u5f3a\u8c03\u8d28\u91cf\u800c\u975e\u901f\u5ea6\uff0c\u5f3a\u8c03\u53d1\u5c55\u800c\u975e\u589e\u957f\n\u3000\u3000\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u4e2d\u56fd\u7ecf\u6d4e\u518d\u4e00\u6b21\u505a\u51fa\u660e\u786e\u7684\u8def\u5f84\u9009\u62e9\u3002\n\u3000\u3000\u2014\u2014\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\uff0c\u5b9e\u73b0\u96c6\u7ea6\u578b\u7ecf\u6d4e\u589e\u957f\uff0c\u63d0\u9ad8\u5168\u8981\u7d20\u751f\u4ea7\u7387\u3002\n\u3000\u3000\u201c\u2018\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u2019\u8fd9\u4e00\u8bba\u65ad\u7684\u672c\u8d28\u542b\u4e49\uff0c\u5c31\u662f\u6211\u56fd\u7ecf\u6d4e\u5df2\u7ecf\u4ece\u4e3b\u8981\u4f9d\u9760\u589e\u52a0\u7269\u8d28\u8d44\u6e90\u6d88\u8017\u5b9e\u73b0\u7684\u7c97\u653e\u578b\u9ad8\u901f\u589e\u957f\uff0c\u8f6c\u53d8\u4e3a\u4e3b\u8981\u4f9d\u9760\u6280\u672f\u8fdb\u6b65\u3001\u6539\u5584\u7ba1\u7406\u548c\u63d0\u9ad8\u52b3\u52a8\u8005\u7d20\u8d28\u5b9e\u73b0\u7684\u96c6\u7ea6\u578b\u589e\u957f\u3002\u201d\u5728\u4e2d\u56fd\u56fd\u9645\u7ecf\u6d4e\u4ea4\u6d41\u4e2d\u5fc3\u5e38\u52a1\u526f\u7406\u4e8b\u957f\u90d1\u65b0\u7acb\u770b\u6765\uff0c\u7ecf\u6d4e\u4ece\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u662f\u5212\u65f6\u4ee3\u7684\u53d8\u5316\u3002\n\u3000\u3000\u201c\u4e2d\u56fd\u7684\u7ecf\u6d4e\u53d1\u5c55\uff0c\u4e0d\u518d\u7ea0\u7ed3\u7ecf\u6d4e\u589e\u901f\u5feb\u4e00\u70b9\u8fd8\u662f\u6162\u4e00\u70b9\uff0c\u800c\u662f\u4ee5\u63d0\u9ad8\u5168\u8981\u7d20\u751f\u4ea7\u7387\u4e3a\u6293\u624b\uff0c\u63a8\u52a8\u7ecf\u6d4e\u53d1\u5c55\u8d28\u91cf\u53d8\u9769\u3001\u6548\u7387\u53d8\u9769\u3001\u52a8\u529b\u53d8\u9769\u3002\u201d\u90d1\u65b0\u7acb\u5f3a\u8c03\u3002\n\u3000\u30002016\u5e74\uff0c\u6211\u56fd\u201c\u767e\u5f3a\u53bf\u201d\u4ee5\u5360\u5168\u56fd2%\u7684\u571f\u5730\u9762\u79ef\u30016%\u7684\u4eba\u53e3\u521b\u9020\u4e86\u8d85\u8fc7\u5168\u56fd11%\u7684GDP\uff1b\u57ce\u9547\u5c45\u6c11\u4eba\u5747\u53ef\u652f\u914d\u6536\u516539165\u5143\uff0c\u6bd4\u5168\u56fd\u5e73\u5747\u6c34\u5e73\u9ad8\u51fa16.5%\uff1b\u4eba\u5747\u793e\u4f1a\u6d88\u8d39\u54c1\u96f6\u552e\u603b\u989d\u4e3a3.1\u4e07\u5143\uff0c\u6bd4\u5168\u56fd\u5e73\u5747\u6c34\u5e73\u9ad8\u51fa29%\u3002\u7eb5\u89c2\u201c\u767e\u5f3a\u53bf\u201d\u7684\u53d1\u5c55\u8981\u8bc0\uff0c\u65e0\u4e00\u4e0d\u662f\u4ece\u539f\u6709\u8d44\u6e90\u3001\u6295\u8d44\u62c9\u52a8\u7684\u7c97\u653e\u578b\u53d1\u5c55\u65b9\u5f0f\uff0c\u5411\u521b\u65b0\u9a71\u52a8\u7684\u96c6\u7ea6\u578b\u53d1\u5c55\u65b9\u5f0f\u8f6c\u53d8\uff0c\u8fce\u6765\u4e86\u5168\u65b0\u7684\u53d1\u5c55\u5951\u673a\u3002\n\u3000\u3000\u201c\u2018\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u2019\u8868\u73b0\u5728\u4ea7\u4e1a\u7ed3\u6784\u4e0a\uff0c\u662f\u7531\u8d44\u6e90\u5bc6\u96c6\u578b\u3001\u52b3\u52a8\u5bc6\u96c6\u578b\u4ea7\u4e1a\u4e3a\u4e3b\u5411\u6280\u672f\u5bc6\u96c6\u578b\u3001\u77e5\u8bc6\u5bc6\u96c6\u578b\u4ea7\u4e1a\u4e3a\u4e3b\u8f6c\u53d8\uff1b\u5728\u4ea7\u54c1\u7ed3\u6784\u4e0a\uff0c\u7531\u4f4e\u6280\u672f\u542b\u91cf\u3001\u4f4e\u9644\u52a0\u503c\u4ea7\u54c1\u4e3a\u4e3b\u5411\u9ad8\u6280\u672f\u542b\u91cf\u3001\u9ad8\u9644\u52a0\u503c\u4ea7\u54c1\u4e3a\u4e3b\u8f6c\u53d8\uff1b\u5728\u7ecf\u6d4e\u6548\u76ca\u4e0a\uff0c\u7531\u9ad8\u6210\u672c\u3001\u4f4e\u6548\u76ca\u5411\u4f4e\u6210\u672c\u3001\u9ad8\u6548\u76ca\u7684\u65b9\u5411\u8f6c\u53d8\uff1b\u5728\u751f\u6001\u73af\u5883\u4e0a\uff0c\u7531\u9ad8\u6392\u653e\u3001\u9ad8\u6c61\u67d3\u5411\u5faa\u73af\u7ecf\u6d4e\u548c\u73af\u5883\u53cb\u597d\u578b\u7ecf\u6d4e\u8f6c\u53d8\u3002\u6700\u7ec8\u5c06\u4f53\u73b0\u4e3a\u56fd\u5bb6\u7ecf\u6d4e\u5b9e\u529b\u4e0d\u65ad\u589e\u5f3a\uff0c\u5c45\u6c11\u6536\u5165\u5f97\u5230\u8f83\u5feb\u589e\u52a0\u3002\u201d\u90d1\u65b0\u7acb\u8bf4\u3002\n\u3000\u3000\u2014\u2014\u5145\u5b9e\u53d1\u5c55\u5185\u6db5\uff0c\u4ee5\u66f4\u5145\u5206\u66f4\u5e73\u8861\u4e3a\u76ee\u6807\uff0c\u589e\u8fdb\u4eba\u6c11\u83b7\u5f97\u611f\u3002\n\u3000\u3000\u4ece\u7ed3\u679c\u5bfc\u5411\u770b\uff0c\u201c\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\u66f4\u7a81\u51fa\u767e\u59d3\u7684\u83b7\u5f97\u611f\u3002\u4e2d\u56fd\u793e\u4f1a\u79d1\u5b66\u7814\u7a76\u9662\u7ecf\u6d4e\u7814\u7a76\u6240\u6240\u957f\u9ad8\u57f9\u52c7\u6307\u51fa\uff0c\u201c\u9ad8\u901f\u201d\u6307\u5411\u6570\u91cf\u6216\u89c4\u6a21\uff0c\u5f80\u5f80\u662f\u7ecf\u6d4e\u53d1\u5c55\u521d\u7ea7\u9636\u6bb5\u7684\u76ee\u6807\uff0c\u7528\u4ee5\u6807\u6ce8\u201c\u5feb\u4e0d\u5feb\u201d\uff1b\u201c\u9ad8\u8d28\u91cf\u201d\u5219\u662f\u7ecf\u6d4e\u53d1\u5c55\u8fbe\u5230\u4e00\u5b9a\u6c34\u5e73\u4e4b\u540e\u624d\u4f1a\u6709\u7684\u76ee\u6807\uff0c\u5f3a\u8c03\u8d28\u91cf\u548c\u6548\u76ca\uff0c\u662f\u56de\u7b54\u201c\u597d\u4e0d\u597d\u201d\u3002\n\u3000\u3000\u201c\u589e\u957f\u4ec5\u6307\u7ecf\u6d4e\u603b\u91cf\u7684\u6269\u5f20\uff1b\u53d1\u5c55\u7684\u5185\u6db5\u5219\u66f4\u4e3a\u4e30\u5bcc\uff0c\u662f\u4e3a\u4e86\u6ee1\u8db3\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u3002\u201d\u9ad8\u57f9\u52c7\u8bf4\uff0c\u6211\u56fd\u5df2\u89e3\u51b3\u4e86\u5341\u51e0\u4ebf\u4eba\u7684\u6e29\u9971\u95ee\u9898\uff0c\u603b\u4f53\u4e0a\u5b9e\u73b0\u5c0f\u5eb7\uff0c\u4e0d\u4e45\u5c06\u5168\u9762\u5efa\u6210\u5c0f\u5eb7\u793e\u4f1a\uff0c\u4eba\u6c11\u4e0d\u4ec5\u5bf9\u7269\u8d28\u6587\u5316\u751f\u6d3b\u63d0\u51fa\u4e86\u66f4\u9ad8\u8981\u6c42\uff0c\u800c\u4e14\u5728\u6c11\u4e3b\u3001\u6cd5\u6cbb\u3001\u516c\u5e73\u3001\u6b63\u4e49\u3001\u5b89\u5168\u3001\u73af\u5883\u7b49\u65b9\u9762\u7684\u8981\u6c42\u65e5\u76ca\u589e\u957f\u3002\u56e0\u6b64\uff0c\u7ecf\u6d4e\u53d1\u5c55\u8981\u4ece\u5355\u7eaf\u8ffd\u6c42\u603b\u91cf\u6269\u5c55\uff0c\u8f6c\u53d8\u4e3a\u9002\u5e94\u4eba\u4eec\u66f4\u9ad8\u6807\u51c6\u7684\u3001\u66f4\u52a0\u591a\u6837\u5316\u7684\u9700\u6c42\u3002\n\u3000\u3000\u4e3a\u4f55\u73b0\u5728\u63d0\u51fa\u201c\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\uff1f\n\u3000\u3000\u76f4\u9762\u65b0\u65f6\u4ee3\u77db\u76fe\uff0c\u7a81\u7834\u53d1\u5c55\u74f6\u9888\n\u3000\u3000\u5386\u53f2\u5730\u770b\uff0c\u7ecf\u6d4e\u53d1\u5c55\u5728\u91cd\u590d\u201c\u7e41\u8363\u2014\u8870\u9000\u2014\u590d\u82cf\u2014\u589e\u957f\u201d\u6ce2\u6d6a\u5f0f\u524d\u8fdb\u7684\u540c\u65f6\uff0c\u4ee5\u79d1\u6280\u3001\u4eba\u529b\u8d44\u6e90\u7b49\u8981\u7d20\u8fdb\u6b65\u4e3a\u65b0\u52a8\u80fd\uff0c\u89e3\u51b3\u5236\u7ea6\u7ecf\u6d4e\u53d1\u5c55\u7684\u6df1\u5c42\u6b21\u77db\u76fe\uff0c\u5b9e\u73b0\u5411\u4e0a\u7684\u603b\u8d8b\u52bf\u3002\u201c\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\uff0c\u662f\u6211\u4eec\u76f4\u9762\u65b0\u65f6\u4ee3\u4e3b\u8981\u77db\u76fe\uff0c\u9002\u5e94\u7ecf\u6d4e\u65b0\u5e38\u6001\u7684\u5fc5\u987b\u9009\u62e9\u548c\u7d27\u8feb\u4efb\u52a1\u3002\n\u3000\u3000\u2014\u2014\u73b0\u9636\u6bb5\u7684\u4e3b\u8981\u77db\u76fe\u8981\u6c42\u6211\u4eec\u653e\u5f03\u901f\u5ea6\u504f\u597d\uff0c\u91cd\u89c6\u53d1\u5c55\u8d28\u91cf\u3002\n\u3000\u3000\u9ad8\u57f9\u52c7\u8ba4\u4e3a\uff0c\u201c\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\u8fd9\u4e00\u91cd\u8981\u8bba\u65ad\uff0c\u662f\u4e0e\u6211\u56fd\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\u5df2\u7ecf\u8f6c\u53d8\u4e3a\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u548c\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u7684\u53d1\u5c55\u4e4b\u95f4\u7684\u77db\u76fe\u76f8\u4e00\u81f4\u7684\u3002\n\u3000\u3000\u76ee\u524d\uff0c\u6211\u56fd\u80fd\u6e90\u3001\u539f\u6750\u6599\u6d88\u8017\u603b\u91cf\u504f\u5927\uff0c\u5355\u4f4dGDP\u80fd\u6e90\u6d88\u8017\u504f\u9ad8\u3002\u201c\u7c97\u653e\u578b\u7ecf\u6d4e\u589e\u957f\u5bf9\u8d44\u6e90\u3001\u73af\u5883\u9020\u6210\u8d8a\u6765\u8d8a\u5927\u7684\u538b\u529b\uff0c\u8fd9\u6837\u7684\u589e\u957f\u6a21\u5f0f\u96be\u4ee5\u6301\u7eed\u3002\u201d\u90d1\u65b0\u7acb\u6307\u51fa\u3002\n\u3000\u3000\u8fd1\u51e0\u5e74\uff0c\u534e\u5317\u3001\u534e\u4e1c\u5730\u533a\u51fa\u73b0\u5927\u9762\u79ef\u96fe\u973e\u5929\u6c14\uff0c\u4e00\u4e9b\u4e0e\u73af\u5883\u6c61\u67d3\u6709\u5173\u7684\u75be\u75c5\u53d1\u751f\u7387\u4e0a\u5347\u3002\u201c\u5982\u679c\u53d1\u5c55\u9700\u8981\u4ed8\u51fa\u4eba\u6c11\u5065\u5eb7\u7684\u4ee3\u4ef7\uff0c\u90a3\u5c31\u8fdd\u80cc\u4e86\u53d1\u5c55\u7684\u521d\u8877\u3002\u515a\u7684\u5341\u4e5d\u5927\u63d0\u51fa\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u5c31\u662f\u56de\u5e94\u767e\u59d3\u5173\u5207\uff0c\u76f4\u9762\u7ecf\u6d4e\u53d1\u5c55\u7684\u6df1\u5c42\u6b21\u77db\u76fe\u95ee\u9898\u3002\u201d\u90d1\u65b0\u7acb\u8bf4\u3002\n\u3000\u3000\u201c\u4e2d\u56fd\u7ecf\u6d4e\u4fdd\u6301\u4e86\u8fde\u7eed\u51e0\u5341\u5e74\u7684\u9ad8\u589e\u957f\uff0c\u521b\u9020\u4e86\u5927\u91cf\u793e\u4f1a\u8d22\u5bcc\uff0c\u4e2d\u56fd\u4eba\u4ece\u6539\u9769\u5f00\u653e\u4e2d\u83b7\u5f97\u4e86\u5de8\u5927\u7684\u5b9e\u60e0\u3002\u4f46\u7531\u4e8e\u5e02\u573a\u7ecf\u6d4e\u4f53\u5236\u4e0d\u5065\u5168\u3001\u5206\u914d\u5236\u5ea6\u4e0d\u591f\u5b8c\u5584\u7b49\u539f\u56e0\uff0c\u51fa\u73b0\u4e86\u4e00\u4e9b\u503c\u5f97\u5173\u6ce8\u7684\u793e\u4f1a\u7ecf\u6d4e\u95ee\u9898\uff0c\u5982\u57ce\u4e61\u5dee\u8ddd\u3001\u5730\u533a\u5dee\u8ddd\u3001\u884c\u4e1a\u5dee\u8ddd\u7b49\u4ecd\u7136\u5b58\u5728\uff0c\u4eba\u6c11\u7fa4\u4f17\u5bf9\u534f\u8c03\u53d1\u5c55\u3001\u5747\u8861\u53d1\u5c55\u7684\u8bc9\u6c42\u4e0d\u65ad\u63d0\u5347\u3002\u201d\u56fd\u5bb6\u4fe1\u606f\u5316\u4e13\u5bb6\u54a8\u8be2\u59d4\u5458\u4f1a\u59d4\u5458\u6c6a\u7389\u51ef\u8ba4\u4e3a\uff0c\u5fc5\u987b\u5173\u6ce8\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\u7684\u8f6c\u5316\uff0c\u8f6c\u6362\u751f\u4ea7\u65b9\u5f0f\u3001\u8c03\u6574\u5229\u76ca\u683c\u5c40\uff0c\u589e\u52a0\u6c11\u65cf\u7684\u51dd\u805a\u529b\u3001\u5411\u5fc3\u529b\uff0c\u6fc0\u53d1\u5168\u793e\u4f1a\u521b\u9020\u529b\u548c\u53d1\u5c55\u6d3b\u529b\uff0c\u4ece\u800c\u5b9e\u73b0\u66f4\u9ad8\u8d28\u91cf\u3001\u66f4\u6709\u6548\u7387\u3001\u66f4\u52a0\u516c\u5e73\u3001\u66f4\u53ef\u6301\u7eed\u7684\u53d1\u5c55\u3002\n\u3000\u3000\u2014\u2014\u4ece\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u4e5f\u662f\u4e3b\u52a8\u9002\u5e94\u7ecf\u6d4e\u65b0\u5e38\u6001\u3001\u7a81\u7834\u53d1\u5c55\u74f6\u9888\u7684\u73b0\u5b9e\u9009\u62e9\u3002\n\u3000\u3000\u201c\u6211\u4eec\u8981\u9002\u5e94\u7ecf\u6d4e\u589e\u901f\u6362\u6321\u7684\u65b0\u5e38\u6001\uff0c\u4fdd\u6301\u6218\u7565\u4e0a\u7684\u5e73\u5e38\u5fc3\u6001\u3002\u5fc5\u987b\u6de1\u5316\u901f\u5ea6\u2018\u6267\u5ff5\u2019\uff0c\u5f3a\u5316\u8d28\u91cf\u610f\u8bc6\uff0c\u4f18\u5316\u8981\u7d20\u6295\u5165\uff0c\u4fc3\u8fdb\u73b0\u4ee3\u5316\u5efa\u8bbe\u5404\u65b9\u9762\u76f8\u534f\u8c03\u3002\u201d\u4e2d\u56fd\u8d22\u653f\u79d1\u5b66\u7814\u7a76\u9662\u526f\u9662\u957f\u767d\u666f\u660e\u8ba4\u4e3a\uff0c\u4f9d\u9760\u4eba\u624d\u3001\u6280\u672f\u3001\u77e5\u8bc6\u3001\u4fe1\u606f\u7b49\u9ad8\u7ea7\u8981\u7d20\uff0c\u4ee5\u521b\u65b0\u9a71\u52a8\u6765\u6d88\u89e3\u5168\u7403\u5316\u7ea2\u5229\u548c\u6211\u56fd\u4eba\u53e3\u7ea2\u5229\u4e0d\u65ad\u4e0b\u964d\u7684\u4e0d\u5229\u5f71\u54cd\uff0c\u7a81\u7834\u8d44\u6e90\u80fd\u6e90\u3001\u73af\u5883\u7b49\u8981\u7d20\u7684\u74f6\u9888\u5236\u7ea6\uff0c\u5c3d\u5feb\u5b9e\u73b0\u53d1\u5c55\u65b0\u65e7\u52a8\u80fd\u7684\u8f6c\u6362\u3002\n\u3000\u3000\u8be5\u5982\u4f55\u201c\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\uff1f\n\u3000\u3000\u5df2\u7ecf\u521d\u6218\u544a\u6377\uff0c\u672a\u6765\u4ecd\u5e94\u575a\u6301\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\u3001\u4f18\u5316\u7ecf\u6d4e\u7ed3\u6784\u3001\u8f6c\u6362\u589e\u957f\u52a8\u529b\n\u3000\u3000\u201c\u4ece\u9ad8\u901f\u589e\u957f\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\uff0c\u6211\u4eec\u6709\u5e95\u6c14\u3001\u6709\u7ecf\u9a8c\u3002\u201d\u767d\u666f\u660e\u6307\u51fa\uff0c\u76ee\u524d\uff0c\u4f20\u7edf\u4ea7\u4e1a\u7115\u53d1\u751f\u673a\u3001\u65b0\u4ea7\u4e1a\u52bf\u5934\u5f3a\u52b2\u3001\u65b0\u7ecf\u6d4e\u52a0\u901f\u6210\u957f\uff0c\u6211\u56fd\u7ecf\u6d4e\u7ed3\u6784\u8c03\u6574\u6b63\u9010\u6e10\u8d70\u51fa\u9635\u75db\u671f\u3001\u6b65\u5165\u6536\u83b7\u671f\u3002\u201c2016\u5e74\u524d\u4e09\u5b63\u5ea6\u5de5\u4e1a\u4f01\u4e1a\u6240\u5f97\u7a0e5843\u4ebf\u5143\uff0c\u589e\u5e45\u4ec5\u4e3a0.6%\uff1b\u800c\u4eca\u5e74\u540c\u671f\u5de5\u4e1a\u4f01\u4e1a\u6240\u5f97\u7a0e\u540c\u6bd4\u589e\u957f21.3%\uff0c\u589e\u5e45\u7279\u522b\u660e\u663e\u3002\u8fd9\u91cc\u9762\u6709\u53bb\u5e74\u57fa\u6570\u8f83\u4f4e\u3001\u4eca\u5e74\u4ef7\u683c\u4e0a\u6da8\u7b49\u56e0\u7d20\uff0c\u4f46\u66f4\u4e3b\u8981\u7684\u662f\u4f01\u4e1a\u4e3b\u52a8\u8c03\u7ed3\u6784\uff0c\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u589e\u52a0\u503c\u5927\u5e45\u589e\u957f\u7684\u7ed3\u679c\u3002\u201d\n\u3000\u3000\u515a\u7684\u5341\u516b\u5927\u4ee5\u6765\uff0c\u521b\u65b0\u53d1\u5c55\u91ca\u653e\u5f3a\u52b2\u52a8\u80fd\uff0c\u521b\u9020\u4e86\u4e00\u4e2a\u53c8\u4e00\u4e2a\u201c\u4e2d\u56fd\u5947\u8ff9\u201d\uff1b\u534f\u8c03\u53d1\u5c55\u8865\u8db3\u77ed\u677f\uff0c\u8ba9\u53d1\u5c55\u66f4\u8d8b\u7a33\u5065\uff1b\u7eff\u8272\u53d1\u5c55\u575a\u5b9a\u63a8\u8fdb\uff0c\u4eba\u4e0e\u81ea\u7136\u66f4\u52a0\u548c\u8c10\uff1b\u5f00\u653e\u53d1\u5c55\u5b9e\u73b0\u4e92\u5229\u5171\u8d62\uff0c\u53d1\u5c55\u5929\u5730\u66f4\u52a0\u5e7f\u9614\uff1b\u5171\u4eab\u53d1\u5c55\u589e\u8fdb\u4eba\u6c11\u798f\u7949\uff0c\u793e\u4f1a\u516c\u5e73\u6b63\u4e49\u66f4\u52a0\u51f8\u663e\u2026\u2026\u5b9e\u8df5\u5145\u5206\u8bc1\u660e\uff0c\u6211\u4eec\u5df2\u7ecf\u521d\u6218\u544a\u6377\uff0c\u7ad9\u5728\u4e86\u65b0\u65f6\u4ee3\u201c\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\u7684\u5e73\u53f0\u4e0a\u3002\n\u3000\u3000\u4e0d\u8fc7\uff0c\u4ece\u9ad8\u901f\u589e\u957f\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\uff0c\u6211\u4eec\u8fd8\u9762\u4e34\u7740\u8bb8\u591a\u6311\u6218\u3002\n\u3000\u3000\u6bd4\u5982\uff0c\u5f53\u524d\u4ecd\u6709\u76f8\u5f53\u6570\u91cf\u8d44\u91d1\u3001\u571f\u5730\u3001\u52b3\u52a8\u529b\u8d44\u6e90\uff0c\u6c89\u6dc0\u5728\u4e25\u91cd\u8fc7\u5269\u4ea7\u80fd\u3001\u9ad8\u6c61\u67d3\u4ea7\u4e1a\u4e2d\uff0c\u62d6\u7d2f\u4e86\u7ecf\u6d4e\u8f6c\u578b\u548c\u6548\u7387\u63d0\u9ad8\u3002\u8981\u901a\u8fc7\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\uff0c\u628a\u8fd9\u4e9b\u8981\u7d20\u8d44\u6e90\u9010\u6b65\u8f6c\u79fb\u3001\u6ce8\u5165\u65b0\u5174\u3001\u7eff\u8272\u4ea7\u4e1a\u4e2d\u53bb\u3002\n\u3000\u3000\u53c8\u5982\uff0c\u7b80\u653f\u653e\u6743\u6210\u6548\u663e\u8457\uff0c\u4f01\u4e1a\u8f7b\u88c5\u4e0a\u9635\uff0c\u4f46\u5404\u90e8\u95e8\u3001\u5404\u7ea7\u653f\u5e9c\u4e4b\u95f4\u7684\u4fe1\u606f\u7cfb\u7edf\u4e92\u8054\u4e92\u901a\u5de5\u4f5c\u4e9f\u987b\u63d0\u901f\u3002\u201c\u5e94\u8be5\u5f62\u6210\u5168\u56fd\u7edf\u4e00\u653f\u52a1\u670d\u52a1\u5e73\u53f0\uff0c\u624d\u80fd\u8ba9\u4f01\u4e1a\u548c\u7fa4\u4f17\u66f4\u591a\u611f\u53d7\u5230\u2018\u653e\u7ba1\u670d\u2019\u6539\u9769\u6210\u6548\uff0c\u9664\u70e6\u82db\u4e4b\u5f0a\u3001\u65bd\u516c\u5e73\u4e4b\u7b56\u3001\u5f00\u4fbf\u5229\u4e4b\u95e8\u3002\u201d\u6c6a\u7389\u51ef\u8bf4\u3002\n\u3000\u3000\u518d\u5982\uff0c\u7eff\u8272\u53d1\u5c55\u4f9d\u7136\u4efb\u91cd\u9053\u8fdc\u3002\u4ece\u8fd1\u4e24\u5e74\u4e2d\u592e\u73af\u4fdd\u7763\u5bdf\u7ec4\u66dd\u5149\u7684\u6848\u4ef6\u770b\uff0c\u4e00\u4e9b\u5730\u65b9\u4e3a\u4e86\u91d1\u5c71\u94f6\u5c71\u727a\u7272\u7eff\u6c34\u9752\u5c71\uff0c\u8981\u901f\u5ea6\u4e0d\u8981\u8d28\u91cf\u7684\u987d\u75c7\u3001\u964b\u4e60\u4ecd\u7136\u5b58\u5728\u3002\n\u3000\u3000\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u4e0d\u53ef\u9006\uff0c\u9ad8\u8d28\u91cf\u53d1\u5c55\u4efb\u52a1\u4e0d\u80fd\u7b49\uff0c\u9700\u8981\u64b8\u8d77\u8896\u5b50\u52a0\u6cb9\u5e72\u3002\n\u3000\u3000\u515a\u7684\u5341\u4e5d\u5927\u62a5\u544a\u63d0\u51fa\u4e86\u516d\u5927\u6218\u7565\u4e3e\u63aa\uff0c\u5305\u62ec\u6df1\u5316\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4ee5\u63a8\u52a8\u4ea7\u4e1a\u7ed3\u6784\u3001\u4ea7\u54c1\u7ed3\u6784\u8f6c\u578b\u5347\u7ea7\uff1b\u52a0\u5feb\u5efa\u8bbe\u521b\u65b0\u578b\u56fd\u5bb6\uff0c\u4ee5\u81ea\u4e3b\u521b\u65b0\u7684\u6280\u672f\u5e26\u52a8\u8d28\u91cf\u6548\u76ca\u63d0\u9ad8\uff1b\u5b9e\u65bd\u4e61\u6751\u632f\u5174\u6218\u7565\u548c\u533a\u57df\u534f\u8c03\u53d1\u5c55\u6218\u7565\uff0c\u4ee5\u89e3\u51b3\u7ecf\u6d4e\u53d1\u5c55\u4e0d\u5e73\u8861\u3001\u4e0d\u5145\u5206\u95ee\u9898\uff0c\u91ca\u653e\u7ecf\u6d4e\u53d1\u5c55\u65b0\u52a8\u80fd\uff1b\u52a0\u5feb\u5b8c\u5584\u793e\u4f1a\u4e3b\u4e49\u5e02\u573a\u7ecf\u6d4e\u4f53\u5236\uff0c\u5efa\u7acb\u6709\u5229\u4e8e\u63d0\u9ad8\u7ecf\u6d4e\u53d1\u5c55\u8d28\u91cf\u548c\u6539\u5584\u751f\u6001\u73af\u5883\u7684\u4f53\u5236\u673a\u5236\uff0c\u589e\u5f3a\u7ecf\u6d4e\u53d1\u5c55\u6d3b\u529b\uff1b\u63a8\u52a8\u5f62\u6210\u5168\u9762\u5f00\u653e\u65b0\u683c\u5c40\uff0c\u66f4\u597d\u5730\u5229\u7528\u5168\u7403\u5e02\u573a\u548c\u8d44\u6e90\u3002\n\u3000\u3000\u201c\u672a\u6765\u4ecd\u5e94\u575a\u6301\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\u3001\u4f18\u5316\u7ecf\u6d4e\u7ed3\u6784\u3001\u8f6c\u6362\u589e\u957f\u52a8\u529b\u3002\u201d\u9ad8\u57f9\u52c7\u6307\u51fa\uff0c\u672a\u6765\u7ecf\u6d4e\u53d1\u5c55\u4ecd\u8981\u4ece\u89c4\u6a21\u901f\u5ea6\u578b\u7c97\u653e\u589e\u957f\u8f6c\u5411\u8d28\u91cf\u6548\u7387\u578b\u96c6\u7ea6\u589e\u957f\uff0c\u4ece\u589e\u91cf\u6269\u80fd\u4e3a\u4e3b\u8f6c\u5411\u8c03\u6574\u5b58\u91cf\u3001\u505a\u4f18\u589e\u91cf\u5e76\u4e3e\u7684\u6df1\u5ea6\u8c03\u6574\uff1b\u4ece\u4f20\u7edf\u589e\u957f\u70b9\u8f6c\u5411\u65b0\u7684\u589e\u957f\u70b9\u3002\u201c\u8fd9\u4e9b\u4efb\u52a1\u540c\u5341\u516b\u5927\u4ee5\u6765\u6240\u5f62\u6210\u7684\u7ecf\u6d4e\u653f\u7b56\u6846\u67b6\u662f\u4e00\u81f4\u7684\uff0c\u5373\uff1a\u5efa\u7acb\u5728\u7ecf\u6d4e\u53d1\u5c55\u65b0\u5e38\u6001\u7684\u91cd\u5927\u5224\u65ad\u57fa\u7840\u4e4b\u4e0a\u7684\uff0c\u4ee5\u65b0\u53d1\u5c55\u7406\u5ff5\u4e3a\u6307\u5bfc\u3001\u4ee5\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4e3a\u4e3b\u7ebf\u7684\u7ecf\u6d4e\u653f\u7b56\u6846\u67b6\u3002\u63a5\u4e0b\u6765\uff0c\u8981\u5728\u8d2f\u5f7b\u843d\u5b9e\u515a\u7684\u5341\u4e5d\u5927\u7cbe\u795e\u7684\u8fc7\u7a0b\u4e2d\uff0c\u5c06\u8fd9\u4e9b\u653f\u7b56\u6301\u7eed\u6df1\u5316\u3001\u7ec6\u5316\uff0c\u5e76\u4e88\u4ee5\u521a\u6027\u843d\u5b9e\u3002\u201d\u9ad8\u57f9\u52c7\u8bf4\u3002\uff08\u8bb0\u8005 \u66f2\u54f2\u6db5\uff09"}, {"emotion": "neg", "id": "018095c88aff7adc099f007c54d443d4d9", "c_t": "\u5973\u5b50\u88ab\u649e200\u5143\u79c1\u4e86 \u9694\u5929\u5374\u75c5\u5371\u5165\u9662\u82b1\u63897\u4e07\u3000\u3000\u539f\u6807\u9898\uff1a\u5973\u5b50\u88ab\u649e\u53f8\u673a\u7ed9200\u5757\u94b1\u79c1\u4e86\uff0c\u9694\u5929\u75c5\u5371\u82b1\u63897\u4e07\uff01\u6ce8\u610f\uff1a\u8fd9\u4e9b\u60c5\u51b5\u4e0d\u80fd\u79c1\u4e86\uff01\n\u3000\u3000\u5468\u7403\u5a25\u5973\u58eb\u4e00\u4e2a\u6708\u524d\u6b65\u884c\u56de\u5bb6\u65f6\uff0c\u88ab\u4e00\u8f86\u9ed1\u8272\u5c0f\u8f7f\u8f66\u649e\u5012\u5728\u5730\uff0c\u81ea\u8ba4\u65e0\u5927\u788d\u7684\u5979\uff0c\u540c\u610f\u53f8\u673a\u63d0\u51fa\u7684200\u5143\u79c1\u4e86\u3002\u5c82\u6599\uff0c\u7b2c\u4e8c\u5929\u5979\u8179\u90e8\u5267\u75db\u5165\u9662\uff0c\u5374\u88ab\u53d1\u73b0\u6574\u4e2a\u5185\u810f\u591a\u5904\u7834\u88c2\u3001\u632b\u4f24\uff0c\u4e00\u5ea6\u4e0b\u4e86\u75c5\u5371\u901a\u77e5\u4e66\uff0c\u4f4f\u9662\u8fd1\u4e00\u4e2a\u6708\u82b1\u8d39\u7ea67\u4e07\uff01\n{IMG:1}\n\u3000\u3000\u533b\u7597\u5355\u636e\u8bc1\u660e\u7167\u7247\n\u3000\u3000\u9047\u8f66\u7978200\u5143\u79c1\u4e86 \n\u3000\u3000\u7b2c\u4e8c\u5929\u8179\u75db\u5165\u9662\u75c5\u5371 \n\u3000\u3000\u201c\u54ce\uff0c\u5f53\u65f6\u5e94\u8be5\u9a6c\u4e0a\u5230\u533b\u9662\u68c0\u67e5\u7684\uff01\u201d\u4eca\u5e7443\u5c81\u7684\u5468\u7403\u5a25\u662f\u6e56\u5357\u4eba\uff0c\u592b\u59bb\u4fe9\u5206\u522b\u5728\u5357\u4e94\u3001\u6eaa\u5934\u793e\u533a\u7684\u5de5\u5382\u5185\u505a\u666e\u5de5\u3002\u5982\u4eca\u51fa\u9662\u7684\u5979\u9762\u9ec4\u808c\u7626\uff0c\u8179\u90e8\u8fd8\u6302\u7740\u4e00\u4e2a\u836f\u6c34\u888b\uff0c\u684c\u9762\u4e0a\u5806\u6ee1\u4e86\u836f\u74f6\u3002\n\u3000\u3000\u201c\u6361\u56de\u4e86\u4e00\u6761\u547d\uff01\u201d\u5468\u7403\u5a25\u544a\u8bc9\u5c0f\u7f16\uff0c\u8f66\u7978\u53d1\u751f\u57289\u67081\u65e5\u665a\u4e0a11\u70b9\uff0c\u5f53\u65f6\u5979\u5728\u535a\u89c8\u5927\u9053\u5357\u4e94\u8def\u6bb5\u7684\u6b65\u884c\u56de\u5bb6\uff0c\u4e00\u8f86\u9ed1\u8272\u7684\u5c0f\u8f7f\u8f66\u7a81\u7136\u7a9c\u51fa\uff0c\u5c06\u5979\u649e\u5012\u5728\u5730\u3002\n{IMG:2}\n\u3000\u3000\u51fa\u9662\u8fd1\u4e00\u4e2a\u6708\uff0c\u8eab\u4e0a\u8fd8\u6302\u7740\u836f\u6c34\u888b\n\u3000\u3000\u201c\u88ab\u649e\u5012\u540e\u819d\u76d6\u6709\u70b9\u64e6\u4f24\uff0c\u809a\u5b50\u6709\u70b9\u75bc\uff0c\u5f53\u65f6\u89c9\u5f97\u6ca1\u4ec0\u4e48\u5927\u788d\uff0c\u53f8\u673a\u8bf4\u8d54200\u5143\u79c1\u4e86\uff0c\u6211\u53c8\u4e0d\u662f\u8bb9\u4eba\u7684\u4eba\uff0c\u5c31\u540c\u610f\u4e86\u3002\u201d\u5468\u7403\u5a25\u8bf4\uff0c\u5f53\u65f6\u8087\u4e8b\u7537\u5b50\u66fe\u63d0\u51fa\u5e26\u5979\u53bb\u533b\u9662\u68c0\u67e5\uff0c\u4f46\u5979\u89c9\u5f97\u4e00\u4e2a\u4eba\u6709\u70b9\u5bb3\u6015\uff0c\u800c\u4e14\u8ba4\u4e3a\u6ca1\u5927\u788d\u4e0d\u9700\u8981\u9ebb\u70e6\u4eba\u5bb6\uff0c\u5c31\u6ca1\u4e0a\u8f66\u8ddf\u53bb\u3002\n\u3000\u3000\u4e5f\u56e0\u6b64\uff0c\u5979\u538b\u6839\u4e5f\u6ca1\u60f3\u8fc7\u8bb0\u4e0b\u5bf9\u65b9\u7684\u8f66\u724c\u53f7\u3001\u624b\u673a\u53f7\u3002\n\u3000\u3000\u8c01\u77e5\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u5468\u7403\u5a25\u611f\u89c9\u809a\u5b50\u5f88\u75db\u3002\u201c\u5979\u9762\u8272\u82cd\u767d\uff0c\u5f53\u65f6\u6211\u8fd8\u4ee5\u4e3a\u5979\u5403\u9519\u4e86\u4e1c\u897f\uff0c\u5e26\u5979\u53bb\u539a\u8857\u533b\u9662\u68c0\u67e5\u3002\u201d \u5468\u7403\u5a25\u7684\u4e08\u592b\u5f20\u5171\u8f89\u8bf4\uff0c\u81ea\u5df1\u600e\u4e48\u4e5f\u6ca1\u60f3\u5230\uff0c\u533b\u751f\u7684\u8bca\u65ad\u8ba9\u4ed6\u5927\u5413\u4e00\u8df3\u3002\u201c\u533b\u751f\u8bf4\u5979\u80f0\u817a\u65ad\u4e86\uff0c\u8981\u7acb\u5373\u624b\u672f\uff01\u201d\u4ed6\u751a\u81f3\u8fd8\u63a5\u5230\u4e86\u75c5\u5371\u901a\u77e5\u4e66\u3002\n{IMG:3}\n\u3000\u3000\u51fa\u9662\u5c0f\u7ed3\n\u3000\u3000\u80f0\u817a\u65ad\u4e86\u5185\u810f\u632b\u4f24 \n\u3000\u3000\u6cbb\u7597\u8fd1\u6708\u82b1\u8d397\u4e07 \n\u3000\u3000\u7ecf\u8179\u90e8CT\u3001\u589e\u5f3aTC\u68c0\u67e5\uff0c\u5468\u7403\u5a25\u88ab\u53d1\u73b0\u80f0\u817a\u65ad\u88c2\uff01\n\u3000\u3000\u201c\u75c5\u4eba\u80f0\u817a\u4f53\u9888\u90e8\u65ad\u88c2\uff0c\u6574\u4e2a\u5185\u810f\u9519\u5904\u632b\u4f24\uff0c\u5982\u809d\u810f\u4e5f\u90e8\u5206\u632b\u4f24\uff0c\u5fc5\u987b\u9a6c\u4e0a\u52a8\u624b\u672f\uff01\u201d \u533b\u9662\u533b\u751f\u83ab\u4f1f\u950b\u5206\u6790\u8bf4\uff0c\u80f0\u817a\u88ab\u649e\u65ad\u88c2\uff0c\u5468\u7403\u5a25\u88ab\u649e\u540e\u5e94\u8be5\u6709\u9690\u75db\u7684\uff0c\u4e0d\u8fc7\u9aa4\u7136\u88ab\u649e\u53d7\u60ca\u53ef\u80fd\u6ca1\u592a\u7559\u610f\uff0c\u624d\u5728\u7b2c\u4e8c\u5929\u53d8\u6210\u4e86\u5267\u75db\u3002\n{IMG:4}\n\u3000\u3000\u8bca\u65ad\u4e66\n\u3000\u3000\u83ab\u533b\u751f\u8bf4\uff0c\u7531\u4e8e\u80f0\u817a\u6bd7\u90bb\u4eba\u4f53\u7684\u4f17\u591a\u91cd\u8981\u810f\u5668\uff0c\u624b\u672f\u7684\u96be\u5ea6\u76f8\u5f53\u5927\uff0c\u4f46\u60c5\u51b5\u6bd4\u8f83\u5371\u673a\uff0c\u4ed6\u4eec\u7d27\u6025\u4e3a\u5468\u7403\u5a25\u5b9e\u65bd\u624b\u672f\u3002\u624b\u672f\u7ecf\u5386\u4e863\u4e2a\u5c0f\u65f6\uff0c\u5468\u7403\u5a25\u76f4\u63a5\u8f6c\u5165\u91cd\u75c7\u76d1\u62a4\u5ba4\u3002\u89c2\u5bdf\u4e00\u5929\u540e\uff0c\u624d\u4eceICU\u8f6c\u56de\u4e86\u666e\u901a\u75c5\u623f\u3002\n\u3000\u3000\u624b\u672f\u867d\u7136\u6210\u529f\uff0c\u4f46\u4e8b\u60c5\u5374\u6ca1\u5b8c\u3002\u7531\u4e8e\u5185\u810f\u591a\u6709\u53d7\u4f24\uff0c\u5468\u7403\u5a25\u8fd8\u4f4f\u9662\u4e86\u8fd1\u6708\u6cbb\u7597\uff0c\u624d\u5728\u8fd1\u65e5\u51fa\u9662\u4e86\uff0c\u82b1\u8d39\u533b\u7597\u8d39\u7528\u8fd17\u4e07\u5143\u3002\u7531\u4e8e\u80f0\u817a\u88ab\u5207\u9664\u4e86\u4e00\u4e9b\uff0c\u5979\u8fd8\u51fa\u73b0\u4e86\u7cd6\u5c3f\u75c5\u7684\u75c5\u75c7\u3002\n\u3000\u3000\u9762\u5bf9\u98de\u6765\u6a2a\u7978\uff0c\u4e00\u5bb6\u4eba\u60ca\u9b42\u672a\u5b9a\uff0c\u800c\u533b\u836f\u8d39\u51e0\u4e4e\u90fd\u662f\u8ddf\u4eb2\u621a\u670b\u53cb\u5de5\u53cb\u501f\u7684\u3002\u201c\u4ee5\u524d\u6211\u8001\u5a46\u5f88\u7701\u5403\u4fed\u7528\u7684\uff0c\u5e73\u65f6\u4e0b\u73ed\u540e\u8fd8\u53bb\u8def\u8fb9\u6361\u74f6\u5b50\u5356\u94b1\u8d34\u8865\u5bb6\u7528\uff0c\u5982\u4eca\uff0c\u5979\u6ca1\u529e\u6cd5\u4e0a\u73ed\uff0c\u6211\u4e00\u4e2a\u4eba\u5de5\u8d44\u53ea\u67092000\u591a\u5143\uff0c\u5c0f\u5b69\u8981\u4e0a\u5b66\u3001\u7236\u6bcd\u8001\u4e86\u8981\u8d61\u517b\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u4e9b\uff0c\u5f20\u5171\u8f89\u4e00\u8138\u6101\u5bb9\uff0c\u800c\u5468\u7403\u5a25\u5219\u5728\u4e00\u65c1\u62b9\u6cea\u3002\n\u3000\u3000\u201c\u6211\u4eec\u73b0\u5728\u5c31\u662f\u60f3\u5e0c\u671b\uff0c\u8087\u4e8b\u8f66\u80fd\u51fa\u6765\u627f\u62c5\u4e00\u70b9\u6211\u4eec\u7684\u8d39\u7528\uff0c\u6bd5\u7adf\u6211\u5f53\u521d\u4e5f\u4f53\u8c05\u4ed6\u2026\u2026\u201d\u5468\u7403\u5a25\u6ce3\u4e0d\u6210\u58f0\uff0c\u5e0c\u671b\u793e\u4f1a\u80fd\u4f38\u51fa\u63f4\u624b\uff0c\u5e2e\u52a9\u4ed6\u4eec\u4e00\u5bb6\u8d70\u51fa\u56f0\u5883\u3002\n\u3000\u3000\u6700\u65b0\u8fdb\u5c55 \n\u3000\u3000\u4ea4\u8b66\u4e3a\u5176\u7533\u8bf7\u6551\u52a9\u57fa\u91d1 \n\u3000\u3000\u5165\u9662\u540e\uff0c\u5f20\u5171\u8f89\u7acb\u5373\u62a5\u8b66\uff0c\u5f53\u5929\u4ea4\u8b66\u4e5f\u5230\u533b\u9662\u548c\u4e8b\u6545\u73b0\u573a\u4e86\u89e3\u60c5\u51b5\u3002\u83b7\u6089\u5468\u7403\u5a25\u5bb6\u5ead\u60c5\u51b5\u540e\uff0c\u7acb\u5373\u4e3a\u5176\u7533\u8bf7\u4e86\u4e1c\u839e\u5e02\u9053\u8def\u4ea4\u901a\u4e8b\u6545\u793e\u4f1a\u6551\u52a9\u57fa\u91d1\u3002\n{IMG:5}\n\u3000\u3000\u4e1c\u839e\u5e02\u9053\u8def\u4ea4\u901a\u4e8b\u6545\u793e\u4f1a\u6551\u52a9\u57fa\u91d1\n\u3000\u3000\u4e1c\u839e\u4ea4\u8b66\u63d0\u9192\uff0c\u8f66\u7978\u9020\u6210\u7684\u9690\u6027\u4f24\u5bb3\u6bd4\u8f83\u591a\uff0c\u4e5f\u8bb8\u5f53\u65f6\u6ca1\u4e8b\uff0c\u4e8b\u540e\u624d\u4f1a\u611f\u89c9\u5230\uff0c\u5efa\u8bae\u8f66\u7978\u4e2d\u5982\u679c\u662f\u8eab\u4f53\u53d7\u5230\u521b\u4f24\uff0c\u6700\u597d\u662f\u5728\u8087\u4e8b\u53f8\u673a\u7684\u966a\u540c\u4e0b\u53bb\u533b\u9662\u505a\u68c0\u67e5\u786e\u4fdd\u5b89\u5168\u3002\u5728\u79c1\u4e86\u7684\u540c\u65f6\uff0c\u4e5f\u8981\u5b66\u4f1a\u4fdd\u62a4\u81ea\u5df1\uff0c\u83b7\u53d6\u4e00\u4e9b\u51ed\u8bc1\uff0c\u5982\u8bb0\u4f4f\u5bf9\u65b9\u8f66\u53f7\u548c\u9a7e\u9a76\u5458\u59d3\u540d\uff0c\u8bb0\u4e0b\u624b\u673a\u53f7\u7801\u7b49\u65b9\u4fbf\u4e8b\u540e\u8054\u7cfb\u3002\n\u3000\u3000\u4f46\u6700\u4fdd\u9669\u7684\u529e\u6cd5\u662f\uff0c\u53d7\u4f24\u540e\uff0c\u65e0\u8bba\u6253\u7b97\u79c1\u4e86\u8fd8\u662f\u8d70\u7a0b\u5e8f\uff0c\u90fd\u5e94\u8be5\u62a5\u4e2a\u8b66\u3002\n\u3000\u3000\u8981\u6ce8\u610f\u4e0b\u9762\u8fd9\u4e9b\u60c5\u51b5\n\u3000\u3000\u4e0d\u80fd\u79c1\u4e86\uff1a \n\u3000\u30001\u3001\u548c\u624b\u7eed\u4e0d\u5168\u8f66\u8f86\u53d1\u751f\u4e8b\u6545\n\u3000\u3000\u673a\u52a8\u8f66\u724c\u7167\u3001\u8f66\u8f86\u68c0\u9a8c\u5408\u683c\u6807\u5fd7\u3001\u8f66\u8f86\u4fdd\u9669\u6807\u5fd7\uff0c\u4e0e\u7f3a\u4e4f\u8fd9\u4e09\u79cd\u4efb\u4f55\u4e00\u79cd\u6216\u4ee5\u4e0a\u7684\u8f66\u8f86\u53d1\u751f\u4e8b\u6545\u4e0d\u80fd\u79c1\u4e86\uff01\n\u3000\u30002\u3001\u5f53\u4e8b\u53f8\u673a\u9152\u9a7e\u6216\u836f\u9a7e\n\u3000\u3000\u4e8b\u6545\u5f53\u4e8b\u53f8\u673a\u6709\u996e\u9152\u3001\u670d\u7528\u836f\u54c1\uff08\u56fd\u5bb6\u7ba1\u5236\u7cbe\u795e\u836f\u54c1\u6216\u8005\u9ebb\u9189\u836f\u54c1\uff09\u548c\u5438\u6bd2\u9a7e\u9a76\u884c\u4e3a\u7684\u4e0d\u80fd\u79c1\u4e86\uff01\n\u3000\u30003\u3001\u53cc\u65b9\u5bf9\u4e8b\u6545\u539f\u56e0\u548c\u8d23\u4efb\u5f52\u5c5e\u6709\u4e89\u8bae\n\u3000\u3000\u5bf9\u4ea4\u901a\u4e8b\u6545\u9020\u6210\u7684\u4e8b\u5b9e\u6216\u8005\u5f62\u6210\u539f\u56e0\u6709\u5f02\u8bae\u7684\u3001\u5bf9\u8d23\u4efb\u5212\u5206\u6709\u4e89\u8bae\u7684\uff0c\u4e0d\u80fd\u79c1\u4e86\u3002\n\u3000\u30004\u3001\u635f\u574f\u4e86\u516c\u5171\u8bbe\u65bd\u7684\u4e8b\u6545\n\u3000\u3000\u4e8b\u6545\u9020\u6210\u4e86\u516c\u5171\u8bbe\u65bd\u548c\u5efa\u7b51\u7269\u7684\u7834\u574f\uff0c\u4e0d\u80fd\u53ea\u987e\u79c1\u4e8b\uff0c\u5fd8\u4e86\u516c\u4e8b\u3002\n\u3000\u30005\u3001\u5355\u65b9\u53d1\u751f\u4ea4\u901a\u4e8b\u6545\n\u3000\u3000\u901a\u4fd7\u5730\u8bf4\uff0c\u5355\u65b9\u4ea4\u901a\u4e8b\u6545\u662f\u6307\u4e0d\u6d89\u53ca\u7b2c\u4e09\u65b9\u4eba\u5458\u4f24\uff08\u4ea1\uff09\u6216\u8d22\u7269\u635f\u5931\u7684\u4ea4\u901a\u4e8b\u6545\uff0c\u5373\u78b0\u649e\u5916\u754c\u7269\u4f53\uff0c\u81ea\u8eab\u8f66\u8f86\u635f\u574f\uff0c\u4f46\u5916\u754c\u7269\u4f53\u65e0\u635f\u574f\u6216\u8005\u65e0\u9700\u8d54\u507f\u3002\n\u3000\u30006\u3001\u65e0\u8bc1\u9a7e\u9a76\u6216\u53f8\u673a\u4e0d\u80fd\u51fa\u793a\u9a7e\u9a76\u8bc1\n\u3000\u3000\u9a7e\u9a76\u8bc1\u8fc7\u671f\u3001\u5931\u6548\u6216\u8005\u6ca1\u6709\u9a7e\u9a76\u8bc1\u5f00\u8f66\u4e0a\u8def\uff0c\u8fd9\u80af\u5b9a\u4e0d\u80fd\u79c1\u4e86\uff0c\u9700\u8981\u4ea4\u8b66\u5224\u5b9a\u5177\u4f53\u662f\u65e0\u8bc1\u9a7e\u9a76\u8fd8\u662f\u5fd8\u5e26\u9a7e\u9a76\u8bc1\u3002\n\u3000\u30007\u3001\u4e8b\u6545\u9020\u6210\u4e86\u4eba\u5458\u4f24\u4ea1\n\u3000\u3000\u9020\u6210\u4eba\u5458\u4f24\u4ea1\u7684\u4e8b\u6545\u6bd4\u8f83\u590d\u6742\uff0c\u5f53\u4e8b\u8f66\u4e3b\u5f88\u96be\u5224\u65ad\u5177\u4f53\u8d23\u4efb\u60c5\u51b5\u3002\u9996\u5148\u9700\u8981\u505a\u7684\u662f\u62a5\u8b66\u53ca\u65f6\u5bf9\u4f24\u8005\u6025\u6551\u3002"}, {"emotion": "neg", "id": "015453ba20b36bb1ae2dca7240a1478704", "c_t": "\u4e2d\u949e\u56fd\u9f0e\u57fa\u51c6\u94f6\u4ef7\u4eca\u5929\u591a\u5c11\u4e00\u514b\uff082017\u5e7410\u670831\u65e5\uff09\u4e2d\u949e\u56fd\u9f0e\u57fa\u51c6\u94f6\u4ef7\u4eca\u5929\u591a\u5c11\u4e00\u514b\uff082017\u5e7410\u670831\u65e5\uff09                                                          \u4ea7\u54c1\u540d\u79f0              \u4ea7\u54c1\u4ef7\u683c              \u4ef7\u683c\u5355\u4f4d              \u6da8\u8dcc\u4e2d\u949e\u56fd\u9f0e\u57fa\u51c6\u94f6\u4ef7                  3.815                  \u5143/\u514b                  \u6da8"}, {"emotion": "neg", "id": "01d14cedd9425e643a84d3043660cd51d5", "c_t": "\u4e13\u5bb6\u89e3\u8bfb\uff1a\u5982\u4f55\u7406\u89e3\u4e2d\u56fd\u7ecf\u6d4e\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u3000\u3000\u5982\u4f55\u7406\u89e3\u4e2d\u56fd\u7ecf\u6d4e\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55(\u805a\u7126\u5341\u4e5d\u5927\u62a5\u544a\u00b7\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5)\n\u3000\u3000\u672c\u62a5\u8bb0\u8005 \u66f2\u54f2\u6db5\n\u3000\u3000\u521a\u521a\u95ed\u5e55\u7684\u515a\u7684\u5341\u4e5d\u5927\uff0c\u505a\u51fa\u4e86\u201c\u6211\u56fd\u7ecf\u6d4e\u5df2\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\u8fd9\u4e00\u5386\u53f2\u6027\u8bba\u65ad\u3002\n\u3000\u3000\u4e3a\u4ec0\u4e48\u8bf4\u4e2d\u56fd\u7ecf\u6d4e\u8f6c\u5411\u4e86\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff1f\u4e0e\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u76f8\u6bd4\uff0c\u65b0\u9636\u6bb5\u4f1a\u5448\u73b0\u51fa\u54ea\u4e9b\u65b0\u53d8\u5316\u3001\u65b0\u8d8b\u52bf\uff1f\u6211\u4eec\u5728\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u8fc7\u7a0b\u4e2d\u5982\u4f55\u6293\u4f4f\u673a\u9047\uff0c\u653b\u5173\u593a\u9698\uff0c\u51b3\u80dc\u5168\u9762\u5efa\u6210\u5c0f\u5eb7\u793e\u4f1a\uff0c\u5411\u201c\u4e24\u4e2a\u4e00\u767e\u5e74\u201d\u76ee\u6807\u633a\u8fdb\uff1f\n\u3000\u3000\u5982\u4f55\u7406\u89e3\u201c\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\uff1f\n\u3000\u3000\u5f3a\u8c03\u8d28\u91cf\u800c\u975e\u901f\u5ea6\uff0c\u5f3a\u8c03\u53d1\u5c55\u800c\u975e\u589e\u957f\n\u3000\u3000\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u4e2d\u56fd\u7ecf\u6d4e\u518d\u4e00\u6b21\u505a\u51fa\u660e\u786e\u7684\u8def\u5f84\u9009\u62e9\u3002\n\u3000\u3000\u2014\u2014\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\uff0c\u5b9e\u73b0\u96c6\u7ea6\u578b\u7ecf\u6d4e\u589e\u957f\uff0c\u63d0\u9ad8\u5168\u8981\u7d20\u751f\u4ea7\u7387\u3002\n\u3000\u3000\u201c\u2018\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u2019\u8fd9\u4e00\u8bba\u65ad\u7684\u672c\u8d28\u542b\u4e49\uff0c\u5c31\u662f\u6211\u56fd\u7ecf\u6d4e\u5df2\u7ecf\u4ece\u4e3b\u8981\u4f9d\u9760\u589e\u52a0\u7269\u8d28\u8d44\u6e90\u6d88\u8017\u5b9e\u73b0\u7684\u7c97\u653e\u578b\u9ad8\u901f\u589e\u957f\uff0c\u8f6c\u53d8\u4e3a\u4e3b\u8981\u4f9d\u9760\u6280\u672f\u8fdb\u6b65\u3001\u6539\u5584\u7ba1\u7406\u548c\u63d0\u9ad8\u52b3\u52a8\u8005\u7d20\u8d28\u5b9e\u73b0\u7684\u96c6\u7ea6\u578b\u589e\u957f\u3002\u201d\u5728\u4e2d\u56fd\u56fd\u9645\u7ecf\u6d4e\u4ea4\u6d41\u4e2d\u5fc3\u5e38\u52a1\u526f\u7406\u4e8b\u957f\u90d1\u65b0\u7acb\u770b\u6765\uff0c\u7ecf\u6d4e\u4ece\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u662f\u5212\u65f6\u4ee3\u7684\u53d8\u5316\u3002\n\u3000\u3000\u201c\u4e2d\u56fd\u7684\u7ecf\u6d4e\u53d1\u5c55\uff0c\u4e0d\u518d\u7ea0\u7ed3\u7ecf\u6d4e\u589e\u901f\u5feb\u4e00\u70b9\u8fd8\u662f\u6162\u4e00\u70b9\uff0c\u800c\u662f\u4ee5\u63d0\u9ad8\u5168\u8981\u7d20\u751f\u4ea7\u7387\u4e3a\u6293\u624b\uff0c\u63a8\u52a8\u7ecf\u6d4e\u53d1\u5c55\u8d28\u91cf\u53d8\u9769\u3001\u6548\u7387\u53d8\u9769\u3001\u52a8\u529b\u53d8\u9769\u3002\u201d\u90d1\u65b0\u7acb\u5f3a\u8c03\u3002\n\u3000\u30002016\u5e74\uff0c\u6211\u56fd\u201c\u767e\u5f3a\u53bf\u201d\u4ee5\u5360\u5168\u56fd2%\u7684\u571f\u5730\u9762\u79ef\u30016%\u7684\u4eba\u53e3\u521b\u9020\u4e86\u8d85\u8fc7\u5168\u56fd11%\u7684GDP\uff1b\u57ce\u9547\u5c45\u6c11\u4eba\u5747\u53ef\u652f\u914d\u6536\u516539165\u5143\uff0c\u6bd4\u5168\u56fd\u5e73\u5747\u6c34\u5e73\u9ad8\u51fa16.5%\uff1b\u4eba\u5747\u793e\u4f1a\u6d88\u8d39\u54c1\u96f6\u552e\u603b\u989d\u4e3a3.1\u4e07\u5143\uff0c\u6bd4\u5168\u56fd\u5e73\u5747\u6c34\u5e73\u9ad8\u51fa29%\u3002\u7eb5\u89c2\u201c\u767e\u5f3a\u53bf\u201d\u7684\u53d1\u5c55\u8981\u8bc0\uff0c\u65e0\u4e00\u4e0d\u662f\u4ece\u539f\u6709\u8d44\u6e90\u3001\u6295\u8d44\u62c9\u52a8\u7684\u7c97\u653e\u578b\u53d1\u5c55\u65b9\u5f0f\uff0c\u5411\u521b\u65b0\u9a71\u52a8\u7684\u96c6\u7ea6\u578b\u53d1\u5c55\u65b9\u5f0f\u8f6c\u53d8\uff0c\u8fce\u6765\u4e86\u5168\u65b0\u7684\u53d1\u5c55\u5951\u673a\u3002\n\u3000\u3000\u201c\u2018\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u2019\u8868\u73b0\u5728\u4ea7\u4e1a\u7ed3\u6784\u4e0a\uff0c\u662f\u7531\u8d44\u6e90\u5bc6\u96c6\u578b\u3001\u52b3\u52a8\u5bc6\u96c6\u578b\u4ea7\u4e1a\u4e3a\u4e3b\u5411\u6280\u672f\u5bc6\u96c6\u578b\u3001\u77e5\u8bc6\u5bc6\u96c6\u578b\u4ea7\u4e1a\u4e3a\u4e3b\u8f6c\u53d8\uff1b\u5728\u4ea7\u54c1\u7ed3\u6784\u4e0a\uff0c\u7531\u4f4e\u6280\u672f\u542b\u91cf\u3001\u4f4e\u9644\u52a0\u503c\u4ea7\u54c1\u4e3a\u4e3b\u5411\u9ad8\u6280\u672f\u542b\u91cf\u3001\u9ad8\u9644\u52a0\u503c\u4ea7\u54c1\u4e3a\u4e3b\u8f6c\u53d8\uff1b\u5728\u7ecf\u6d4e\u6548\u76ca\u4e0a\uff0c\u7531\u9ad8\u6210\u672c\u3001\u4f4e\u6548\u76ca\u5411\u4f4e\u6210\u672c\u3001\u9ad8\u6548\u76ca\u7684\u65b9\u5411\u8f6c\u53d8\uff1b\u5728\u751f\u6001\u73af\u5883\u4e0a\uff0c\u7531\u9ad8\u6392\u653e\u3001\u9ad8\u6c61\u67d3\u5411\u5faa\u73af\u7ecf\u6d4e\u548c\u73af\u5883\u53cb\u597d\u578b\u7ecf\u6d4e\u8f6c\u53d8\u3002\u6700\u7ec8\u5c06\u4f53\u73b0\u4e3a\u56fd\u5bb6\u7ecf\u6d4e\u5b9e\u529b\u4e0d\u65ad\u589e\u5f3a\uff0c\u5c45\u6c11\u6536\u5165\u5f97\u5230\u8f83\u5feb\u589e\u52a0\u3002\u201d\u90d1\u65b0\u7acb\u8bf4\u3002\n\u3000\u3000\u2014\u2014\u5145\u5b9e\u53d1\u5c55\u5185\u6db5\uff0c\u4ee5\u66f4\u5145\u5206\u66f4\u5e73\u8861\u4e3a\u76ee\u6807\uff0c\u589e\u8fdb\u4eba\u6c11\u83b7\u5f97\u611f\u3002\n\u3000\u3000\u4ece\u7ed3\u679c\u5bfc\u5411\u770b\uff0c\u201c\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\u66f4\u7a81\u51fa\u767e\u59d3\u7684\u83b7\u5f97\u611f\u3002\u4e2d\u56fd\u793e\u4f1a\u79d1\u5b66\u7814\u7a76\u9662\u7ecf\u6d4e\u7814\u7a76\u6240\u6240\u957f\u9ad8\u57f9\u52c7\u6307\u51fa\uff0c\u201c\u9ad8\u901f\u201d\u6307\u5411\u6570\u91cf\u6216\u89c4\u6a21\uff0c\u5f80\u5f80\u662f\u7ecf\u6d4e\u53d1\u5c55\u521d\u7ea7\u9636\u6bb5\u7684\u76ee\u6807\uff0c\u7528\u4ee5\u6807\u6ce8\u201c\u5feb\u4e0d\u5feb\u201d\uff1b\u201c\u9ad8\u8d28\u91cf\u201d\u5219\u662f\u7ecf\u6d4e\u53d1\u5c55\u8fbe\u5230\u4e00\u5b9a\u6c34\u5e73\u4e4b\u540e\u624d\u4f1a\u6709\u7684\u76ee\u6807\uff0c\u5f3a\u8c03\u8d28\u91cf\u548c\u6548\u76ca\uff0c\u662f\u56de\u7b54\u201c\u597d\u4e0d\u597d\u201d\u3002\n\u3000\u3000\u201c\u589e\u957f\u4ec5\u6307\u7ecf\u6d4e\u603b\u91cf\u7684\u6269\u5f20\uff1b\u53d1\u5c55\u7684\u5185\u6db5\u5219\u66f4\u4e3a\u4e30\u5bcc\uff0c\u662f\u4e3a\u4e86\u6ee1\u8db3\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u3002\u201d\u9ad8\u57f9\u52c7\u8bf4\uff0c\u6211\u56fd\u5df2\u89e3\u51b3\u4e86\u5341\u51e0\u4ebf\u4eba\u7684\u6e29\u9971\u95ee\u9898\uff0c\u603b\u4f53\u4e0a\u5b9e\u73b0\u5c0f\u5eb7\uff0c\u4e0d\u4e45\u5c06\u5168\u9762\u5efa\u6210\u5c0f\u5eb7\u793e\u4f1a\uff0c\u4eba\u6c11\u4e0d\u4ec5\u5bf9\u7269\u8d28\u6587\u5316\u751f\u6d3b\u63d0\u51fa\u4e86\u66f4\u9ad8\u8981\u6c42\uff0c\u800c\u4e14\u5728\u6c11\u4e3b\u3001\u6cd5\u6cbb\u3001\u516c\u5e73\u3001\u6b63\u4e49\u3001\u5b89\u5168\u3001\u73af\u5883\u7b49\u65b9\u9762\u7684\u8981\u6c42\u65e5\u76ca\u589e\u957f\u3002\u56e0\u6b64\uff0c\u7ecf\u6d4e\u53d1\u5c55\u8981\u4ece\u5355\u7eaf\u8ffd\u6c42\u603b\u91cf\u6269\u5c55\uff0c\u8f6c\u53d8\u4e3a\u9002\u5e94\u4eba\u4eec\u66f4\u9ad8\u6807\u51c6\u7684\u3001\u66f4\u52a0\u591a\u6837\u5316\u7684\u9700\u6c42\u3002\n\u3000\u3000\u4e3a\u4f55\u73b0\u5728\u63d0\u51fa\u201c\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\uff1f\n\u3000\u3000\u76f4\u9762\u65b0\u65f6\u4ee3\u77db\u76fe\uff0c\u7a81\u7834\u53d1\u5c55\u74f6\u9888\n\u3000\u3000\u5386\u53f2\u5730\u770b\uff0c\u7ecf\u6d4e\u53d1\u5c55\u5728\u91cd\u590d\u201c\u7e41\u8363\u2014\u8870\u9000\u2014\u590d\u82cf\u2014\u589e\u957f\u201d\u6ce2\u6d6a\u5f0f\u524d\u8fdb\u7684\u540c\u65f6\uff0c\u4ee5\u79d1\u6280\u3001\u4eba\u529b\u8d44\u6e90\u7b49\u8981\u7d20\u8fdb\u6b65\u4e3a\u65b0\u52a8\u80fd\uff0c\u89e3\u51b3\u5236\u7ea6\u7ecf\u6d4e\u53d1\u5c55\u7684\u6df1\u5c42\u6b21\u77db\u76fe\uff0c\u5b9e\u73b0\u5411\u4e0a\u7684\u603b\u8d8b\u52bf\u3002\u201c\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\uff0c\u662f\u6211\u4eec\u76f4\u9762\u65b0\u65f6\u4ee3\u4e3b\u8981\u77db\u76fe\uff0c\u9002\u5e94\u7ecf\u6d4e\u65b0\u5e38\u6001\u7684\u5fc5\u987b\u9009\u62e9\u548c\u7d27\u8feb\u4efb\u52a1\u3002\n\u3000\u3000\u2014\u2014\u73b0\u9636\u6bb5\u7684\u4e3b\u8981\u77db\u76fe\u8981\u6c42\u6211\u4eec\u653e\u5f03\u901f\u5ea6\u504f\u597d\uff0c\u91cd\u89c6\u53d1\u5c55\u8d28\u91cf\u3002\n\u3000\u3000\u9ad8\u57f9\u52c7\u8ba4\u4e3a\uff0c\u201c\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u201d\u8fd9\u4e00\u91cd\u8981\u8bba\u65ad\uff0c\u662f\u4e0e\u6211\u56fd\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\u5df2\u7ecf\u8f6c\u53d8\u4e3a\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u548c\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u7684\u53d1\u5c55\u4e4b\u95f4\u7684\u77db\u76fe\u76f8\u4e00\u81f4\u7684\u3002\n\u3000\u3000\u76ee\u524d\uff0c\u6211\u56fd\u80fd\u6e90\u3001\u539f\u6750\u6599\u6d88\u8017\u603b\u91cf\u504f\u5927\uff0c\u5355\u4f4dGDP\u80fd\u6e90\u6d88\u8017\u504f\u9ad8\u3002\u201c\u7c97\u653e\u578b\u7ecf\u6d4e\u589e\u957f\u5bf9\u8d44\u6e90\u3001\u73af\u5883\u9020\u6210\u8d8a\u6765\u8d8a\u5927\u7684\u538b\u529b\uff0c\u8fd9\u6837\u7684\u589e\u957f\u6a21\u5f0f\u96be\u4ee5\u6301\u7eed\u3002\u201d\u90d1\u65b0\u7acb\u6307\u51fa\u3002\n\u3000\u3000\u8fd1\u51e0\u5e74\uff0c\u534e\u5317\u3001\u534e\u4e1c\u5730\u533a\u51fa\u73b0\u5927\u9762\u79ef\u96fe\u973e\u5929\u6c14\uff0c\u4e00\u4e9b\u4e0e\u73af\u5883\u6c61\u67d3\u6709\u5173\u7684\u75be\u75c5\u53d1\u751f\u7387\u4e0a\u5347\u3002\u201c\u5982\u679c\u53d1\u5c55\u9700\u8981\u4ed8\u51fa\u4eba\u6c11\u5065\u5eb7\u7684\u4ee3\u4ef7\uff0c\u90a3\u5c31\u8fdd\u80cc\u4e86\u53d1\u5c55\u7684\u521d\u8877\u3002\u515a\u7684\u5341\u4e5d\u5927\u63d0\u51fa\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u5c31\u662f\u56de\u5e94\u767e\u59d3\u5173\u5207\uff0c\u76f4\u9762\u7ecf\u6d4e\u53d1\u5c55\u7684\u6df1\u5c42\u6b21\u77db\u76fe\u95ee\u9898\u3002\u201d\u90d1\u65b0\u7acb\u8bf4\u3002\n\u3000\u3000\u201c\u4e2d\u56fd\u7ecf\u6d4e\u4fdd\u6301\u4e86\u8fde\u7eed\u51e0\u5341\u5e74\u7684\u9ad8\u589e\u957f\uff0c\u521b\u9020\u4e86\u5927\u91cf\u793e\u4f1a\u8d22\u5bcc\uff0c\u4e2d\u56fd\u4eba\u4ece\u6539\u9769\u5f00\u653e\u4e2d\u83b7\u5f97\u4e86\u5de8\u5927\u7684\u5b9e\u60e0\u3002\u4f46\u7531\u4e8e\u5e02\u573a\u7ecf\u6d4e\u4f53\u5236\u4e0d\u5065\u5168\u3001\u5206\u914d\u5236\u5ea6\u4e0d\u591f\u5b8c\u5584\u7b49\u539f\u56e0\uff0c\u51fa\u73b0\u4e86\u4e00\u4e9b\u503c\u5f97\u5173\u6ce8\u7684\u793e\u4f1a\u7ecf\u6d4e\u95ee\u9898\uff0c\u5982\u57ce\u4e61\u5dee\u8ddd\u3001\u5730\u533a\u5dee\u8ddd\u3001\u884c\u4e1a\u5dee\u8ddd\u7b49\u4ecd\u7136\u5b58\u5728\uff0c\u4eba\u6c11\u7fa4\u4f17\u5bf9\u534f\u8c03\u53d1\u5c55\u3001\u5747\u8861\u53d1\u5c55\u7684\u8bc9\u6c42\u4e0d\u65ad\u63d0\u5347\u3002\u201d\u56fd\u5bb6\u4fe1\u606f\u5316\u4e13\u5bb6\u54a8\u8be2\u59d4\u5458\u4f1a\u59d4\u5458\u6c6a\u7389\u51ef\u8ba4\u4e3a\uff0c\u5fc5\u987b\u5173\u6ce8\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\u7684\u8f6c\u5316\uff0c\u8f6c\u6362\u751f\u4ea7\u65b9\u5f0f\u3001\u8c03\u6574\u5229\u76ca\u683c\u5c40\uff0c\u589e\u52a0\u6c11\u65cf\u7684\u51dd\u805a\u529b\u3001\u5411\u5fc3\u529b\uff0c\u6fc0\u53d1\u5168\u793e\u4f1a\u521b\u9020\u529b\u548c\u53d1\u5c55\u6d3b\u529b\uff0c\u4ece\u800c\u5b9e\u73b0\u66f4\u9ad8\u8d28\u91cf\u3001\u66f4\u6709\u6548\u7387\u3001\u66f4\u52a0\u516c\u5e73\u3001\u66f4\u53ef\u6301\u7eed\u7684\u53d1\u5c55\u3002\n\u3000\u3000\u2014\u2014\u4ece\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u4e5f\u662f\u4e3b\u52a8\u9002\u5e94\u7ecf\u6d4e\u65b0\u5e38\u6001\u3001\u7a81\u7834\u53d1\u5c55\u74f6\u9888\u7684\u73b0\u5b9e\u9009\u62e9\u3002\n\u3000\u3000\u201c\u6211\u4eec\u8981\u9002\u5e94\u7ecf\u6d4e\u589e\u901f\u6362\u6321\u7684\u65b0\u5e38\u6001\uff0c\u4fdd\u6301\u6218\u7565\u4e0a\u7684\u5e73\u5e38\u5fc3\u6001\u3002\u5fc5\u987b\u6de1\u5316\u901f\u5ea6\u2018\u6267\u5ff5\u2019\uff0c\u5f3a\u5316\u8d28\u91cf\u610f\u8bc6\uff0c\u4f18\u5316\u8981\u7d20\u6295\u5165\uff0c\u4fc3\u8fdb\u73b0\u4ee3\u5316\u5efa\u8bbe\u5404\u65b9\u9762\u76f8\u534f\u8c03\u3002\u201d\u4e2d\u56fd\u8d22\u653f\u79d1\u5b66\u7814\u7a76\u9662\u526f\u9662\u957f\u767d\u666f\u660e\u8ba4\u4e3a\uff0c\u4f9d\u9760\u4eba\u624d\u3001\u6280\u672f\u3001\u77e5\u8bc6\u3001\u4fe1\u606f\u7b49\u9ad8\u7ea7\u8981\u7d20\uff0c\u4ee5\u521b\u65b0\u9a71\u52a8\u6765\u6d88\u89e3\u5168\u7403\u5316\u7ea2\u5229\u548c\u6211\u56fd\u4eba\u53e3\u7ea2\u5229\u4e0d\u65ad\u4e0b\u964d\u7684\u4e0d\u5229\u5f71\u54cd\uff0c\u7a81\u7834\u8d44\u6e90\u80fd\u6e90\u3001\u73af\u5883\u7b49\u8981\u7d20\u7684\u74f6\u9888\u5236\u7ea6\uff0c\u5c3d\u5feb\u5b9e\u73b0\u53d1\u5c55\u65b0\u65e7\u52a8\u80fd\u7684\u8f6c\u6362\u3002\n\u3000\u3000\u8be5\u5982\u4f55\u201c\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\uff1f\n\u3000\u3000\u5df2\u7ecf\u521d\u6218\u544a\u6377\uff0c\u672a\u6765\u4ecd\u5e94\u575a\u6301\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\u3001\u4f18\u5316\u7ecf\u6d4e\u7ed3\u6784\u3001\u8f6c\u6362\u589e\u957f\u52a8\u529b\n\u3000\u3000\u201c\u4ece\u9ad8\u901f\u589e\u957f\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\uff0c\u6211\u4eec\u6709\u5e95\u6c14\u3001\u6709\u7ecf\u9a8c\u3002\u201d\u767d\u666f\u660e\u6307\u51fa\uff0c\u76ee\u524d\uff0c\u4f20\u7edf\u4ea7\u4e1a\u7115\u53d1\u751f\u673a\u3001\u65b0\u4ea7\u4e1a\u52bf\u5934\u5f3a\u52b2\u3001\u65b0\u7ecf\u6d4e\u52a0\u901f\u6210\u957f\uff0c\u6211\u56fd\u7ecf\u6d4e\u7ed3\u6784\u8c03\u6574\u6b63\u9010\u6e10\u8d70\u51fa\u9635\u75db\u671f\u3001\u6b65\u5165\u6536\u83b7\u671f\u3002\u201c2016\u5e74\u524d\u4e09\u5b63\u5ea6\u5de5\u4e1a\u4f01\u4e1a\u6240\u5f97\u7a0e5843\u4ebf\u5143\uff0c\u589e\u5e45\u4ec5\u4e3a0.6%\uff1b\u800c\u4eca\u5e74\u540c\u671f\u5de5\u4e1a\u4f01\u4e1a\u6240\u5f97\u7a0e\u540c\u6bd4\u589e\u957f21.3%\uff0c\u589e\u5e45\u7279\u522b\u660e\u663e\u3002\u8fd9\u91cc\u9762\u6709\u53bb\u5e74\u57fa\u6570\u8f83\u4f4e\u3001\u4eca\u5e74\u4ef7\u683c\u4e0a\u6da8\u7b49\u56e0\u7d20\uff0c\u4f46\u66f4\u4e3b\u8981\u7684\u662f\u4f01\u4e1a\u4e3b\u52a8\u8c03\u7ed3\u6784\uff0c\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u589e\u52a0\u503c\u5927\u5e45\u589e\u957f\u7684\u7ed3\u679c\u3002\u201d\n\u3000\u3000\u515a\u7684\u5341\u516b\u5927\u4ee5\u6765\uff0c\u521b\u65b0\u53d1\u5c55\u91ca\u653e\u5f3a\u52b2\u52a8\u80fd\uff0c\u521b\u9020\u4e86\u4e00\u4e2a\u53c8\u4e00\u4e2a\u201c\u4e2d\u56fd\u5947\u8ff9\u201d\uff1b\u534f\u8c03\u53d1\u5c55\u8865\u8db3\u77ed\u677f\uff0c\u8ba9\u53d1\u5c55\u66f4\u8d8b\u7a33\u5065\uff1b\u7eff\u8272\u53d1\u5c55\u575a\u5b9a\u63a8\u8fdb\uff0c\u4eba\u4e0e\u81ea\u7136\u66f4\u52a0\u548c\u8c10\uff1b\u5f00\u653e\u53d1\u5c55\u5b9e\u73b0\u4e92\u5229\u5171\u8d62\uff0c\u53d1\u5c55\u5929\u5730\u66f4\u52a0\u5e7f\u9614\uff1b\u5171\u4eab\u53d1\u5c55\u589e\u8fdb\u4eba\u6c11\u798f\u7949\uff0c\u793e\u4f1a\u516c\u5e73\u6b63\u4e49\u66f4\u52a0\u51f8\u663e\u2026\u2026\u5b9e\u8df5\u5145\u5206\u8bc1\u660e\uff0c\u6211\u4eec\u5df2\u7ecf\u521d\u6218\u544a\u6377\uff0c\u7ad9\u5728\u4e86\u65b0\u65f6\u4ee3\u201c\u9ad8\u8d28\u91cf\u53d1\u5c55\u201d\u7684\u5e73\u53f0\u4e0a\u3002\n\u3000\u3000\u4e0d\u8fc7\uff0c\u4ece\u9ad8\u901f\u589e\u957f\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\uff0c\u6211\u4eec\u8fd8\u9762\u4e34\u7740\u8bb8\u591a\u6311\u6218\u3002\n\u3000\u3000\u6bd4\u5982\uff0c\u5f53\u524d\u4ecd\u6709\u76f8\u5f53\u6570\u91cf\u8d44\u91d1\u3001\u571f\u5730\u3001\u52b3\u52a8\u529b\u8d44\u6e90\uff0c\u6c89\u6dc0\u5728\u4e25\u91cd\u8fc7\u5269\u4ea7\u80fd\u3001\u9ad8\u6c61\u67d3\u4ea7\u4e1a\u4e2d\uff0c\u62d6\u7d2f\u4e86\u7ecf\u6d4e\u8f6c\u578b\u548c\u6548\u7387\u63d0\u9ad8\u3002\u8981\u901a\u8fc7\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\uff0c\u628a\u8fd9\u4e9b\u8981\u7d20\u8d44\u6e90\u9010\u6b65\u8f6c\u79fb\u3001\u6ce8\u5165\u65b0\u5174\u3001\u7eff\u8272\u4ea7\u4e1a\u4e2d\u53bb\u3002\n\u3000\u3000\u53c8\u5982\uff0c\u7b80\u653f\u653e\u6743\u6210\u6548\u663e\u8457\uff0c\u4f01\u4e1a\u8f7b\u88c5\u4e0a\u9635\uff0c\u4f46\u5404\u90e8\u95e8\u3001\u5404\u7ea7\u653f\u5e9c\u4e4b\u95f4\u7684\u4fe1\u606f\u7cfb\u7edf\u4e92\u8054\u4e92\u901a\u5de5\u4f5c\u4e9f\u987b\u63d0\u901f\u3002\u201c\u5e94\u8be5\u5f62\u6210\u5168\u56fd\u7edf\u4e00\u653f\u52a1\u670d\u52a1\u5e73\u53f0\uff0c\u624d\u80fd\u8ba9\u4f01\u4e1a\u548c\u7fa4\u4f17\u66f4\u591a\u611f\u53d7\u5230\u2018\u653e\u7ba1\u670d\u2019\u6539\u9769\u6210\u6548\uff0c\u9664\u70e6\u82db\u4e4b\u5f0a\u3001\u65bd\u516c\u5e73\u4e4b\u7b56\u3001\u5f00\u4fbf\u5229\u4e4b\u95e8\u3002\u201d\u6c6a\u7389\u51ef\u8bf4\u3002\n\u3000\u3000\u518d\u5982\uff0c\u7eff\u8272\u53d1\u5c55\u4f9d\u7136\u4efb\u91cd\u9053\u8fdc\u3002\u4ece\u8fd1\u4e24\u5e74\u4e2d\u592e\u73af\u4fdd\u7763\u5bdf\u7ec4\u66dd\u5149\u7684\u6848\u4ef6\u770b\uff0c\u4e00\u4e9b\u5730\u65b9\u4e3a\u4e86\u91d1\u5c71\u94f6\u5c71\u727a\u7272\u7eff\u6c34\u9752\u5c71\uff0c\u8981\u901f\u5ea6\u4e0d\u8981\u8d28\u91cf\u7684\u987d\u75c7\u3001\u964b\u4e60\u4ecd\u7136\u5b58\u5728\u3002\n\u3000\u3000\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\u4e0d\u53ef\u9006\uff0c\u9ad8\u8d28\u91cf\u53d1\u5c55\u4efb\u52a1\u4e0d\u80fd\u7b49\uff0c\u9700\u8981\u64b8\u8d77\u8896\u5b50\u52a0\u6cb9\u5e72\u3002\n\u3000\u3000\u515a\u7684\u5341\u4e5d\u5927\u62a5\u544a\u63d0\u51fa\u4e86\u516d\u5927\u6218\u7565\u4e3e\u63aa\uff0c\u5305\u62ec\u6df1\u5316\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4ee5\u63a8\u52a8\u4ea7\u4e1a\u7ed3\u6784\u3001\u4ea7\u54c1\u7ed3\u6784\u8f6c\u578b\u5347\u7ea7\uff1b\u52a0\u5feb\u5efa\u8bbe\u521b\u65b0\u578b\u56fd\u5bb6\uff0c\u4ee5\u81ea\u4e3b\u521b\u65b0\u7684\u6280\u672f\u5e26\u52a8\u8d28\u91cf\u6548\u76ca\u63d0\u9ad8\uff1b\u5b9e\u65bd\u4e61\u6751\u632f\u5174\u6218\u7565\u548c\u533a\u57df\u534f\u8c03\u53d1\u5c55\u6218\u7565\uff0c\u4ee5\u89e3\u51b3\u7ecf\u6d4e\u53d1\u5c55\u4e0d\u5e73\u8861\u3001\u4e0d\u5145\u5206\u95ee\u9898\uff0c\u91ca\u653e\u7ecf\u6d4e\u53d1\u5c55\u65b0\u52a8\u80fd\uff1b\u52a0\u5feb\u5b8c\u5584\u793e\u4f1a\u4e3b\u4e49\u5e02\u573a\u7ecf\u6d4e\u4f53\u5236\uff0c\u5efa\u7acb\u6709\u5229\u4e8e\u63d0\u9ad8\u7ecf\u6d4e\u53d1\u5c55\u8d28\u91cf\u548c\u6539\u5584\u751f\u6001\u73af\u5883\u7684\u4f53\u5236\u673a\u5236\uff0c\u589e\u5f3a\u7ecf\u6d4e\u53d1\u5c55\u6d3b\u529b\uff1b\u63a8\u52a8\u5f62\u6210\u5168\u9762\u5f00\u653e\u65b0\u683c\u5c40\uff0c\u66f4\u597d\u5730\u5229\u7528\u5168\u7403\u5e02\u573a\u548c\u8d44\u6e90\u3002\n\u3000\u3000\u201c\u672a\u6765\u4ecd\u5e94\u575a\u6301\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\u3001\u4f18\u5316\u7ecf\u6d4e\u7ed3\u6784\u3001\u8f6c\u6362\u589e\u957f\u52a8\u529b\u3002\u201d\u9ad8\u57f9\u52c7\u6307\u51fa\uff0c\u672a\u6765\u7ecf\u6d4e\u53d1\u5c55\u4ecd\u8981\u4ece\u89c4\u6a21\u901f\u5ea6\u578b\u7c97\u653e\u589e\u957f\u8f6c\u5411\u8d28\u91cf\u6548\u7387\u578b\u96c6\u7ea6\u589e\u957f\uff0c\u4ece\u589e\u91cf\u6269\u80fd\u4e3a\u4e3b\u8f6c\u5411\u8c03\u6574\u5b58\u91cf\u3001\u505a\u4f18\u589e\u91cf\u5e76\u4e3e\u7684\u6df1\u5ea6\u8c03\u6574\uff1b\u4ece\u4f20\u7edf\u589e\u957f\u70b9\u8f6c\u5411\u65b0\u7684\u589e\u957f\u70b9\u3002\u201c\u8fd9\u4e9b\u4efb\u52a1\u540c\u5341\u516b\u5927\u4ee5\u6765\u6240\u5f62\u6210\u7684\u7ecf\u6d4e\u653f\u7b56\u6846\u67b6\u662f\u4e00\u81f4\u7684\uff0c\u5373\uff1a\u5efa\u7acb\u5728\u7ecf\u6d4e\u53d1\u5c55\u65b0\u5e38\u6001\u7684\u91cd\u5927\u5224\u65ad\u57fa\u7840\u4e4b\u4e0a\u7684\uff0c\u4ee5\u65b0\u53d1\u5c55\u7406\u5ff5\u4e3a\u6307\u5bfc\u3001\u4ee5\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4e3a\u4e3b\u7ebf\u7684\u7ecf\u6d4e\u653f\u7b56\u6846\u67b6\u3002\u63a5\u4e0b\u6765\uff0c\u8981\u5728\u8d2f\u5f7b\u843d\u5b9e\u515a\u7684\u5341\u4e5d\u5927\u7cbe\u795e\u7684\u8fc7\u7a0b\u4e2d\uff0c\u5c06\u8fd9\u4e9b\u653f\u7b56\u6301\u7eed\u6df1\u5316\u3001\u7ec6\u5316\uff0c\u5e76\u4e88\u4ee5\u521a\u6027\u843d\u5b9e\u3002\u201d\u9ad8\u57f9\u52c7\u8bf4\u3002\n\u7f16\u8f91\uff1a"}, {"emotion": "neg", "id": "0120b6b61b7f35003cae2a32e3aa2288f1", "c_t": "\u5356\u7684\u592a\u706b\uff1aiPhone X\u63a8\u52a8\u82f9\u679c\u80a1\u4ef7\u731b\u5347\u3000\u3000\u3010TechWeb\u62a5\u9053\u3011\u82f9\u679ciPhone 8/8 Plus\u7cfb\u5217\u906d\u9047\u4e86\u82f9\u679c\u53f2\u4e0a\u4e3a\u6570\u4e0d\u591a\u7684\u552e\u4ef7\u72c2\u8dcc\"\u6ed1\u94c1\u5362\"\uff0c\u53ef\u80fd\u65e9\u5c31\u9884\u6599\u5230\u8fd9\u4e2a\u7ed3\u679c\uff0c\u82f9\u679c\u8fd8\u662f\u9884\u7559iPhone X\u6765\u633d\u6551\u9893\u52bf\uff0c\u5728\u4e0a\u5468\u4e9415:01\u5206\uff0c\u82f9\u679c\u5b98\u65b9\u7f51\u7ad9\u548c\u5929\u732b\u540c\u6b65\u5f00\u542f\u4e86iPhone X\u7684\u9884\u552e\u3002\u4e0d\u5c11\u5403\u74dc\u7fa4\u4f17\u5f88\u597d\u5947iPhone X\u771f\u5b9e\u9500\u91cf\u5982\u4f55\u3002\n\u3000\u3000\u82f9\u679c\u5bf9\u5916\u5ba3\u79f0\uff0ciPhone X\u5b9e\u9645\u8d2d\u4e70\u9700\u6c42\u5b8c\u5168\u8d85\u51fa\u4e86\u82f9\u679c\u7684\u9884\u671f\uff0c\u4ec5\u5f00\u5356\u51e0\u5206\u949f\u5f8c\uff0c\u9996\u6279\u8d27\u6e90\u5c31\u5df2\u7ecf\u5728\u5168\u7403\u8303\u56f4\u5185\u5356\u5b8c\uff0c\u8fd9\u8ba9\u4ed6\u4eec\u611f\u5230\u60ca\u8bb6\u3002\"\n\u3000\u3000\u4e0e\u6b64\u540c\u65f6\uff0c\u82f9\u679c\u79f0iPhone X\u7684\u9884\u8d2d\u91cf\u5df2\"\u521b\u5386\u53f2\u65b0\u9ad8\"\u3002\u82f9\u679c\u53d1\u8a00\u4eba\u8868\u793a\uff0c\"\u4ece\u521d\u6b65\u53cd\u5e94\u6765\u770b\uff0c\u7528\u6237\u9700\u6c42\u6b63\u521b\u4e0b\u5386\u53f2\u65b0\u9ad8\u3002\u6211\u4eec\u6b63\u81f4\u529b\u4e8e\u5c3d\u5feb\u8ba9\u6bcf\u4e2a\u60f3\u4e70\u7684\u7528\u6237\u4e70\u5230\u8fd9\u6b3e\u5177\u6709\u9769\u547d\u6027\u7684\u7684\u65b0\u4ea7\u54c1\u3002\"\n\u3000\u3000\u4ece\u5b98\u65b9\u53d1\u8a00\u4e2d\u53ef\u4ee5\u770b\u5230\uff0ciPhone X\u771f\u7684\u662f\u5356\u706b\u4e86\uff0c\u8fd9\u70b9\u4ece\u82f9\u679c\u80a1\u4ef7\u4e2d\u4e5f\u80fd\u770b\u5230\u86db\u4e1d\u9a6c\u8ff9\uff0c\u7ee7\u4e0a\u5468\u4e94\u80a1\u4ef7\u4e0a\u6da8 3.6% \u4e4b\u5f8c\uff0c\u82f9\u679c\u80a1\u4ef7\u5468\u4e00\u7ee7\u7eed\u9ad8\u5f00\u9ad8\u8d70\u3002\u82f9\u679c\u80a1\u4ef7\u76d8\u4e2d\u66f4\u662f\u521b\u51fa 168.07\u7f8e\u5143\u7684\u5386\u53f2\u65b0\u9ad8\u3002\n\u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0ciPhone X\u9996\u6279\u62a2\u8d2d\u673a\u578b\uff0c\u5c06\u4f1a\u572811\u67083\u65e5\u6b63\u5f0f\u53d1\u8d27\uff0c\u9996\u6279\u673a\u578b\u6ea2\u4ef7\u7387\u5f88\u9ad8\uff0c\u6700\u9ad8\u5e02\u573a\u66fe\u5f00\u51fa1W8\u56de\u6536\u4ef7\uff0c\u53ef\u89c1\u5bf9\u4e8e\u8fd9\u6b3e\u5168\u65b0\u624b\u673a\uff0c\u6d88\u8d39\u8005\u5b8c\u5168\u62db\u67b6\u4e0d\u4f4f\u3002"}, {"emotion": "neg", "id": "01a59829178534297bf61b01aa6c1ffed7", "c_t": "\u6df1\u79cb\u65f6\u8282\u51b7\u7a7a\u6c14\u9891\u7e41 \u591a\u5730\u8fce\u4e0b\u534a\u5e74\u201c\u6700\u51b7\u65e9\u6668\u201d\u3000\u3000\u4e2d\u65b0\u7f51\u5317\u4eac10\u670831\u65e5\u753530\u65e5\uff0c\u5728\u51b7\u7a7a\u6c14\u7684\u5f71\u54cd\u4e0b\uff0c\u4e0a\u6d77\u3001\u6b66\u6c49\u3001\u5408\u80a5\u3001\u6d4e\u5357\u7b49\u57ce\u5e02\u8fce\u6765\u4eca\u5e74\u4e0b\u534a\u5e74\u201c\u6700\u51b7\u65e9\u6668\u201d\uff0c\u79cb\u610f\u8d8a\u6765\u8d8a\u6d53\u3002\u636e\u6c14\u8c61\u9884\u62a5\uff0c31\u65e5\uff0c\u6c5f\u6dee\u3001\u6c5f\u5357\u3001\u534e\u5357\u7684\u4e0d\u5c11\u5730\u65b9\u6700\u4f4e\u6c14\u6e29\u8fd8\u5c06\u521b\u4e0b\u65b0\u4f4e\uff0c\u6c5f\u82cf\u3001\u5b89\u5fbd\u3001\u6e56\u5317\u7b49\u5730\u65e9\u665a\u5bd2\u610f\u52a0\u91cd\u3002\n\u8d44\u6599\u56fe\uff1a\u65b0\u7586\u4e4c\u9c81\u6728\u9f50\u5e02\u8857\u5934\uff0c\u5916\u51fa\u6c11\u4f17\u811a\u6b65\u5306\u5306\u3002 \u4e2d\u65b0\u793e\u8bb0\u8005 \u5218\u65b0\u6444\n\u3000\u3000\u591a\u5730\u8fce\u4eca\u5e74\u4e0b\u534a\u5e74\u201c\u6700\u51b7\u65e9\u6668\u201d\n\u3000\u3000\u8fdb\u5165\u5341\u6708\u672b\uff0c\u4e0d\u5c11\u6c11\u4f17\u5728\u81ea\u5df1\u7684\u5fae\u4fe1\u670b\u53cb\u5708\u4e2d\u611f\u6168\uff0c\u201c\u6ca1\u6709\u6700\u51b7\uff0c\u53ea\u6709\u66f4\u51b7\u3002\u201d\u8fd9\u4e24\u5929\uff0c\u4e0d\u5c11\u5730\u65b9\u7684\u6c14\u6e29\u786e\u5b9e\u521b\u4e0b\u4e86\u4eca\u5e74\u4e0b\u534a\u5e74\u65b0\u4f4e\u3002\n\u3000\u3000\u4f8b\u5982\uff0c\u4ece\u6e56\u5317\u7701\u6c14\u8c61\u5c40\u5b98\u7f51\u53d1\u5e03\u7684\u6d88\u606f\u6765\u770b\uff0c29\u65e5\uff0c\u6e56\u5317\u5927\u90e8\u5730\u533a\u5317\u98ce\u56db\u8d77\uff0c\u6700\u9ad8\u6c14\u6e29\u51fa\u73b0\u4e0d\u540c\u7a0b\u5ea6\u4e0b\u6ed1\u3002\u968f\u7740\u51b7\u7a7a\u6c14\u5728\u591c\u95f4\u4e0d\u65ad\u6e17\u900f\uff0c\u5bd2\u610f\u4e5f\u572830\u65e5\u65e9\u6668\u663e\u73b0\u4e86\u51fa\u6765\u3002\n\u3000\u3000\u76d1\u6d4b\u6570\u636e\u663e\u793a\uff0c30\u65e5\u65e9\u6668\uff0c\u6e56\u5317\u4e1c\u90e8\u5730\u533a\u6700\u4f4e\u6c14\u6e29\u666e\u904d\u53ea\u67095-8\u2103\uff0c\u5bd2\u610f\u5341\u8db3\u3002\u54b8\u5b81\u3001\u6b66\u6c49\u3001\u9ec4\u77f3\u7b49\u4e1c\u90e8\u5927\u90e8\u5206\u53bf\u5e02\u5237\u65b0\u4e86\u4e0b\u534a\u5e74\u6765\u7684\u6c14\u6e29\u65b0\u4f4e\uff1b\u6b66\u6c4930\u65e5\u65e9\u6668\u6700\u4f4e\u6c14\u6e29\u4ec5\u67095.5\u2103\uff0c\u4e0e\u5e38\u5e74\u5341\u6708\u4e0b\u65ec\u5e73\u5747\u6700\u4f4e\u6c14\u6e2912.6\u2103\u76f8\u6bd4\uff0c\u504f\u4f4e7\u2103\u4ee5\u4e0a\u3002\n\u3000\u3000\u518d\u5982\uff0c\u636e\u5a92\u4f53\u62a5\u9053\uff0c30\u65e5\u65e9\u6668\uff0c\u4e0a\u6d77\u6700\u4f4e\u6c14\u6e29\u666e\u904d\u572810\u2103\u4e0a\u4e0b\u3002\u76d1\u6d4b\u663e\u793a\uff0c\u622a\u81f330\u65e5\u6e05\u66686\u65f6\uff0c\u4e2d\u5fc3\u57ce\u533a\u5f90\u5bb6\u6c47\u7ad9\u6700\u4f4e\u6c14\u6e29\u4e3a10.9\u2103\uff0c\u5d07\u660e\u3001\u677e\u6c5f\u548c\u5609\u5b9a\u7684\u6c14\u6e29\u5747\u8dcc\u783410\u2103\uff0c\u5206\u522b\u4e3a8.4\u30019.2\u548c9.8\u2103\uff0c\u4e3a\u4e0b\u534a\u5e74\u6765\u6700\u51b7\u65e9\u6668\u3002\n\u3000\u3000\u800c\u5728\u8fbd\u5b81\uff0c30\u65e5\u65e9\u6668\uff0c\u629a\u987a\u3001\u4e39\u4e1c\u3001\u961c\u65b0\u7b49\u5730\u533a\u7684\u6700\u4f4e\u6e29\u8dcc\u7834\u51b0\u70b9\uff0c\u5728-5\u2103\u81f3-1\u2103\u4e4b\u95f4\uff0c\u7701\u4f1a\u6c88\u9633\u6700\u4f4e\u6e29\u4ec5-5\u2103\u3002\n\u3000\u3000\u53e6\u636e\u4e2d\u56fd\u5929\u6c14\u7f51\u62a5\u9053\uff0c31\u65e5\uff0c\u6c5f\u6dee\u3001\u6c5f\u5357\u3001\u534e\u5357\u7684\u4e0d\u5c11\u5730\u65b9\u6700\u4f4e\u6c14\u6e29\u8fd8\u5c06\u521b\u4e0b\u65b0\u4f4e\uff0c\u6c5f\u82cf\u3001\u5b89\u5fbd\u3001\u6e56\u5317\u7b49\u5730\u65e9\u665a\u5bd2\u610f\u52a0\u91cd\uff0c30\u65e5\u548c31\u65e5\uff0c\u5168\u56fd\u5c06\u670913\u4e2a\u5de6\u53f3\u7684\u7701\u4f1a\u7ea7\u57ce\u5e02\u6700\u4f4e\u6c14\u6e29\u521b\u4e0b\u534a\u5e74\u6765\u65b0\u4f4e\u3002\n\u8d44\u6599\u56fe\uff1a\u516b\u8fbe\u5cad\u56fd\u5bb6\u68ee\u6797\u516c\u56ed\u8fdb\u5165\u7ea2\u53f6\u6700\u4f73\u89c2\u8d4f\u671f\u3002\u4f55\u5efa\u52c7 \u6444\n\u3000\u3000\u8fd9\u4e9b\u5730\u65b9\u9010\u6e10\u56de\u6696\n\u3000\u3000\u90a3\u4e48\uff0c\u8fd9\u4e00\u8f6e\u8f83\u5f3a\u964d\u6e29\u7684\u8fc7\u7a0b\u4f55\u65f6\u7ed3\u675f\u5462\uff1f\n\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c30\u65e5\u767d\u5929\u5f00\u59cb\uff0c\u5317\u65b9\u5927\u90e8\u5206\u5730\u533a\u51b7\u7a7a\u6c14\u5f71\u54cd\u9010\u6e10\u7ed3\u675f\u3002\u4ee5\u8fbd\u5b81\u4e3a\u4f8b\uff0c\u636e\u5a92\u4f53\u62a5\u9053\uff0c31\u65e5\u65e9\u6668\uff0c\u9664\u629a\u987a\u3001\u4e39\u4e1c\u3001\u961c\u65b0\u6700\u4f4e\u6e29\u8fd8\u57280\u2103\u4ee5\u4e0b\uff0c\u5176\u4ed6\u5730\u533a\u5747\u5c06\u56de\u5347\u81f3\u51b0\u70b9\u53ca\u4ee5\u4e0a\u300231\u65e5\u767d\u5929\u6c14\u6e29\u5c0f\u5e45\u4e0a\u5347\uff0c\u5927\u90e8\u5730\u533a\u6700\u9ad8\u6c14\u6e29\u5c06\u8fbe16-18\u2103\u3002\n\u3000\u3000\u6e56\u5317\u7701\u6c14\u8c61\u5c40\u5219\u53d1\u5e03\u901a\u62a5\u79f0\uff0c31\u65e5\u8d77\u968f\u7740\u51b7\u7a7a\u6c14\u5f71\u54cd\u8d8b\u4e8e\u7ed3\u675f\uff0c\u5168\u7701\u7684\u9ad8\u4f4e\u6e29\u5c06\u7f13\u7f13\u56de\u5347\uff0c\u9884\u8ba1\u5230\u4e8611\u67081\u65e5\uff0c\u5927\u90e8\u5730\u533a\u6700\u9ad8\u6c14\u6e29\u5c06\u6500\u5347\u81f320-23\u2103\uff0c\u6700\u4f4e\u6c14\u6e29\u4e5f\u4f1a\u91cd\u56de10\u2103\u4ee5\u4e0a\u3002\n\u3000\u3000\u5c71\u4e1c\u6d4e\u5357\u7b49\u57ce\u5e02\u572830\u65e5\u8fce\u6765\u4eca\u5e74\u4e0b\u534a\u5e74\u201c\u6700\u51b7\u65e9\u6668\u201d\u540e\uff0c\u4e5f\u5c06\u8fce\u6765\u56de\u6696\u5929\u6c14\u3002\n\u3000\u3000\u636e\u5a92\u4f53\u62a5\u9053\uff0c\u9884\u8ba131\u65e5\u9c81\u897f\u5357\u3001\u9c81\u897f\u5317\u548c\u9c81\u4e2d\u7684\u5317\u90e8\u5730\u533a\u6700\u9ad8\u6c14\u6e29\u56de\u5347\u81f318\u2103\u5de6\u53f3\uff0c\u5176\u4ed6\u5730\u533a\u6700\u9ad8\u6c14\u6e29\u4e3a16\u2103\u4e0a\u4e0b\u3002\u4e0d\u8fc7\uff0c\u65e9\u665a\u6c14\u6e29\u4f9d\u7136\u572810\u2103\u4ee5\u4e0b\uff0c\u4f53\u611f\u5bd2\u51b7\u3002\n\u56fe\u7247\u6765\u6e90\uff1a\u4e2d\u592e\u6c14\u8c61\u53f0\u5b98\u7f51\n\u3000\u3000\u672a\u6765\u51e0\u5929\u5168\u56fd\u5929\u6c14\u8d70\u52bf\u5982\u4f55\uff1f\n\u3000\u3000\u5c3d\u7ba1\u4e0d\u5c11\u5730\u65b9\u5c06\u4ece\u8fd9\u4e24\u5929\u5f00\u59cb\u8fce\u6765\u56de\u6696\u5929\u6c14\uff0c\u4f46\u5728\u9752\u85cf\u9ad8\u539f\u7684\u4e00\u4e9b\u5730\u65b9\uff0c\u5373\u5c06\u8fce\u6765\u4e00\u6b21\u4f34\u968f\u964d\u96e8\u3001\u964d\u96ea\u7684\u964d\u6e29\u8fc7\u7a0b\u3002\n\u3000\u3000\u4e2d\u592e\u6c14\u8c61\u53f030\u65e5\u665a\u53d1\u5e03\u901a\u62a5\u79f0\uff0c\u53d7\u5357\u652f\u69fd\u5f71\u54cd\uff0c\u9884\u8ba110\u670830\u65e5\u591c\u95f4\u81f311\u67081\u65e5\uff0c\u9752\u85cf\u9ad8\u539f\u4e1c\u90e8\u5c06\u6709\u4e00\u6b21\u96e8\u96ea\u964d\u6e29\u5929\u6c14\u8fc7\u7a0b\u3002\n\u3000\u3000\u5176\u4e2d\uff0c\u897f\u85cf\u4e1c\u5317\u90e8\u3001\u9752\u6d77\u5357\u90e8\u3001\u5ddd\u897f\u9ad8\u539f\u5317\u90e8\u6709\u5c0f\u5230\u4e2d\u96ea\u6216\u96e8\u5939\u96ea\uff0c\u5c40\u5730\u6709\u5927\u96ea\uff0c\u4e91\u5357\u897f\u5317\u90e8\u3001\u897f\u85cf\u4e1c\u5357\u90e8\u548c\u5ddd\u897f\u9ad8\u539f\u5357\u90e8\u6709\u4e2d\u96e8\u6216\u5927\u96e8\uff0c\u5176\u4e2d\u4e91\u5357\u897f\u5317\u90e8\u5c40\u5730\u6709\u66b4\u96e8\u3002\n\u3000\u3000\u4f53\u73b0\u5728\u6e29\u5ea6\u4e0a\uff0c\u9884\u8ba1\u9752\u85cf\u9ad8\u539f\u4e2d\u4e1c\u90e8\u5c06\u67094-6\u2103\u7684\u964d\u6e29\uff0c\u5c40\u5730\u964d\u6e29\u53ef\u8fbe8-10\u2103\u3002\n\u3000\u3000\u6b64\u5916\uff0c\u53d7\u51b7\u7a7a\u6c14\u5f71\u54cd\uff0c\u9884\u8ba110\u670830\u65e5\u591c\u95f4\u81f331\u65e5\u767d\u5929\uff0c\u53f0\u6e7e\u6d77\u5ce1\u3001\u53f0\u6e7e\u4e1c\u5357\u6d0b\u9762\u3001\u5df4\u58eb\u6d77\u5ce1\u3001\u5357\u6d77\u5317\u90e8\u548c\u4e2d\u90e8\u6d77\u57df\u5c06\u67097-8\u7ea7\u3001\u9635\u98ce9-10\u7ea7\u7684\u4e1c\u5317\u5927\u98ce\u3002\u4e3a\u6b64\uff0c\u4e2d\u592e\u6c14\u8c61\u53f010\u670830\u65e518\u65f6\u7ee7\u7eed\u53d1\u5e03\u6d77\u4e0a\u5927\u98ce\u9884\u62a5\u3002\n\u3000\u3000\u6709\u6c14\u8c61\u4e13\u5bb6\u63d0\u9192\uff0c\u8fd1\u671f\u51b7\u7a7a\u6c14\u6d3b\u52a8\u9891\u7e41\uff0c\u6bcf\u6b21\u51b7\u7a7a\u6c14\u5230\u6765\u90fd\u4f1a\u51fa\u73b0\u4e00\u6b21\u660e\u663e\u7684\u964d\u6e29\uff0c\u800c\u540e\u53c8\u8f6c\u6674\u56de\u6696\uff0c\u4f46\u603b\u4f53\u8d8b\u52bf\u662f\u5929\u6c14\u8d8a\u6765\u8d8a\u5bd2\u51b7\u3002\u8fd1\u671f\uff0c\u5929\u6c14\u53d8\u5316\u5267\u70c8\uff0c\u9700\u53ca\u65f6\u589e\u6dfb\u8863\u7269\uff0c\u6ce8\u610f\u9632\u5bd2\u4fdd\u6696\uff0c\u4ee5\u9632\u611f\u5192\u3002(\u5b8c)\n\u7f16\u8f91\uff1a"}, {"emotion": "neg", "id": "01180ec5c38ab9c7be2c05466dfed178f7", "c_t": "\u8981\u7528\u5236\u5ea6\u4e4b\u529b\u6839\u9664\u7279\u6743\u7279\u6743\uff0c\u4e0d\u662f\u6b63\u5f53\u4e2a\u4eba\u5229\u76ca\uff0c\u4e0d\u662f\u6b63\u5e38\u5de5\u4f5c\u804c\u6743\uff0c\u800c\u662f\u4e00\u79cd\u201c\u6cd5\u5916\u4e4b\u6743\u201d\uff0c\u662f\u5728\u6cd5\u5f8b\u548c\u5236\u5ea6\u89c4\u5b9a\u4e4b\u5916\uff0c\u5229\u7528\u6743\u529b\u641e\u7279\u6b8a\u3001\u8c0b\u79c1\u5229\uff0c\u591a\u5403\u591a\u5360\uff0c\u8d2a\u5403\u8d2a\u5360\u3002\u4eba\u6c11\u7fa4\u4f17\u7684\u5fc3\u5730\u662f\u660e\u955c\uff0c\u90a3\u4e9b\u5f00\u7740\u5b9d\u9a6c\u9886\u4f4e\u4fdd\u3001\u80fd\u529b\u54c1\u884c\u90fd\u4e0d\u884c\u7167\u6837\u6709\u597d\u5de5\u4f5c\u3001\u89c6\u95ef\u7ea2\u706f\u4e3a\u513f\u620f\u7b49\u602a\u73b0\u8c61\uff0c\u5c31\u662f\u201c\u522b\u4eba\u4e0d\u53ef\u4ee5\u505a\u7684\uff0c\u4ed6\u5374\u80fd\u505a\uff1b\u522b\u4eba\u505a\u4e86\u6709\u4e8b\u7684\uff0c\u4ed6\u5374\u6ca1\u4e8b\u513f\u201d\u7684\u7279\u6743\uff0c\u8fd9\u79cd\u201c\u76f8\u5bf9\u5265\u593a\u611f\u201d\u662f\u4eba\u6c11\u7fa4\u4f17\u6700\u75db\u6068\u7684\u3002\n\u5341\u516b\u5927\u4ee5\u6765\uff0c\u968f\u7740\u5168\u9762\u4ece\u4e25\u6cbb\u515a\u201c\u4e00\u9524\u63a5\u7740\u4e00\u9524\u6572\u3001\u4e00\u6263\u63a5\u7740\u4e00\u6263\u626d\u201d\uff0c\u4e00\u4e9b\u660e\u76ee\u5f20\u80c6\u5927\u641e\u6743\u94b1\u4ea4\u6613\u7684\u8150\u8d25\u884c\u5f84\u4e0d\u89c1\u4e86\uff0c\u4f46\u662f\u201c\u4e00\u4eba\u5f97\u9053\u3001\u9e21\u72ac\u5347\u5929\u201d\uff0c\u201c\u6709\u6743\u5c31\u6709\u4e00\u5207\u201d\u7b49\u7279\u6743\u601d\u60f3\u4e0e\u7279\u6743\u4f5c\u98ce\u76d8\u8e1e\u5728\u4e00\u4e9b\u515a\u5458\u5e72\u90e8\u7684\u601d\u60f3\u201c\u51bb\u571f\u5c42\u201d\u5e76\u6ca1\u6709\u88ab\u94f2\u9664\uff0c\u4ed6\u4eec\u5bfb\u7740\u5347\u5b66\u3001\u8003\u516c\u52a1\u5458\u3001\u529e\u4f01\u4e1a\u3001\u4e0a\u9879\u76ee\u3001\u664b\u7ea7\u3001\u4e70\u623f\u5b50\u3001\u627e\u5de5\u4f5c\u3001\u51fa\u56fd\u7b49\u5404\u79cd\u673a\u4f1a\uff0c\u62c9\u5173\u7cfb\u3001\u641e\u95e8\u9053\uff0c\u4e3e\u8d77\u51a0\u5195\u5802\u7687\u7684\u7406\u7531\u3001\u62ff\u7740\u201c\u540d\u6b63\u8a00\u987a\u201d\u7684\u4f9d\u636e\uff0c\u5728\u4eba\u6c11\u7fa4\u4f17\u773c\u76ae\u5e95\u4e0b\u505a\u7834\u574f\u89c4\u77e9\u7684\u201c\u5e26\u5934\u4eba\u201d\uff0c\u5e76\u4ee5\u803b\u4e3a\u8363\u3002\u8fd9\u4e9b\u7279\u6743\u601d\u60f3\u548c\u7279\u6743\u73b0\u8c61\u8f83\u4e4b\u89e6\u72af\u6cd5\u7eaa\u7684\u8150\u8d25\u884c\u4e3a\u5177\u6709\u66f4\u6df1\u7684\u9690\u853d\u6027\u548c\u987d\u56fa\u6027\uff0c\u662f\u8150\u8d25\u7684\u6839\u6e90\u4e4b\u4e00\uff0c\u662f\u4ece\u4e0d\u6562\u8150\u5230\u4e0d\u60f3\u8150\u8f6c\u53d8\u8def\u9014\u4e0a\u7684\u201c\u62e6\u8def\u864e\u201d\uff0c\u5fc5\u987b\u7528\u5236\u5ea6\u4e4b\u529b\u52a0\u4ee5\u6839\u9664\u3002\n\u4e60\u8fd1\u5e73\u603b\u4e66\u8bb0\u5728\u5341\u4e5d\u5927\u62a5\u544a\u4e2d\u6307\u51fa\uff1a\u201c\u4efb\u4f55\u7ec4\u7ec7\u548c\u4e2a\u4eba\u90fd\u4e0d\u5f97\u6709\u8d85\u8d8a\u5baa\u6cd5\u6cd5\u5f8b\u7684\u7279\u6743\uff0c\u7edd\u4e0d\u5141\u8bb8\u4ee5\u8a00\u4ee3\u6cd5\u3001\u4ee5\u6743\u538b\u6cd5\u3001\u9010\u5229\u8fdd\u6cd5\u3001\u5f87\u79c1\u6789\u6cd5\u3002\u201d\u540c\u65f6\u8981\u6c42\u201c\u5404\u7ea7\u9886\u5bfc\u5e72\u90e8\u8981\u589e\u5f3a\u6c11\u4e3b\u610f\u8bc6\uff0c\u53d1\u626c\u6c11\u4e3b\u4f5c\u98ce\uff0c\u63a5\u53d7\u4eba\u6c11\u76d1\u7763\uff0c\u5f53\u597d\u4eba\u6c11\u516c\u4ec6\u3002\u201d\u201c\u575a\u51b3\u53cd\u5bf9\u4e00\u5207\u635f\u5bb3\u4eba\u6c11\u5229\u76ca\u3001\u8131\u79bb\u7fa4\u4f17\u7684\u884c\u4e3a\u3002\u201d\n\u7531\u6b64\u53ef\u89c1\uff0c\u6c11\u4e3b\u4e0e\u6cd5\u6cbb\u76f8\u7ed3\u5408\uff0c\u4ece\u56fd\u5bb6\u6cbb\u7406\u4f53\u7cfb\u7684\u89d2\u5ea6\u53cd\u5bf9\u7279\u6743\u5c06\u6210\u4e3a\u52aa\u529b\u7684\u65b9\u5411\u3002\u6211\u4eec\u9996\u5148\u8981\u5728\u5b8f\u89c2\u4e0a\u63a8\u8fdb\u6743\u529b\u8fd0\u884c\u7684\u5236\u5ea6\u5316\u3001\u89c4\u8303\u5316\uff0c\u8981\u5065\u5168\u79d1\u5b66\u6709\u6548\u7684\u6743\u529b\u5236\u7ea6\u548c\u534f\u8c03\u673a\u5236\uff0c\u7834\u9664\u90e8\u95e8\u5229\u76ca\u3001\u5730\u533a\u5229\u76ca\u548c\u5c0f\u96c6\u56e2\u5229\u76ca\u7684\u85e9\u7bf1\u3002\u5176\u6b21\u8981\u5728\u5177\u4f53\u64cd\u4f5c\u4e0a\uff0c\u5f15\u5165\u65b0\u6280\u672f\u65b0\u7406\u5ff5\uff0c\u7528\u201c\u5236\u5ea6\u5316\u3001\u4fe1\u606f\u5316\u3001\u516c\u5f00\u5316\u201d\u5c06\u6743\u529b\u53ca\u5176\u201c\u6811\u836b\u201d\u5747\u7f6e\u4e8e\u9633\u5149\u4e4b\u4e0b\uff0c\u8ba9\u6bcf\u4e00\u4f4d\u516c\u804c\u4eba\u5458\u6ca1\u6709\u7279\u6743\u7684\u91ca\u653e\u7a7a\u95f4\uff1b\u7b2c\u4e09\u8981\u4e25\u683c\u8d2f\u5f7b\u843d\u5b9e\u300a\u515a\u7ae0\u300b\u3001\u300a\u5173\u4e8e\u515a\u5185\u653f\u6cbb\u751f\u6d3b\u7684\u82e5\u5e72\u51c6\u5219\u300b\u7b49\u515a\u5185\u6cd5\u89c4\uff0c\u52a0\u5f3a\u5b97\u65e8\u610f\u8bc6\u548c\u515a\u6027\u9524\u70bc\uff0c\u6df1\u5165\u63a8\u8fdb\u201c\u4e0d\u5fd8\u521d\u5fc3\u3001\u7262\u8bb0\u4f7f\u547d\u201d\u7b49\u4e13\u9898\u6559\u80b2\uff0c\u5e76\u5f62\u6210\u5236\u5ea6\uff0c\u4ee5\u6b64\u6fc0\u53d1\u515a\u5185\u6e05\u6c14\u3001\u6db5\u517b\u793e\u4f1a\u6b63\u6c14\uff0c\u94f2\u9664\u7279\u6743\u601d\u60f3\u7684\u571f\u58e4\uff0c\u4ee5\u516c\u5e73\u6fc0\u53d1\u515a\u548c\u56fd\u5bb6\u7684\u6d3b\u529b\uff0c\u4ee5\u6e05\u6b63\u83b7\u5f97\u4eba\u6c11\u7fa4\u4f17\u7684\u4fe1\u4efb\uff0c\u53bb\u52aa\u529b\u5b9e\u73b0\u6d77\u664f\u6cb3\u6e05\u3001\u5929\u6e05\u6c14\u6717\u7684\u590d\u5174\u68a6\u3002\n\u8c2d\u5251\n\u6765\u6e90\uff1a\u4e2d\u56fd\u5e38\u5dde\u7f51 \u89c5\u6e21\u65f6\u8bc4\n\u539f\u7a3f\u6807\u9898\uff1a\n\u539f\u7a3f\u4f5c\u8005\uff1a"}, {"emotion": "neg", "id": "01856bf2f152d9b4bca6728a13fdb94eb0", "c_t": "\u91d1\u878d\u6848\u4f8b\u5206\u6790\uff1a\u90ae\u50a8\u94f6\u884c\u643a\u624bIBM\u6253\u9020\u533a\u5757\u94fe\u8d44\u4ea7\u6258\u7ba1\u7cfb\u7edf1\u670810\u65e5\uff0c\u4e2d\u56fd\u90ae\u653f\u50a8\u84c4\u94f6\u884c\u548c\u56fd\u5916\u4e92\u8054\u7f51\u5de8\u5934IBM\