ff0c\u5e0c\u671b\u901a\u8fc7\u5ba2\u89c2\u6570\u636e\u800c\u975e\u540d\u58f0\u6216\u4f20\u95fb\u6765\u8bc4\u5224\u4e2d\u56fd\u4f01\u4e1a\u9886\u5bfc\u8005\u7684\u8868\u73b0\u3002\u6211\u4eec\u8ba4\u540c\u8fd9\u79cd\u505a\u6cd5\uff0c\u4f46\u4e5f\u611f\u5230\u6b64\u6cd5\u5e76\u4e0d\u5168\u9762\uff0c\u56e0\u4e3a\u8d22\u52a1\u6570\u636e\u65e0\u6cd5\u4f53\u73b0\u9886\u5bfc\u8005\u5728\u5e02\u573a\u4e1a\u7ee9\u4e4b\u5916\u7684\u8868\u73b0\u3002\u7531\u4e8e\u6211\u4eec\u7684\u76ee\u6807\u662f\u81f4\u529b\u4e8e\u8861\u91cfCEO\u771f\u6b63\u7684\u957f\u671f\u4ef7\u503c\uff0c\u6240\u4ee5\uff0c\u4eca\u5e74\u9664\u4e86\u5c06\u80a1\u4e1c\u56de\u62a5\u548c\u5e02\u503c\u53d8\u5316\u89c6\u4e3a\u8003\u5bdf\u91cd\u70b9\u4e4b\u5916\uff0c\u6211\u4eec\u51b3\u5b9a\u66f4\u8fdb\u4e00\u6b65----\u5f15\u5165\u516c\u53f8\u5728\u53ef\u6301\u7eed\u53d1\u5c55\u65b9\u9762\u7684\u8bc4\u4f30\uff0c\u5373\u5c06\u73af\u5883\u3001\u793e\u4f1a\u548c\u6cbb\u7406\uff08ESG\uff09\u6210\u679c\u4f5c\u4e3a\u4e00\u9879\u8861\u91cf\u6807\u51c6\u3002\n\u6709ESG\u6307\u6807\u8fdb\u5165\u8bc4\u4f30\u4f53\u7cfb\uff0c\u518d\u52a0\u4e0a\u8fc7\u53bb\u4e00\u5e74\u4e2d\u56fd\u516c\u53f8\u7684\u80a1\u4ef7\u6da8\u8dcc\uff0c\u6211\u4eec2017\u4e2d\u56fd\u767e\u4f73CEO\u699c\u5355\u4e2d\u51fa\u73b0\u4e8637\u4f4d\u65b0CEO\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u670963\u4f4dCEO\u8fde\u7eed\u4e24\u5e74\u4e0a\u699c\u3002\u8dcc\u51fa\u699c\u5355\u7684CEO\uff0c\u539f\u56e0\u6765\u81ea\u4ee5\u4e0b\u51e0\u70b9\uff1a\u4e00\u662f\u5378\u4efb\u6216\u9000\u4f11\uff1b\u4e8c\u662f\u516c\u53f8\u80a1\u4ef7\u8868\u73b0\u4e0d\u4f73\uff1b\u4e09\u662fESG\u7684\u8bc4\u5206\u4e0d\u9ad8\u4ece\u800c\u4e0e\u699c\u5355\u65e0\u7f18\u3002\u4eca\u5e74\u5165\u699c\u7684100\u4f4dCEO\u5e73\u5747\u4efb\u671f\u4e3a14\u5e74\uff0c\u5728\u4efb\u671f\u95f4\u5e73\u5747\u521b\u90201648%\u7684\u80a1\u4e1c\u603b\u56de\u62a5\uff0c\u5e73\u5747\u5e74\u56de\u62a5\u7387\u4e3a22%\u3002\n{IMG:1}\n\u6392\u5728\u4eca\u5e74\u699c\u5355\u7b2c\u4e00\u540d\u7684\u662f\u4f0a\u5229\u80a1\u4efd\uff08\u65d7\u4e0b\u62e5\u6709\u91d1\u5178\u3001\u5b89\u6155\u5e0c\u3001QQ\u661f\u3001\u91d1\u9886\u51a0\u3001\u7545\u8f7b\u548c\u5de7\u4e50\u5179\u7b49\u4e73\u5236\u54c1\u54c1\u724c\uff09CEO\u6f58\u521a\u3002\u6f58\u521a\u81ea2002\u5e74\u5c31\u4efbCEO\u540e\uff0c\u5e26\u9886\u4f0a\u5229\u65bd\u884c\u8d28\u91cf\u9886\u5148\u6218\u7565\uff0c\u63a8\u8fdb\u5168\u94fe\u521b\u65b0\u5e76\u6784\u5efa\u5168\u7403\u667a\u6167\u94fe\uff0c\u8f83\u65e9\u5e03\u5c40\u56fd\u9645\u5316\u6574\u5408\u5168\u7403\u8d44\u6e90\uff0c\u4f7f\u5f97\u516c\u53f8\u6210\u4e3a\u56fd\u5185\u89c4\u6a21\u6700\u5927\u3001\u4ea7\u54c1\u7ebf\u6700\u5168\u7684\u4e73\u5236\u54c1\u4f01\u4e1a\uff0c\u8fde\u7eed\u591a\u5e74\u51c0\u8d44\u4ea7\u6536\u76ca\u7387\u90fd\u4fdd\u6301\u572820%\u4ee5\u4e0a\u3002\u6b64\u5916\uff0c\u6f58\u521a\u9886\u5bfc\u4f0a\u5229\u5728ESG\u9886\u57df\"\u5f00\u521b\u4e86\u4e09\u4e2a\u7b2c\u4e00\uff1a\u7b2c\u4e00\u4e2a\u5b8c\u6574\u7684\u5065\u5eb7\u4e2d\u56fd\u793e\u4f1a\u8d23\u4efb\u4f53\u7cfb\u3001\u7b2c\u4e00\u4e2a\u793e\u4f1a\u8d23\u4efb\u5e94\u6025\u9884\u6848\u3001\u884c\u4e1a\u5185\u7b2c\u4e00\u4efd\u4f01\u4e1a\u516c\u6c11\u62a5\u544a\u3002\" 2016\u5e74\uff0c\u8377\u5170\u5408\u4f5c\u94f6\u884c\u53d1\u5e03\"\u5168\u7403\u4e73\u4e1a20\u5f3a\"\uff0c\u4f0a\u5229\u6392\u540d\u7b2c\u516b\u4f4d\uff0c\u5728\u4e9a\u6d32\u4e73\u4f01\u4e2d\u6392\u540d\u7b2c\u4e00\u3002\u53bb\u5e74\u699c\u5355\u4e2d\uff0c\u6f58\u521a\u6392\u540d\u7b2c\u5341\u4e00\u4f4d\uff0c\u4f46\u4ece\u8d22\u52a1\u6307\u6807\u548cESG\u6392\u540d\u4e24\u65b9\u9762\u7efc\u5408\u8003\u91cf\uff0c\u4f0a\u5229\u8868\u73b0\u6700\u4e3a\u5353\u8d8a\uff0c\u56e0\u6b64\uff0c\u6f58\u521a\u4eca\u5e74\u6392\u540d\u8dc3\u5347\u81f3\u699c\u9996\u3002\n\u5982\u679c\u5355\u7eaf\u53c2\u8003\u8d22\u52a1\u56de\u62a5\uff0c\u817e\u8baf\u521b\u59cb\u4eba\u517cCEO\u9a6c\u5316\u817e\u5c06\u4f1a\u548c\u53bb\u5e74\u4e00\u6837\u7ee7\u7eed\u9886\u8dd1\u6574\u4e2a\u699c\u5355\u3002\u8fd9\u5bb6\u63d0\u4f9b\u4e92\u8054\u7f51\u670d\u52a1\u7684\u516c\u53f8\u76ee\u524d\u5e02\u503c\u8d85\u8fc73\u4e07\u4ebf\u6e2f\u5143\uff0c\u9a6c\u5316\u817e\u5728\u4efb\u671f\u5185\u7ed9\u80a1\u4e1c\u5e26\u6765\u7684\u603b\u56de\u62a5\u8fbe\u523030716%\uff0c\u8fc7\u53bb\u7684\u4e00\u5e74\u91cc\u53c8\u589e\u957f\u4e8655%\u3002\u4e0d\u8fc7\uff0c\u7531\u4e8e\u817e\u8bafESG\u6307\u6807\u6392\u540d\u4e0d\u4f73\uff0c\u9a6c\u5316\u817e\u5728\u4eca\u5e74\u7684\u699c\u5355\u4e2d\u8dcc\u843d\u81f3\u7b2c\u4e03\u4f4d\u3002\u6211\u4eec\u54a8\u8be2\u7684ESG\u8bc4\u4f30\u673a\u6784\u957f\u671f\u8ddf\u8e2a\u5458\u5de5\u3001\u73af\u5883\u3001\u793e\u4f1a\u548c\u6cbb\u74064\u5927\u7c7b\u768412\u9879ESG\u5173\u952e\u7ee9\u6548\u6307\u6807\uff0c\u4ed6\u4eec\u8ba4\u4e3a\uff0c\u817e\u8baf\u5f97\u5206\u6700\u4f4e\u7684\u5173\u952e\u6307\u6807\u662f\u900f\u660e\u5ea6\u4e0e\u62a5\u544a\uff0c\u5c3d\u7ba1\u5b83\u5728\u916c\u85aa\u548c\u798f\u5229\u65b9\u9762\u8868\u73b0\u8f83\u597d\uff0c\u4f46\u5176\u4ed6\u6240\u6709\u8bc4\u5206\u6307\u6807\u5747\u4f4e\u4e8e\u5e73\u5747\u6c34\u5e73\u3002\n\u503c\u5f97\u6ce8\u610f\u7684\u662f\uff0c\u5728ESG\u6307\u6807\u4e0a\u8868\u73b0\u6700\u597d\u7684\u4f01\u4e1a\u662f\u5efa\u53d1\u80a1\u4efd\u3002\u5bf9\u4e8e\u5efa\u53d1\u80a1\u4efd\uff0cESG\u8bc4\u4f30\u673a\u6784\u7684\u770b\u6cd5\u662f\u5176\u5728\u6cbb\u7406\u548c\u73af\u5883\u65b9\u9762\u5f97\u5206\u5f88\u9ad8\uff0c\u5728\u4f7f\u7528\u78b3\u548c\u5176\u4ed6\u8d44\u6e90\u91cf\u8fdc\u4f4e\u4e8e\u5e73\u5747\u6c34\u5e73\uff0c\u5177\u6709\"\u68c0\u4e3e\u5236\u5ea6\"\u7b49\u53cd\u8150\u63aa\u65bd\uff0c\u800c\u6240\u6709\u76d1\u63a7\u6765\u6e90\u63d0\u4f9b\u7684\u79ef\u6781\u53cd\u9988\u76f8\u5bf9\u7edf\u4e00\u5e76\u4e14\u6ca1\u6709\u6d88\u6781\u4fe1\u606f\u3002\u5efa\u53d1\u8463\u4e8b\u957f\u5f20\u52c7\u5cf0\u7684\u60f3\u6cd5\u662f\"\u5f53\u7ecf\u6d4e\u6548\u76ca\u4e0eESG\u53d1\u751f\u77db\u76fe\u65f6\uff0c\u53ea\u80fd\u4ee5ESG\u7684\u8003\u91cf\u4e3a\u51b3\u7b56\u57fa\u7840\u3002\u8fd9\u662f\u4e00\u4e2a\u8d1f\u8d23\u4efb\u4f01\u4e1a\u7684\u5fc5\u7136\u9009\u62e9\u3002\"\u4ed6\u8bf4\uff0c\"\u4f01\u4e1aESG\u610f\u8bc6\u7684\u589e\u5f3a\u4e5f\u53ef\u80fd\u4e3a\u4f01\u4e1a\u5236\u8ba2\u516c\u53f8\u6295\u8d44\u6218\u7565\u63d0\u4f9b\u66f4\u5927\u7684\u60f3\u8c61\u7a7a\u95f4\u3002\"\n\u6211\u4eec\u53d1\u73b0\uff0c\u4eca\u5e74100\u4f4d\u5165\u699c\u4f01\u4e1a\u5bb6\u638c\u7ba1\u7684\u516c\u53f8\u603b\u5e02\u503c\u4e3a10.27\u4e07\u4ebf\u4eba\u6c11\u5e01\uff08\u6c47\u7387\u8c03\u6574\uff09\uff0c\u699c\u5355\u4e2d\u670949\u4f4dCEO\u6765\u81ea\u5de5\u4e1a\u3001\u4fe1\u606f\u6280\u672f\u548c\u5730\u4ea7\u7b493\u5927\u884c\u4e1a\u3002\u5973\u6027\u9886\u5bfc\u8005\u4f9d\u7136\u9c9c\u89c1\uff0c\u4eca\u5e74\u53ea\u67094\u4f4d\u5165\u699c\uff0c\u548c\u53bb\u5e74\u4eba\u6570\u76f8\u540c\u3002\u53e6\u5916\uff0c\u6709\u4e00\u70b9\u987b\u544a\u77e5\u5404\u4f4d\u8bfb\u8005\uff1a\u4e0d\u5c11\u4e2d\u56fd\u516c\u53f8\u5176\u5b9e\u5e76\u6ca1\u6709CEO\u8fd9\u4e2a\u804c\u4f4d\uff0c\u4f46\u6211\u4eec\u5bf9CEO\u7684\u5b9a\u4e49\u662f\"\u62e5\u6709\u4f01\u4e1a\u51b3\u7b56\u6743\uff0c\u5e76\u4e3a\u4e1a\u7ee9\u6210\u8d25\u6700\u7ec8\u8d1f\u8d23\"\u7684\u4eba\uff0c\u6240\u4ee5\u5728\u7ecf\u8fc7\u9010\u4e00\u5224\u5b9a\u540e\uff0c\u6211\u4eec\u628a\u90e8\u5206\u516c\u53f8\u7684\u8463\u4e8b\u957f\u79f0\u4e3aCEO\u3002\n2017\u4e2d\u56fd\u767e\u4f73CEO\u699c\u5355\n{IMG:2}\n{IMG:3}\n{IMG:4}\n{IMG:5}\n{IMG:6}\n{IMG:7}\n{IMG:8}\n{IMG:9}\n{IMG:10}\n\uff08* \u6807\u8bb0\u7684\u5f90\u7559\u5e73\u5df2\u4e8e2017\u5e748\u6708\u5378\u4efb\uff0c\u5468\u601d\u5df2\u4e8e2017\u5e748\u6708\u5378\u4efb\uff0c\u9c81\u51a0\u7403\u5df2\u4e8e2017\u5e7410\u670825\u65e5\u53bb\u4e16\uff0c\u6797\u590d\u5df2\u4e8e2017\u5e745\u6708\u5378\u4efb\uff0c\u674e\u654f\u6167\u5df2\u4e8e2017\u5e747\u6708\u5378\u4efb\uff0c\u9ec4\u8fea\u53572017\u5e748\u6708\u5378\u4efb\u3002\uff09\n\u6700\u4f73CEO\u6392\u540d\u8ba1\u7b97\u65b9\u6cd5\uff08\u4e2d\u56fd\u699c\u5355\uff09\n\u672c\u6b21\u4e2d\u56fd\u767e\u4f73CEO\u6392\u884c\u699c\uff0c\u6211\u4eec\u6db5\u76d6\u6240\u6709\u4e2d\u56fd\u7684\u4e0a\u5e02\u4f01\u4e1a\uff08\u5305\u62ec\u5728\u4e0a\u6d77\u3001\u6df1\u5733\u3001\u9999\u6e2f\u3001\u53f0\u6e7e\u3001\u7ebd\u7ea6\u548c\u7eb3\u65af\u8fbe\u514b\u516c\u5f00\u53d1\u884c\u80a1\u7968\u7684\u516c\u53f8\uff09\u3002\u6bcf\u5bb6\u91c7\u6837\u4f01\u4e1a\u7684\u5e02\u503c\u3001\u8d44\u4ea7\u548c\u6536\u5165\u57282016\u5e74\u5e74\u5e95\u7684\u6570\u636e\u5747\u8d85\u8fc7100\u4ebf\u5143\u4eba\u6c11\u5e01\u3002\u5bf9\u6bcf\u5bb6\u4f01\u4e1a\u622a\u81f32017\u5e744\u670830\u65e5\u5728\u4efb\u7684CEO\u8fdb\u884c\u8003\u5bdf\u3002\u4e3a\u786e\u4fdd\u8bc4\u4f30\u6709\u636e\u53ef\u4f9d\u3001\u6750\u6599\u7fd4\u5b9e\uff0c\u6211\u4eec\u5254\u9664\u4e86\u4efb\u671f\u672a\u6ee1\u4e24\u5e74\u7684CEO\u3002\u81f3\u6b64\uff0c\u5171\u6709370\u5bb6\u516c\u53f8\u7684369\u4f4dCEO\uff08\u6709\u4e00\u4f4dCEO\u540c\u65f6\u517c\u4efb2\u5bb6\u516c\u53f8\uff09\u53c2\u9009\u3002\n\u6211\u4eec\u7684\u7814\u7a76\u56e2\u961f\u7531\u300a\u54c8\u4f5b\u5546\u4e1a\u8bc4\u8bba\u300b\u4e2d\u6587\u7248\u548c\u82f1\u58eb\u56fd\u9645\u5546\u5b66\u9662\uff08INSEAD\uff09\u5171\u540c\u7ec4\u6210\u3002\u516c\u53f8\u7b5b\u9009\u53caCEO\u76f8\u5173\u4fe1\u606f\u7531\u300a\u54c8\u4f5b\u5546\u4e1a\u8bc4\u8bba\u300b\u4e2d\u6587\u7248\u8d1f\u8d23\uff1b\u6570\u636e\u5206\u6790\u7531\u82f1\u58eb\u7684\u7eb3\u5a1c&middot;\u51af&middot;\u8d1d\u5c14\u52aa\u7279\uff08Nana von Bernuth\uff09\u7387\u9886\uff0c\u4ee5\u53ca\u6765\u81eaEleven Strategy & Management\u7684\u4e24\u4f4d\u6570\u636e\u7a0b\u5e8f\u5458\u83ab\u6717\u5fb7&middot;\u65af\u56fe\u7279\uff08Morand Struder\uff09\u548c\u4e39\u5c3c\u5c14&middot;\u8d1d\u7eb3\u5fb7\u65af\uff08Daniel Bernardes\uff09\u534f\u52a9\u8fdb\u884c\u3002\u4eceDatastream\u548cWorldscope\u4e24\u4e2a\u6570\u636e\u5e93\u4e2d\u6536\u96c6\u5404\u516c\u53f8CEO\u81ea\u4e0a\u4efb\u4e4b\u65e5\u8d77\u81f32017\u5e744\u670830\u65e5\u671f\u95f4\u7684\u65e5\u5e38\u8d22\u52a1\u6570\u636e\u3002\u5bf9\u4e8e2000\u5e74\u4ee5\u524d\u4e0a\u4efb\u7684CEO\uff0c\u6211\u4eec\u4ece2000\u5e741\u67081\u65e5\u4e4b\u540e\u8ba1\u7b97\u5176\u516c\u53f8\u6536\u76ca\uff0c\u56e0\u4e3a\u5f88\u591a\u4e2d\u56fd\u516c\u53f8\u5728\u8fd9\u4e2a\u65e5\u671f\u4e4b\u524d\u7684\u6570\u636e\u5e76\u4e0d\u5b8c\u5907\u3002\n\u7814\u7a76\u56e2\u961f\u8ba1\u7b97\u4e86\u6bcf\u4f4dCEO\u5728\u4efb\u671f\u95f4\u76843\u9879\u8bc4\u4f30\u6307\u6807\uff1a\u4e0a\u5e02\u5730\u8c03\u6574\u540e\u80a1\u4e1c\u603b\u56de\u62a5\uff08\u5305\u62ec\u80a1\u606f\u518d\u6295\uff09\uff0c\u8be5\u6307\u6807\u5254\u9664\u4e86\u516c\u53f8\u4e0a\u5e02\u5730\u80a1\u5e02\u6574\u4f53\u589e\u957f\u5e26\u6765\u7684\u589e\u957f\uff1b\u884c\u4e1a\u8c03\u6574\u540e\u80a1\u4e1c\u603b\u56de\u62a5\uff08\u5305\u62ec\u80a1\u606f\u518d\u6295\uff09\uff0c\u8be5\u6307\u6807\u5254\u9664\u4e86\u516c\u53f8\u6240\u5728\u884c\u4e1a\u6574\u4f53\u53d1\u5c55\u5e26\u6765\u7684\u589e\u957f\uff1b\u5e02\u503c\u53d8\u5316\uff08\u80a1\u606f\u3001\u80a1\u7968\u53d1\u884c\u548c\u80a1\u4efd\u56de\u8d2d\u8c03\u6574\u540e\uff09\uff0c\u4ee5\u901a\u8d27\u81a8\u80c0\u8c03\u6574\u540e\u7684\u7f8e\u5143\u8fdb\u884c\u8861\u91cf\u3002\n\u968f\u540e\u6211\u4eec\u5c06\u6240\u6709CEO\u5206\u522b\u6309\u7167\u4ee5\u4e0a3\u9879\u8d22\u52a1\u6307\u6807\u4ece\u7b2c1\uff08\u6700\u9ad8\uff09\u5230\u7b2c370\uff08\u6700\u4f4e\uff09\u8fdb\u884c\u6392\u540d\uff0c3\u6b21\u6392\u540d\u5e73\u5747\u503c\u5373\u4e3a\u8be5CEO\u603b\u7684\u8d22\u52a1\u6392\u540d\u5f97\u5206\u3002\u7efc\u5408\u8fd0\u75283\u9879\u6307\u6807\uff0c\u4fdd\u8bc1\u4e86\u8bc4\u4f30\u65b9\u6cd5\u7684\u7f1c\u5bc6\u4e0e\u5e73\u8861\uff1a\u524d\u4e24\u4e2a\u6307\u6807\u53ef\u80fd\u5bf9\u5c0f\u516c\u53f8\u6709\u5229\uff08\u8d77\u70b9\u4f4e\u7684\u516c\u53f8\u66f4\u5bb9\u6613\u83b7\u5f97\u663e\u8457\u7684\u80a1\u4e1c\u56de\u62a5\u589e\u957f\uff09\uff0c\u800c\u5e02\u503c\u53d8\u5316\u8fd9\u4e00\u6307\u6807\u5219\u6709\u5229\u4e8e\u5927\u516c\u53f8\u3002\n\u4e3a\u8bc4\u4f30CEO\u5728\u975e\u8d22\u52a1\u65b9\u9762\u7684\u8868\u73b0\uff0c\u300a\u54c8\u4f5b\u5546\u4e1a\u8bc4\u8bba\u300b\u4e2d\u6587\u7248\u54a8\u8be2\u4e86CSRHub\u516c\u53f8\uff08www.csrhub.com\uff09\uff0cCSRHub\u662f\u4e00\u5bb6\u5bf9\u4f01\u4e1a\u793e\u4f1a\u8d23\u4efb\u548c\u53ef\u6301\u7eed\u53d1\u5c55\u8fdb\u884c\u8bc4\u4f30\u7684\u673a\u6784\uff0c\u5b83\u5bf9\u6765\u81ea13\u5bb6ESG\u5206\u6790\u5e08\u6570\u636e\u5e93\u7684\u6570\u636e\u8fdb\u884c\u6536\u96c6\u3001\u7edf\u8ba1\u548c\u6807\u51c6\u5316\uff0c\u4e3b\u8981\u4e0e\u5e0c\u671b\u6539\u5584ESG\u8868\u73b0\u7684\u4f01\u4e1a\u8fdb\u884c\u5408\u4f5c\uff0c\u5bf9133\u4e2a\u56fd\u5bb6\u548c135\u4e2a\u884c\u4e1a\u4e2d\u768417500\u5bb6\u516c\u53f8\u8fdb\u884c\u8bc4\u4f30\u3002\u6839\u636eCSRHub\u7684\u7814\u7a76\u6570\u636e\uff0c\u6211\u4eec\u5f97\u51fa\u6240\u6709\u4f01\u4e1a\u7684ESG\u6392\u540d\u3002\n\u6700\u540e\uff0c\u6211\u4eec\u7ed9\u4e883\u9879\u8d22\u52a1\u6392\u540d\u603b\u6743\u91cd80%\uff0cESG\u6392\u540d\u6743\u91cd20%\uff0c\u636e\u6b64\u7efc\u5408\u5f97\u51faCEO\u6700\u7ec8\u6392\u540d\u3002\n{IMG:11}"}, {"emotion": "neg", "id": "0143e51b6282847ba112310cde5d55bd8a", "c_t": "\u9ad8\u901a\u4e0d\u613f\u88ab\u535a\u901a\u6536\u8d2d\uff0c\u80cc\u540e\u7684\u5fae\u5999\u535a\u5f08\u548c\u6f5c\u5728\u9ebb\u70e6\u535a\u901a\u6536\u8d2d\u9ad8\u901a\u662f\u5426\u6210\u884c\uff0c\u5c06\u662f\u4e00\u4e2a\u653f\u6cbb\u3001\u6cd5\u5f8b\u3001\u8d44\u672c\u5e02\u573a\u76f8\u4e92\u4ea4\u7ec7\u7684\u590d\u6742\u7efc\u5408\u7ed3\u679c\u3002\n{IMG:1}\n\uff08\u8d44\u6599\u56fe\uff09\n\u300a\u8d22\u7ecf\u300b\u8bb0\u8005\u8c22\u4e3d\u5bb9\u5468\u6e90/\u6587\n\u7f8e\u56fd\u534a\u5bfc\u4f53\u516c\u53f8\u535a\u901a\u7ec8\u4e8e\u5ba3\u5e03\u4e86\u4e00\u7b14\u9ad8\u8fbe1300\u4ebf\u7f8e\u5143\u7684\u6536\u8d2d\u8981\u7ea6\uff0c\u5982\u679c\u6210\u884c\uff0c\u8fd9\u5c06\u662f\u5168\u7403\u5386\u53f2\u4e0a\u6700\u5927\u7684\u4e00\u7b14\u534a\u5bfc\u4f53\u516c\u53f8\u6536\u8d2d\u8ba1\u5212\u3002\u535a\u901a\u8ba1\u5212\u6536\u8d2d\u7684\u5bf9\u8c61\u662f\u53e6\u4e00\u5bb6\u7f8e\u56fd\u534a\u5bfc\u4f53\u516c\u53f8\u9ad8\u901a\uff0c\u540e\u8005\u662f\u5168\u7403\u6700\u5927\u7684\u79fb\u52a8\u82af\u7247\u5236\u9020\u5546\u3002\n\u535a\u901a\u5728\u6536\u8d2d\u63d0\u6848\u4e2d\u63d0\u51fa\u4ee5\u6bcf\u80a170.00\u7f8e\u5143\u7684\u4ef7\u683c\u6536\u8d2d\u9ad8\u901a\uff0c\u6bcf\u80a1\u5c06\u5305\u62ec60.00\u7f8e\u5143\u73b0\u91d1\u548c10.00\u7f8e\u5143\u535a\u901aBroadcom\u80a1\u7968\uff0c\u6b64\u5916\uff0c\u4ea4\u6613\u8fd8\u5c06\u5305\u62ec\u535a\u901a\u613f\u610f\u7ee7\u7eed\u5b8c\u6210\u9ad8\u901a\u65a5\u8d44380\u4ebf\u7f8e\u5143\u6536\u8d2d\u6069\u667a\u6d66\u534a\u5bfc\u4f53\uff08NXP Semiconductors\uff09\u7684\u4ea4\u6613\u3002\u628a\u4e0a\u8ff0\u5408\u5e76\u5728\u4e00\u8d77\uff0c\u535a\u901a\u6536\u8d2d\u9ad8\u901a\u9700\u652f\u4ed8\u8d85\u8fc71300\u4ebf\u7f8e\u5143\u3002\n\u8fd91300\u4ebf\u7f8e\u5143\u4e2d\uff0c\u8d85\u8fc785%\u4e3a\u73b0\u91d1\u6536\u8d2d\u3002\u65b9\u6848\u6bd411\u67082\u65e5\u9ad8\u901a\u666e\u901a\u80a1\u6536\u76d8\u4ef7\u683c\u9ad8\u51fa28\uff05\uff0c\u6bd4\u9ad8\u901a\u672a\u53d7\u5f71\u54cd\u768430\u5929\u4ea4\u6613\u91cf\u5e73\u5747\u4ef7\u683c\u6ea2\u4ef733\uff05\u3002\n\u9ad8\u901a\u516c\u53f8\u968f\u5373\u56de\u5e94\uff0c\u5c06\u5ba1\u67e5\u8be5\u63d0\u6848\uff0c\u5e76\u4e3a\u80a1\u4e1c\u7684\u6700\u4f73\u5229\u76ca\u884c\u4e8b\u3002\u9ad8\u901a\u968f\u5373\u7ed9\u300a\u8d22\u7ecf\u300b\u8bb0\u8005\u7684\u56de\u5e94\u79f0\uff0c\u9ad8\u901a\u8463\u4e8b\u4f1a\u548c\u7ba1\u7406\u5c42\u4e00\u76f4\u90fd\u5728\u7ee7\u7eed\u6267\u884c\u5176\u7b56\u7565\uff0c\u4f7f\u516c\u53f8\u5728\u4fdd\u6301\u8d22\u52a1\u7a33\u5065\u548c\u826f\u597d\u8d44\u672c\u56de\u62a5\u8ba1\u5212\u7684\u540c\u65f6\uff0c\u4e13\u6ce8\u4e8e\u6295\u8d44\u90a3\u4e9b\u80fd\u591f\u4f7f\u5176\u5728\u79fb\u52a8\u9886\u57df\u7684\u9886\u5bfc\u5730\u4f4d\u6269\u5c55\u5230\u5168\u65b0\u9886\u57df\u7684\u673a\u4f1a\u3002\u51c6\u786e\u5b9a\u4f4d\uff0c\u8fce\u63a5\u4e0b\u4e00\u9636\u6bb5\u7684\u76c8\u5229\u589e\u957f\u3002\u968f\u7740\u5176\u6280\u672f\u548c\u4ea7\u54c1\u8def\u7ebf\u56fe\u6269\u5c55\u81f3\u5168\u65b0\u7684\u884c\u4e1a\u4e2d\uff0c\u9ad8\u901a\u62e5\u6709\u4e86\u5de8\u5927\u7684\u673a\u9047,\u53ef\u4ee5\u4e3a\u80a1\u4e1c\u521b\u9020\u663e\u8457\u7684\u9644\u52a0\u4ef7\u503c\u3002\n\u603b\u7ed3\u4e00\u4e0b\uff1a\u9ad8\u901a\u5e76\u4e0d\u613f\u610f\u88ab\u6536\u8d2d\uff0c\u9ad8\u901a\u4f9d\u9760\u81ea\u5df1\u7684\u529b\u91cf\u5c31\u53ef\u4ee5\u4e3a\u80a1\u4e1c\u521b\u9020\u65b0\u4e00\u8f6e\u7684\u4ef7\u503c\u3002\n\u662f\u5426\u4f1a\u8d70\u5230\u654c\u610f\u5e76\u8d2d\u8fd9\u4e00\u6b65\uff1f\n\u5916\u754c\u8ba4\u4e3a\uff0c\u5982\u679c\u535a\u901a\u548c\u9ad8\u901a\u5408\u5e76\uff0c\u5c06\u6210\u4e3a\u5168\u4e16\u754c\u7b2c\u4e09\u5927\u534a\u5bfc\u4f53\u516c\u53f8\uff0c\u4ec5\u6b21\u4e8e\u82f1\u7279\u5c14\u548c\u4e09\u661f\u7535\u5b50\u3002\u540c\u65f6\uff0c\u8fd9\u5bb6\u65b0\u516c\u53f8\u4e5f\u5c06\u5728\u624b\u673a\u901a\u4fe1\u82af\u7247\uff08\u5305\u62ecWi-Fi\u6216\u8005\u57fa\u5e26\u82af\u7247\uff09\u9886\u57df\u5360\u6709\u5de8\u5927\u5f71\u54cd\u529b\u3002\n\u82e5\u63092016\u5e74\u8425\u6536\u6765\u8bf4\uff0c\u9ad8\u901a\u6392\u7b2c\u4e09\uff0cSK\u6d77\u529b\u58eb\u6392\u7b2c\u56db\uff0c\u535a\u901a\u4ec5\u6392\u540d\u7b2c\u4e94\u3002\u4e8c\u8005\u76f8\u52a0\u4e4b\u540e\u5c06\u62c9\u8fd1\u4e0e\u6392\u540d\u7b2c\u4e8c\u7684\u4e09\u661f\u7684\u8ddd\u79bb\u3002\n\u4e0d\u8fc7\uff0c\u5c31\u7b97\u4e0d\u88ab\u5e76\u8d2d\uff0c\u9ad8\u901a\u76ee\u524d\u4e5f\u5df2\u7ecf\u662f\u5168\u7403\u7b2c\u4e09\u3002\n{IMG:2}\n2016\u5e74\u5168\u7403\u6536\u5165\u524d\u5341\u534a\u5bfc\u4f53\u5382\u5546\uff08\u5355\u4f4d\uff1a\u767e\u4e07\u7f8e\u5143\uff09 \u6765\u6e90\uff1aGartner\uff082017\u5e745\u6708\uff09\n\u5982\u679c\u9ad8\u901a\u62d2\u7edd\u88ab\u6536\u8d2d\uff0c\u800c\u535a\u901a\u575a\u6301\uff0c\u4e8c\u8005\u672a\u6765\u4e00\u6bb5\u65f6\u95f4\u5185\u5c06\u5f88\u6709\u53ef\u80fd\u5c55\u5f00\u4e00\u573a\u8d44\u672c\u5927\u6218----\u535a\u901a\u53ef\u76f4\u63a5\u5411\u80a1\u4e1c\u53d1\u8d77\u6536\u8d2d\u8981\u7ea6\uff0c\u6f14\u53d8\u4e3a\u654c\u610f\u6536\u8d2d\uff0c\u9ad8\u901a\u4e5f\u53ef\u4f9d\u636e\u7f8e\u56fd\u8d44\u672c\u5e02\u573a\u89c4\u5219\u53d1\u8d77\"\u6bd2\u4e38\u8ba1\u5212\"\u53cd\u51fb\u3002\n\u6240\u8c13\"\u654c\u610f\u6536\u8d2d\"\uff0c\u662f\u6307\u6536\u8d2d\u516c\u53f8\u5728\u672a\u7ecf\u76ee\u6807\u516c\u53f8\u8463\u4e8b\u4f1a\u5141\u8bb8\uff0c\u4e0d\u7ba1\u5bf9\u65b9\u662f\u5426\u540c\u610f\u7684\u60c5\u51b5\u4e0b\uff0c\u6240\u8fdb\u884c\u7684\u6536\u8d2d\u6d3b\u52a8\u3002\u5f53\u4e8b\u53cc\u65b9\u91c7\u7528\u5404\u79cd\u653b\u9632\u7b56\u7565\u5b8c\u6210\u6536\u8d2d\u884c\u4e3a\uff0c\u5e76\u5e0c\u671b\u53d6\u5f97\u63a7\u5236\u6027\u80a1\u6743\uff0c\u6210\u4e3a\u5927\u80a1\u4e1c\u3002\u8fdb\u884c\u654c\u610f\u6536\u8d2d\u7684\u6536\u8d2d\u516c\u53f8\u4e00\u822c\u88ab\u79f0\u4f5c\"\u9ed1\u8863\u9a91\u58eb\"\u3002\n\u80a1\u4e1c\u63a5\u53d7\"\u654c\u610f\u6536\u8d2d\"\u7684\u77ed\u671f\u83b7\u5229\u884c\u4e3a\uff0c\u5f80\u5f80\u662f\u548c\u516c\u53f8\u7684\u957f\u671f\u53d1\u5c55\u76f8\u8fdd\u80cc\u7684\u3002\u9ad8\u901a\u76ee\u524d\u6b63\u5904\u4e8e4G\u54115G\u66f4\u8fed\u7684\u654f\u611f\u5173\u952e\u65f6\u671f\uff0c\u5df2\u7ecf\u5efa\u7acb\u8d77\u4e00\u7cfb\u5217\u7684\u4eba\u529b\u8d44\u672c\u3001\u4f9b\u9500\u7f51\u7edc\u3001\u503a\u52a1\u5173\u7cfb\u7b49\u4e0e\u6218\u7565\u7a33\u5b9a\u6027\u5bc6\u5207\u76f8\u5173\uff0c\u8fd9\u4e9b\u5b89\u6392\u5982\u679c\u4efb\u610f\u88ab\u80a1\u4e1c\u77ed\u671f\u83b7\u5229\u52a8\u673a\u6240\u6253\u65ad\uff0c\u5fc5\u5c06\u5f71\u54cd\u6574\u4f53\u53d1\u5c55\u6548\u7387\u3002\n\u9ad8\u901a\u662f\u4e00\u5bb6\u80a1\u6743\u5206\u6563\u7684\u516c\u53f8\uff0c\u6392\u540d\u524d\u5341\u5927\u80a1\u4e1c\u5747\u4e3a\u673a\u6784\u6295\u8d44\u8005\uff0c\u6301\u80a1\u6570\u91cf\u4ece1.08%-6.84%\u4e0d\u7b49\u3002\n{IMG:3}\n\u6240\u8c13\"\u6bd2\u4e38\u8ba1\u5212\"\uff0c\u7531\u7f8e\u56fd\u8457\u540d\u7684\u5e76\u8d2d\u5f8b\u5e08\u9a6c\u4e01&middot;\u5229\u666e\u987f\uff08MartinLipton\uff091982\u5e74\u53d1\u660e\uff0c\u6b63\u5f0f\u540d\u79f0\u4e3a\"\u80a1\u6743\u644a\u8584\u53cd\u6536\u8d2d\u63aa\u65bd\"\uff0c\u6700\u521d\u7684\u5f62\u5f0f\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u76ee\u6807\u516c\u53f8\u5411\u666e\u901a\u80a1\u80a1\u4e1c\u53d1\u884c\u4f18\u5148\u80a1\uff0c\u4e00\u65e6\u516c\u53f8\u88ab\u6536\u8d2d\uff0c\u80a1\u4e1c\u6301\u6709\u7684\u4f18\u5148\u80a1\u5c31\u53ef\u4ee5\u8f6c\u6362\u4e3a\u4e00\u5b9a\u6570\u989d\u7684\u6536\u8d2d\u65b9\u80a1\u7968\u3002\u4e8e1985\u5e74\u5728\u7f8e\u56fd\u7279\u62c9\u534e\u6cd5\u9662\u88ab\u5224\u51b3\u5408\u6cd5\u5316\u3002\n\u6bd2\u4e38\u8ba1\u5212\u6709\u591a\u79cd\u65b9\u5f0f\uff0c\u6838\u5fc3\u7684\u4e00\u79cd\u662f\u5927\u91cf\u5b9a\u5411\u53d1\u884c\u65b0\u80a1\uff0c\u7a00\u91ca\u6076\u610f\u6536\u8d2d\u65b9\u6240\u6301\u6709\u7684\u80a1\u4efd\u6bd4\u4f8b\uff0c\u4ece\u800c\u63d0\u9ad8\u6536\u8d2d\u6240\u9700\u7684\u8d44\u91d1\u6210\u672c\u548c\u6700\u7ec8\u6536\u76ca\uff1b\u53e6\u4e00\u79cd\u662f\u7ba1\u7406\u5c42\u7ea6\u5b9a\u96c6\u4f53\u8f9e\u804c\uff0c\u4ee5\u63d0\u9ad8\u88ab\u6536\u8d2d\u540e\u7684\u8fd0\u8425\u98ce\u9669\u3002\n1985\u5e74\uff0c\u7f8e\u56fd\u5fb7\u62c9\u74e6\u65af\u5207\u65af\u5229(Delawance Chancery)\u6cd5\u9662\u5224\u51b3\u6bd2\u4e38\u672f\u5408\u6cd5\uff0c\u786e\u8ba4\u6bd2\u4e38\u8ba1\u5212\u7684\u5b9e\u65bd\u65e0\u987b\u80a1\u4e1c\u76f4\u63a5\u6279\u51c6\u5373\u53ef\u5b9e\u65bd\u3002\u6bd2\u4e38\u8ba1\u5212\u56e0\u6b64\u5728\u516b\u5341\u5e74\u4ee3\u540e\u671f\u5f00\u59cb\u5728\u7f8e\u56fd\u88ab\u91c7\u7528\u3002\n\u6bd2\u4e38\u8ba1\u5212\u4e00\u7ecf\u91c7\u7528\uff0c\u81f3\u5c11\u4f1a\u4ea7\u751f\u4e24\u4e2a\u6548\u679c\uff1a\u5176\u4e00\uff0c\u5bf9\u6076\u610f\u6536\u8d2d\u65b9\u4ea7\u751f\u5a01\u6151\u4f5c\u7528\uff1b\u5176\u4e8c\uff0c\u5bf9\u91c7\u7528\u8be5\u8ba1\u5212\u7684\u516c\u53f8\u6709\u5174\u8da3\u7684\u6536\u8d2d\u65b9\u4f1a\u51cf\u5c11\u30022005\u5e74\uff0c\u65b0\u6d6a\u5728\u9762\u5bf9\u76db\u5927\u6536\u8d2d\u7684\u65f6\u5019\uff0c\u5c31\u662f\u91c7\u7528\u4e86\u6bd2\u4e38\u8ba1\u5212\u3002\u6700\u7ec8\u76db\u5927\u53ea\u80fd\u65e0\u5948\u653e\u5f03\u65b0\u6d6a\u3002\n\u4e0d\u8fc7\uff0c\u5728\u516c\u53f8\u6cbb\u7406\u8d8a\u6765\u8d8a\u53d7\u91cd\u89c6\u7684\u4eca\u5929\uff0c\u6bd2\u4e38\u8ba1\u5212\u7684\u91c7\u7528\u7387\u5df2\u5927\u5e45\u5ea6\u4e0b\u964d\u3002\u4e00\u662f\u516c\u53f8\u6cbb\u7406\u8bc4\u4f30\u673a\u6784\u5f80\u5f80\u7ed9\u90a3\u4e9b\u6709\u6bd2\u4e38\u8ba1\u5212\u7684\u516c\u53f8\u8f83\u4f4e\u7684\u8bc4\u7ea7\uff1b\u4e8c\u662f\u6295\u8d44\u8005\u4e5f\u4e0d\u613f\u610f\u770b\u5230\u8463\u4e8b\u4f1a\u4eba\u4e3a\u8bbe\u7acb\u4e00\u9053\u963b\u788d\u8d44\u672c\u81ea\u7531\u6d41\u901a\u7684\u969c\u788d\u3002\n\u56e0\u6b64\uff0c\u5c31\u7b97\u9ad8\u901a\u62d2\u7edd\u88ab\u6536\u8d2d\uff0c\u629b\u5f00\u653f\u6cbb\u3001\u6cd5\u5f8b\u56e0\u7d20\uff0c\u5355\u7eaf\u4ece\u6218\u7565\u3001\u8d44\u672c\u548c\u4e1a\u52a1\u5c42\u9762\u770b\uff0c\u53cc\u65b9\u662f\u5426\u4f1a\u8d70\u5230\u8fd9\u4e00\u6b65\uff0c\u4e5f\u662f\u4e00\u4e2a\u672a\u5b9a\u4e4b\u6570\u3002\n\u653f\u5e9c\u662f\u5426\u653e\u884c\u80cc\u540e\u7684\u590d\u6742\u56e0\u7d20\n\u8981\u5b8c\u6210\u8fd9\u9879\u6536\u8d2d\uff0c\u8fd8\u9700\u8981\u7ecf\u8fc7\u7f8e\u56fd\u653f\u5e9c\u5ba1\u6279\u3001\u5168\u7403\u5176\u4ed6\u91cd\u8981\u5730\u533a\u653f\u5e9c\u5ba1\u67e5\u7b49\u9636\u6bb5\uff0c\u6700\u5173\u952e\u7684\u662f\uff0c\u5f53\u4e8b\u4eba\u9ad8\u901a\u662f\u4e0d\u662f\u613f\u610f\u88ab\u6536\u8d2d\uff1f\n\u7f8e\u56fdFTC\uff08\u7f8e\u56fd\u8054\u90a6\u8d38\u6613\u59d4\u5458\u4f1aFederal Trade Commission\uff09\u7684\u4e3b\u8981\u6267\u6cd5\u4f9d\u636e\u300a\u8c22\u5c14\u66fc\u6cd5\u300b\uff0c\u53cd\u5784\u65ad\u7684\u5b9e\u8d28\u662f\u901a\u8fc7\u5bf9\u5927\u4f01\u4e1a\u5784\u65ad\u5730\u4f4d\u7684\u9650\u5236\u4fdd\u8bc1\u5e02\u573a\u7ecf\u6d4e\u7684\u7ade\u4e89\u4e0e\u6d3b\u529b\uff0c\u4fc3\u8fdb\u7ecf\u6d4e\u53d1\u5c55\uff0c\u4fdd\u62a4\u6d88\u8d39\u8005\u6743\u76ca\u3002\n\u7f8e\u56fd\u53cd\u5784\u65ad\u7acb\u6cd5\u5df2\u6709\u4e0a\u767e\u5e74\u7684\u5386\u53f2\uff0c\u6700\u65e9\u53ef\u8ffd\u6eaf\u81f31890\u5e74\u9881\u5e03\u7684\u300a\u8c22\u5c14\u66fc\u6cd5\u300b\u548c1914\u5e74\u9881\u5e03\u7684\u300a\u8054\u90a6\u8d38\u6613\u59d4\u5458\u4f1a\u6cd5\u300b\u53ca\u300a\u514b\u83b1\u987f\u6cd5\u300b\uff0c\u8fd9\u4e09\u90e8\u6cd5\u5f8b\u6210\u4e3a\u7f8e\u56fd\u53cd\u5784\u65ad\u7684\u6846\u67b6\u6027\u6cd5\u5f8b\u3002\u867d\u7136\u8fc7\u53bb\u51e0\u5341\u5e74\u4e92\u8054\u7f51\u9ad8\u79d1\u6280\u884c\u4e1a\u5feb\u901f\u53d1\u5c55\u5f15\u9886\u5168\u7403\u53d1\u751f\u91cd\u5927\u53d8\u9769\uff0c\u7f8e\u56fd\u56fd\u4f1a\u548c\u653f\u5e9c\u8ba4\u4e3a\u8fd9\u4e9b\u53cd\u5784\u65ad\u6cd5\u4ecd\u7136\u9002\u7528\u5982\u4eca\u7684\u4e92\u8054\u7f51\u884c\u4e1a\u3002\n\u535a\u901a\u62df\u6536\u8d2d\u9ad8\u901a\uff0c\u4ece\u5927\u7684\u6cd5\u5f8b\u5c42\u9762\u4e0a\u770b\u5c5e\u4e8e\u6a2a\u5411\u5408\u5e76\uff08Horizontal Integration\uff09\u3002\u5982\u7f8e\u56fd\u6ce2\u97f3\u98de\u673a\u5236\u9020\u516c\u53f8\u4e0e\u9ea6\u9053\u98de\u673a\u5236\u9020\u516c\u53f8\u7684\u5408\u5e76\uff0c\u6cd5\u56fd\u96f7\u8bfa\u6c7d\u8f66\u5236\u9020\u516c\u53f8\u4e0e\u745e\u66f2\u4f0f\u5c14\u6c7d\u8f66\u5236\u9020\u516c\u53f8\u7684\u5408\u5e76\uff0c\u5747\u5c5e\u6a2a\u5411\u5408\u5e76\u3002\n\u6a2a\u5411\u5408\u5e76\u4e00\u8d2f\u662f\u7f8e\u56fd\u53cd\u5784\u65ad\u6cd5\u4e2d\u4e3b\u8981\u4e25\u683c\u7ba1\u5236\u5bf9\u8c61\u3002\u7f8e\u56fd\u7684\u4f01\u4e1a\u5408\u5e76\u6cd5\u8ba4\u4e3a\uff0c\u6df7\u5408\u5408\u5e76\u867d\u7136\u4e0d\u80fd\u76f4\u63a5\u63d0\u9ad8\u4e00\u4e2a\u90e8\u95e8\u7684\u96c6\u4e2d\u5ea6\uff0c\u4f46\u4ece\u957f\u8fdc\u548c\u53d1\u5c55\u7684\u89d2\u5ea6\u770b\uff0c\u5b83\u4eec\u4e5f\u80fd\u63a8\u52a8\u7ecf\u6d4e\u96c6\u4e2d\u548c\u5e02\u573a\u52bf\u529b\u7684\u589e\u957f\uff0c\u56e0\u800c\u5e94\u5f53\u4e88\u4ee5\u6cd5\u5f8b\u63a7\u5236\u3002\n\u7f8e\u56fd\u53f8\u6cd5\u90e8\u548c\u8054\u90a6\u8d38\u6613\u59d4\u5458\u4f1a2010\u5e74\u8054\u5408\u53d1\u5e03\u4e86\u4fee\u8ba2\u7248\u7684\u300a\u6a2a\u5411\u5408\u5e76\u6307\u5357\u300b\u4ee5\u6307\u5bfc\u5e76\u8d2d\u4ea4\u6613\u53cd\u5784\u65ad\u5ba1\u67e5\uff0c\u5176\u7a81\u51fa\u7279\u70b9\u4e4b\u4e00\u662f\u964d\u4f4e\u4e86\u76f8\u5173\u5e02\u573a\u754c\u5b9a\u7684\u91cd\u8981\u6027\uff0c\u589e\u52a0\u4e86\u5bf9\u5e76\u8d2d\u4ea4\u6613\u662f\u5426\u4f1a\u51cf\u5c11\u521b\u65b0\u7684\u5f71\u54cd\u5206\u6790\uff0c\u8db3\u89c1\u5bf9\u4fdd\u62a4\u521b\u65b0\u7684\u9ad8\u5ea6\u91cd\u89c6\u3002\n\u4e0d\u8fc7\uff0c\u4ece\u5177\u4f53\u7ec6\u5206\u9886\u57df\u6765\u770b\uff0c\u535a\u901a\u7684\u4e1a\u52a1\u8303\u56f4\u548c\u9ad8\u901a\u53c8\u6709\u6240\u533a\u9694\u3002\u535a\u901a\u4e3b\u8981\u9488\u5bf9WiFi\u73af\u5883\u4e0b\u7684\u5bb6\u5ead\u3001\u529e\u516c\u5ba4\u7b49\u73af\u5883\u4e2d\u4f20\u9012\u8bed\u97f3\u3001\u6570\u636e\u548c\u591a\u5a92\u4f53\u82af\u7247\uff1b\u800c\u9ad8\u901a\u516c\u53f8\uff08Qualcomm\uff09\uff0c\u5219\u662f\u4e16\u754c\u4e0a\u6700\u5927\u7684\u624b\u673a\u82af\u7247\u5236\u9020\u5546\uff0c\u8fd1\u4e24\u5e74\u5728\u7269\u8054\u7f51\u9886\u57df\u4e5f\u6709\u6240\u5e03\u5c40\u3002\u53cc\u65b9\u5404\u81ea\u6d89\u53ca\u5230\u7684\u4ea7\u54c1\u591a\u8fbe\u6570\u5341\u79cd\u3002\n\u53cc\u65b9\u7684\u4ea7\u54c1\u9635\u5217\u4e2d\uff0c\u6709\u53ef\u80fd\u662f\u7ade\u4e89\u5173\u7cfb\u7684\uff0c\u6709\u53ef\u80fd\u662f\u91cd\u53e0\u6216\u7eb5\u5411\u5173\u7cfb\uff0c\u9ad8\u79d1\u6280\u884c\u4e1a\u6240\u8c13\u7684\u8de8\u4e1a\u7ade\u4e89\u548c\u7ecf\u6d4e\u5173\u8054\uff0c\u5f80\u5f80\u901a\u8fc7\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u5f0f\u4ea7\u751f\u3002\n\u8fd9\u4e5f\u610f\u5473\u7740\uff0c\u9ad8\u901a\u4e0e\u535a\u901a\u7684\u5173\u7cfb\u4e0d\u80fd\u6e05\u6670\u754c\u5b9a\uff0c\u4f9d\u636e\u73b0\u884c\u7684\u53cd\u5784\u65ad\u6cd5\uff0cFTC\u600e\u4e48\u5224\uff0c\u662f\u5426\u901a\u8fc7\uff0c\u7f8e\u56fd\u653f\u5e9c\u5728\u5176\u4e2d\u7684\u53ef\u64cd\u4f5c\u7a7a\u95f4\u4e0d\u5c0f\u3002\n\u503c\u5f97\u6ce8\u610f\u7684\u662f\uff0c\u7f8e\u56fd\u534e\u76db\u987f\u65f6\u95f411\u67082\u65e5\uff0c\u7f8e\u56fd\u603b\u7edf\u7279\u6717\u666e\u4e0e\u535a\u901a\u516c\u53f8CEO\u970d\u514b&middot;\u8c2d\u5728\u767d\u5bab\u5ba3\u5e03\u535a\u901a\u516c\u53f8\u603b\u90e8\u5c06\u8fc1\u56de\u7f8e\u56fd\u3002 \u535a\u901a\u8fd0\u8425\u603b\u90e8\u4e00\u76f4\u4f4d\u4e8e\u52a0\u5dde\u5723\u4f55\u585e\uff0c\u4f46\u4e24\u5e74\u524d\u56e0\u7a0e\u52a1\u539f\u56e0\u5c06\u6ce8\u518c\u5730\u8fc1\u5230\u4e86\u65b0\u52a0\u5761\u3002\n\u7279\u6717\u666e\u7528\u4f4e\u7a0e\u6536\u653f\u7b56\u5438\u5f15\u535a\u901a\u56de\u6d41\u7f8e\u56fd\u3002\u535a\u901a\u9996\u5e2d\u6267\u884c\u5b98Hock Tan\u5f53\u65f6\u8868\u793a\uff0c\u5171\u548c\u515a\u7a0e\u6539\u8ba1\u5212\u7684\u901a\u8fc7\u662f\u8be5\u516c\u53f8\u51b3\u5b9a\u5c06\u603b\u90e8\u8fc1\u56de\u7f8e\u56fd\u7684\u539f\u56e0\u4e4b\u4e00\u3002\u6839\u636e\u8fd9\u4e00\u8ba1\u5212\uff0c\u7f8e\u56fd\u7684\u4f01\u4e1a\u7a0e\u5c06\u4ece\u73b0\u5728\u768435%\u964d\u81f320%\u3002\n\u4e5f\u5c31\u662f\u8bf4\uff0c\u535a\u901a\u6536\u8d2d\u9ad8\u901a\u8fd9\u4e2a\u6848\u4f8b\uff0c\u662f\u5426\u88ab\u7f8e\u56fd\u653f\u5e9c\u653e\u884c\uff0c\u5c06\u662f\u4e00\u4e2a\u653f\u6cbb\u3001\u6cd5\u5f8b\u3001\u8d44\u672c\u5e02\u573a\u76f8\u4e92\u4ea4\u7ec7\u7684\u590d\u6742\u7efc\u5408\u7ed3\u679c\u3002\n\u5c31\u7b97\u7f8e\u56fd\u653f\u5e9c\u653e\u884c\uff0c\u7531\u4e8e\u4e24\u5bb6\u516c\u53f8\u90fd\u662f\u4f53\u91cf\u5de8\u5927\u7684\u8de8\u56fd\u516c\u53f8\uff0c\u5176\u4ed6\u91cd\u8981\u56fd\u5bb6\u548c\u5730\u533a\u7684\u5ba1\u67e5\u662f\u5426\u901a\u8fc7\u4e5f\u662f\u5de8\u5927\u7684\u53d8\u91cf\u3002\n\u5b89\u6770\u5f8b\u5e08\u4e8b\u52a1\u6240\u5408\u4f19\u4eba\u5b8b\u8fce\u544a\u8bc9\u300a\u8d22\u7ecf\u300b\u8bb0\u8005\uff0c\u8fd9\u4e2a\u4ea4\u6613\u5e94\u8be5\u9700\u8981\u5411\u7f8e\u56fdFTC\u6216\u8005DOJ\u8fdb\u884c\u53cd\u5784\u65ad\u7533\u62a5\uff0c\u540c\u65f6\u9700\u8981\u5728\u5168\u7403\u8bb8\u591a\u5176\u4ed6\u6cd5\u57df\u7533\u62a5\u3002\u5982\u679c\u7f8e\u56fd\u5ba1\u67e5\u540e\u5426\u51b3\u4e86\uff0c\u5176\u4ed6\u6cd5\u57df\u90fd\u6279\u51c6\u4e86\uff0c\u8fd9\u4e2a\u4ea4\u6613\u4ecd\u7136\u65e0\u6cd5\u8fdb\u884c\u3002\u4f46\u662f\uff0c\u5982\u679c\u9ad8\u901a\u548c\u535a\u901a\u4e0eFTC\u6216DOJ\u8fdb\u884c\u8c08\u5224\u5e76\u5bf9\u4ea4\u6613\u8fdb\u884c\u8c03\u6574\u3001\u6216\u8005\u505a\u51fa\u67d0\u4e9b\u627f\u8bfa\u80fd\u591f\u6d88\u9664\u7f8e\u56fd\u53cd\u5784\u65ad\u673a\u6784\u7684\u987e\u8651\uff0c\u5219\u4ecd\u6709\u53ef\u80fd\u88ab\u9644\u6761\u4ef6\u6279\u51c6\u3002\uff08\u300a\u8d22\u7ecf\u300b\u9a7b\u7845\u8c37\u8bb0\u8005\u5218\u6cd3\u541b\u5bf9\u672c\u6587\u4ea6\u6709\u8d21\u732e\uff09\n{IMG:4}\n{IMG:5}"}, {"emotion": "neg", "id": "01a63b9ff971cf603d27e82515d056296d", "c_t": "\u6559\u80b2\u8be5\u662f\u75db\u82e6\u8fd8\u662f\u5feb\u4e50\u7684\u73b0\u5728\u793e\u4f1a\u4e0a\u6709\u8fd9\u4e48\u4e00\u79cd\u89c2\u70b9\uff0c\u751a\u81f3\u4e00\u4e9b\u4e13\u5bb6\u5b66\u8005\u4e5f\u90fd\u8fd9\u6837\u8ba4\u4e3a\uff1a\u6559\u80b2\u4e0d\u5c3d\u662f\u5feb\u4e50\u7684\uff0c\u91cc\u9762\u9690\u542b\u7740\u75db\u82e6\u7684\u6210\u5206\u3002\u6b63\u56e0\u4e3a\u6559\u80b2\u7684\u8fd9\u4e2a\u7279\u70b9\uff0c\u52a0\u4e0a\u5b69\u5b50\u8fd8\u672a\u6210\u5e74\uff0c\u7f3a\u4e4f\u5bf9\u4e16\u754c\u7684\u8db3\u591f\u8ba4\u8bc6\uff0c\u4e0d\u80fd\u72ec\u7acb\u5224\u65ad\uff0c\u56e0\u6b64\u4ed6\u4eec\u8ba4\u4e3a\uff0c\u6210\u4eba\u4e0d\u80fd\u4e00\u5473\u7ed9\u5b69\u5b50\u8ba9\u6b65\uff0c\u800c\u5fc5\u987b\u7ba1\u6559\u548c\u60e9\u6212\u3002\n\u8fd9\u79cd\u89c2\u70b9\u7684\u51fa\u73b0\uff0c\u5728\u5bb6\u957f\u4e2d\u5177\u6709\u4e00\u5b9a\u7684\u4ee3\u8868\u6027\uff0c\u4e5f\u6709\u4e00\u5b9a\u7684\u5408\u7406\u6027\u3002\u9759\u4e0b\u6765\u601d\u8003\uff0c\u8fd9\u6837\u4e00\u79cd\u770b\u6cd5\uff0c\u4e3b\u8981\u5305\u542b\u4e24\u4e2a\u65b9\u9762\u7684\u95ee\u9898\uff1a\u4e00\u662f\u6559\u80b2\u662f\u5feb\u4e50\u7684\uff0c\u8fd8\u662f\u75db\u82e6\u7684\uff1f\u4e8c\u662f\u6559\u80b2\u8981\u4e0d\u8981\u5bf9\u5b69\u5b50\u8fdb\u884c\u7ba1\u6559\u548c\u60e9\u6212\uff1f\u8fd9\u4e24\u4e2a\u95ee\u9898\uff0c\u76f4\u6307\u6559\u5b66\u80b2\u4eba\u7684\u672c\u8d28\uff0c\u5373\u4fbf\u5728\u7406\u8bba\u754c\uff0c\u4e5f\u662f\u4ec1\u8005\u89c1\u4ec1\u3002\n\u6559\u80b2\u662f\u5feb\u4e50\u7684\uff0c\u8fd8