ff0c\u5728\u534f\u540c\u6846\u67b6\u4e0b\uff0c\u4eac\u6d25\u5180\u4ea4\u901a\u8fd0\u8f93\u62b1\u56e2\u53d6\u6696\u7684\u4e00\u4f53\u5316\u683c\u5c40\u65e5\u6e10\u660e\u6717\uff0c\u534f\u540c\u53d1\u5c55\u8fc8\u5411\u7eb5\u6df1\u3002\n\u3000\u30002015\u5e7411\u6708\uff0c\u5317\u4eac\u3001\u5929\u6d25\u3001\u6cb3\u5317\u6b63\u5f0f\u7b7e\u7f72\u300a\u5317\u4eac\u5e02\u5929\u6d25\u5e02\u6cb3\u5317\u7701\u73af\u5883\u6267\u6cd5\u8054\u52a8\u5de5\u4f5c\u673a\u5236\u5408\u4f5c\u534f\u8bae\u300b\u540e\uff0c\u4e09\u5730\u534f\u8c03\u8054\u52a8\uff0c\u5f00\u5c55\u4e86\u91cd\u6c61\u67d3\u5929\u6c14\u5e94\u6025\u54cd\u5e94\u7b49\u8054\u52a8\u6267\u6cd5\u884c\u52a8\u3002\u6cb3\u5317\u7701\u73af\u5883\u4fdd\u62a4\u5385\u526f\u5385\u957f\u6bb7\u5e7f\u5e73\u8bf4\uff0c\u5168\u7701\u7d2f\u8ba1\u51fa\u52a8\u6267\u6cd5\u4eba\u54585.12\u4e07\u4eba\u6b21\uff0c\u68c0\u67e5\u4f01\u4e1a4.6\u4e07\u5bb6\u6b21\uff0c\u6709\u529b\u9707\u6151\u4e86\u73af\u5883\u8fdd\u6cd5\u884c\u4e3a\uff0c\u6709\u6548\u904f\u5236\u4e86\u73af\u5883\u6c61\u67d3\u4e8b\u4ef6\u7684\u53d1\u751f\uff0c\u7279\u522b\u662f\u5728\u91cd\u70b9\u533a\u57df\u5145\u5206\u53d1\u6325\u4e86\u8054\u9632\u8054\u63a7\u5bf9\u6539\u5584\u73af\u5883\u8d28\u91cf\u7684\u79ef\u6781\u4f5c\u7528\u3002\n\u3000\u3000\u6cb3\u5317\u8fd8\u4e0e\u4eac\u6d25\u53ca\u5468\u8fb9\u7701\u533a\u5171\u540c\u7f16\u5236\u300a\u4eac\u6d25\u5180\u53ca\u5468\u8fb9\u5730\u533a\u5927\u6c14\u6c61\u67d3\u9632\u6cbb\u4e2d\u957f\u671f\u89c4\u5212\u300b\uff0c\u6709\u5e8f\u63a8\u8fdb\u533a\u57df\u5927\u6c14\u6c61\u67d3\u6cbb\u7406\u5de5\u4f5c\u3002\n\u3000\u3000\u6cb3\u5317\u7701\u5de5\u4e1a\u548c\u4fe1\u606f\u5316\u5385\u526f\u5385\u957f\u5218\u6c38\u4ead\u8bf4\uff0c2015\u5e74\u4ee5\u6765\uff0c\u4eac\u6d25\u5180\u4e09\u5730\u8054\u5408\u4e3e\u529e\u4eac\u6d25\u5180\u4ea7\u4e1a\u8f6c\u79fb\u7cfb\u5217\u5bf9\u63a5\u6d3b\u52a8\uff0c\u7d2f\u8ba1\u5171\u63a8\u8fdb\u4eac\u6d25\u4ea7\u4e1a\u8f6c\u79fb\u9879\u76ee400\u4e2a\u4ee5\u4e0a\uff0c\u6295\u8d44\u8fd1\u4e07\u4ebf\u5143\u4eba\u6c11\u5e01\u3002\n\u3000\u3000\u57fa\u672c\u516c\u5171\u670d\u52a1\u5747\u7b49\u5316\u6c34\u5e73\u4e0d\u65ad\u63d0\u9ad8\u3002\u6cb3\u5317\u7701\u53d1\u6539\u59d4\u526f\u4e3b\u4efb\u3001\u7701\u534f\u540c\u529e\u5e38\u52a1\u526f\u4e3b\u4efb\u738b\u7acb\u5fe0\u8868\u793a\uff0c\u4eac\u6d25\u5180\u4e09\u5730\u5b9e\u73b0\u4e86\u4e13\u4e1a\u6280\u672f\u4eba\u5458\u804c\u4e1a\u8d44\u683c\u3001\u804c\u79f0\u8bc4\u5ba1\u8bc1\u4e66\u548c\u90e8\u5206\u533b\u7597\u673a\u6784\u68c0\u9a8c\u7ed3\u679c\u4e92\u8ba4\u3001\u533b\u5b66\u5f71\u50cf\u68c0\u67e5\u8d44\u6599\u5171\u4eab\uff0c\u5168\u7701300\u4f59\u5bb6\u4e8c\u7ea7\u4ee5\u4e0a\u533b\u7597\u673a\u6784\u4e0e\u4eac\u6d25\u533b\u9662\u5efa\u7acb\u4e86\u591a\u79cd\u5f62\u5f0f\u7684\u5408\u4f5c\u5173\u7cfb\uff0c\u6cb3\u5317\u9ad8\u6821\u4e0e\u4eac\u6d25\u9ad8\u6821(\u542b\u90e8\u59d4\u9ad8\u6821)\u7ec4\u5efa\u4e86\u4e00\u6279\u534f\u540c\u521b\u65b0\u8054\u76df\u3002\n\u3000\u3000\u6b64\u5916\uff0c\u6cb3\u5317\u8fd8\u4e0e\u4eac\u6d25\u5efa\u7acb\u4e86\u5bf9\u53e3\u5e2e\u6276\u673a\u5236\uff0c\u201c\u5341\u4e09\u4e94\u201d\u671f\u95f4\u4eac\u6d25\u7ed3\u5bf9\u5e2e\u6276\u6cb3\u531721\u4e2a\u53bf(\u533a)\uff0c\u5b89\u6392\u5e2e\u6276\u8d44\u91d147.68\u4ebf\u5143\u3002\n\u3000\u3000\u738b\u7acb\u5fe0\u8868\u793a\uff0c\u5b9e\u8df5\u8bc1\u660e\uff0c\u4eac\u6d25\u5180\u534f\u540c\u53d1\u5c55\u662f\u758f\u89e3\u5317\u4eac\u975e\u9996\u90fd\u529f\u80fd\u3001\u8865\u9f50\u6cb3\u5317\u53d1\u5c55\u77ed\u677f\u7684\u6709\u6548\u9014\u5f84\uff0c\u662f\u6253\u9020\u65b0\u7684\u533a\u57df\u7ecf\u6d4e\u589e\u957f\u6781\u3001\u6784\u7b51\u4e16\u754c\u7ea7\u57ce\u5e02\u7fa4\u7684\u6218\u7565\u4e4b\u4e3e\uff0c\u7ed9\u6cb3\u5317\u5e26\u6765\u7684\u79ef\u6781\u5f71\u54cd\u548c\u63a8\u52a8\u4f5c\u7528\u662f\u5168\u65b9\u4f4d\u548c\u6df1\u5c42\u6b21\u7684\uff0c\u662f\u5386\u53f2\u6027\u7684\u548c\u8f6c\u6298\u6027\u7684\u3002\n\u3000\u3000\u8c08\u53ca\u4e0b\u4e00\u6b65\u5de5\u4f5c\u601d\u8def\u65f6\uff0c\u738b\u7acb\u5fe0\u8bf4\uff0c\u7d27\u7d27\u6293\u4f4f\u627f\u63a5\u5317\u4eac\u975e\u9996\u90fd\u529f\u80fd\u758f\u89e3\u8fd9\u4e00\u201c\u725b\u9f3b\u5b50\u201d\uff0c\u52a0\u5feb\u63a8\u52a8\u6cb3\u5317\u201c\u4e24\u7ffc\u201d\u53d1\u5c55(\u4e00\u7ffc\u662f\u4ee52022\u5e74\u5317\u4eac\u51ac\u5965\u4f1a\u4e3a\u5951\u673a\uff0c\u63a8\u8fdb\u5f20\u5317\u5730\u533a\u5efa\u8bbe\uff0c\u53e6\u4e00\u7ffc\u662f\u96c4\u5b89\u65b0\u533a)\uff0c\u7ee7\u7eed\u63a8\u8fdb\u4e09\u4e2a\u9886\u57df\u7387\u5148\u7a81\u7834\uff0c\u7740\u529b\u843d\u5b9e\u201c\u4e09\u533a\u4e00\u57fa\u5730\u201d\u56db\u5927\u529f\u80fd\u5b9a\u4f4d(\u5373\u5efa\u8bbe\u201c\u5168\u56fd\u73b0\u4ee3\u5546\u8d38\u7269\u6d41\u91cd\u8981\u57fa\u5730\u3001\u4ea7\u4e1a\u8f6c\u578b\u5347\u7ea7\u8bd5\u9a8c\u533a\u3001\u65b0\u578b\u57ce\u9547\u5316\u4e0e\u57ce\u4e61\u7edf\u7b79\u793a\u8303\u533a\u3001\u4eac\u6d25\u5180\u751f\u6001\u73af\u5883\u652f\u6491\u533a\u201d)\uff0c\u8fdb\u4e00\u6b65\u52a0\u5f3a\u4e0e\u4eac\u6d25\u5bf9\u63a5\u5408\u4f5c\uff0c\u53d1\u6325\u6539\u9769\u521b\u65b0\u5f15\u9886\u793a\u8303\u4f5c\u7528\uff0c\u63a8\u52a8\u4eac\u6d25\u5180\u534f\u540c\u53d1\u5c55\u5411\u7eb5\u6df1\u62d3\u5c55\u3002\n\u3000\u3000\u5218\u6c38\u4ead\u8868\u793a\uff0c\u6df1\u5165\u63a8\u8fdb\u4eac\u6d25\u5180\u4ea7\u4e1a\u534f\u540c\u53d1\u5c55\uff0c\u672a\u6765\u6cb3\u5317\u8981\u505a\u597d\u201c\u52a0\u51cf\u4e58\u9664\u201d\uff0c\u5373\u4e3b\u52a8\u627f\u63a5\u4eac\u6d25\u4f18\u8d28\u4ea7\u80fd\uff0c\u575a\u5b9a\u4e0d\u79fb\u5316\u89e3\u8fc7\u5269\u4ea7\u80fd\uff0c\u52a0\u5feb\u4f20\u7edf\u4ea7\u4e1a\u8fc8\u5411\u4e2d\u9ad8\u7aef\uff0c\u8865\u9f50\u4fe1\u606f\u5316\u548c\u65b0\u5174\u4ea7\u4e1a\u77ed\u677f\u3002(\u5b8c)\u731c\u60a8\u559c\u6b22\uff1a\u5927\u53d1888\u5a31\u4e50\u57ce\u4f18\u60e0\u7801"}, {"emotion": "neg", "id": "01694c2be73b01aeef0dca5dde77f64218", "c_t": "\u7ec4\u56fe:\u970d\u601d\u71d518\u5c81\u9752\u6da9\u65e7\u7167\u66dd\u5149 \u773c\u795e\u6e29\u67d4\u6e05\u7eaf\u52a8\u4eba\u6b66\u6c49\u4e16\u754c\u98de\u884c\u8005\u5927\u4f1a \u767e\u5bf9\u65b0\u4eba\u70ed\u6c14\u7403\u5a5a\u793c\u521b\u57fa\u5c3c\u65af\u7eaa\n6\u65e5\u5728\u6b64\u95f4\u4e3e\u884c\u7684\u4e16\u754c\u98de\u884c\u8005\u5927\u4f1a\u4e0a\uff0c\u7ecf\u4e0a\u6d77\u5927\u4e16\u754c\u57fa\u5c3c\u65af\u603b\u90e8\u8ba4\u8bc1\uff0c\u201c\u767e\u5bf9\u65b0\u4eba\u70ed\u6c14\u7403\u7a7a\u4e2d\u5a5a\u793c\u201d\u521b\u4e0b\u5927\u4e16\u754c\u57fa\u5c3c\u65af...\u3010\u8be6\u7ec6\u3011\n\u5f3a\u56fe\u6253\u699c2017-11-07 05:44:16"}, {"emotion": "neg", "id": "015dc14e28271a3361ad916a7d6aa371df", "c_t": "\u73af\u4fdd\u90e8\uff1a\u505a\u597d\u6392\u6c61\u8bb8\u53ef\u5236\u4e0e\u56fa\u5b9a\u6c61\u67d3\u6e90\u73af\u5883\u7ba1\u7406\u8854\u63a5{IMG:1}\n\u3000\u3000\u8d44\u6599\u56fe\uff1a\u88ab\u6c61\u67d3\u7684\u6cb3\u6d41\u3002\u4e2d\u65b0\u793e\u53d1\u56fe\u7247\u6765\u6e90\uff1aCNSPHOTO\n\u3000\u3000\u4e2d\u65b0\u7f5111\u67087\u65e5\u7535\u636e\u73af\u5883\u4fdd\u62a4\u90e8\u7f51\u7ad9\u6d88\u606f\uff0c\u73af\u5883\u4fdd\u62a4\u90e8\u90e8\u957f\u674e\u5e72\u677011\u67086\u65e5\u5728\u4eac\u4e3b\u6301\u53ec\u5f00\u73af\u5883\u4fdd\u62a4\u90e8\u90e8\u52a1\u4f1a\u8bae\uff0c\u5ba1\u8bae\u5e76\u539f\u5219\u901a\u8fc7\u300a\u6392\u6c61\u8bb8\u53ef\u7ba1\u7406\u529e\u6cd5(\u8bd5\u884c)\u300b(\u4ee5\u4e0b\u7b80\u79f0\u300a\u529e\u6cd5\u300b)\u3002\u4f1a\u8bae\u6307\u51fa\uff0c\u8981\u52a0\u5feb\u63a8\u8fdb\u300a\u6392\u6c61\u8bb8\u53ef\u7ba1\u7406\u6761\u4f8b\u300b\u51fa\u53f0\uff0c\u8fdb\u4e00\u6b65\u592f\u5b9e\u6392\u6c61\u8bb8\u53ef\u5236\u5b9e\u65bd\u7684\u6cd5\u5f8b\u8d23\u4efb\u3002\n\u3000\u3000\u4f1a\u8bae\u6307\u51fa\uff0c\u8981\u5bf9\u5404\u9879\u89c4\u5b9a\u8fdb\u884c\u6df1\u5165\u7814\u7a76\u3001\u5468\u5bc6\u8003\u8651\u3001\u5ba1\u614e\u5904\u7406\uff0c\u5e76\u52a0\u5feb\u63a8\u52a8\u300a\u529e\u6cd5\u300b\u51fa\u53f0\u5b9e\u65bd\u3002\u8981\u52a0\u5927\u5ba3\u4f20\u5f15\u5bfc\u529b\u5ea6\uff0c\u8ba9\u6392\u6c61\u5355\u4f4d\u5145\u5206\u8ba4\u8bc6\u5230\u5e94\u5c65\u884c\u7684\u8d23\u4efb\u548c\u4e49\u52a1\uff0c\u786e\u4fdd\u300a\u529e\u6cd5\u300b\u5b9e\u65bd\u53d6\u5f97\u826f\u597d\u6548\u679c\u3002\n\u3000\u3000\u4f1a\u8bae\u5f3a\u8c03\uff0c\u300a\u529e\u6cd5\u300b\u7684\u540e\u7eed\u4fee\u6539\u5b8c\u5584\uff0c\u8981\u628a\u63e1\u597d\u4ee5\u4e0b\u51e0\u4e2a\u65b9\u9762\uff1a\u6839\u636e\u56fd\u5bb6\u548c\u5730\u65b9\u6807\u51c6\uff0c\u51c6\u786e\u786e\u5b9a\u6c61\u67d3\u7269\u8bb8\u53ef\u6392\u653e\u6d53\u5ea6\uff1b\u6392\u6c61\u8bb8\u53ef\u8bc1\u4f5c\u4e3a\u653f\u5e9c\u548c\u6392\u6c61\u5355\u4f4d\u4e4b\u95f4\u7684\u5951\u7ea6\uff0c\u8981\u660e\u786e\u544a\u77e5\u6392\u6c61\u5355\u4f4d\u5e94\u5c3d\u7684\u6cd5\u5f8b\u4e49\u52a1\uff0c\u4fc3\u8fdb\u6392\u6c61\u5355\u4f4d\u5728\u8bb8\u53ef\u8bc1\u6709\u6548\u671f\u5185\u6ee1\u8db3\u6cd5\u5f8b\u548c\u6807\u51c6\u8981\u6c42\uff1b\u5408\u6cd5\u4f9d\u89c4\u505a\u597d\u884c\u4e1a\u6e05\u7406\uff0c\u6682\u65f6\u4e0d\u5177\u5907\u6392\u6c61\u8bb8\u53ef\u7533\u8bf7\u6761\u4ef6\u7684\u4f01\u4e1a\u4e5f\u8981\u7eb3\u5165\u76d1\u7ba1\u8303\u56f4\uff0c\u660e\u786e\u6574\u6539\u8981\u6c42\u548c\u671f\u9650\uff1b\u4e0e\u7701\u4ee5\u4e0b\u73af\u4fdd\u673a\u6784\u76d1\u6d4b\u76d1\u5bdf\u6267\u6cd5\u5782\u76f4\u7ba1\u7406\u5236\u5ea6\u6539\u9769\u76f8\u8854\u63a5\uff0c\u6392\u6c61\u8bb8\u53ef\u8bc1\u6838\u53d1\u4e3b\u4f53\u4e3b\u8981\u5728\u7701\u3001\u5e02\u4e24\u7ea7\uff1b\u8fdb\u4e00\u6b65\u5b8c\u5584\u6cd5\u5f8b\u8d23\u4efb\uff0c\u786e\u4fdd\u5404\u9879\u5904\u7f5a\u624b\u6bb5\u8fd0\u7528\u5f97\u5f53\uff0c\u63a8\u52a8\u6392\u6c61\u8bb8\u53ef\u5236\u6709\u6548\u5b9e\u65bd\u548c\u4e25\u683c\u76d1\u7ba1\uff1b\u4ee5\u53ca\u8fdb\u4e00\u6b65\u7ec6\u5316\u5ef6\u7eed\u548c\u53d8\u66f4\u6392\u6c61\u8bb8\u53ef\u8bc1\u7684\u76f8\u5173\u89c4\u5b9a\uff0c\u660e\u786e\u4f01\u4e1a\u81ea\u52a8\u5728\u7ebf\u76d1\u6d4b\u7684\u6cd5\u5f8b\u4e49\u52a1\uff0c\u660e\u786e\u5168\u56fd\u6392\u6c61\u8bb8\u53ef\u4fe1\u606f\u5e73\u53f0\u5efa\u8bbe\u548c\u7ef4\u62a4\u8d23\u4efb\u3002\n\u3000\u3000\u4f1a\u8bae\u6307\u51fa\uff0c\u8981\u52a0\u5feb\u63a8\u8fdb\u300a\u6392\u6c61\u8bb8\u53ef\u7ba1\u7406\u6761\u4f8b\u300b\u51fa\u53f0\uff0c\u8fdb\u4e00\u6b65\u592f\u5b9e\u6392\u6c61\u8bb8\u53ef\u5236\u5b9e\u65bd\u7684\u6cd5\u5f8b\u8d23\u4efb\u3002\u7ec6\u5316\u5404\u9879\u89c4\u5b9a\uff0c\u505a\u597d\u6392\u6c61\u8bb8\u53ef\u5236\u4e0e\u73af\u5883\u5f71\u54cd\u8bc4\u4ef7\u3001\u4f01\u4e8b\u4e1a\u5355\u4f4d\u6392\u6c61\u603b\u91cf\u63a7\u5236\u3001\u6392\u6c61\u6743\u4ea4\u6613\u7b49\u76f8\u5173\u56fa\u5b9a\u6c61\u67d3\u6e90\u73af\u5883\u7ba1\u7406\u5236\u5ea6\u7684\u8854\u63a5\uff0c\u5f62\u6210\u76f8\u4e92\u652f\u6491\u7684\u5f3a\u5316\u6c61\u67d3\u8005\u8d23\u4efb\u7ba1\u63a7\u4f53\u7cfb\u3002\n\u3000\u3000\u4f1a\u8bae\u5f3a\u8c03\uff0c\u5b9e\u65bd\u6392\u6c61\u8bb8\u53ef\u5236\u5bf9\u5b9e\u73b0\u5de5\u4e1a\u6c61\u67d3\u6e90\u5168\u9762\u8fbe\u6807\u6392\u653e\u3001\u843d\u5b9e\u91cd\u70b9\u5730\u533a\u7a7a\u6c14\u8d28\u91cf\u6539\u5584\u76ee\u6807\u7b49\u5de5\u4f5c\u5177\u6709\u91cd\u8981\u610f\u4e49\u3002\u8981\u670d\u52a1\u597d\u4e2d\u5fc3\u5de5\u4f5c\uff0c\u8fdb\u4e00\u6b65\u52a0\u5927\u5bf9\u65e0\u8bc1\u6392\u6c61\u548c\u4e0d\u6309\u8bc1\u6392\u6c61\u7684\u68c0\u67e5\u5904\u7f5a\u529b\u5ea6\uff0c\u52a0\u5927\u5bf9\u8fdd\u89c4\u9879\u76ee\u6e05\u7406\u6574\u987f\u529b\u5ea6\uff0c\u4e3a\u4e2d\u5fc3\u4efb\u52a1\u7684\u5b8c\u6210\u63d0\u4f9b\u6709\u529b\u652f\u6491\u3002"}, {"emotion": "neg", "id": "019aef1c198b813e6caa36badda163faf1", "c_t": "\u4e3a\u4e86\u80fd\u591f\u6700\u5148\u4e70\u5230\u65b0\u51fa\u7684\u82f9\u679c\u624b\u673a \u4ed6\u4eec\u6392\u4e86\u516b\u5e74\u7684\u961f{IMG:1}\n{IMG:2}\n1            /            7                                                \u4e3a\u4e86\u80fd\u591f\u6700\u5148\u4e70\u5230\u65b0\u51fa\u7684\u82f9\u679c\u624b\u673a \u4ed6\u4eec\u6392\u4e86\u516b\u5e74\u7684\u961f"}, {"emotion": "neg", "id": "01c54e09ddefcf9f10e06e96acbc1cd9d6", "c_t": "\u7126\u70b9\u4e4b\u4e89\uff1a\u5df2\u88ab\u67e5\u5c01\u7684\u4e0d\u52a8\u4ea7\u8f6c\u79fb\u767b\u8bb0\u662f\u5426\u5408\u6cd5\uff1f\u3000\u3000\u4e0d\u52a8\u4ea7\u9519\u8bef\u767b\u8bb0\u6848\u4e00\u5ba1\u5224\u51b3\u4e1a\u4e3b\u80dc\u8bc9\u88ab\u544a\u63d0\u8d77\u4e0a\u8bc9\n\u3000\u3000\u201c\u98de\u201d\u8d701\u5e74\u7684\u623f\u5b50\u8fd8\u80fd\u201c\u98de\u201d\u56de\u6765\u5417\uff1f\n\u3000\u3000\u7126\u70b9\u4e4b\u4e89\uff1a\u5df2\u88ab\u67e5\u5c01\u7684\u4e0d\u52a8\u4ea7\u8f6c\u79fb\u767b\u8bb0\u662f\u5426\u5408\u6cd5\uff1f\n\u3000\u3000\u53bb\u5e74\u4e0b\u534a\u5e74\uff0c\u4f5b\u5c71\u5357\u6d77\u51fa\u73b0\u4e00\u4f8b\u4e0d\u52a8\u4ea7\u9519\u8bef\u767b\u8bb0\u6848\uff0c\u6a0a\u5148\u751f\u5df2\u7ecf\u62ff\u5230\u624b\u7684\u4e0d\u52a8\u4ea7\u767b\u8bb0\u8bc1\u88ab\u5357\u6d77\u533a\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u5ba3\u544a\u4f5c\u5e9f\u3002\u5c31\u8fd9\u6837\uff0c\u4ef7\u503c290\u4e07\u5143\u7684\u623f\u5b50\u4ece\u6a0a\u5148\u751f\u624b\u4e2d\u201c\u98de\u201d\u8d70\u4e86\u3002\n\u3000\u3000\u8be5\u4e8b\u4ef6\u53d1\u751f\u540e\uff0c\u672c\u62a5\u6301\u7eed\u5173\u6ce8\u6b64\u6848\u3002\u8fd1\u65e5\uff0c\u5e7f\u5dde\u65e5\u62a5\u5168\u5a92\u4f53\u8bb0\u8005\u4ece\u5f53\u4e8b\u4eba\u5904\u83b7\u6089\uff0c\u8be5\u6848\u5df2\u4e8e2017\u5e749\u670829\u65e5\u5ba3\u5224\u3002\u987a\u5fb7\u4eba\u6c11\u6cd5\u9662\u5224\u51b3\u64a4\u9500\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u4f5c\u51fa\u7684\u30102016\u3011\u7b2c1\u53f7\u300a\u64a4\u9500\u4e0d\u52a8\u4ea7\u767b\u8bb0\u51b3\u5b9a\u4e66\u300b\uff0c\u5224\u51b3\u751f\u6548\u540e15\u65e5\u5185\u5c06\u6d89\u6848\u623f\u4ea7\u7684\u4e0d\u52a8\u4ea7\u6743\u6062\u590d\u5230\u6a0a\u5148\u751f\u6240\u6709\u3002\n\u3000\u3000\u8fd9\u610f\u5473\u7740\uff0c\u201c\u98de\u201d\u8d70\u4e86\u6574\u65741\u5e74\u7684\u623f\u5b50\uff0c\u53c8\u6709\u5e0c\u671b\u201c\u98de\u201d\u56de\u5230\u6a0a\u5148\u751f\u540d\u4e0b\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e8b\u8fd8\u6ca1\u90a3\u4e48\u5feb\u5c18\u57c3\u843d\u5b9a\u3002\u4e0a\u5468\u88ab\u544a\u65b9\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u63d0\u8d77\u4e0a\u8bc9\uff0c\u6a0a\u5148\u751f\u5df2\u6536\u5230\u6cd5\u9662\u4f20\u7968\uff0c\u76ee\u524d\u6848\u4ef6\u8fd8\u5728\u8fdb\u4e00\u6b65\u5ba1\u7406\u4e2d\u3002\n\u3000\u3000\u9886\u4e86\u4e0d\u52a8\u4ea7\u8bc1\u540e\u53d1\u73b0\u623f\u5b50\u88ab\u67e5\u5c01\n\u3000\u3000\u65f6\u95f4\u56de\u6eaf\u52302016\u5e745\u6708\u4efd\u3002\n\u3000\u3000\u6a0a\u5148\u751f\u770b\u4e2d\u4e86\u5434\u5148\u751f\u540d\u4e0b\u7684\u6c34\u97f5\u5c1a\u90fd\u7684\u4e24\u5957\u623f\u4ea7\uff0c\u53cc\u65b9\u5728\u4e2d\u4ecb\u673a\u6784\u7684\u89c1\u8bc1\u4e0b\u7b7e\u8ba2\u4e86\u623f\u5730\u4ea7\u4e70\u5356\u5408\u540c\u3002\u6309\u7167\u7ea6\u5b9a\uff0c\u8fd9\u4e24\u5957\u623f\u4ea7\u5171\u8ba1290\u4e07\u5143\uff0c\u5176\u4e2d\u6bcf\u5957\u4ef7\u683c\u4e3a145\u4e07\u5143\u30022016\u5e748\u670816\u65e5\uff0c\u6a0a\u5148\u751f\u5411\u5434\u5148\u751f\u652f\u4ed8\u4e86140\u4e07\u5143\u7684\u623f\u6b3e\uff0c\u5269\u4e0b\u7684150\u4e07\u623f\u6b3e\uff0c\u6a0a\u5148\u751f\u8ddf\u5434\u5148\u751f\u7ea6\u5b9a\u53d6\u5f97\u4ea7\u6743\u53d8\u66f4\u4e4b\u540e\u518d\u5411\u94f6\u884c\u62b5\u62bc\uff0c\u53e6\u884c\u652f\u4ed8\u30028\u670818\u65e5\uff0c\u6a0a\u5148\u751f\u548c\u5434\u5148\u751f\u4e00\u8d77\u5230\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u67e5\u518c\uff0c\u5f53\u65f6\uff0c\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u5411\u5434\u5148\u751f\u51fa\u5177\u4e86\u300a\u4f5b\u5c71\u5e02(\u5357\u6d77\u533a)\u623f\u4ea7\u67e5\u8be2\u8bc1\u660e\u300b\uff0c\u8bc1\u660e\u8be5\u4e0d\u52a8\u4ea7\u65e0\u62b5\u62bc\u3001\u65e0\u67e5\u5c01\u3002\n\u3000\u3000\u8fd9\u4e00\u5207\u770b\u4e0a\u53bb\u5f88\u6b63\u5e38\u4e5f\u5f88\u987a\u5229\uff0c\u4f46\u662f\u4e00\u5468\u4e4b\u540e\uff0c\u4e8b\u60c5\u5cf0\u56de\u8def\u8f6c\u3002\n\u3000\u3000\u6839\u636e\u6cd5\u9662\u5ba1\u7406\uff0c\u5c31\u5728\u6a0a\u5148\u751f\u548c\u5434\u5148\u751f\u67e5\u518c\u540e\u4e00\u5468\uff0c\u53732016\u5e748\u670824\u65e5\uff0c\u96f7\u5dde\u6cd5\u9662\u5411\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u9001\u8fbe\u300a\u6c11\u4e8b\u88c1\u5b9a\u4e66\u300b\u4ee5\u53ca\u300a\u534f\u52a9\u6267\u884c\u901a\u77e5\u4e66\u300b\uff0c\u8981\u6c42\u67e5\u5c01\u5434\u5148\u751f\u540d\u4e0b\u6c34\u97f5\u5c1a\u90fd\u7684\u4e24\u5957\u623f\u4ea7\uff0c\u4e5f\u5c31\u662f\u6a0a\u5148\u751f\u8d2d\u4e70\u7684\u8fd9\u4e24\u5957\u623f\u4ea7\u3002\u67e5\u5c01\u671f\u95f4\u4ece2016\u5e748\u670824\u65e5\u8d77\u81f32019\u5e748\u670823\u65e5\u6b62\u3002\u800c\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u4e5f\u4e8e\u5f53\u65e5\u529e\u7406\u4e86\u6d89\u6848\u623f\u4ea7\u7684\u9884\u67e5\u5c01\u767b\u8bb0\u3002\n\u3000\u3000\u5bf9\u6b64\u5e76\u4e0d\u77e5\u60c5\u7684\u6a0a\u5148\u751f\u4e8e2016\u5e749\u67082\u65e5\u5230\u7a0e\u52a1\u90e8\u95e8\u7f34\u7eb3\u4e86\u76f8\u5173\u7a0e\u8d39\uff0c\u4e4b\u540e\u5411\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u7533\u8bf7\u529e\u7406\u6d89\u6848\u4e0d\u52a8\u4ea7\u4ea7\u6743\u767b\u8bb0\u3002\u8e4a\u8df7\u7684\u662f\uff0c\u5728\u8fd9\u4e2a\u65f6\u95f4\u70b9\u4e0a\uff0c\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u7684\u5de5\u4f5c\u4eba\u5458\u5e76\u6ca1\u6709\u53d1\u73b0\u6d89\u6848\u623f\u4ea7\u5df2\u7ecf\u8fdb\u884c\u4e86\u67e5\u5c01\u767b\u8bb0\u3002\u4e8e\u662f\uff0c\u8be5\u5c40\u4e3a\u6a0a\u5148\u751f\u548c\u5434\u5148\u751f\u529e\u7406\u4e86\u4e0d\u52a8\u4ea7\u8f6c\u79fb\u767b\u8bb0\uff0c\u5e76\u4e8e9\u670819\u65e5\u5411\u6a0a\u5148\u751f\u6838\u53d1\u4e86\u4e0d\u52a8\u4ea7\u767b\u8bb0\u8bc1\uff0c\u6d89\u6848\u4e0d\u52a8\u4ea7\u7684\u6743\u5c5e\u4eba\u767b\u8bb0\u4e3a\u6a0a\u5148\u751f\u3002\u5f539\u670828\u65e5\u6a0a\u5148\u751f\u5411\u94f6\u884c\u7533\u8bf7\u62b5\u62bc\u8d37\u6b3e\u88ab\u62d2\u65f6\uff0c\u6a0a\u5148\u751f\u624d\u53d1\u73b0\u8fd9\u5957\u623f\u5904\u4e8e\u88ab\u67e5\u5c01\u72b6\u6001\uff0c\u65e0\u6cd5\u8d37\u6b3e\u3002\u6a0a\u5148\u751f\u7acb\u5373\u4e0e\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u6c9f\u901a\uff0c\u786e\u8ba4\u4e86\u81ea\u5df1\u7684\u8fd9\u5957\u623f\u88ab\u67e5\u5c01\u3002\n\u3000\u3000\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u8ba9\u6a0a\u5148\u751f\u59cb\u6599\u672a\u53ca\u3002\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u4e8e2016\u5e7410\u670817\u65e5\u4f5c\u51fa\u4e86\u300a\u64a4\u9500\u4e0d\u52a8\u4ea7\u767b\u8bb0\u51b3\u5b9a\u4e66\u300b\uff0c\u540c\u65f6\u5c06\u6d89\u6848\u623f\u4ea7\u6062\u590d\u5230\u67e5\u5c01\u767b\u8bb0\u72b6\u6001\u3002\u7531\u4e8e\u6a0a\u5148\u751f\u4e0d\u80af\u4ea4\u56de\u5df2\u7ecf\u9881\u53d1\u7ed9\u4ed6\u7684\u4e0d\u52a8\u4ea7\u767b\u8bb0\u8bc1\uff0c\u4e8e\u662f\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c4011\u67088\u65e5\u5728\u5176\u5b98\u7f51\u4e0a\u516c\u544a\u6d89\u6848\u4e0d\u52a8\u4ea7\u6743\u8bc1\u4e66\u4f5c\u5e9f\u3002\n\u3000\u3000\u5c31\u8fd9\u6837\uff0c\u6a0a\u5148\u751f\u83ab\u540d\u5176\u5999\u201c\u4e22\u201d\u4e86\u81ea\u5df1\u7684\u623f\u5b50\uff0c\u5176\u4ea7\u6743\u4eba\u8d44\u683c\u88ab\u53d6\u6d88\u4e86\u3002\u6a0a\u5148\u751f\u4e0d\u670d\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u505a\u51fa\u7684\u300a\u64a4\u9500\u4e0d\u52a8\u4ea7\u767b\u8bb0\u51b3\u5b9a\u4e66\u300b\uff0c\u4e8e\u662f\u5c06\u8be5\u5c40\u544a\u4e0a\u4e86\u6cd5\u5ead\uff0c\u8981\u6c42\u64a4\u9500\u8be5\u51b3\u5b9a\u3002\n\u3000\u3000\u901a\u8fc7\u6cd5\u9662\u5ba1\u7406\u67e5\u660e\uff0c\u5728\u6574\u4e2a\u4e8b\u4ef6\u4e2d\uff0c\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u786e\u5b9e\u662f\u56e0\u5de5\u4f5c\u5931\u8bef\u5728\u67e5\u5c01\u671f\u95f4\u529e\u7406\u4e86\u6d89\u6848\u4e0d\u52a8\u4ea7\u7684\u6743\u5c5e\u8f6c\u79fb\u767b\u8bb0\uff0c\u800c\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u6d89\u6848\u7684\u623f\u5c4b\u786e\u5b9e\u5904\u4e8e\u6cd5\u9662\u67e5\u5c01\u72b6\u6001\u4e2d\u3002\u90a3\u4e48\uff0c\u8fd9\u6837\u7684\u8f6c\u79fb\u767b\u8bb0\u662f\u5426\u5408\u6cd5\uff1f\n\u3000\u3000\u987a\u5fb7\u4eba\u6c11\u6cd5\u9662\u8ba4\u4e3a\uff0c\u6839\u636e\u300a\u4e2d\u534e\u4eba\u6c11\u5171\u548c\u56fd\u57ce\u5e02\u623f\u5730\u4ea7\u7ba1\u7406\u6cd5\u300b\u7b2c\u4e09\u5341\u516b\u6761\u7b2c(\u4e8c)\u9879\u89c4\u5b9a\uff0c\u201c\u53f8\u6cd5\u673a\u5173\u548c\u884c\u653f\u673a\u5173\u4f9d\u6cd5\u88c1\u5b9a\u3001\u51b3\u5b9a\u67e5\u5c01\u6216\u8005\u4ee5\u5176\u4ed6\u5f62\u5f0f\u9650\u5236\u623f\u5730\u4ea7\u6743\u5229\u7684\u201d\uff0c\u201c\u4e0d\u5f97\u8f6c\u8ba9\u201d\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5df2\u88ab\u4eba\u6c11\u6cd5\u9662\u67e5\u5c01\u7684\u623f\u4ea7\uff0c\u4f9d\u6cd5\u4e0d\u5f97\u8f6c\u8ba9\u3002\u6240\u4ee5\uff0c\u5728\u8fd9\u4e2a\u6848\u4ef6\u4e2d\uff0c\u4ece2016\u5e748\u670824\u65e5\u8d77\uff0c\u5373\u6d89\u6848\u623f\u4ea7\u88ab\u67e5\u5c01\u7684\u7b2c\u4e00\u5929\u5f00\u59cb\u5c31\u4e0d\u80fd\u8f6c\u8ba9\u4e86\u3002\u56e0\u6b64\uff0c\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c402016\u5e749\u6708\u4efd\u529e\u7406\u7684\u6d89\u6848\u4e0d\u52a8\u4ea7\u8f6c\u79fb\u767b\u8bb0\uff0c\u5176\u884c\u4e3a\u662f\u8fdd\u6cd5\u7684\u3002\n\u3000\u3000\u884c\u653f\u673a\u5173\u80fd\u5426\u6709\u6743\u64a4\u9500\u4e0d\u5408\u6cd5\u7684\u767b\u8bb0\u884c\u4e3a\uff1f\u89c6\u60c5\u51b5\u800c\u5b9a\u3002\u65e2\u7136\u8fd9\u6837\u7684\u8f6c\u79fb\u767b\u8bb0\u884c\u4e3a\u662f\u8fdd\u6cd5\u7684\uff0c\u662f\u5426\u610f\u5473\u7740\u4e3b\u7ba1\u90e8\u95e8\u53ef\u4ee5\u201c\u77e5\u9519\u5c31\u6539\u201d\uff0c\u64a4\u9500\u5df2\u7ecf\u8fdd\u6cd5\u7684\u767b\u8bb0\u884c\u4e3a\u5462\uff1f\n\u3000\u3000\u987a\u5fb7\u4eba\u6c11\u6cd5\u9662\u8ba4\u4e3a\uff0c\u6839\u636e\u300a\u6700\u9ad8\u4eba\u6c11\u6cd5\u9662\u5ba1\u7406\u623f\u5c4b\u767b\u8bb0\u6848\u82e5\u5e72\u95ee\u9898\u7684\u89c4\u5b9a\u300b\u7b2c\u5341\u4e00\u6761\u7b2c\u4e09\u6b3e\uff0c\u201c\u88ab\u8bc9\u623f\u5c4b\u767b\u8bb0\u884c\u4e3a\u8fdd\u6cd5\uff0c\u4f46\u5224\u51b3\u64a4\u9500\u5c06\u7ed9\u516c\u5171\u5229\u76ca\u9020\u6210\u91cd\u5927\u635f\u5931\u6216\u8005\u623f\u5c4b\u5df2\u4e3a\u7b2c\u4e09\u4eba\u5584\u610f\u53d6\u5f97\u7684\uff0c\u5224\u51b3\u786e\u8ba4\u88ab\u8bc9\u884c\u4e3a\u8fdd\u6cd5\uff0c\u4e0d\u64a4\u9500\u767b\u8bb0\u884c\u4e3a\u3002\u201d\u5728\u6574\u4e2a\u8f6c\u79fb\u767b\u8bb0\u8fc7\u7a0b\u4e2d\uff0c\u6a0a\u5148\u751f\u5e76\u4e0d\u77e5\u6653\u8be5\u623f\u4ea7\u5df2\u88ab\u67e5\u5c01\uff0c\u53ef\u89c6\u4e3a\u5584\u610f\u53d6\u5f97\u4e86\u6d89\u6848\u4e0d\u52a8\u4ea7\u7684\u4ea7\u6743\uff0c\u56e0\u6b64\uff0c\u5373\u4f7f\u662f\u5728\u6d89\u6848\u4e0d\u52a8\u4ea7\u767b\u8bb0\u884c\u4e3a\u8fdd\u6cd5\u7684\u60c5\u51b5\u4e0b\uff0c\u4e5f\u4e0d\u5e94\u64a4\u9500\u767b\u8bb0\u884c\u4e3a\u3002\n\u3000\u3000\u6240\u4ee5\uff0c\u987a\u5fb7\u4eba\u6c11\u6cd5\u9662\u5224\u51b3\uff1a\u64a4\u9500\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u4f5c\u51fa\u7684\u5357\u4e0d\u52a8\u4ea7\u30102016\u3011\u7b2c1\u53f7\u300a\u64a4\u9500\u4e0d\u52a8\u4ea7\u767b\u8bb0\u51b3\u5b9a\u4e66\u300b\uff1b\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u5728\u8be5\u5224\u51b3\u751f\u654815\u65e5\u5185\u5c06\u6d89\u6848\u623f\u4ea7\u7684\u4e0d\u52a8\u4ea7\u6743\u5c5e\u6027\u6062\u590d\u767b\u8bb0\u4e3a\u6a0a\u5148\u751f\u6240\u6709\u3002\u4e0a\u5468\u5357\u6d77\u56fd\u571f\u57ce\u5efa\u548c\u6c34\u52a1\u5c40\u53c8\u63d0\u8d77\u4e0a\u8bc9\uff0c\u6848\u4ef6\u8fd8\u5728\u8fdb\u4e00\u6b65\u5ba1\u7406\u4e2d\u3002"}, {"emotion": "neg", "id": "0189004da024c41073890b13f41cf0538d", "c_t": "\u770b\u4e86\u90a3\u4e48\u591a\u65f6\u9ae6\u5916\u5957 \u6211\u53ea\u60f3\u5148\u4e70\u5b83\u539f\u6807\u9898\uff1a\u770b\u4e86\u90a3\u4e48\u591a\u65f6\u9ae6\u5916\u5957\u6211\u53ea\u60f3\u5148\u4e70\u5b83\n{IMG:1}\n\u683c\u7eb9\u897f\u88c5\u662f\u897f\u88c5\u754c\u4e00\u4e2a\u7ecf\u5178\u7684\u5b58\u5728\uff0c\u82f1\u4f26\u683c\u5b50\u4e0e\u7387\u6027\u897f\u88c5\u7684\u7ed3\u5408\uff0c\u590d\u53e4\u800c\u6587\u827a\uff0c\u4eca\u5e74\u79cb\u51ac\u5c24\u5176\u62a2\u624b\uff0c\u5728\u8857\u62cd\u4e2d\u7ecf\u5e38\u80fd\u770b\u5230\u5b83\u7684\u8eab\u5f71\u3002\n{IMG:2}\n\u4f46\u683c\u7eb9\u897f\u88c5\u7684\u96f7\u533a\u5343\u4e07\u8981\u6ce8\u610f\uff0c\u590d\u53e4\u611f\u5341\u8db3\u7684\u5b83\u5bb9\u6613\u7a7f\u51fa\u8001\u65e7\u3001\u571f\u6c14\u7684\u611f\u89c9\uff0c\u9664\u975e\u4f60\u6709\u6a21\u7279\u7684\u7ea4\u7626\u8eab\u6750\u548c\u6c14\u573a\uff0c\u4e0d\u7136\u6700\u597d\u4e0d\u8981\u6210\u5957\u7a7f\u3002\n{IMG:3}\nCeline Dion\u7684\u683c\u7eb9\u897f\u88c5\u867d\u7136\u6709\u628a\u8170\u7ebf\u624e\u51fa\u6765\uff0c\u4f46\u683c\u7eb9+\u683c\u7eb9\u7684\u642d\u914d\u8ba9\u56fe\u6848\u8fc7\u4e8e\u7a81\u51fa\uff0c\u4e0d\u5e78\u6210\u4e3a\u4e00\u5927\u7247\u884c\u8d70\u7684\u9a6c\u8d5b\u514b\u3002\n{IMG:4}"}, {"emotion": "neg", "id": "01c3598a8fc217777cd91a55cc9be5082c", "c_t": "\u821f\u5c71\u5b9a\u6d77\uff1a\u91d1\u79cb\u65f6\u8282\u67da\u9999\u6d53\u3000\u3000\u539f\u6807\u9898\uff1a\u5b9a\u6d77\uff1a\u91d1\u79cb\u65f6\u8282\u67da\u9999\u6d53\n{IMG:1}\n11\u67086\u65e5\uff0c\u821f\u5c71\u5e02\u5b9a\u6d77\u533a\u767d\u6cc9\u9547\u768b\u6cc4\u6751\u6c11\u6b63\u5728\u91c7\u6458\u9999\u67da\u3002\n\u3000\u3000\u6d59\u6c5f\u5728\u7ebf11\u67087\u65e5\u8baf\uff08\u6d59\u6c5f\u5728\u7ebf\u62cd\u53cb \u80e1\u793e\u53cb \u6444\uff0911\u67086\u65e5\uff0c\u821f\u5c71\u5e02\u5b9a\u6d77\u533a\u767d\u6cc9\u9547\u768b\u6cc4\u6751\u6c11\u6b63\u5728\u91c7\u6458\u9999\u67da\u3002\u768b\u6cc4\u9999\u67da\u4e3a\u821f\u5c71\u5730\u65b9\u540d\u67da\uff0c\u4eca\u5e74\u5168\u9547\u79cd\u690d\u76843000\u4f59\u4ea9\u9999\u67da\uff0c\u91d1\u9ec4\u8272\u7684\u679c\u5b9e\u6302\u6ee1\u679d\u5934\uff0c\u5448\u73b0\u51fa\u4e00\u7247\u4e30\u6536\u7684\u559c\u60a6\u3002"}, {"emotion": "neg", "id": "01ef4cd8186e2cf12bb41dba2ad129d3e2", "c_t": "\u4e95\u76d6\u4e0a\u7684\u98ce\u666f \u65e5\u672c\u9c9c\u660e\u7684\u57ce\u5e02\u540d\u7247\uff08\u7ec4\u56fe\uff09\u3000\u3000\u539f\u6807\u9898\uff1a\u4e95\u76d6\u4e0a\u7684\u98ce\u666f \u65e5\u672c\u9c9c\u660e\u7684\u57ce\u5e02\u540d\u7247\n\u3000\u3000\u5728\u65e5\u672c\uff0c\u4e95\u76d6\u5df2\u7ecf\u6210\u4e3a\u9c9c\u660e\u7684\u57ce\u5e02\u540d\u7247\uff0c\u4eba\u4eec\u5c06\u5b83\u8bbe\u8ba1\u5f97\u4e94\u82b1\u516b\u95e8\uff0c\u6709\u7684\u91c7\u7528\u5e02\u82b1\u3001\u5e02\u6811\u4f5c\u4e3a\u56fe\u6848\uff0c\u6709\u7684\u7528\u5f53\u5730\u571f\u7279\u4ea7\u6216\u624b\u5de5\u827a\u54c1\uff0c\u6709\u7684\u8fd8\u4f1a\u628a\u540d\u80dc\u6216\u5386\u53f2\u6545\u4e8b\u753b\u4e0a\u53bb\u3002\u56fe\u7247\u4ece\u4e0a\u5230\u4e0b\u4f9d\u6b21\u4e3a\u9e1f\u53d6\u53bf\u67ef\u5357\u4e95\u76d6\u3001\u5188\u5c71\u5e02\u548c\u67f3\u4e95\u5e02\u7684\u6d88\u9632\u4e95\u76d6\u3001\u957f\u91ce\u53bf\u53e4\u98ce\u4e95\u76d6\u3001\u5948\u826f\u5c0f\u9e7f\u4e95\u76d6\u3002\n\u3000\u3000\u636e\u8bf4\u4e0a\u4e16\u7eaa80\u5e74\u4ee3\uff0c\u65e5\u672c\u5141\u8bb8\u5e02\u6c11\u8bbe\u8ba1\u4e95\u76d6\uff0c\u9f13\u52b1\u5404\u5730\u81ea\u884c\u5f00\u53d1\u5177\u6709\u672c\u5730\u7279\u8272\u7684\u4e95\u76d6\u8bbe\u8ba1\uff0c\u8ba9\u4e95\u76d6\u66f4\u52a0\u5438\u5f15\u773c\u7403\uff0c\u76ee\u7684\u662f\u4e3a\u4e86\u5f15\u8d77\u4eba\u4eec\u5bf9\u8017\u8d44\u5de8\u5927\u7684\u6392\u6c61\u7cfb\u7edf\u7684\u5173\u6ce8\u3002\u5c31\u8fd9\u6837\uff0c\u4e2a\u6027\u4e95\u76d6\u5728\u65e5\u672c\u6d41\u884c\u8d77\u6765\u3002\u5982\u4eca\uff0c\u8fd8\u51fa\u73b0\u4e86\u5f88\u591a\u201c\u4e95\u76d6\u7c89\u4e1d\u56e2\u201d\uff0c\u5230\u5404\u5730\u8ffd\u5bfb\u7279\u8272\u4e95\u76d6\u3002 (\u6797 \u5a1c)\n\u3000\u3000\u6765\u6e90\uff1a\u4eba\u6c11\u7f51-\u73af\u7403\u65f6\u62a5"}, {"emotion": "neg", "id": "01e048311e26c867be80e8c1b5c85d55d1", "c_t": "\u65e5\u672c\u65e9\u7a3b\u7530\u5927\u5b66\u56e0\u6536\u5230\u70b8\u5f39\u8b66\u544a\u505c\u8bfe \u8b66\u65b9\u5c01\u9501\u516c\u8def\u56fd\u9645\u5728\u7ebf\u4e13\u7a3f\uff1a\u636e\u65e5\u672c\u300a\u671d\u65e5\u65b0\u95fb\u300b11\u67087\u65e5\u62a5\u9053\uff0c\u65e5\u672c\u65e9\u7a3b\u7530\u5927\u5b66\u4e8e7\u65e5\u65e9\u6668\u53d1\u5e03\u4e00\u5219\u901a\u77e5\uff0c\u79f0\u7531\u4e8e\u6536\u5230\u70b8\u5f39\u8b66\u544a\uff0c\u7981\u6b62\u6240\u6709\u4eba\u57287\u65e5\u4e0a\u53489\u65f6\u5230\u4e0b\u534814\u65f6\u671f\u95f4\u8fdb\u5165\u6821\u56ed\uff0c\u5e76\u5ba3\u5e03\u4e0a\u5348\u505c\u8bfe\u3002\n{IMG:1}\n\u636e\u6089\uff