u7740\u7684\u6001\u5ea6\u704c\u6ce8\u4e8e\u4e3a\u5b9e\u73b0\u4e2d\u534e\u6c11\u65cf\u4f1f\u5927\u590d\u5174\u7684\u4e2d\u56fd\u68a6\u800c\u594b\u6597\u3002\n\u3000\u3000\u201c\u9752\u5e74\u5174\u5219\u56fd\u5bb6\u5174\uff0c\u9752\u5e74\u5f3a\u5219\u56fd\u5bb6\u5f3a\u3002\u9752\u5e74\u4e00\u4ee3\u6709\u7406\u60f3\u3001\u6709\u672c\u9886\u3001\u6709\u62c5\u5f53\uff0c\u56fd\u5bb6\u5c31\u6709\u524d\u9014\uff0c\u6c11\u65cf\u5c31\u6709\u5e0c\u671b\u3002\u4e2d\u56fd\u68a6\u662f\u5386\u53f2\u7684\u3001\u73b0\u5b9e\u7684\uff0c\u4e5f\u662f\u672a\u6765\u7684;\u662f\u6211\u4eec\u8fd9\u4e00\u4ee3\u7684\uff0c\u66f4\u662f\u9752\u5e74\u4e00\u4ee3\u7684\u3002\u201d\u5728\u5341\u4e5d\u5927\u62a5\u544a\u4e2d\uff0c\u4e60\u8fd1\u5e73\u603b\u4e66\u8bb0\u5bf9\u5e7f\u5927\u9752\u5e74\u5bc4\u8bed\u6bb7\u5207\u5e0c\u671b\uff0c\u9f13\u821e\u7740\u4e00\u4e2a\u53c8\u4e00\u4e2a\u5fc3\u6000\u7956\u56fd\u7684\u70ed\u8840\u9752\u5e74\u575a\u5b9a\u4eba\u751f\u7406\u60f3\uff0c\u4ee5\u9752\u6625\u4e3a\u7b14\u4e66\u5199\u7956\u56fd\u66f4\u52a0\u7e41\u8363\u5f3a\u5927\u7684\u65f6\u4ee3\u534e\u7ae0\u3002\n\u3000\u3000\u201c\u5f53\u9752\u6625\u9047\u4e0a\u65b0\u65f6\u4ee3\uff0c\u6211\u4eec\u662f\u5982\u6b64\u5e78\u8fd0\uff0c\u80fd\u591f\u7528\u81ea\u5df1\u7684\u594b\u6597\u89c1\u8bc1\u8fd9\u4e2a\u4f1f\u5927\u7684\u65f6\u4ee3\uff0c\u6211\u4eec\u7684\u6210\u5c31\u611f\u3001\u83b7\u5f97\u611f\u3001\u5b89\u5168\u611f\u5c06\u524d\u6240\u672a\u6709\u5730\u4e0e\u56fd\u5bb6\u7684\u7e41\u8363\u53d1\u5c55\u7d27\u5bc6\u76f8\u8fde\u3002\u201d\u5927\u4e09\u5b66\u751f\u5b63\u6000\u677e\u8ba4\u4e3a\uff0c\u4e60\u8fd1\u5e73\u603b\u4e66\u8bb0\u7684\u4e00\u756a\u8bb2\u8bdd\uff0c\u662f\u65b0\u65f6\u4ee3\u5bf9\u9752\u5e74\u7684\u53ec\u5524\uff0c\u4e3a\u5e7f\u5927\u9752\u5e74\u63d0\u4f9b\u4e86\u65e0\u6bd4\u5e7f\u9614\u7684\u821e\u53f0\uff0c\u4e2d\u534e\u6c11\u65cf\u4f1f\u5927\u590d\u5174\u7684\u4e2d\u56fd\u68a6\u7ec8\u5c06\u5728\u4e00\u4ee3\u4ee3\u9752\u5e74\u7684\u63a5\u529b\u594b\u6597\u4e2d\u53d8\u4e3a\u73b0\u5b9e\u3002\n\u3000\u3000\u524d\u51e0\u5929\uff0c\u80fd\u6e90\u4e0e\u52a8\u529b\u5de5\u7a0b\u4e13\u4e1a\u7684\u5927\u4e09\u5b66\u751f\u5218\u5b66\u6881\u53c2\u52a0\u4e86\u5144\u5f1f\u5b66\u6821\u8d35\u5dde\u7406\u5de5\u5927\u5b66\u5173\u4e8e\u5b66\u4e60\u8d2f\u5f7b\u515a\u7684\u5341\u4e5d\u5927\u7cbe\u795e\u62a5\u544a\u4f1a\uff0c\u611f\u89e6\u9887\u6df1\uff0c\u4ed6\u8bf4\uff1a\u201c\u5341\u4e5d\u5927\u62a5\u544a\u4e2d\uff0c\u63d0\u5230\u65b0\u65f6\u4ee3\u3001\u65b0\u601d\u60f3\u3001\u65b0\u5f81\u7a0b\u7b49\u7b49\u5f88\u591a\u4e2a\u2018\u65b0\u2019\uff0c\u6bcf\u4e00\u4e2a\u2018\u65b0\u2019\u90fd\u662f\u6211\u4eec\u4f5c\u4e3a\u5f53\u4ee3\u9752\u5e74\u7684\u8d23\u4efb\uff0c\u662f\u6211\u4eec\u524d\u8fdb\u548c\u594b\u6597\u7684\u76ee\u6807\u3002\u201d\u4f5c\u4e3a\u65b0\u65f6\u4ee3\u7684\u5927\u5b66\u751f\uff0c\u5218\u5b66\u6881\u8868\u793a\uff0c\u8981\u575a\u5b9a\u4e0d\u5fd8\u521d\u5fc3\u8ddf\u515a\u8d70\uff0c\u4e89\u5f53\u201c\u4e09\u6709\u201d\u9752\u5e74\uff0c\u4e3a\u65b0\u65f6\u4ee3\u8fc8\u4e0a\u65b0\u53f0\u9636\u8d21\u732e\u529b\u91cf\u3002\n\u3000\u3000\u201c\u6211\u4eec\u5e94\u8be5\u662f\u6709\u672c\u9886\u3001\u6709\u62c5\u5f53\u3001\u811a\u8e0f\u5b9e\u5730\u7684\u5b66\u751f\u5e72\u90e8\uff0c\u8981\u4e3a\u8001\u5e08\u6392\u5fe7\u89e3\u96be\uff0c\u4e3a\u540c\u5b66\u4eec\u505a\u597d\u670d\u52a1\u5de5\u4f5c\uff0c\u4e3a\u8d35\u5dde\u5927\u5b66\u7684\u5efa\u8bbe\u8d21\u732e\u81ea\u5df1\u7684\u529b\u91cf\u3002\u201d\u5bf9\u4e8e\u8d35\u5dde\u5927\u5b66\u6821\u5b66\u751f\u4f1a\u4e3b\u5e2d\u5f6d\u7f57\u9814\u6765\u8bf4\uff0c\u5e26\u9886\u597d\u56e2\u961f\u3001\u4ee5\u8eab\u4f5c\u5219\u505a\u597d\u699c\u6837\u3001\u66f4\u597d\u5730\u5f15\u5bfc\u548c\u5e2e\u52a9\u540c\u5b66\u5c31\u662f\u4ed6\u628a\u81ea\u5df1\u953b\u9020\u6210\u582a\u5f53\u6c11\u65cf\u590d\u5174\u5927\u4efb\u65b0\u9752\u5e74\u7684\u7b2c\u4e00\u6b65\u3002\n\u3000\u3000\u201c\u9752\u5e74\u515a\u5458\u5e94\u8be5\u4e0e\u5b66\u751f\u5e72\u90e8\u4e00\u8d77\u8d77\u5230\u793a\u8303\u4f5c\u7528\uff0c\u5173\u952e\u65f6\u523b\u9876\u5f97\u4e0a\u53bb\u3002\u201d\u5927\u56db\u5b66\u751f\u949f\u5927\u52c7\u8ba4\u4e3a\uff0c\u4f5c\u4e3a\u65b0\u65f6\u4ee3\u7684\u9752\u5e74\u5927\u5b66\u751f\uff0c\u5e94\u8be5\u73cd\u60dc\u5927\u597d\u9752\u6625\u65f6\u5149\uff0c\u4e0d\u4ec5\u8981\u505a\u515a\u7684\u7a81\u51fb\u961f\u3001\u6392\u5934\u5175\uff0c\u66f4\u8981\u505a\u5408\u683c\u7684\u5efa\u8bbe\u8005\u548c\u63a5\u73ed\u4eba\u3002\n\u3000\u3000\u793e\u4f1a\u5de5\u4f5c\u4e13\u4e1a\u7684\u5927\u4e09\u5b66\u751f\u9648\u4e3d\u82b3\u5219\u53e6\u8f9f\u8e4a\u5f84\uff0c\u4ece\u5982\u4f55\u628a\u201c\u4eba\u201d\u505a\u597d\u7684\u89d2\u5ea6\u9610\u8ff0\u4e86\u5f53\u4ee3\u5927\u5b66\u751f\u4f5c\u4e3a\u65b0\u65f6\u4ee3\u63a5\u73ed\u4eba\u5e94\u8be5\u6709\u7684\u62c5\u5f53\uff0c\u5979\u8bf4\uff1a\u201c\u8fd9\u4e2a\u62c5\u5f53\u5e76\u4e0d\u4e00\u5b9a\u662f\u8981\u4f60\u5f53\u5927\u82f1\u96c4\u53bb\u6551\u56fd\u6551\u6c11\uff0c\u800c\u5e94\u8be5\u662f\u575a\u5b88\u597d\u81ea\u5df1\u7684\u804c\u8d23\u3001\u4fdd\u6709\u5584\u826f\u7684\u672c\u771f\u3002\u201d\n\u3000\u3000\u5979\u4e3e\u4f8b\u8bf4\uff0c\u5982\u679c\u4f60\u4ee5\u540e\u6210\u4e3a\u4e00\u4e2a\u5546\u8d29\uff0c\u5c31\u4e0d\u8981\u7f3a\u65a4\u5c11\u4e24;\u5982\u679c\u4f60\u4ee5\u540e\u6210\u4e3a\u4e00\u4e2a\u5efa\u7b51\u5e08\uff0c\u5c31\u4e0d\u8981\u5077\u5de5\u51cf\u6599;\u5982\u679c\u4f60\u4ee5\u540e\u6210\u4e3a\u4e86\u4e00\u540d\u4eba\u6c11\u8001\u5e08\uff0c\u5c31\u4e0d\u8981\u8bef\u4eba\u5b50\u5f1f;\u5982\u679c\u4f60\u73b0\u5728\u662f\u4e00\u540d\u5b66\u751f\uff0c\u90a3\u5c31\u8be5\u597d\u597d\u5b66\u4e60\u2026\u2026\u201c\u65b0\u65f6\u4ee3\u65b0\u5f81\u7a0b\uff0c\u6211\u4eec\u8981\u6210\u4e3a\u90a3\u4e2a\u65e0\u6127\u4e8e\u81ea\u5df1\uff0c\u65e0\u6127\u4e8e\u56fd\u5bb6\u3001\u6709\u76ca\u4e8e\u4eba\u6c11\u7684\u2018\u4eba\u2019\u3002\u201d\n\u3000\u3000\u201c\u65b0\u65f6\u4ee3\u662f\u9752\u5e74\u65bd\u5c55\u624d\u534e\u7684\u5e7f\u9614\u821e\u53f0\uff0c\u6211\u4eec\u5e94\u5728\u8fd9\u4e2a\u821e\u53f0\u4e0a\u6325\u6d12\u6211\u4eec\u7684\u9752\u6625\u624d\u667a\uff0c\u4e3a\u4e2d\u56fd\u68a6\u7684\u5b9e\u73b0\u589e\u6dfb\u4e00\u7816\u4e00\u74e6\u3002\u201d\u5927\u4e09\u5b66\u751f\u5353\u739b\u8ba4\u4e3a\uff0c\u5341\u4e5d\u5927\u4e3a\u5e7f\u5927\u9752\u5e74\u6307\u660e\u4e86\u524d\u8fdb\u65b9\u5411\uff0c\u6ce8\u5165\u4e86\u5f3a\u5927\u52a8\u529b\uff0c\u201c\u65e0\u8bba\u6211\u4eec\u5c06\u6765\u8d70\u4e0a\u600e\u6837\u7684\u5c97\u4f4d\uff0c\u90fd\u8981\u7825\u783a\u524d\u8fdb\uff0c\u4e0d\u5fd8\u521d\u5fc3\uff0c\u7262\u8bb0\u4f7f\u547d\uff0c\u4e0e\u65b0\u65f6\u4ee3\u5171\u524d\u8fdb!\u201d\u7f16\u8f91\uff1a\u5f20\u7490\n{IMG:2}\n{IMG:3}"}, {"emotion": "neg", "id": "01042c6d7c0939bb8b0ec8f4518db782ae", "c_t": "\u529e\u597d\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u662f\u7f8e\u597d\u6559\u80b2\u5e94\u6709\u4e4b\u4e49\u3000\u3000\u4f5c\u8005\uff1a\u5218\u4e3d\u7fa4\n\u3000\u3000\u5f53\u524d\u6211\u56fd\u793e\u4f1a\u7684\u4e3b\u8981\u77db\u76fe\u5df2\u7ecf\u8f6c\u5316\u4e3a\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u548c\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u53d1\u5c55\u4e4b\u95f4\u7684\u77db\u76fe\u3002\u5728\u7b14\u8005\u770b\u6765\uff0c\u5c31\u6559\u80b2\u800c\u8a00\uff0c\u5176\u4e3b\u8981\u77db\u76fe\u4f53\u73b0\u4e3a\u4eba\u4eec\u5bf9\u7f8e\u597d\u6559\u80b2\u7684\u9700\u6c42\u4e0e\u6559\u80b2\u53d1\u5c55\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u4e4b\u95f4\u7684\u77db\u76fe\uff0c\u8fd9\u79cd\u53d1\u5c55\u7684\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u4e5f\u8868\u73b0\u4e3a\u57ce\u4e61\u6559\u80b2\u4e4b\u95f4\u7684\u4e0d\u5e73\u8861\u4e0e\u4e61\u6751\u6559\u80b2\u7684\u4e0d\u5145\u5206\u3002\n\u3000\u3000\u968f\u7740\u57ce\u9547\u5316\u8fdb\u7a0b\u7684\u52a0\u5feb\u548c\u4eba\u4eec\u5bf9\u7f8e\u597d\u6559\u80b2\u7684\u9700\u6c42\u9a71\u52a8\uff0c\u4e61\u6751\u6559\u80b2\u540c\u65f6\u5448\u73b0\u51fa\u4e24\u79cd\u5e76\u884c\u8d8b\u52bf\uff1a\u5373\u4e61\u9547\u5927\u89c4\u6a21\u5b66\u6821\u7684\u6d8c\u73b0\u4e0e\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u5c0f\u73ed\u5316\u7684\u81ea\u7136\u5f62\u6210\u3002\u6211\u4eec\u8c03\u7814\u53d1\u73b0\uff0c\u519c\u6751\u4eba\u53e3\u5411\u57ce\u9547\u7684\u5927\u8fc1\u5f99\u76f4\u63a5\u5bfc\u81f4\u57ce\u9547\u5927\u89c4\u6a21\u5b66\u6821\u7684\u51fa\u73b0\uff0c\u5728\u4e00\u4e9b\u4e61\u9547\u4e2d\u5fc3\u5c0f\u5b66\uff0c\u5927\u73ed\u989d\u4e0d\u5728\u5c11\u6570\u3002\u4ece\u7a7a\u95f4\u8303\u56f4\u6765\u770b\uff0c\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u5728\u6211\u56fd\u5e7f\u4e3a\u5b58\u5728\u3002\u6709\u7edf\u8ba1\u6570\u636e\u663e\u793a\uff0c\u6211\u56fd\u4ecd\u670910\u4e07\u4f59\u6240\u5b66\u751f\u4e0d\u8db3100\u4eba\u7684\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\uff0c\u5360\u4e61\u6751\u5b66\u6821\u603b\u6570\u7684\u4e00\u534a\u4ee5\u4e0a\u3002\u800c\u4e14\u53ef\u4ee5\u9884\u8ba1\uff0c\u8fdb\u5165\u540e\u64a4\u70b9\u5e76\u6821\u65f6\u4ee3\uff0c\u52a0\u4e0a\u57ce\u9547\u5316\u8fdb\u7a0b\u7684\u52a0\u901f\uff0c\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u6570\u91cf\u6216\u5c06\u7ee7\u7eed\u589e\u52a0\u3002\u4ece\u65f6\u95f4\u7ef4\u5ea6\u6765\u770b\uff0c\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u5e76\u4e0d\u5b8c\u5168\u662f\u6211\u56fd\u57ce\u9547\u5316\u8fdb\u7a0b\u4e2d\u7684\u4e00\u79cd\u77ed\u671f\u6027\u3001\u8fc7\u6e21\u6027\u7684\u5b66\u6821\u5f62\u6001\uff0c\u5b83\u5c06\u662f\u6211\u56fd\u4e61\u6751\u6559\u80b2\u7684\u5e38\u6001\u6837\u5f0f\u800c\u5728\u8f83\u957f\u65f6\u95f4\u5185\u5b58\u5728\u3002\n\u3000\u3000\u5341\u4e5d\u5927\u62a5\u544a\u6307\u51fa\u8981\u201c\u9ad8\u5ea6\u91cd\u89c6\u519c\u6751\u4e49\u52a1\u6559\u80b2\u201d\uff0c\u8981\u201c\u52aa\u529b\u8ba9\u6bcf\u4e2a\u5b69\u5b50\u90fd\u80fd\u4eab\u6709\u516c\u5e73\u800c\u6709\u8d28\u91cf\u7684\u6559\u80b2\u201d\u3002\u5c0f\u89c4\u6a21\u5b66\u6821\u4f5c\u4e3a\u519c\u6751\u4e49\u52a1\u6559\u80b2\u7684\u91cd\u8981\u7ec4\u6210\u548c\u8584\u5f31\u73af\u8282\uff0c\u5c24\u5176\u8981\u9ad8\u5ea6\u91cd\u89c6\u3001\u91cd\u70b9\u5173\u6ce8\u3002\u56e0\u4e3a\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u6ee1\u8db3\u4e86\u519c\u6751\u540e20%\u5bb6\u5ead\u7684\u6559\u80b2\u9700\u6c42\uff0c\u529e\u597d\u8fd9\u4e9b\u5c0f\u89c4\u6a21\u5b66\u6821\uff0c\u662f\u4fc3\u8fdb\u6559\u80b2\u5747\u8861\u53d1\u5c55\u3001\u63d0\u5347\u6559\u80b2\u8d28\u91cf\u7684\u6258\u5e95\u5de5\u7a0b\uff0c\u4e8b\u5173\u56fd\u8ba1\uff0c\u66f4\u6d89\u6c11\u751f\u3002\n\u3000\u3000\u7b14\u8005\u5efa\u8bae\uff0c\u6709\u5173\u90e8\u95e8\u5e94\u8be5\u51fa\u53f0\u4e13\u95e8\u6027\u653f\u7b56\u4ee5\u9488\u5bf9\u6027\u89e3\u51b3\uff1a\u5305\u62ec\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u7684\u6559\u5e08\u914d\u7f6e\u5982\u4f55\u7ed9\u4e88\u7279\u6b8a\u8003\u8651\uff1b\u5e08\u8303\u9662\u6821\u5982\u4f55\u4e3a\u5c0f\u89c4\u6a21\u5b66\u6821\u5168\u79d1\u578b\u5b9a\u5411\u57f9\u517b\u672c\u571f\u4f18\u79c0\u5e08\u8303\u751f\uff1b\u5982\u4f55\u9488\u5bf9\u5c0f\u89c4\u6a21\u5b66\u6821\u590d\u8bd5\u6559\u5b66\u3001\u5305\u73ed\u6559\u5b66\u7b49\u7279\u70b9\u5f00\u5c55\u9488\u5bf9\u6027\u7684\u6559\u5e08\u57f9\u8bad\uff1b\u5982\u4f55\u5229\u7528\u73b0\u4ee3\u4fe1\u606f\u6280\u672f\u5b9e\u73b0\u4f18\u8d28\u6559\u80b2\u8d44\u6e90\u7684\u5145\u5206\u5171\u4eab\u7b49\u3002\u53ea\u6709\u4e61\u6751\u5c0f\u89c4\u6a21\u5b66\u6821\u4ece\u201c\u5c0f\u800c\u5f31\u201d\u201c\u5c0f\u800c\u5dee\u201d\u8d70\u5411\u201c\u5c0f\u800c\u7f8e\u201d\u201c\u5c0f\u800c\u4f18\u201d\u201c\u5c0f\u800c\u7279\u201d\uff0c\u6211\u4eec\u7684\u7f8e\u597d\u6559\u80b2\u624d\u6709\u53ef\u80fd\uff0c\u6211\u4eec\u7684\u7f8e\u597d\u751f\u6d3b\u624d\u5f97\u4ee5\u7acb\u57fa\u3002\uff08\u5218\u4e3d\u7fa4\uff09\n{IMG:1}\n[\u8d23\u4efb\u7f16\u8f91:\u5218\u671d]"}, {"emotion": "neg", "id": "01fe71007d615e913e0b1a407294cf0133", "c_t": "\u3010\u5b66\u601d\u8df5\u609f\u00b7\u5341\u4e5d\u5927\u3011\u5728\u4e16\u754c\u4e0a\u9ad8\u9ad8\u4e3e\u8d77\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u4f1f\u5927\u65d7\u5e1c\u3000\u3000\u6c6a\u4ead\u53cb \u4e2d\u56fd\u4eba\u6c11\u5927\u5b66\u9a6c\u514b\u601d\u4e3b\u4e49\u5b66\u9662\u6559\u6388\u3001\u535a\u58eb\u751f\u5bfc\u5e08\n\u3000\u3000\u515a\u7684\u5341\u4e5d\u5927\u521b\u7acb\u4e86\u4e60\u8fd1\u5e73\u65b0\u65f6\u4ee3\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u601d\u60f3,\u5ba3\u5e03\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u8fdb\u5165\u65b0\u65f6\u4ee3\u3002\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u4e0d\u65ad\u53d6\u5f97\u91cd\u5927\u6210\u5c31\u5e76\u80dc\u5229\u8fc8\u8fdb\u65b0\u65f6\u4ee3,\u5728\u5168\u4e16\u754c\u6811\u8d77\u4e00\u9762\u4ee4\u4eba\u5411\u5f80\u7684\u65d7\u5e1c\u3002\u5b83\u5bf9\u4e16\u754c\u793e\u4f1a\u4e3b\u4e49\u7684\u9996\u8981\u610f\u4e49,\u662f\u8bc1\u660e\u4e86\u9a6c\u514b\u601d\u4e3b\u4e49\u662f\u79d1\u5b66\u771f\u7406,\u4ee5\u9c9c\u6d3b\u7684\u5b9e\u8df5\u548c\u521b\u65b0\u7684\u7406\u8bba,\u4e0d\u65ad\u5f00\u8f9f\u9a6c\u514b\u601d\u4e3b\u4e49\u53d1\u5c55\u65b0\u5883\u754c,\u8ba9\u79d1\u5b66\u793e\u4f1a\u4e3b\u4e49\u572821\u4e16\u7eaa\u7115\u53d1\u51fa\u65b0\u7684\u84ec\u52c3\u751f\u673a\u3002\n\u3000\u3000\u9a6c\u514b\u601d\u4e3b\u4e49\u63ed\u793a\u4e86\u81ea\u7136\u754c\u3001\u4eba\u7c7b\u793e\u4f1a\u548c\u601d\u7ef4\u53d1\u5c55\u666e\u904d\u89c4\u5f8b,\u5728\u4eba\u7c7b\u601d\u60f3\u53f2\u4e0a\u6ca1\u6709\u4e00\u79cd\u601d\u60f3\u7406\u8bba\u80fd\u8fbe\u5230\u9a6c\u514b\u601d\u4e3b\u4e49\u7684\u9ad8\u5ea6,\u4e5f\u6ca1\u6709\u4e00\u79cd\u5b66\u8bf4\u80fd\u50cf\u9a6c\u514b\u601d\u4e3b\u4e49\u90a3\u6837\u5bf9\u4e16\u754c\u4ea7\u751f\u4e86\u5982\u6b64\u5de8\u5927\u7684\u5f71\u54cd,\u4efb\u4f55\u65f6\u5019\u4efb\u4f55\u60c5\u51b5\u4e0b\u90fd\u4e0d\u80fd\u52a8\u6447\u9a6c\u514b\u601d\u4e3b\u4e49\u7684\u6307\u5bfc\u5730\u4f4d\u3002\u540c\u65f6,\u9a6c\u514b\u601d\u4e3b\u4e49\u5e76\u6ca1\u6709\u7ed3\u675f\u771f\u7406,\u800c\u662f\u5f00\u8f9f\u4e86\u901a\u5411\u771f\u7406\u7684\u9053\u8def,\u5fc5\u5b9a\u968f\u7740\u65f6\u4ee3\u3001\u5b9e\u8df5\u548c\u79d1\u5b66\u7684\u53d1\u5c55\u800c\u4e0d\u65ad\u53d1\u5c55,\u4e0d\u53ef\u80fd\u4e00\u6210\u4e0d\u53d8\u3002\u5982\u4f55\u5728\u5b9e\u8df5\u4e2d\u575a\u6301\u548c\u53d1\u5c55\u9a6c\u514b\u601d\u4e3b\u4e49,\u628a\u672c\u56fd\u7684\u793e\u4f1a\u4e3b\u4e49\u4e8b\u4e1a\u4e0d\u65ad\u63a8\u5411\u524d\u8fdb,\u662f\u5404\u56fd\u9a6c\u514b\u601d\u4e3b\u4e49\u8005\u548c\u5171\u4ea7\u515a\u4eba\u80a9\u8d1f\u7684\u4f7f\u547d\u548c\u4efb\u52a1\u3002\n\u3000\u3000\u4e2d\u56fd\u5171\u4ea7\u515a\u521b\u7acb\u4e4b\u521d\u5c31\u628a\u9a6c\u514b\u601d\u5217\u5b81\u4e3b\u4e49\u5199\u5728\u65d7\u5e1c\u4e0a,\u575a\u6301\u5e76\u8fd0\u7528\u9a6c\u514b\u601d\u5217\u5b81\u4e3b\u4e49\u57fa\u672c\u539f\u7406\u63a2\u7d22\u4e2d\u56fd\u9769\u547d\u7684\u9053\u8def\u5e76\u53d6\u5f97\u80dc\u5229,\u5f62\u6210\u4e86\u6bdb\u6cfd\u4e1c\u601d\u60f3\u8fd9\u4e00\u9a6c\u514b\u601d\u4e3b\u4e49\u4e2d\u56fd\u5316\u7684\u7b2c\u4e00\u4e2a\u4f1f\u5927\u6210\u679c\u3002\u65b0\u4e2d\u56fd\u6210\u7acb\u540e,\u6211\u4eec\u515a\u7ee7\u7eed\u63a8\u8fdb\u793e\u4f1a\u4e3b\u4e49\u9769\u547d\u548c\u793e\u4f1a\u4e3b\u4e49\u5efa\u8bbe\u4e8b\u4e1a,\u53d6\u5f97\u4e86\u4e3e\u4e16\u77a9\u76ee\u7684\u91cd\u5927\u6210\u5c31,\u4e3a\u6539\u9769\u5f00\u653e\u5960\u5b9a\u4e86\u6839\u672c\u7684\u653f\u6cbb\u524d\u63d0\u548c\u5236\u5ea6\u57fa\u7840,\u4e3a\u4e2d\u56fd\u53d1\u5c55\u5bcc\u5f3a\u3001\u4e2d\u56fd\u4eba\u6c11\u751f\u6d3b\u5bcc\u88d5\u5960\u5b9a\u4e86\u575a\u5b9e\u57fa\u7840\u3002\n\u3000\u3000\u6539\u9769\u5f00\u653e\u4ee5\u6765,\u6211\u4eec\u515a\u7ee7\u7eed\u575a\u6301\u628a\u9a6c\u514b\u601d\u5217\u5b81\u4e3b\u4e49\u57fa\u672c\u539f\u7406\u540c\u4e2d\u56fd\u5177\u4f53\u5b9e\u9645\u548c\u65f6\u4ee3\u7279\u5f81\u7ed3\u5408\u8d77\u6765,\u89e3\u653e\u601d\u60f3\u3001\u5b9e\u4e8b\u6c42\u662f\u3001\u4e0e\u65f6\u4ff1\u8fdb\u3001\u6c42\u771f\u52a1\u5b9e,\u5728\u5b9e\u8df5\u4e2d\u8ba4\u8bc6\u771f\u7406\u3001\u628a\u63e1\u89c4\u5f8b,\u7528\u53d1\u5c55\u7740\u7684\u9a6c\u514b\u601d\u4e3b\u4e49\u6307\u5bfc\u65b0\u7684\u5b9e\u8df5,\u7528\u65b0\u7684\u5b9e\u8df5\u4e30\u5bcc\u548c\u53d1\u5c55\u9a6c\u514b\u601d\u4e3b\u4e49,\u4e0d\u65ad\u63a8\u8fdb\u65b0\u7684\u5b9e\u8df5\u57fa\u7840\u4e0a\u7684\u7406\u8bba\u521b\u65b0,\u5b9e\u73b0\u7406\u8bba\u521b\u65b0\u548c\u5b9e\u8df5\u521b\u65b0\u826f\u6027\u4e92\u52a8,\u5728\u6b64\u8fc7\u7a0b\u4e2d\u5f62\u6210\u4e86\u9093\u5c0f\u5e73\u7406\u8bba\u3001\u201c\u4e09\u4e2a\u4ee3\u8868\u201d\u91cd\u8981\u601d\u60f3\u548c\u79d1\u5b66\u53d1\u5c55\u89c2,\u8fd9\u662f\u9a6c\u514b\u601d\u4e3b\u4e49\u4e2d\u56fd\u5316\u7684\u53c8\u4e00\u4f1f\u5927\u6210\u679c\u3002\n\u3000\u3000\u515a\u7684\u5341\u516b\u5927\u4ee5\u6765,\u5728\u4ee5\u4e60\u8fd1\u5e73\u540c\u5fd7\u4e3a\u6838\u5fc3\u7684\u515a\u4e2d\u592e\u575a\u5f3a\u9886\u5bfc\u4e0b,\u515a\u548c\u56fd\u5bb6\u5404\u9879\u4e8b\u4e1a\u53d6\u5f97\u5386\u53f2\u6027\u6210\u5c31,\u53d1\u751f\u5386\u53f2\u6027\u53d8\u9769\u3002\u515a\u7684\u5341\u4e5d\u5927\u7ad9\u5728\u65b0\u7684\u65f6\u4ee3\u9ad8\u5ea6,\u9762\u5411\u51b3\u80dc\u5168\u9762\u5efa\u6210\u5c0f\u5eb7\u793e\u4f1a\u3001\u593a\u53d6\u65b0\u65f6\u4ee3\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u4f1f\u5927\u80dc\u5229\u7684\u65b0\u5f81\u7a0b,\u4ee5\u975e\u51e1\u7684\u7406\u8bba\u52c7\u6c14\u3001\u5353\u8d8a\u7684\u653f\u6cbb\u667a\u6167\u3001\u5f3a\u70c8\u7684\u4f7f\u547d\u62c5\u5f53,\u9996\u6b21\u63d0\u51fa\u548c\u7cfb\u7edf\u9610\u8ff0\u4e86\u4e60\u8fd1\u5e73\u65b0\u65f6\u4ee3\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u601d\u60f3\u3002\n\u3000\u3000\u4e60\u8fd1\u5e73\u65b0\u65f6\u4ee3\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u601d\u60f3,\u4ece\u7406\u8bba\u548c\u5b9e\u8df5\u7ed3\u5408\u4e0a\u7cfb\u7edf\u56de\u7b54\u65b0\u65f6\u4ee3\u575a\u6301\u548c\u53d1\u5c55\u4ec0\u4e48\u6837\u7684\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u3001\u600e\u6837\u575a\u6301\u548c\u53d1\u5c55\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u8fd9\u4e2a\u5927\u4e3b\u9898,\u5b83\u662f\u5bf9\u9a6c\u514b\u601d\u5217\u5b81\u4e3b\u4e49\u3001\u6bdb\u6cfd\u4e1c\u601d\u60f3\u3001\u9093\u5c0f\u5e73\u7406\u8bba\u3001\u201c\u4e09\u4e2a\u4ee3\u8868\u201d\u91cd\u8981\u601d\u60f3\u3001\u79d1\u5b66\u53d1\u5c55\u89c2\u7684\u7ee7\u627f\u548c\u53d1\u5c55,\u662f\u9a6c\u514b\u601d\u4e3b\u4e49\u4e2d\u56fd\u5316\u6700\u65b0\u6210\u679c,\u662f\u515a\u548c\u4eba\u6c11\u5b9e\u8df5\u7ecf\u9a8c\u548c\u96c6\u4f53\u667a\u6167\u7684\u7ed3\u6676,\u662f\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u7406\u8bba\u4f53\u7cfb\u7684\u91cd\u8981\u7ec4\u6210\u90e8\u5206,\u662f\u5168\u515a\u5168\u56fd\u4eba\u6c11\u4e3a\u5b9e\u73b0\u4e2d\u534e\u6c11\u65cf\u4f1f\u5927\u590d\u5174\u800c\u594b\u6597\u7684\u884c\u52a8\u6307\u5357,\u5168\u515a\u5fc5\u987b\u957f\u671f\u575a\u6301\u5e76\u4e0d\u65ad\u53d1\u5c55\u3002\n\u3000\u3000\u8fd9\u4e00\u601d\u60f3\u7684\u8bde\u751f,\u4e0d\u4ec5\u662f\u5bf9\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u7406\u8bba\u7684\u5386\u53f2\u6027\u8d21\u732e,\u662f\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u7406\u8bba\u7684\u4e00\u6b21\u4f1f\u5927\u98de\u8dc3,\u800c\u4e14\u4e5f\u662f\u9a6c\u514b\u601d\u4e3b\u4e49\u57fa\u672c\u539f\u7406\u540c\u5f53\u4ee3\u4e2d\u56fd\u5b9e\u8df5\u53d1\u5c55\u4e0e\u65f6\u4ee3\u8981\u6c42\u76f8\u7ed3\u5408\u7684\u4e00\u6b21\u4f1f\u5927\u98de\u8dc3,\u5bf9\u4e8e\u63a2\u7d22\u5171\u4ea7\u515a\u6267\u653f\u89c4\u5f8b\u3001\u793e\u4f1a\u4e3b\u4e49\u5efa\u8bbe\u89c4\u5f8b\u3001\u4eba\u7c7b\u793e\u4f1a\u53d1\u5c55\u89c4\u5f8b\u4e5f\u5177\u6709\u5386\u53f2\u6027\u7684\u8d21\u732e\u3002\n\u3000\u3000\u4e3a\u65e0\u4ea7\u9636\u7ea7\u6c42\u89e3\u653e,\u4e3a\u4eba\u6c11\u8c0b\u5229\u76ca,\u662f\u9a6c\u514b\u601d\u4e3b\u4e49\u653f\u515a\u7684\u6839\u672c\u5b97\u65e8,\u4e5f\u662f\u533a\u522b\u4e8e\u5176\u4ed6\u4e00\u5207\u653f\u515a\u7684\u6839\u672c\u6807\u5fd7\u3002\u4ece\u56fd\u9645\u5171\u4ea7\u4e3b\u4e49\u8fd0\u52a8\u548c\u4e16\u754c\u793e\u4f1a\u4e3b\u4e49\u53d1\u5c55\u7684\u5386\u53f2\u770b,\u90a3\u4e9b\u7ecf\u5386\u4e86\u632b\u6298\u5931\u8d25\u7684\u56fd\u5bb6,\u65e0\u4e0d\u7f3a\u4e4f\u4e00\u4e2a\u575a\u5f3a\u7684\u9a6c\u514b\u601d\u4e3b\u4e49\u653f\u515a\u7684\u6b63\u786e\u9886\u5bfc,\u5e76\u56e0\u6b64\u5931\u53bb\u4e86\u4eba\u6c11\u7684\u652f\u6301,\u5bfc\u81f4\u9769\u547d\u5931\u8d25\u751a\u81f3\u53d1\u751f\u4ea1\u515a\u4ea1\u56fd\u7684\u60b2\u5267\u3002\u4e2d\u56fd\u5171\u4ea7\u515a\u4ece\u6210\u7acb\u4ee5\u6765,\u65e0\u8bba\u9769\u547d\u3001\u5efa\u8bbe\u8fd8\u662f\u6539\u9769,\u90fd\u59cb\u7ec8\u91cd\u89c6\u515a\u7684\u5efa\u8bbe,\u59cb\u7ec8\u575a\u6301\u4eba\u6c11\u81f3\u4e0a\u7684\u7406\u5ff5,\u575a\u6301\u4e00\u5207\u4e3a\u4e86\u4eba\u6c11,\u4e00\u5207\u4f9d\u9760\u4eba\u6c11,\u575a\u4fe1\u515a\u7684\u6839\u57fa\u5728\u4eba\u6c11\u3001\u515a\u7684\u529b\u91cf\u5728\u4eba\u6c11,\u5145\u5206\u53d1\u6325\u5e7f\u5927\u4eba\u6c11\u7fa4\u4f17\u79ef\u6781\u6027\u3001\u4e3b\u52a8\u6027\u3001\u521b\u9020\u6027,\u4ece\u800c\u5e26\u9886\u4eba\u6c11\u8de8\u8fc7\u4e00\u9053\u53c8\u4e00\u9053\u6c9f\u574e,\u53d6\u5f97\u4e00\u4e2a\u53c8\u4e00\u4e2a\u80dc\u5229,\u4e3a\u4e2d\u534e\u6c11\u65cf\u4f5c\u51fa\u4e86\u4f1f\u5927\u5386\u53f2\u8d21\u732e\u3002\u4e2d\u56fd\u5171\u4ea7\u515a\u52a0\u5f3a\u81ea\u8eab\u5efa\u8bbe\u548c\u53d1\u5c55\u53d6\u5f97\u7684\u5b9d\u8d35\u7ecf\u9a8c,\u5bf9\u4e8e\u4e30\u5bcc\u548c\u53d1\u5c55\u9a6c\u514b\u601d\u4e3b\u4e49\u5efa\u515a\u5b66\u8bf4,\u63a2\u7d22\u65b0\u7684\u5386\u53f2\u6761\u4ef6\u4e0b\u5171\u4ea7\u515a\u6267\u653f\u7684\u89c4\u5f8b,\u65e0\u7591\u5177\u6709\u91cd\u8981\u7684\u610f\u4e49\u3002\n\u3000\u3000\u9a6c\u514b\u601d\u4e3b\u4e49\u63ed\u793a\u4e86\u793e\u4f1a\u4e3b\u4e49\u53d6\u4ee3\u8d44\u672c\u4e3b\u4e49\u7684\u5386\u53f2\u5fc5\u7136\u6027,\u81f3\u4e8e\u5404\u56fd\u5171\u4ea7\u515a\u4eba\u5982\u4f55\u63a2\u7d22\u672c\u56fd\u9769\u547d\u9053\u8def\u3001\u7279\u522b\u662f\u9769\u547d\u6210\u529f\u540e\u5982\u4f55\u5efa\u8bbe\u793e\u4f1a\u4e3b\u4e49,\u7ecf\u5178\u4f5c\u5bb6\u6ca1\u6709\u7ed9\u51fa\u8be6\u7ec6\u7684\u65b9\u6848,\u4e5f\u4e0d\u53ef\u80fd\u7ed9\u51fa\u3002\u4e2d\u56fd\u5171\u4ea7\u515a\u5e26\u9886\u4e2d\u56fd\u4eba\u6c11\u4e0d\u4ec5\u8d70\u51fa\u4e86\u4e00\u6761\u7b26\u5408\u4e2d\u56fd\u56fd\u60c5\u7684\u9769\u547d\u9053\u8def,\u800c\u4e14\u8d70\u51fa\u4e86\u4e00\u6761\u7b26\u5408\u4e2d\u56fd\u5b9e\u9645\u7684\u793e\u4f1a\u4e3b\u4e49\u53d1\u5c55\u9053\u8def\u3002\u4e2d\u56fd\u6539\u9769\u5f00\u653e\u4e8b\u4e1a\u7684\u6210\u529f\u7ecf\u9a8c,\u79d1\u5b66\u56de\u7b54\u4e86\u843d\u540e\u56fd\u5bb6\u5982\u4f55\u901a\u8fc7\u6539\u9769\u5b9e\u73b0\u53d1\u5c55\u3001\u8d70\u5411\u5bcc\u88d5\u5f3a\u76db\u7684\u7406\u5ff5\u548c\u9014\u5f84,\u6781\u5927\u5730\u4e30\u5bcc\u4e86\u9a6c\u514b\u601d\u4e3b\u4e49\u548c\u79d1\u5b66\u793e\u4f1a\u4e3b\u4e49\u7406\u8bba,\u62d3\u5c55\u5e76\u6df1\u5316\u4e86\u5bf9\u793e\u4f1a\u4e3b\u4e49\u5efa\u8bbe\u89c4\u5f8b\u7684\u8ba4\u8bc6\u3002\n\u3000\u3000\u552f\u7269\u53f2\u89c2\u6307\u660e\u4e86\u793e\u4f1a\u4e3b\u4e49\u53d6\u4ee3\u8d44\u672c\u4e3b\u4e49\u3001\u4eba\u7c7b\u6700\u7ec8\u8d70\u5411\u5171\u4ea7\u4e3b\u4e49\u7684\u5149\u660e\u524d\u666f\u3002\u4e2d\u56fd\u5171\u4ea7\u515a\u4ece\u6210\u7acb\u4e4b\u65e5\u8d77,\u5c31\u628a\u6700\u7ec8\u5b9e\u73b0\u5171\u4ea7\u4e3b\u4e49\u5199\u5165\u515a\u7ae0\u3002\u4ece\u90a3\u4e4b\u540e,\u4e2d\u56fd\u5171\u4ea7\u515a\u5e26\u9886\u4eba\u6c11\u4e00\u6b65\u4e00\u6b65\u5730\u671d\u8fd9\u4e2a\u76ee\u6807\u594b\u8fdb,\u5e76\u628a\u6700\u9ad8\u7eb2\u9886\u540c\u6700\u4f4e\u7eb2\u9886\u6709\u673a\u5730\u7edf\u4e00\u8d77\u6765\u3002\u6bcf\u4e00\u4e2a\u6700\u4f4e\u7eb2\u9886\u7684\u5b9e\u73b0,\u5c31\u610f\u5473\u7740\u5411\u6700\u9ad8\u7eb2\u9886\u524d\u8fdb\u4e86\u4e00\u6b65\u3002\u65b0\u6c11\u4e3b\u4e3b\u4e49\u9769\u547d\u65f6\u671f,\u660e\u786e\u4e86\u9769\u547d\u6210\u529f\u540e\u7684\u76ee\u6807\u662f\u5728\u4e2d\u56fd\u641e\u793e\u4f1a\u4e3b\u4e49\u3002\u968f\u7740\u65b0\u4e2d\u56fd\u7684\u6210\u7acb\u548c\u793e\u4f1a\u4e3b\u4e49\u9769\u547d\u7684\u5b8c\u6210,\u6211\u56fd\u8fdb\u5165\u793e\u4f1a\u4e3b\u4e49\u9636\u6bb5\u3002\u6539\u9769\u5f00\u653e\u540e,\u6211\u4eec\u515a\u53c8\u660e\u786e\u628a\u4ece1956\u5e74\u793e\u4f1a\u4e3b\u4e49\u6539\u9020\u57fa\u672c\u5b8c\u6210\u523021\u4e16\u7eaa\u4e2d\u53f6\u793e\u4f1a\u4e3b\u4e49\u73b0\u4ee3\u5316\u7684\u57fa\u672c\u5b9e\u73b0\u81f3\u5c11100\u5e74\u7684\u65f6\u95f4\u5217\u4e3a\u793e\u4f1a\u4e3b\u4e49\u521d\u7ea7\u9636\u6bb5\u3002\u515a\u7684\u5341\u4e5d\u5927\u7ee7\u7eed\u660e\u786e\u63d0\u51fa\u5206\u4e24\u6b65\u8d70\u628a\u6211\u56fd\u5efa\u6210\u793e\u4f1a\u4e3b\u4e49\u73b0\u4ee3\u5316\u5f3a\u56fd\u7684\u594b\u6597\u76ee\u6807:\u7b2c\u4e00\u6b65\u662f\u5728\u5168\u9762\u5efa\u6210\u5c0f\u5eb7\u793e\u4f1a\u7684\u57fa\u7840\u4e0a,\u63d0\u524d15\u5e74,\u5373\u52302035\u5e74,\u57fa\u672c\u5b9e\u73b0\u793e\u4f1a\u4e3b\u4e49\u73b0\u4ee3\u5316;\u7b2c\u4e8c\u6b65,\u5728\u57fa\u672c\u5b9e\u73b0\u73b0\u4ee3\u5316\u7684\u57fa\u7840\u4e0a,\u518d\u594b\u659715\u5e74,\u52302050\u5e74\u628a\u6211\u56fd\u5efa\u6210\u5bcc\u5f3a\u6c11\u4e3b\u6587\u660e\u548c\u8c10\u7f8e\u4e3d\u7684\u793e\u4f1a\u4e3b\u4e49\u73b0\u4ee3\u5316\u5f3a\u56fd\u3002\u53ef\u4ee5\u60f3\u89c1,\u968f\u7740\u8fd9\u4e00\u76ee\u6807\u5728\u5c06\u6765\u7684\u5b9e\u73b0,\u4e2d\u56fd\u7684\u793e\u4f1a\u4e3b\u4e49\u5c06\u8fc8\u5165\u66f4\u52a0\u53d1\u8fbe\u66f4\u52a0\u9ad8\u7ea7\u7684\u9636\u6bb5,\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u5c06\u5728\u5168\u4e16\u754c\u4ea7\u751f\u66f4\u5927\u7684\u5f71\u54cd\u3002\u5171\u4ea7\u4e3b\u4e49\u662f\u5168\u4eba\u7c7b\u7684\u4e8b\u4e1a,\u4e2d\u56fd\u5171\u4ea7\u515a\u9886\u5bfc\u4e2d\u56fd\u4eba\u6c11\u6309\u7167\u9a6c\u514b\u601d\u4e3b\u4e49\u63ed\u793a\u7684\u793e\u4f1a\u53d1\u5c55\u89c4\u5f8b\u4e0d\u65ad\u524d\u884c,\u5fc5\u5c06\u6df1\u5316\u5bf9\u4eba\u7c7b\u793e\u4f1a\u53d1\u5c55\u89c4\u5f8b\u7684\u8ba4\u8bc6,\u4e3a\u89e3\u51b3\u4eba\u7c7b\u95ee\u9898\u8d21\u732e\u8d8a\u6765\u8d8a\u591a\u4e2d\u56fd\u667a\u6167\u548c\u4e2d\u56fd\u65b9\u6848,\u4e3a\u4eba\u7c7b\u53d1\u5c55\u8fdb\u6b65\u4e0d\u65ad\u4f5c\u51fa\u66f4\u5927\u8d21\u732e\u3002(\u672c\u6587\u7cfb\u4e2d\u56fd\u4eba\u6c11\u5927\u5b6621\u4e16\u7eaa\u9a6c\u514b\u601d\u4e3b\u4e49\u7814\u7a76\u534f\u540c\u521b\u65b0\u4e2d\u5fc3\u9636\u6bb5\u6027\u6210\u679c)"}, {"emotion": "neg", "id": "01236ec7992ad27413df89511cf60ef378", "c_t": "\u5c0f\u5b66\u751f\u5730\u94c1\u91cc\u81ea\u5e26\u684c\u677f\u5199\u4f5c\u4e1a \u