68c0\u67e5\uff0c\u4ee5\u671f\u67e5\u660e\u5e15\u591a\u514b\u662f\u5426\u60a3\u6709\u4e2d\u98ce\u3001\u80bf\u7624\u3001\u766b\u75eb\u7b49\u8111\u529f\u80fd\u969c\u788d\u75be\u75c5\uff0c\u4ece\u800c\u4e3a\u6848\u4ef6\u8c03\u67e5\u63d0\u4f9b\u5e2e\u52a9\u3002\n\u3000\u3000\u300a\u7ebd\u7ea6\u65f6\u62a5\u300b\u63f4\u5f15\u65af\u5766\u798f\u5927\u5b66\u533b\u5b66\u4e2d\u5fc3\u8d1f\u8d23\u4eba\u3001\u795e\u7ecf\u75c5\u5b66\u6559\u6388\u5965\u6258\u00b7\u6c49\u5185\u65af\u00b7\u6c83\u683c\u5c14\u7684\u8bdd\u62a5\u9053\uff0c\u7814\u7a76\u4eba\u5458\u5c06\u4ece\u5e15\u591a\u514b\u5927\u8111\u4e2d\u5265\u79bb\u51fa\u7eb8\u7247\u8584\u5ea6\u7684\u7070\u8d28\uff0c\u5e76\u4ece\u7ec6\u80de\u5c42\u9762\u5bf9\u5176\u8fdb\u884c\u7814\u7a76\u3002\n\u3000\u3000\u6c83\u683c\u8868\u793a\uff0c\u5e0c\u671b\u8fd9\u6b21\u5c38\u68c0\u80fd\u4e3a\u6848\u4ef6\u7684\u8fdb\u5c55\u5e26\u6765\u5e2e\u52a9\u3002\u6b64\u524d\u7684\u8089\u773c\u68c0\u67e5\u672a\u53d1\u73b0\u53cd\u5e38\u60c5\u51b5\u3002\u4e0d\u8fc7\u6c83\u683c\u8bf4\uff0c\u5e15\u591a\u514b\u81ea\u6740\u65f6\u4ea7\u751f\u7684\u521b\u4f24\u53ef\u80fd\u4f1a\u7ed9\u6b64\u6b21\u8111\u7ec4\u7ec7\u68c0\u67e5\u5e26\u6765\u5f71\u54cd\u3002\n\u3000\u3000\u672a\u53c2\u4e0e\u6b64\u6b21\u5c38\u68c0\u5de5\u4f5c\u7684\u201c\u56fd\u5bb6\u6cd5\u533b\u534f\u4f1a\u201d\u4e3b\u5e2d\u5e03\u8d56\u6069\u00b7\u5f7c\u5f97\u68ee\u900f\u9732\uff0c\u6807\u51c6\u5c38\u68c0\u7a0b\u5e8f\u4e00\u822c\u5305\u62ec\u68c0\u9a8c\u5927\u8111\u662f\u5426\u611f\u67d3\u6216\u5b58\u5728\u80bf\u7624\u3001\u8840\u7ba1\u5f02\u5e38\u7b49\u60c5\u51b5\u3002\u8fdb\u4e00\u6b65\u68c0\u67e5\u4f1a\u901a\u8fc7\u663e\u5fae\u955c\u89c2\u5bdf\u5927\u8111\u7ec4\u7ec7\u7ec6\u80de\uff0c\u5224\u65ad\u88ab\u68c0\u8005\u662f\u5426\u60a3\u6709\u6162\u6027\u521b\u4f24\u6027\u8111\u75c5\u53d8\u7b49\u529f\u80fd\u9000\u5316\u6027\u75be\u75c5\u3002\u5f7c\u5f97\u68ee\u8ba4\u4e3a\uff0c\u7814\u7a76\u4eba\u5458\u53ef\u80fd\u8fd8\u4f1a\u5bf9\u5e15\u591a\u514b\u8111\u7ec4\u7ec7\u8fdb\u884c\u5206\u5b50\u6c34\u5e73\u7684\u68c0\u67e5\u3002\n\u3000\u3000\u6b64\u6b21\u5c38\u68c0\u8ddd\u62c9\u65af\u7ef4\u52a0\u65af\u67aa\u6848\u5df2\u6709\u4e00\u4e2a\u6708\u65f6\u95f4\u300210\u67081\u65e5\uff0c\u5e15\u591a\u514b\u6301\u67aa\u4ece\u8fd9\u5ea7\u201c\u8d4c\u57ce\u201d\u7684\u66fc\u5fb7\u52d2\u6d77\u6e7e\u9152\u5e9732\u5c42\u5ba2\u623f\u5411\u697c\u4e0b\u53c2\u52a0\u9732\u5929\u97f3\u4e50\u8282\u7684\u4eba\u7fa4\u626b\u5c04\uff0c\u9020\u6210\u5927\u7ea660\u4eba\u4e27\u751f\u3001500\u591a\u4eba\u53d7\u4f24\u3002\u5e15\u591a\u514b\u5728\u8b66\u5bdf\u8fdb\u5165\u623f\u95f4\u524d\u671d\u5934\u90e8\u5f00\u67aa\u81ea\u6740\u3002\n\u3000\u3000\u8b66\u65b9\u5c1a\u672a\u67e5\u660e\u5e15\u591a\u514b\u4f5c\u6848\u52a8\u673a\u3002\u5728\u4e00\u4e9b\u533b\u5b66\u4e13\u5bb6\u770b\u6765\uff0c\u5373\u4fbf\u5c38\u68c0\u663e\u793a\u5e15\u591a\u514b\u60a3\u6709\u8111\u529f\u80fd\u969c\u788d\u75be\u75c5\uff0c\u4e5f\u65e0\u6cd5\u5c31\u6b64\u63a8\u65ad\u60a3\u75c5\u5f15\u8d77\u751a\u81f3\u76f4\u63a5\u5bfc\u81f4\u60e8\u6848\u7684\u53d1\u751f\u3002\u5f7c\u5f97\u68ee\u8bf4\uff1a\u201c\u5373\u4fbf\u4e00\u4e2a\u4eba\u60a3\u6709\u8111\u529f\u80fd\u969c\u788d\u75be\u75c5\uff0c\u4e5f\u4e0d\u610f\u5473\u7740\u75be\u75c5\u4f1a\u9a71\u4f7f\u4ed6\u53bb\u505a\u4ec0\u4e48\u4e8b\u60c5\u3002\u201d\n\u3000\u3000\u795e\u7ecf\u5b66\u5bb6\u3001\u7f8e\u56fd\u56fd\u5bb6\u536b\u751f\u7814\u7a76\u6240\u795e\u7ecf\u7cfb\u7edf\u53d1\u80b2\u53ca\u53ef\u5851\u6027\u90e8\u95e8\u4e3b\u7ba1\u9053\u683c\u62c9\u65af\u00b7\u83f2\u5c14\u5179\u8ba4\u4e3a\uff0c\u5927\u89c4\u6a21\u67aa\u51fb\u6216\u6050\u6016\u88ad\u51fb\u7b49\u66b4\u529b\u4e8b\u4ef6\u4e2d\uff0c\u5f88\u5c11\u6709\u5acc\u7591\u4eba\u5b58\u5728\u8111\u529f\u80fd\u5f02\u5e38\u7684\u60c5\u51b5\uff0c\u4e0d\u8fc7\u7cbe\u795e\u95ee\u9898\u5374\u80fd\u5bfc\u81f4\u8fd9\u7c7b\u4e8b\u4ef6\u53d1\u751f\u3002\n\u3000\u3000\u83f2\u5c14\u5179\u8868\u793a\uff0c\u7c7b\u4f3c\u4e8b\u4ef6\u7684\u884c\u51f6\u8005\u4e00\u822c\u662f\u6709\u81ea\u6740\u503e\u5411\u7684\u7cbe\u795e\u75c5\u60a3\u8005\uff0c\u4ed6\u4eec\u5c06\u5b64\u7acb\u65e0\u52a9\u7684\u60c5\u7eea\u548c\u53cd\u793e\u4f1a\u884c\u4e3a\u85cf\u5728\u5fc3\u5e95\uff0c\u5e76\u628a\u8fd9\u4e9b\u8d1f\u9762\u611f\u53d7\u89c6\u4e3a\u5bf9\u81ea\u8eab\u7684\u5a01\u80c1\u3002\n\u3000\u3000\u201c\u8b66\u65b9\u5bfb\u627e\u4ed6\u4eec\u7684\u4f5c\u6848\u52a8\u673a\u65f6\uff0c\u603b\u662f\u65e0\u679c\u800c\u7ec8\u3002\u51f6\u624b\u4f3c\u4e4e\u662f\u56e0\u6124\u6012\u800c\u72af\u7f6a\uff0c\u5176\u5b9e\u8fd9\u7c7b\u72af\u7f6a\u5e76\u4e0d\u9700\u8981\u52a8\u673a\uff0c\u662f\u4e00\u79cd\u51b2\u52a8\u72af\u7f6a\u3002\u201d\n\u3000\u3000\u65e9\u5728\u4e0a\u4e16\u7eaa60\u5e74\u4ee3\uff0c\u7f8e\u56fd\u5c31\u53d1\u751f\u8fc7\u7c7b\u4f3c\u6848\u4ef6\u30021966\u5e748\u67081\u65e5\uff0c\u67e5\u5c14\u65af\u00b7\u60e0\u7279\u66fc\u67aa\u6740\u4e24\u540d\u5bb6\u4eba\u540e\uff0c\u524d\u5f80\u5f97\u514b\u8428\u65af\u5927\u5b66\u5965\u65af\u6c40\u6821\u533a\uff0c\u4ece\u6821\u56ed\u4e00\u5ea7\u949f\u697c\u5411\u4e0b\u626b\u5c04\uff0c\u9020\u621016\u4eba\u6b7b\u4ea1\u300131\u4eba\u53d7\u4f24\u3002\u60e0\u7279\u66fc\u540c\u8b66\u65b9\u4ea4\u706b\u65f6\u88ab\u6253\u6b7b\u3002\u7814\u7a76\u4eba\u5458\u540e\u6765\u53d1\u73b0\uff0c\u60e0\u7279\u66fc\u8111\u90e8\u957f\u6709\u4e00\u9897\u6838\u6843\u5927\u5c0f\u7684\u80bf\u7624\u3002\u5f53\u65f6\u6709\u8bc4\u8bba\u79f0\uff0c\u80bf\u7624\u5bfc\u81f4\u60e0\u7279\u66fc\u5927\u5f00\u6740\u6212\uff0c\u4e0d\u8fc7\u8fd9\u79cd\u8bf4\u6cd5\u6570\u5341\u5e74\u6765\u4ecd\u5b58\u4e89\u8bae\u3002"}, {"emotion": "neg", "id": "01749968791f1d4175d8f8858e7fbbfb50", "c_t": "2017\u5e74\uff0c\u5fb7\u56fd\u5c06\u7eaa\u5ff5\u9a6c\u4e01\u00b7\u8def\u5fb7\u5b97\u6559\u6539\u9769500\u5468\u5e74\u3000\u3000\u3010\u82f1\u56fd\u300a\u7ecf\u6d4e\u5b66\u4eba\u300b\u6742\u5fd7\u5728\u5c55\u671b2017\u5e74\u503c\u5f97\u7eaa\u5ff5\u7684\u5927\u4e8b\u65f6\uff0c\u7279\u522b\u70b9\u51fa\u4e86\u4fc4\u7f57\u65af\u548c\u5fb7\u56fd\u3002\u4fc4\u7f57\u65af\u5c06\u8fce\u6765\u4e8c\u6708\u9769\u547d\u548c\u5341\u6708\u9769\u547d\u7206\u53d1100\u5468\u5e74\u7eaa\u5ff5\uff0c\u800c\u5fb7\u56fd\u5c06\u8fce\u6765\u9a6c\u4e01\u00b7\u8def\u5fb7\u9886\u5bfc\u7684\u5b97\u6559\u6539\u9769\u4e94\u767e\u5468\u5e74\u7eaa\u5ff5\uff0c\u5bf9\u5fb7\u56fd\u4eba\u6765\u8bf4\uff0c\u8def\u5fb7\u4e0d\u4f46\u5728\u795e\u5b66\u610f\u4e49\u4e0a\u91cd\u5851\u4e86\u57fa\u7763\u6559\uff0c\u800c\u4e14\u4e5f\u662f\u8fd1\u4ee3\u5fb7\u610f\u5fd7\u6c11\u65cf\u6587\u5316\u6781\u4e3a\u91cd\u8981\u7684\u7f14\u9020\u8005\u4e4b\u4e00\u3002\u89c2\u5bdf\u8005\u7f51\u6b66\u5b88\u54f2\u8bd1\uff0c\u9a6c\u529b\u6821\u3011\n\u3000\u3000\u5982\u679c\u4f60\u4eca\u5e74\u8e0f\u8db3\u5fb7\u56fd\u5883\u5185\uff0c\u5f88\u53ef\u80fd\u5c31\u4f1a\u5728\u67d0\u5730\u770b\u5230\u6302\u8d77\u7684\u9a6c\u4e01\u00b7\u8def\u5fb7\u7684\u753b\u50cf\uff1a\u5bbd\u539a\u7684\u4e0b\u5df4\uff0c\u51b7\u5cfb\u7684\u9762\u5bb9\u30022017\u5e74\uff0c\u5fb7\u56fd\u5c06\u5728\u56fd\u5185\u7684100\u591a\u4e2a\u5730\u533a\u4e3e\u529e\u8d85\u8fc71000\u591a\u573a\u7684\u9a6c\u4e01\u00b7\u8def\u5fb7\u7eaa\u5ff5\u6d3b\u52a8\u3002500\u5e74\u524d\uff0c\u4e5f\u5c31\u662f1517\u5e74\uff0c\u9a6c\u4e01\u00b7\u8def\u5fb7\u6bc5\u7136\u51b3\u7136\u5730\u5728\u7ef4\u6ed5\u5821\u57ce\u5821\u6559\u5802\u5927\u95e8\u4e0a\u8d34\u4e0a\u4e86\u7740\u540d\u4e86\u300a95\u6761\u8bba\u7eb2\u300b\u3002\u4ed6\u594b\u8eab\u671d\u7740\u4f20\u7edf\u57fa\u7763\u6559\u795e\u6743\u7684\u96f7\u9706\u4e00\u51fb\uff0c\u6380\u5f00\u4e86\u5b97\u6559\u6539\u9769\u7684\u5927\u5e55\uff0c\u4e5f\u6781\u5176\u6df1\u523b\u5730\u6539\u53d8\u4e86\u5fb7\u56fd\u548c\u5168\u4e16\u754c\u3002\n\u3000\u3000\u201c\u94a6\u5b9a\u7248\u201d\u8def\u5fb7\u753b\u50cf\n\u3000\u3000\u5176\u5b9e\u5728\u5fb7\u56fd\u56fd\u5185\uff0c\u8def\u5fb7\u672c\u4eba\u5448\u73b0\u51fa\u6765\u7684\u610f\u4e49\u5e76\u4e0d\u4e3b\u8981\u6307\u5411\u795e\u5b66\u95ee\u9898\u3002\u5728\u7ecf\u5386\u4e86\u6570\u4ee3\u4eba\u7684\u4e16\u4fd7\u5316\u8fd0\u52a8\u4e4b\u5f8c\uff0c\u66f4\u522b\u8bf4\u4e1c\u5fb7\u8fd8\u957f\u671f\u88ab\u5949\u884c\u65e0\u795e\u8bba\u7684\u5171\u4ea7\u4e3b\u4e49\u8005\u7edf\u6cbb\u8fc7\u51e0\u5341\u5e74\uff0c\u5b97\u6559\u6539\u9769\u7684\u610f\u4e49\u5728\u5fb7\u56fd\u4e0d\u4ec5\u4ec5\u662f\u201c\u5c5e\u795e\u201d\u7684\uff0c\u5b83\u8fd8\u5851\u9020\u4e86\u5fb7\u56fd\u7684\u8bed\u8a00\u3001\u7cbe\u795e\u4e16\u754c\u548c\u751f\u6d3b\u65b9\u5f0f\u3002\n\u3000\u3000\u51e0\u4e2a\u4e16\u7eaa\u4ee5\u6765\u6559\u6d3e\u7eb7\u4e89\u4e00\u76f4\u6495\u626f\u7740\u8fd9\u4e2a\u56fd\u5bb6\uff0c\u5728\u4eca\u5929\u7684\u5fb7\u56fd\uff0c\u65b0\u6559\u5f92\u548c\u5929\u4e3b\u6559\u5f92\u5404\u5360\u603b\u4eba\u53e3\u768430%\u5de6\u53f3\u3002\u4f46\u662f\u572819\u4e16\u7eaa\u666e\u9c81\u58eb\u7edf\u4e00\u5fb7\u56fd\u7684\u8fdb\u7a0b\u4e2d\uff0c\u8def\u5fb7\u5b97\u53d6\u5f97\u4e86\u201c\u6587\u5316\u6218\u4e89\u201d\u7684\u80dc\u5229\u3002\u4e00\u672c\u63a8\u4ecb\u8def\u5fb7\u5f71\u54cd\u529b\u7684\u4e66\u300a\u5fb7\u56fd\uff0c\u8def\u5fb7\u4e4b\u5730\u300b\u7684\u4f5c\u8005\u514b\u91cc\u65af\u8482\u5b89\u59ae\u00b7\u827e\u6b47\u5c14\uff08Christine Eichel\uff09\u8bf4\uff1a\u201c\u8fc7\u53bb\u7684\u90a3\u4e9b\u5178\u578b\u7684\u5fb7\u56fd\u2018\u65b0\u6559\u5f92\u2019\uff0c\u57fa\u672c\u4e0a\u5728\u4eca\u5929\u5c31\u88ab\u4eba\u4eec\u8ba4\u4e3a\u662f\u5178\u578b\u7684\u2018\u5fb7\u56fd\u4eba\u2019\u3002\u201d\n\u3000\u3000\u6211\u4eec\u5148\u8c08\u8c08\u57fa\u7763\u6559\u7f8e\u5b66\u3002\u5bf9\u8def\u5fb7\u6765\u8bf4\u8fd9\u66fe\u7ecf\u548c\u5176\u4ed6\u8bae\u9898\u4e00\u6837\u91cd\u8981\u3002\u4ed6\u8ba4\u4e3a\uff0c\u57fa\u7763\u5f92\u4eec\u5b8c\u5168\u80fd\u901a\u8fc7\u8036\u7a23\u5c31\u80fd\u4fdd\u8bc1\u5f97\u6551\uff0c\u4f46\u6559\u5f92\u4eec\u6709\u4e49\u52a1\u8fc7\u4e00\u79cd\u80fd\u201c\u914d\u4eab\u201d\u5f97\u6551\u7684\u751f\u6d3b\u3002\u7981\u6b32\u4e3b\u4e49\u65e8\u5728\u6821\u9a8c\u4e2a\u4eba\u4e4b\u826f\u5fc3\uff0c\u94fa\u5f20\u5962\u4f88\u7684\u751f\u6d3b\u4f1a\u5bfc\u81f4\u6559\u5f92\u53ef\u803b\u5730\u5fc3\u6709\u65c1\u9a9b\u3002\u8fd9\u79cd\u82e6\u4fee\u7684\u751f\u6d3b\u65b9\u5f0f\u80fd\u572820\u4e16\u7eaa\u521d\u7684\u5305\u8c6a\u65af\u5efa\u7b51\u5b66\u6d3e\uff08Bauhaus architecture\uff09\u4e2d\u8fd8\u80fd\u7aa5\u89c1\u4e00\u4e8c\uff0c\u751a\u81f3\u4e5f\u80fd\u5728\u4eca\u5929\u5b9c\u5bb6\uff08IKEA\uff09\u7684\u5bb6\u5177\u98ce\u683c\u4e2d\u770b\u51fa\u4e0d\u5c11\u75d5\u8ff9\u3002\n\u3000\u3000\u745e\u5178\u54c1\u724c\u5b9c\u5bb6\u7684\u5bb6\u5c45\u98ce\u683c\uff1a\u7b80\u7ea6\u4e0d\u6d6e\u5938\n\u3000\u3000\u73b0\u4efb\u5fb7\u56fd\u603b\u7406\u9ed8\u514b\u5c14\u7684\u8001\u7238\u5c31\u662f\u4e00\u4e2a\u5178\u578b\u7684\u8def\u5fb7\u5b97\u4fee\u58eb\uff0c\u4ece\u9ed8\u514b\u5c14\u7684\u7a7f\u7740\u3001\u529e\u516c\u5ba4\u7684\u88c5\u9970\u548c\u996e\u98df\u4e60\u60ef\u4e0a\u8fd8\u80fd\u9c9c\u660e\u5730\u770b\u51fa\u8def\u5fb7\u6559\u6d3e\u7684\u98ce\u8c8c\uff0c\u800c\u4e14\u5fb7\u56fd\u603b\u7edf\u9ad8\u514b\u672c\u4eba\u4e4b\u524d\u5c31\u662f\u4e00\u4e2a\u8def\u5fb7\u5b97\u7684\u7267\u5e08\u3002\u8fd9\u4e8c\u4eba\u5728\u8bbf\u95ee\u5df4\u9ece\u65f6\uff0c\u540c\u4f1a\u4eab\u53d7\u6cd5\u56fd\u603b\u7edf\u6446\u7ed9\u4ed6\u4eec\u7684\u6d6e\u5938\u6392\u573a\uff0c\u4e0d\u8fc7\u8fd9\u4e00\u5957\u5728\u67cf\u6797\u5374\u884c\u4e0d\u901a\u3002\n\u3000\u3000\u548c\u5176\u4ed6\u7684\u65b0\u6559\u6539\u9769\u8005\u4eec\u4e00\u6837\uff0c\u8def\u5fb7\u5bf9\u89c6\u89c9\u611f\u5b98\u4e0a\u7684\u8d58\u7269\u76f8\u5f53\u6392\u65a5\uff0c\u4e0d\u8fc7\u4ed6\u80af\u5b9a\u97f3\u4e50\u5728\u5b97\u6559\u4e2d\u7684\u4f5c\u7528\u3002\u745e\u58eb\u65b0\u6559\u6539\u9769\u8005\u52a0\u5c14\u6587\u548c\u8328\u6e29\u5229\u5374\u5c06\u97f3\u4e50\u89c6\u4e3a\u8089\u4f53\u7684\u8bf1\u60d1\u800c\u6392\u65a5\u4e4b\u3002\u5bf9\u8def\u5fb7\u6765\u8bf4\uff0c\u97f3\u4e50\u662f\u80fd\u9f13\u821e\u4f17\u6559\u5f92\u5bf9\u6297\u9b54\u9b3c\u7684\u795e\u5723\u6b66\u5668\u3002\u4ed6\u5e0c\u671b\u6559\u5f92\u4eec\u80fd\u5728\u6559\u5802\u548c\u5bb6\u91cc\uff0c\u5728\u4e50\u5668\u7684\u4f34\u594f\u4e0b\u4e00\u8d77\u9ad8\u5531\u3002\u76f4\u5230\u4eca\u5929\u7684\u5fb7\u56fd\uff0c\u516c\u4f17\u8d44\u52a9\u7684\u4ea4\u54cd\u4e50\u56e2\u5c31\u6709130\u5bb6\uff0c\u6bd4\u5176\u4ed6\u4efb\u4f55\u56fd\u5bb6\u90fd\u591a\u3002\u97f3\u4e50\u4f1a\u7684\u6c1b\u56f4\u4e5f\u795e\u4f3c\u5e84\u4e25\u8083\u7a46\u7684\u5e03\u9053\u4f1a\u3002\n\u3000\u3000\u8def\u5fb7\u7684\u7cbe\u795e\u9057\u4ea7\u8fd8\u4f53\u73b0\u5728\u8fd9\u4e00\u4e8b\u5b9e\u4e2d\uff1a\u5fb7\u56fd\u4eba\u53e3\u6570\u91cf\u5728\u5168\u4e16\u754c\u4ec5\u80fd\u6392\u7b2c17\u4f4d\uff0c\u4f46\u662f\u5fb7\u56fd\u662f\u4ec5\u6b21\u4e8e\u7f8e\u56fd\u7684\u5168\u7403\u7b2c\u4e8c\u5927\u56fe\u4e66\u5e02\u573a\u3002\u8def\u5fb7\u5c06\u5723\u7ecf\u7ffb\u8bd1\u6210\u4e86\u5fb7\u8bed\u4e4b\u5f8c\uff0c\u4ed6\u5e0c\u671b\u4eba\u65e0\u5206\u7537\u5973\u8001\u5e7c\u8d2b\u5bcc\u90fd\u8981\u9605\u8bfb\u5fb7\u8bed\u7248\u5723\u7ecf\u3002\u81f3\u6b64\u4e4b\u5f8c\uff0c\u65b0\u6559\u5f92\u7684\u6587\u76f2\u7387\u5f00\u59cb\u4f4e\u4e8e\u5929\u4e3b\u6559\u5f92\uff0c\u5e76\u6700\u7ec8\u6c24\u6c32\u51fa\u4e86\u5fb7\u56fd\u4eba\u7684\u4e66\u5377\u6c14\u3002\n\u3000\u3000\u5fb7\u56fd\u4eba\u5728\u5730\u94c1\u5185\u9605\u8bfb\n\u3000\u3000\u6700\u5f8c\uff0c\u548c\u8def\u5fb7\u4e0e\u5851\u9020\u5fb7\u56fd\u76f8\u5173\u8054\u7684\u53e6\u4e00\u4e2a\u91cd\u8981\u8bae\u9898\u662f\u8d22\u5bcc\u89c2\u3002\u5728\u8fd9\u4e00\u70b9\u4e0a\uff0c\u5929\u4e3b\u6559\u5f92\u4eec\u5728\u5fcf\u6094\u4e4b\u5f8c\u7ea0\u7ed3\u4e8e\u4e00\u79cd\u7c7b\u5c42\u53e0\u52a0\u7684\u8d1f\u7f6a\u611f\uff0c\u6240\u4ee5\u559c\u6b22\u201c\u79ef\u503a\u201d\uff08\u5fb7\u8bed\u4e2d\u503a\u52a1Schulden\u548c\u7f6a\u5b7dSchuld\u6709\u7740\u76f8\u540c\u7684\u8bcd\u6839\uff09\uff0c\u4f46\u662f\u65b0\u6559\u5f92\u4eec\u5374\u53cd\u5176\u9053\u800c\u884c\u4e4b\uff0c\u5021\u5bfc\u79ef\u84c4\u4e43\u9053\u5fb7\u4e4b\u5fc5\u987b\u3002\n\u3000\u3000\u8fd9\u4e24\u79cd\u6559\u6d3e\u7684\u91d1\u94b1\u89c2\u7684\u5206\u6b67\u4e5f\u8bb8\u5728\u67d0\u4e9b\u5357\u6b27\u56fd\u5bb6\u91cc\u8fd8\u80fd\u5f97\u5230\u56de\u54cd\uff08\u4e0d\u7ba1\u8fd9\u4e2a\u7c7b\u6bd4\u662f\u5426\u6070\u5f5