u7528\u6237\u60f3\u8981\u53eb\u5b83\u53eb\u4ec0\u4e48\u5c31\u53ef\u4ee5\u3002\n\u3000\u3000\u58f0\u7eb9\u8bc6\u522b\u53ef\u4ee5\u8ba9\u4f60\u8ba4\u8bc6\u5bb6\u91cc\u7684\u6bcf\u4e00\u4e2a\u4eba\uff0c\u4e2a\u6027\u5316\u8bed\u97f3\u5408\u6210\uff0c\u5f53\u4f60\u7528\u4e00\u4e2a\u4eba\u7684\u58f0\u97f33\u52305\u5206\u949f\uff0c\u8bad\u7ec3\u7684\u65f6\u5019\u8fd9\u53f0\u8bbe\u5907\u5c31\u53ef\u4ee5\u5b8c\u5168\u9002\u5e94\u3002\n\u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u6280\u672f\u662f\u8bed\u79cd\u8bc6\u522b\uff0c\u5f53\u4f60\u8ddf\u5b83\u8bb2\u4e2d\u6587\uff0c\u5b83\u5c31\u8ddf\u4f60\u8bb2\u4e2d\u6587\uff1b\u5f53\u4f60\u8ddf\u5b83\u8bb2\u82f1\u6587\uff0c\u5b83\u5c31\u8ddf\u4f60\u8bb2\u82f1\u6587\uff0c\u4e0d\u9700\u8981\u91cd\u65b0\u914d\u7f6e\u548c\u91cd\u542f\u3002\u5305\u62ec\u5927\u5bb6\u5bf9\u4e8e\u81ea\u5df1\u7684\u7279\u5b9a\u573a\u666f\u7684\u81ea\u7136\u8bed\u8a00\u7684\u4e00\u4e9b\u5bf9\u8bdd\uff0c\u5927\u5bb6\u53ef\u4ee5\u505a\u533b\u7597\u3001\u54a8\u8be2\u3001\u5ba2\u670d\u3001\u9910\u996e\u7b49\u4efb\u4f55\u65b9\u9762\u7684\u4e1c\u897f\uff0c\u6211\u4eec\u4f1a\u63d0\u4f9b\u8fd9\u6837\u7684\u5f15\u64ce\u7ed9\u5927\u5bb6\u3002\u6240\u6709\u7684\u4e1c\u897f\u90fd\u5168\u9762\u5f00\u653e\u3002\n\u3000\u3000\u6240\u4ee5\uff0cRokid\u6709\u4ec0\u4e48\uff0c\u4f60\u4eec\u5c31\u6709\u4ec0\u4e48\u3002\u8fd9\u4e2a\u5f00\u53d1\u5957\u4ef6\u7684\u6574\u5957\u65b9\u6848\u5c06\u548cRokid\u5171\u4eab\u6574\u4e2a\u5e73\u53f0\uff0c\u6211\u4eec\u4ea7\u54c1\u5177\u5907\u7684\u80fd\u529b\u7b2c\u4e00\u5929\u5c31\u540c\u6b65\u51fa\u6765\u3002\n\u3000\u3000\u5185\u5bb9\u548c\u670d\u52a1\n\u3000\u3000\u4eca\u5929\u4e0d\u4ec5\u4ec5\u662f\u6709\u57fa\u672c\u7684\u4e1c\u897f\uff0c\u5982\u679c\u5927\u5bb6\u6709\u5174\u8da3\u8fd8\u53ef\u4ee5\u6311\u9009\u8fd9\u4e48\u591a\u5185\u5bb9\u548c\u670d\u52a1\u3002\u8fd9\u91cc\u8fb9\u4e5f\u5217\u51fa\u6765\u4e86\u4e00\u4e9b\uff0c\u97f3\u4e50\u3001\u6709\u58f0\u4e66\u3001\u513f\u7ae5\u6545\u4e8b\u3001\u65b0\u95fb\u3001IOT\u3001\u5b9e\u65f6\u7ffb\u8bd1\u3001\u8d2d\u7269\u548c\u652f\u4ed8\u3001\u5929\u6c14\u3001\u533b\u7597\u3001\u65f6\u95f4\u65e5\u5386\u3001\u884c\u4e8b\u5386\u3001\u95f9\u949f\u3001\u6c47\u7387\u3001\u5916\u5356\u3001\u5feb\u9012\u7b49\u3002\n\u3000\u3000\u6700\u8fd1\u7f8e\u56fd\u6700\u65b0\u505a\u4e86\u4e00\u4e2a\u6210\u679c\uff0c\u5b8c\u5168\u652f\u6301\u65e0\u9650\u5236\u5bf9\u8bdd\u7684\u8bed\u8a00\u6e38\u620f\uff0c\u4f60\u4eec\u4e70\u6211\u4eec\u7684\u4ea7\u54c1\u5347\u7ea7\u4ee5\u5f8c\u5c31\u53ef\u4ee5\u73a9\u6e38\u620f\u300a\u72fc\u4eba\u6740\u300b\uff0c\u53ef\u4ee5\u8fdb\u884c\u4e3b\u6301\uff0c\u8fd8\u53ef\u4ee5\u505a\u5c0f\u5b69\u7684\u6570\u5b66\u6559\u5b66\u6d3b\u52a8\uff0c\u8fd9\u90fd\u662f\u7a81\u7834\u5f53\u524d\u8bed\u97f3\u548c\u8bed\u4e49\u80fd\u529b\u7684\u65e0\u9650\u5236\u591a\u4eba\u5bf9\u8bdd\u7684\u6280\u672f\uff0c\u8fd9\u79cd\u6280\u672f\u90fd\u4f1a\u63d0\u4f9b\u7ed9\u5927\u5bb6\uff0c\u5c31\u5728\u8fd9\u4e2a\u5957\u4ef6\u91cc\u8fb9\u3002\n\u3000\u3000\u62ff\u8fd9\u4e2a\u5957\u4ef6\u53ef\u4ee5\u505a\u4ec0\u4e48\uff1f\n\u3000\u3000\u795d\u94ed\u660e\uff1a\u5927\u5bb6\u53ef\u4ee5\u770b\u5230\u963f\u91cc\u5c0f\u667a\u3001\u5c0f\u7c73\u3001Lifesmart\u3001Broadink\uff0c\u4ee5\u53ca\u6b63\u5728\u56e0\u4e3a\u5408\u4f5c\u7684\u5408\u4f5c\u4f19\u4f34\uff0c\u8fd9\u4e2a\u4ea7\u54c1\u5df2\u7ecf\u5177\u5907\u4e86\u8fd9\u4e9b\u80fd\u529b\u3002\n\u3000\u3000\u5728\u7aef\u4e0a\uff0c\u8bed\u97f3\u7684\u7ed3\u6784\u548c\u9ea6\u9635\u4e0e\u6027\u80fd\u662f\u76f4\u63a5\u76f8\u5173\u7684\uff0c\u8fd9\u4e9b\u4e1c\u897f\u6709\u5f88\u591a\u516c\u53f8\u8981\u82b1\u4e00\u5e74\u751a\u81f3\u4e24\u5e74\u7684\u65f6\u95f4\u624d\u80fd\u591f\u8c03\u4f18\uff0c\u4eca\u5929\u6211\u8fdeME\u3001EE\u90fd\u4f1a\u7528\u5230\u5b83\u3002\u5305\u62ecAI\u7aef\u4e0a\u8ba1\u7b97\uff08\u4fe1\u53f7\u5904\u7406\u3001\u81ea\u5b9a\u4e49\u5524\u9192\u3001\u566a\u97f3\u3001\u56de\u58f0\u6d88\u9664\u7b49\uff09\u90fd\u4f1a\u5728\u67b6\u6784\u4e0a\u5b8c\u6210\u3002\u5305\u62ec\u6211\u4eec\u6700\u8fd1\u4e0aGPU\u7684\u4eba\u5de5\u667a\u80fd\u7684\u82af\u7247\u91cc\u8fdb\u884c\u5904\u7406\u3002\u5f53\u7136\u5305\u62ec\u64cd\u4f5c\u7cfb\u7edf\uff0c\u4f60\u8981\u5f00\u53d1\u81ea\u5df1\u7684\u5e94\u7528\uff0c\u4fdd\u8bc1\u8ba1\u7b97\u80fd\u529b\u7684\u60c5\u51b5\u4e0b\uff0c\u53ef\u4ee5\u5c3d\u91cf\u5730\u5236\u9020\u51fa\u66f4\u8d34\u8fd1\u8001\u767e\u59d3\u4ef7\u683c\u7684\u4ea7\u54c1\u3002\n\u3000\u3000\u6240\u6709\u7684\u8bbe\u8ba1\u90fd\u662f\u5f00\u653e\u7684\uff0c\u5982\u679c\u4f60\u89c9\u5f97Rokid\u4e0d\u591f\u597d\uff0c\u4f60\u89c9\u5f97\u81ea\u5df1\u7684\u80fd\u529b\u591f\u5f3a\uff0c\u4e5f\u53ef\u4ee5\u5c06\u5b83\u6362\u6389\uff0c\u6211\u4eec\u4f1a\u628a\u6240\u6709\u7684\u4e1c\u897f\u5411\u5927\u5bb6\u5f00\u653e\u3002\n\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e9b\u80fd\u529b\u8fd8\u4e0d\u8db3\u591f\u597d\uff0c\u6240\u4ee5\u5728\u7aef\u4e4b\u5916\u8fd8\u6709\u4e00\u4e2a\u8bed\u97f3\u7684\u6982\u5ff5\u3002\u5305\u62ecAI\u7684\u8ba1\u7b97\u670d\u52a1\uff0c\u8bed\u97f3\u8bc6\u522b\u3001\u8bed\u4e49\u4e16\u754c\u3001\u8bed\u79cd\u8bc6\u522b\u7b49\u8bf8\u591a\u7684\u8bed\u97f3\u7684\u8ba1\u7b97\u5168\u90e8\u4f1a\u516c\u5f00\u3002\n\u3000\u3000\u8fd9\u91cc\u6709\u4e24\u5957\u65b9\u6848\uff0c\u4e00\u5957\u662f\u963f\u91cc\u81ea\u5df1\u7684DST\u4eba\u5de5\u667a\u80fd\u65b9\u6848\uff0c\u4e00\u5957\u662fRokid\u7684\u65b9\u6848\u3002\u5982\u679c\u4f60\u6709\u672c\u4e8b\u4e5f\u53ef\u4ee5\u6362\u6389\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u90fd\u662f\u5f00\u653e\u7684\u3002\u5305\u62ec\u6211\u4eec\u7684\u6e38\u620f\u3001\u97f3\u4e50\u3001\u6709\u58f0\u8bfb\u7269\u3001\u65b0\u95fb\u3001\u5929\u6c14\u7b49\u667a\u6167\u7684\u4e1c\u897f\u90fd\u4f1a\u5411\u5927\u5bb6\u5f00\u653e\u3002\u7b2c\u4e09\u65b9\u7684\u5185\u5bb9\u548c\u670d\u52a1\u4e5f\u4f1a\u6709\uff0c\u6240\u4ee5\u6211\u4eec\u6709\u5b8c\u6574\u7684\u5168\u6808\u7684\u5f00\u653e\u3002\u6240\u6709\u7684\u4e1c\u897f\u90fd\u4e0d\u4f1a\u88ab\u7ed1\u6b7b\u3002\n\u3000\u300070%\u64cd\u4f5c\u7cfb\u7edf\u548c\u6846\u67b6\u4ee3\u7801\uff0c100%\u7684EE\u548cME\u8bbe\u8ba1\u5168\u90e8\u514d\u8d39\u3002\u6240\u4ee5\u8fd9\u662f\u6211\u4eec\u5e0c\u671b\u7ed9\u4ea7\u4e1a\u5e26\u6765\u7684\u4e00\u4e2a\u4e0d\u4e00\u6837\u7684\u6982\u5ff5\u3002"}, {"emotion": "pos", "id": "01fac177de9b0f54190025b5467124695f", "c_t": "\u3010\u5b66\u601d\u8df5\u609f\u00b7\u5341\u4e5d\u5927|\u4e13\u5bb6\u8c08\u3011\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\uff0c\u63a8\u52a8\u5b9e\u73b0\u9ad8\u8d28\u91cf\u53d1\u5c55\u3000\u3000\u6839\u636e\u6211\u56fd\u65b0\u65f6\u4ee3\u65b0\u7279\u5f81\u65b0\u5f62\u52bf\uff0c\u515a\u7684\u5341\u4e5d\u5927\u62a5\u544a\u63d0\u51fa\"\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\"\uff0c\u5e76\u660e\u786e\u6307\u51fa\uff0c\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u662f\u8de8\u8d8a\u5173\u53e3\u7684\u8feb\u5207\u8981\u6c42\u548c\u6211\u56fd\u53d1\u5c55\u7684\u6218\u7565\u76ee\u6807\u3002\u636e\u6b64\uff0c\u6211\u56fd\u7ecf\u6d4e\u53d1\u5c55\u6709\u4e86\u65b0\u7684\u524d\u8fdb\u65b9\u5411\u3002\n\u3000\u3000\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u662f\u9002\u5e94\u65b0\u65f6\u4ee3\u7684\u5ba2\u89c2\u8981\u6c42\u3002\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u8fdb\u5165\u4e86\u65b0\u65f6\u4ee3\uff0c\u8fd9\u662f\u6211\u56fd\u53d1\u5c55\u65b0\u7684\u5386\u53f2\u65b9\u4f4d\u3002\u65b0\u65f6\u4ee3\u662f\u627f\u524d\u542f\u540e\u3001\u7ee7\u5f80\u5f00\u6765\u7684\u65f6\u4ee3\u3002\u6211\u4eec\u4ece\u7ad9\u8d77\u6765\u3001\u5bcc\u8d77\u6765\u5230\u5f3a\u8d77\u6765\u7684\u4f1f\u5927\u98de\u8dc3\uff0c\u5176\u5386\u53f2\u6027\u8f6c\u53d8\u7684\u5b9e\u8df5\u65e0\u4e0d\u8868\u660e\uff0c\u53d1\u5c55\u662f\u89e3\u51b3\u4e2d\u56fd\u6240\u6709\u95ee\u9898\u7684\u5173\u952e\u548c\u57fa\u7840\u3002\u540c\u6837\uff0c\u65b0\u65f6\u4ee3\u5b9e\u73b0\"\u4e24\u4e2a\u4e00\u767e\u5e74\"\u594b\u6597\u76ee\u6807\u3001\u8fdb\u800c\u5168\u9762\u5efa\u6210\u793e\u4f1a\u4e3b\u4e49\u73b0\u4ee3\u5316\u5f3a\u56fd\uff0c\u4e5f\u5fc5\u987b\u7d27\u7d27\u4f9d\u9760\u53d1\u5c55\u3002\u53d1\u5c55\u662f\u5168\u9762\u7684\uff0c\u800c\u8fd9\u5fc5\u987b\u4ee5\u7ecf\u6d4e\u7684\u6301\u7eed\u5065\u5eb7\u53d1\u5c55\u4e3a\u9996\u8981\u524d\u63d0\u3002\u90a3\u4e48\uff0c\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u5c31\u4e3a\u6211\u56fd\u7ecf\u6d4e\u53d1\u5c55\u6307\u660e\u4e86\u65b0\u65b9\u5411\u3001\u63d0\u51fa\u4e86\u65b0\u4efb\u52a1\uff0c\u63a8\u52a8\u4e2d\u56fd\u7ecf\u6d4e\u884c\u7a33\u81f4\u8fdc\uff0c\u4ece\u800c\u4e3a\u5404\u9879\u4e8b\u4e1a\u5168\u9762\u53d1\u5c55\u5960\u5b9a\u575a\u5b9e\u57fa\u7840\u3002\n\u3000\u3000\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u662f\u89e3\u51b3\u65b0\u7684\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\u7684\u4e00\u628a\u94a5\u5319\u3002\u65b0\u65f6\u4ee3\uff0c\u6211\u56fd\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\u5df2\u7ecf\u8f6c\u5316\u4e3a\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u548c\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u7684\u53d1\u5c55\u4e4b\u95f4\u7684\u77db\u76fe\u3002\u8fd9\u610f\u5473\u7740\uff0c\u5fc5\u987b\u4ece\u4f9b\u7ed9\u4fa7\u4e00\u7aef\u5165\u624b\uff0c\u5c06\u63d0\u9ad8\u4f9b\u7ed9\u8d28\u91cf\u4f5c\u4e3a\u4e3b\u653b\u65b9\u5411\uff0c\u4ee5\u9ad8\u8d28\u91cf\u4f9b\u7ed9\u89e3\u51b3\u53d1\u5c55\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u95ee\u9898\uff0c\u6ee1\u8db3\u4eba\u6c11\u65e5\u76ca\u589e\u957f\u7684\u7f8e\u597d\u751f\u6d3b\u9700\u8981\u3002\u6df1\u5316\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u6b63\u662f\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u7684\u91cd\u70b9\u5185\u5bb9\uff0c\u53ef\u89c1\uff0c\u89e3\u51b3\u65b0\u7684\u793e\u4f1a\u4e3b\u8981\u77db\u76fe\uff0c\u8981\u7d27\u7d27\u4f9d\u9760\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u8fd9\u4e00\u6218\u7565\u4e3e\u63aa\u3002\n\u3000\u3000\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u662f\u5b9e\u73b0\u9ad8\u8d28\u91cf\u53d1\u5c55\u7684\u5fc5\u7136\u9014\u5f84\u3002\u5341\u4e5d\u5927\u62a5\u544a\u6307\u51fa\uff0c\u6211\u56fd\u7ecf\u6d4e\u5df2\u7531\u9ad8\u901f\u589e\u957f\u9636\u6bb5\u8f6c\u5411\u9ad8\u8d28\u91cf\u53d1\u5c55\u9636\u6bb5\uff0c\u6b63\u5904\u5728\u8f6c\u53d8\u53d1\u5c55\u65b9\u5f0f\u3001\u4f18\u5316\u7ecf\u6d4e\u7ed3\u6784\u3001\u8f6c\u6362\u589e\u957f\u52a8\u529b\u7684\u653b\u5173\u671f\u3002\u4e00\u65b9\u9762\uff0c\u8fd9\u662f\u5bf9\u6211\u56fd\u7ecf\u6d4e\u53d1\u5c55\u53d6\u5f97\u4e3e\u4e16\u77a9\u76ee\u6210\u5c31\u7684\u603b\u7ed3\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4ece\u7ecf\u6d4e\u8fd0\u884c\u4e2d\u7684\u6df1\u5c42\u6b21\u77db\u76fe\u95ee\u9898\u51fa\u53d1\uff0c\u63d0\u51fa\u9ad8\u8d28\u91cf\u53d1\u5c55\u9762\u4e34\u7684\u91cd\u8981\u4efb\u52a1\uff0c\u4e5f\u5c31\u662f\uff0c\u4ee5\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4e3a\u6293\u624b\u4fc3\u8fdb\u7ecf\u6d4e\u7ed3\u6784\u4f18\u5316\uff0c\u4ee5\u79ef\u6781\u57f9\u80b2\u58ee\u5927\u65b0\u52a8\u80fd\u5b9e\u73b0\u65b0\u65e7\u52a8\u80fd\u63a5\u7eed\u8f6c\u6362\uff0c\u7ecf\u6d4e\u53d1\u5c55\u8981\u5b9e\u73b0\u4ece\u6570\u91cf\u578b\u5411\u9ad8\u8d28\u91cf\u6548\u76ca\u578b\u8f6c\u5316\u3002\u8fd9\u662f\u4e00\u79cd\u4e0d\u540c\u4e8e\u4ee5\u5f80\u7684\u7ecf\u6d4e\u8fd0\u884c\u6001\u52bf\uff0c\u56e0\u6b64\uff0c\u5fc5\u987b\u7acb\u8db3\u4e8e\u6211\u56fd\u7ecf\u6d4e\u53d1\u5c55\u7684\u9636\u6bb5\u6027\u65b0\u7279\u5f81\uff0c\u7528\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u6765\u53cd\u6620\u5e76\u5f3a\u5316\u8fd9\u79cd\u65b0\u7684\u7ecf\u6d4e\u8fd0\u884c\u6001\u52bf\uff0c\u4fc3\u8fdb\u4e2d\u56fd\u7ecf\u6d4e\u6301\u7eed\u5065\u5eb7\u53d1\u5c55\u3002\n\u3000\u3000\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\uff0c\u5341\u4e5d\u5927\u62a5\u544a\u7528\u4e24\u4e2a\"\u575a\u6301\"\u3001\u4e00\u6761\"\u4e3b\u7ebf\"\u3001\u4e09\u9879\"\u53d8\u9769\"\u3001\u4e24\u4e2a\"\u7740\u529b\"\u8fdb\u884c\u4e86\u7cfb\u7edf\u9610\u91ca\u3002\u5177\u4f53\u800c\u8a00\uff0c\u5c31\u662f\u4ee5\u65b0\u53d1\u5c55\u7406\u5ff5\u4e3a\u5f15\u9886\uff0c\u5fc5\u987b\u575a\u6301\u8d28\u91cf\u7b2c\u4e00\u3001\u6548\u76ca\u4f18\u5148\uff0c\u4ee5\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4e3a\u4e3b\u7ebf\uff0c\u63a8\u52a8\u7ecf\u6d4e\u53d1\u5c55\u8d28\u91cf\u53d8\u9769\u3001\u6548\u7387\u53d8\u9769\u3001\u52a8\u529b\u53d8\u9769\uff0c\u63d0\u9ad8\u5168\u8981\u7d20\u751f\u4ea7\u7387\uff0c\u7740\u529b\u52a0\u5feb\u5efa\u8bbe\u5b9e\u4f53\u7ecf\u6d4e\u3001\u79d1\u6280\u521b\u65b0\u3001\u73b0\u4ee3\u91d1\u878d\u3001\u4eba\u529b\u8d44\u6e90\u534f\u540c\u53d1\u5c55\u7684\u4ea7\u4e1a\u4f53\u7cfb\uff0c\u7740\u529b\u6784\u5efa\u5e02\u573a\u673a\u5236\u6709\u6548\u3001\u5fae\u89c2\u4e3b\u4f53\u6709\u6d3b\u529b\u3001\u5b8f\u89c2\u8c03\u63a7\u6709\u5ea6\u7684\u7ecf\u6d4e\u4f53\u5236\u3002\n\u3000\u3000\u5341\u4e5d\u5927\u62a5\u544a\u575a\u6301\u95ee\u9898\u5bfc\u5411\u548c\u76ee\u6807\u5bfc\u5411\uff0c\u660e\u786e\u4e86\u5f53\u524d\u548c\u672a\u6765\u4e00\u6bb5\u65f6\u671f\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u7684\u516d\u5927\u4efb\u52a1\u3002\u5176\u4e2d\uff0c\u4ee5\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u4e3a\u4e3b\u7ebf\uff0c\u5927\u529b\u53d1\u5c55\u5b9e\u4f53\u7ecf\u6d4e\u662f\u7740\u529b\u70b9\uff1b\u5b9e\u65bd\u521b\u65b0\u9a71\u52a8\u53d1\u5c55\u6218\u7565\uff0c\u52a0\u5feb\u5efa\u8bbe\u521b\u65b0\u578b\u56fd\u5bb6\u662f\u6218\u7565\u652f\u6491\uff1b\u5b9e\u65bd\u4e61\u6751\u632f\u5174\u6218\u7565\u548c\u5b9e\u65bd\u533a\u57df\u534f\u8c03\u53d1\u5c55\u6218\u7565\u662f\u7740\u91cd\u89e3\u51b3\u53d1\u5c55\u4e0d\u5e73\u8861\u4e0d\u5145\u5206\u95ee\u9898\u5f97\u7684\u91cd\u8981\u6218\u7565\u4e3e\u63aa\uff1b\u53d1\u6325\u5e02\u573a\u5728\u8d44\u6e90\u914d\u7f6e\u4e2d\u7684\u51b3\u5b9a\u6027\u4f5c\u7528\uff0c\u66f4\u597d\u53d1\u6325\u653f\u5e9c\u4f5c\u7528\uff0c\u52a0\u5feb\u5b8c\u5584\u793e\u4f1a\u4e3b\u4e49\u5e02\u573a\u7ecf\u6d4e\u4f53\u5236\u662f\u4f53\u5236\u673a\u5236\u4fdd\u969c\uff1b\u575a\u6301\u5f15\u8fdb\u6765\u548c\u8d70\u51fa\u53bb\u5e76\u91cd\uff0c\u63a8\u52a8\u5f62\u6210\u5168\u9762\u5f00\u653e\u65b0\u683c\u5c40\u662f\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u4e0d\u53ef\u6216\u7f3a\u7684\u91cd\u8981\u4e00\u73af\uff0c\u662f\u56fd\u5bb6\u7e41\u8363\u53d1\u5c55\u7684\u5fc5\u7531\u4e4b\u8def\u3002\n\u3000\u3000\u603b\u4e4b\uff0c\u5bf9\u4e8e\u73b0\u4ee3\u7ecf\u6d4e\u4f53\u7cfb\uff0c\u5341\u4e5d\u5927\u62a5\u544a\u56de\u7b54\u4e86\u4e3a\u4f55\u5efa\u8bbe\u3001\u5982\u4f55\u5efa\u8bbe\u548c\u5efa\u8bbe\u4ec0\u4e48\u7684\u91cd\u5927\u95ee\u9898\uff0c\u662f\u4e2d\u56fd\u7279\u8272\u793e\u4f1a\u4e3b\u4e49\u8fdb\u5165\u65b0\u65f6\u4ee3\u7ecf\u6d4e\u53d1\u5c55\u7684\u884c\u52a8\u65b9\u7565\uff0c\u5fc5\u5c06\u6709\u529b\u63a8\u52a8\u6211\u56fd\u5b9e\u73b0\u66f4\u9ad8\u8d28\u91cf\u3001\u66f4\u6709\u6548\u7387\u3001\u66f4\u52a0\u516c\u5e73\u3001\u66f4\u53ef\u6301\u7eed\u7684\u53d1\u5c55\u3002(\u5f20\u5fb7\u52c7 \u4e2d\u56fd\u793e\u4f1a\u79d1\u5b66\u9662\u8d22\u7ecf\u6218\u7565\u7814\u7a76\u9662\u7814\u7a76\u5458)"}]