u751f\u7d20\u539f\u6599\u836f\u9886\u57df\u7684\u65b0\u548c\u6210\u3001\u4ebf\u5e06\u533b\u836f\u3001\u82b1\u56ed\u751f\u7269\u7b49\uff1b\u6c11\u8425\u4e13\u79d1\u533b\u9662\u7684\u7f8e\u5e74\u5065\u5eb7\u3001\u7231\u5c14\u773c\u79d1\u7b49\uff1b\u4ee5\u53ca\u5c18\u87a8\u8131\u654f\u836f\u7269\u9f99\u5934\u6211\u6b66\u751f\u7269\u7b49\u3002\n\u3000\u3000\u98ce\u9669\u56e0\u7d20\uff1a \u653f\u7b56\u63a8\u8fdb\u4e0d\u53ca\u9884\u671f\uff1b\u516c\u53f8\u4e1a\u7ee9\u4f4e\u4e8e\u9884\u671f\u7b49\u3002\n\u672c\u7f51\u7ad9\u8f6c\u8f7d\u6587\u7ae0\u4ec5\u4e3a\u4f20\u64ad\u4fe1\u606f\uff0c\u4ea4\u6d41\u5b66\u4e60\u4e4b\u76ee\u7684\uff0c\u5176\u7248\u6743\u5747\u5f52\u539f\u4f5c\u8005\u6240\u6709\uff1b\u51e1\u51fa\u73b0\u5728\u672c\u7f51\u7ad9\u7684\u4fe1\u606f\uff0c\u4ec5\u4f9b\u53c2\u8003\uff0c\u672c\u7f51\u7ad9\u5c06\u5c3d\u529b\u786e\u4fdd\u8f6c\u8f7d\u4fe1\u606f\u7684\u5b8c\u6574\u6027\uff0c\u5982\u539f\u4f5c\u8005\u5bf9\u672c\u7f51\u7ad9\u8f6c\u8f7d\u6587\u7ae0\u6709\u7591\u95ee\uff0c\u8bf7\u53ca\u65f6\u8054\u7cfb\u672c\u7f51\u7ad9\uff0c\u672c\u7f51\u7ad9\u5c06\u79ef\u6781\u7ef4\u62a4\u8457\u4f5c\u6743\u4eba\u7684\u5408\u6cd5\u6743\u76ca\u3002"}, {"emotion": "neg", "id": "0125c3597552a3427f072f5bbb3d497680", "c_t": "\u300a\u5c0f\u4e11\u56de\u9b422\u300b\u201c\u52b3\u6a21\u59d0\u201d\u51fa\u6f14\u5973\u4e3b\u89d2{IMG:1}\n\u3000\u3000\u52b3\u6a21\u59d0\u201d\u6770\u897f\u5361-\u67e5\u65af\u5766\n\u3000\u3000\u641c\u72d0\u5a31\u4e50\u8baf\uff08\u6587/\u8037\u5b50\uff09\u7f8e\u56fd\u534e\u7eb3\u5f71\u7247\u516c\u53f8\u51fa\u54c1\u7684R\u7ea7\u6050\u6016\u7535\u5f71\u300a\u5c0f\u4e11\u56de\u9b42\u300b\uff08IT\uff09\uff0c\u6210\u4e3a\u4e86\u4eca\u5e74\u5317\u7f8e\u7535\u5f71\u5e02\u573a\u7684\u7206\u6b3e\u7535\u5f71\uff0c\u4e0d\u4ec5\u53e3\u7891\u8fc7\u786c\uff0c\u672c\u571f\u7968\u623f\u66f4\u662f\u7a81\u78343\u4ebf\u7f8e\u5143\u3002\u5728\u5982\u6b64\u58ee\u89c2\u7684\u6210\u7ee9\u5355\u9762\u524d\uff0c\u300a\u5c0f\u4e11\u56de\u9b422\u300b\u4e5f\u7d27\u9523\u5bc6\u9f13\u5730\u5f00\u59cb\u7b79\u5907\u8d77\u6765\u3002\u672c\u7247\u5bfc\u6f14\u5b89\u5fb7\u65af-\u7a46\u65af\u5207\u8482\u66fe\u900f\u9732\uff0c\u300a\u5c0f\u4e11\u56de\u9b42\u300b\u7b2c\u4e8c\u90e8\u7684\u7b79\u5907\u5176\u5b9e\u65e9\u5df2\u5f00\u59cb\uff0c\u5e76\u4e14\u5df2\u7ecf\u5b8c\u6210\u4e86\u5267\u672c\u3002\u6839\u636e\u5916\u5a92\u4eca\u65e5\u53d1\u5e03\u7684\u6700\u65b0\u6d88\u606f\uff0c\u201c\u52b3\u6a21\u59d0\u201d\u6770\u897f\u5361-\u67e5\u65af\u5766\u5df2\u786e\u8ba4\u51fa\u6f14\u7b2c\u4e8c\u90e8\u7684\u5973\u4e3b\u89d2\u2014\u2014\u4e5f\u5c31\u662f\u548c\u516d\u4e2a\u7537\u5b69\u4ea7\u751f\u4ea4\u96c6\u7684\u8d1d\u5f17\u8389\u3002\u67e5\u65af\u5766\u8bf4\uff0c\u5979\u4e4b\u524d\u66fe\u4e0e\u5bfc\u6f14\u5b89\u5fb7\u65af-\u7a46\u65af\u5207\u8482\u6210\u529f\u5408\u4f5c\u8fc7\u6050\u6016\u7247\u300a\u6bcd\u4fb5\u300b\uff0c\u5b89\u5fb7\u65af\u662f\u5979\u6700\u597d\u7684\u670b\u53cb\u4e4b\u4e00\uff0c\u5bf9\u4e8e\u52a0\u76df\u300a\u5c0f\u4e11\u56de\u9b42\u300b\uff0c\u5979\u611f\u5230\u975e\u5e38\u8363\u5e78\uff0c\u4e5f\u975e\u5e38\u671f\u5f85\u3002\n\u3000\u3000\u300a\u5c0f\u4e11\u56de\u9b42\u300b\u4e0a\u90e8\u7684\u6545\u4e8b\u4e3b\u8981\u96c6\u4e2d\u4e8e\u5c0f\u5b69\u548c\u5c0f\u4e11\u4e4b\u95f4\u53d1\u751f\u7684\u5173\u8054\uff0c\u800c\u5728\u7b2c\u4e8c\u90e8\u4e2d\uff0c\u65f6\u95f4\u80cc\u666f\u5c06\u8bbe\u5b9a\u572830\u5e74\u4e4b\u540e\uff0c\u5c0f\u5b69\u5b50\u4eec\u90fd\u65e9\u5df2\u957f\u5927\u6210\u4eba\uff0c\u800c\u5c0f\u4e11\u7684\u9634\u5f71\u4e0d\u4ec5\u6ca1\u6709\u6563\u53bb\uff0c\u53cd\u800c\u53c8\u5411\u4ed6\u4eec\u903c\u8fd1\u3002\u7531\u6b64\u53ef\u89c1\uff0c\u300a\u5c0f\u4e11\u56de\u9b422\u300b\u7684\u5356\u70b9\u5341\u5206\u4e30\u5bcc\u3002\u7535\u5f71\u7248\u300a\u5c0f\u4e11\u56de\u9b42\u300b\u6539\u7f16\u81ea\u65af\u8482\u82ac-\u91d1\u51fa\u7248\u4e8e1958\u5e74\u7684\u540c\u540d\u6050\u6016\u5c0f\u8bf4\uff0c\u66fe\u62cd\u6444\u8fc7\u6050\u6016\u7247\u300a\u6bcd\u4fb5\u300b\u7684\u897f\u73ed\u7259\u5bfc\u6f14\u5b89\u5fb7\u65af-\u7a46\u65af\u5207\u8482\u62c5\u5f53\u672c\u7247\u7684\u5bfc\u6f14\u3002\u5f71\u7247\u8bb2\u8ff0\u4e86\u4e00\u7fa4\u5c45\u4f4f\u5728\u201c\u5931\u8d25\u8005\u4ff1\u4e50\u90e8\u201d\u7684\u5b69\u5b50\uff0c\u906d\u9047\u4e00\u4e2a\u5c0f\u4e11\u6a21\u6837\u7684\u574f\u4e1c\u897f\u201c\u4f69\u5c3c\u6000\u65af\u201d\u8650\u5f85\u7684\u6545\u4e8b\u3002\u66fe\u53c2\u6f14\u8fc7\u300a\u5206\u6b67\u8005\u300b\u7cfb\u5217\u4ee5\u53ca\u70ed\u95e8\u5267\u96c6\u300a\u94c1\u6749\u6811\u4e1b\u300b\u7684\u9752\u5e74\u7537\u661f\u6bd4\u5c14-\u65af\u5361\u65af\u52a0\u5fb7\uff0c\u6210\u4e3a\u8be5\u7247\u7684\u5173\u6ce8\u7126\u70b9\u2014\u2014\u4ed6\u626e\u6f14\u7684\u662f\u5927\u53cd\u6d3e\u201c\u5c0f\u4e11\u201d\u201c\u4f69\u5c3c\u6000\u65af\u201d\u3002\n\u3000\u3000\u548c\u4e0a\u4e16\u7eaa90\u5e74\u4ee3\u521d\u7684\u7535\u89c6\u7535\u5f71\u7248\u7565\u6709\u4e0d\u540c\uff0c\u8fd9\u90e8\u5f71\u7247\u6beb\u4e0d\u5fcc\u8bb3\u5730\u5c55\u793a\u4e86\u8650\u5f85\u573a\u9762\u548c\u201c\u6bc1\u706d\u6027\u7684\u521b\u4f24\u201d\uff0c\u8ba9\u5f88\u591a\u5f71\u8ff7\u5982\u5750\u9488\u6be1\uff0c\u4f46\u53f2\u8482\u82ac-\u91d1\u7684\u5c0f\u8bf4\u4ece\u6765\u5c31\u662f\u56e0\u4e3a\u5145\u65a5\u7740\u5982\u6b64\u7279\u522b\u7684\u5143\u7d20\uff0c\u800c\u53d7\u5230\u89c2\u4f17\u7684\u7279\u522b\u5ba0\u7231\u3002\u300a\u5c0f\u4e11\u56de\u9b42\u300b\u7684\u6050\u6016\u6307\u6570\u76f8\u5f53\u4e4b\u9ad8\uff0cMPAA\u4e4b\u524d\u5df2\u7ecf\u6b63\u5f0f\u5c06\u8be5\u7247\u5b9a\u7ea7\u4e3aR\u7ea7\uff0c\u7b2c\u4e8c\u90e8\u4e5f\u5c06\u662f\u4e00\u90e8\u9ed1\u6697\u800c\u75af\u72c2\u7684\u7535\u5f71\u3002\n\u3000\u3000\u300a\u5c0f\u4e11\u56de\u9b422\u300b\u5c06\u57282019\u5e74\u767b\u9646\u5317\u7f8e\u9662\u7ebf\u3002"}, {"emotion": "neg", "id": "017b8cd3529e4962a250cc4c2e6e44083d", "c_t": "\u5750\u7b49\u51e0\u4e2a\u6da8\u505c\uff1f\u6c5f\u5357\u5609\u6377\u590d\u724c \u903e\u5343\u4e07\u624b\u5de8\u5355\u5c01\u6b7b\u6da8\u505c\u677f\u5750\u7b49\u51e0\u4e2a\u6da8\u505c\uff1f\u6c5f\u5357\u5609\u6377\u590d\u724c \u903e\u5343\u4e07\u624b\u5de8\u5355\u5c01\u6b7b\u6da8\u505c\u677f\n2017-11-07 09:46        \u6765\u6e90:\u4e2d\u65b0\u7ecf\u7eac                                                                  360/\u5e76\u8d2d\u91cd\u7ec4/IPO                                                  \n\u539f\u6807\u9898\uff1a\u5750\u7b49\u51e0\u4e2a\u6da8\u505c\uff1f\u6c5f\u5357\u5609\u6377\u590d\u724c \u903e\u5343\u4e07\u624b\u5de8\u5355\u5c01\u6b7b\u6da8\u505c\u677f\n\u4e2d\u65b0\u7ecf\u7eac\u5ba2\u6237\u7aef11\u67087\u65e5\u7535 \u83b7360\u501f\u58f3\u7684\u6c5f\u5357\u5609\u63777\u65e5\u590d\u724c\uff0c\u903e\u5343\u4e07\u624b\u5de8\u5355\u5c01\u6b7b\u6da8\u505c\u677f\u300211\u67086\u65e5\u665a\u95f4\uff0c\u6c5f\u5357\u5609\u6377\u53d1\u5e03\u516c\u544a\u79f0\uff0c\u516c\u53f8\u80a1\u7968\u5c06\u4e8e11\u67087\u65e5(\u661f\u671f\u4e8c)\u5f00\u5e02\u8d77\u590d\u724c\u3002\n{IMG:1}\n\u5728\u5f00\u542f\u79c1\u6709\u5316\u4e24\u5e74\u540e\uff0c\u4e92\u8054\u7f51\u5b89\u5168\u5de8\u5934360\u9009\u62e9\u201c\u501f\u58f3\u201d\u6c5f\u5357\u5609\u6377\u56de\u5f52A\u80a1\u3002\u6c5f\u5357\u5609\u6377\u4e8e11\u67082\u65e5\u665a\u95f4\u53d1\u5e03\u91cd\u5927\u8d44\u4ea7\u91cd\u7ec4\u516c\u544a\uff0c\u6b63\u5f0f\u62ab\u9732\u4e86\u4e09\u516d\u96f6\u79d1\u6280\u80a1\u4efd\u6709\u9650\u516c\u53f8(\u7b80\u79f0360)\u91cd\u7ec4\u4e0a\u5e02\u7684\u65b9\u6848\uff0c360\u5c06\u8d44\u4ea7\u4f5c\u4ef7504\u4ebf\u5143\u6ce8\u5165\u6c5f\u5357\u5609\u6377\u3002\n\u9762\u5bf9\u6295\u8d44\u8005\u63d0\u51fa\u7684\u672c\u6b21\u4ea4\u6613\u6807\u7684\u516c\u53f8\u4f30\u503c\u4f1a\u5426\u504f\u9ad8\u3001\u4e1a\u7ee9\u627f\u8bfa\u53ef\u5b9e\u73b0\u6027\u7684\u95ee\u9898\uff0c360\u516c\u53f8\u8463\u4e8b\u957f\u5468\u9e3f\u794e6\u65e5\u5728\u91cd\u5927\u8d44\u4ea7\u91cd\u7ec4\u5a92\u4f53\u8bf4\u660e\u4f1a\u4e0a\u8868\u793a\uff0c90\u4ebf\u5143\u7684\u4e1a\u7ee9\u627f\u8bfa\u4eca\u5e74\u5df2\u5b8c\u6210\u4e00\u534a\uff0c\u5b8c\u6210\u5168\u5e7422\u4ebf\u7684\u627f\u8bfa\u6709\u4fe1\u5fc3\u3002\n\u4ed6\u8fd8\u8868\u793a\uff0c\u4eca\u5e74\u4e0a\u534a\u5e74360\u6536\u516553\u4ebf\u5143\uff0c\u51c0\u5229\u6da610\u4ebf\u5143\uff0c\u63a5\u8fd1\u5168\u5e74\u7684\u4e00\u534a\uff0c\u4e0b\u534a\u5e74\u6536\u5165\u4f1a\u9ad8\u4e00\u70b9\uff0c\u6240\u4ee5\u4eca\u5e74\u5b8c\u6210\u6709\u4fe1\u5fc3\uff1b\u672a\u6765\u4e09\u5e74\uff0c\u56e0\u4e3a360\u6709\u6210\u719f\u7684\u4e92\u8054\u7f51\u4f53\u7cfb\uff0c\u53ea\u8981\u6709\u7528\u6237\u6709\u6d41\u91cf\u57fa\u7840\uff0c\u968f\u7740\u884c\u4e1a\u53d1\u5c55\u516c\u53f8\u4f1a\u8fdb\u4e00\u6b65\u7684\u53d1\u5c55\uff0c\u672a\u6765\u5c06\u52a0\u5f3a\u4e92\u8054\u7f51\u5185\u5bb9\u5efa\u8bbe\u7b49\uff0c\u4e5f\u770b\u597d\u4e2d\u56fd\u7ecf\u6d4e\u7684\u7a33\u5b9a\u589e\u957f\u3002\n\u636e\u4e2d\u56fd\u7f51\u62a5\u9053\uff0c\u6309\u7167\u89c4\u5b9a\uff0c360\u6b64\u6b21\u4ea4\u6613\u6784\u6210\u91cd\u7ec4\u4e0a\u5e02\uff0c\u9700\u63d0\u4ea4\u8bc1\u76d1\u4f1a\u4e0a\u5e02\u516c\u53f8\u5e76\u8d2d\u91cd\u7ec4\u5ba1\u6838\u59d4\u5458\u4f1a\u5ba1\u6838\u5e76\u7ecf\u4e2d\u56fd\u8bc1\u76d1\u4f1a\u6838\u51c6\u540e\u65b9\u53ef\u5b9e\u65bd\u3002360\u80fd\u5426\u987a\u5229\u95ef\u5173\u4ecd\u6709\u4e0d\u786e\u5b9a\u6027\u3002\u5728\u516c\u544a\u98ce\u9669\u63d0\u793a\u4e2d\uff0c\u6c5f\u5357\u5609\u6377\u4e5f\u6307\u51fa\uff0c\u672c\u6b21\u4ea4\u6613\u6709\u53ef\u80fd\u9762\u4e34\u56e0\u5f02\u5e38\u4ea4\u6613\u800c\u6d89\u5acc\u5185\u5e55\u4ea4\u6613\u4ee5\u53ca\u4ea4\u6613\u5404\u65b9\u5229\u76ca\u4e0d\u4e00\u81f4\u7b49\u539f\u56e0\u800c\u5bfc\u81f4\u6682\u505c\u3001\u4e2d\u6b62\u6216\u53d6\u6d88\u672c\u6b21\u4ea4\u6613\u7684\u98ce\u9669\uff0c\u4ee5\u53ca\u62df\u7f6e\u5165\u8d44\u4ea7\u8bc4\u4f30\u589e\u503c\u8f83\u9ad8\u7684\u98ce\u9669\u3002\n\u4e0d\u8fc7\uff0c\u5728\u4e0a\u5468\u4e94\u8bc1\u76d1\u4f1a\u4e3e\u884c\u7684\u53d1\u5e03\u4f1a\u4e0a\uff0c\u8bc1\u76d1\u4f1a\u65b0\u95fb\u53d1\u8a00\u4eba\u9ad8\u8389\u5728\u56de\u5e94360\u501f\u58f3\u4e0a\u5e02\u65f6\u8868\u793a\uff0c\u5c06\u91cd\u70b9\u652f\u6301\u7b26\u5408\u56fd\u5bb6\u4ea7\u4e1a\u6218\u7565\u53d1\u5c55\u65b9\u5411\u3001\u638c\u63e1\u6838\u5fc3\u6280\u672f\u3001\u5177\u6709\u4e00\u5b9a\u89c4\u6a21\u7684\u4f18\u8d28\u5883\u5916\u4e0a\u5e02\u4e2d\u8d44\u4f01\u4e1a\u53c2\u4e0eA\u80a1\u516c\u53f8\u5e76\u8d2d\u91cd\u7ec4\uff0c\u5e76\u5bf9\u5176\u4e2d\u7684\u91cd\u7ec4\u4e0a\u5e02\u4ea4\u6613\u8fdb\u4e00\u6b65\u4e25\u683c\u8981\u6c42\u3002(\u4e2d\u65b0\u7ecf\u7eacAPP)\u8fd4\u56de\u641c\u72d0\uff0c\u67e5\u770b\u66f4\u591a\n\u8d23\u4efb\u7f16\u8f91\uff1a\u58f0\u660e\uff1a\u672c\u6587\u7531\u5165\u9a7b\u641c\u72d0\u53f7\u7684\u4f5c\u8005\u64b0\u5199\uff0c\u9664\u641c\u72d0\u5b98\u65b9\u8d26\u53f7\u5916\uff0c\u89c2\u70b9\u4ec5\u4ee3\u8868\u4f5c\u8005\u672c\u4eba\uff0c\u4e0d\u4ee3\u8868\u641c\u72d0\u7acb\u573a\u3002      \u9605\u8bfb ()                    \u6295\u8bc9"}, {"emotion": "pos", "id": "012e06ec222f665cfa79ddb4c23238776f", "c_t": "\u3000\u3000\u9648\u8087\u96c4\u6307\u51fa\uff0c\u5de5\u4e1a\u4e92\u8054\u7f51\u4f5c\u4e3a\u65b0\u4e00\u4ee3\u7f51\u7edc\u4fe1\u606f\u6280\u672f\u4e0e\u73b0\u4ee3\u5de5\u4e1a\u878d\u5408\u53d1\u5c55\u50ac\u751f\u7684\u65b0\u4e8b\u7269\uff0c\u662f\u5b9e\u73b0\u751f\u4ea7\u5236\u9020\u9886\u57df\u5168\u8981\u7d20\u3001\u5168\u4ea7\u4e1a\u94fe\u3001\u5168\u4ef7\u503c\u94fe\u8fde\u63a5\u7684\u5173\u952e\u652f\u6491\uff0c\u662f\u5de5\u4e1a\u7ecf\u6d4e\u6570\u5b57\u5316\u3001\u7f51\u7edc\u5316\u3001\u667a\u80fd\u5316\u7684\u91cd\u8981\u57fa\u7840\u8bbe\u65bd\uff0c\u662f\u4e92\u8054\u7f51\u4ece\u6d88\u8d39\u9886\u57df\u5411\u751f\u4ea7\u9886\u57df\u3001\u4ece\u865a\u62df\u7ecf\u6d4e\u5411\u5b9e\u4f53\u7ecf\u6d4e\u62d3\u5c55\u7684\u6838\u5fc3\u8f7d\u4f53\u3002\u52a0\u5feb\u5de5\u4e1a\u4e92\u8054\u7f51\u53d1\u5c55\uff0c\u5bf9\u63a8\u52a8\u5236\u9020\u4e1a\u4e0e\u4e92\u8054\u7f51\u6df1\u5ea6\u878d\u5408\uff0c\u4fc3\u8fdb\u5927\u4f17\u521b\u4e1a\u4e07\u4f17\u521b\u65b0\u548c\u5927\u4e2d\u5c0f\u4f01\u4e1a\u878d\u901a\u53d1\u5c55\uff0c\u5efa\u8bbe\u5236\u9020\u5f3a\u56fd\u3001\u7f51\u7edc\u5f3a\u56fd\u610f\u4e49\u91cd\u5927\u3002\u3000\u3000\u9648\u8087\u96c4\u5f3a\u8c03\uff0c\u5de5\u4e1a\u4e92\u8054\u7f51\u4f5c\u4e3a\u65b0\u4e00\u4ee3\u4fe1\u606f\u6280\u672f\u4e0e\u5236\u9020\u4e1a\u6df1\u5ea6\u878d\u5408\u7684\u65b0\u5174\u4ea7\u7269\uff0c\u6b63\u5728\u6210\u4e3a\u65b0\u5de5\u4e1a\u9769\u547d\u7684\u5173\u952e\u652f\u6491\u548c\u6df1\u5316\"\u4e92\u8054\u7f51+\u5148\u8fdb\u5236\u9020\u4e1a\"\u7684\u91cd\u8981\u57fa\u77f3\uff0c\u5bf9\u672a\u6765\u5de5\u4e1a\u7ecf\u6d4e\u53d1\u5c55\u5c06\u4ea7\u751f\u5168\u65b9\u4f4d\u3001\u6df1\u5c42\u6b21\u3001\u9769\u547d\u6027\u7684\u5f71\u54cd\u3002\u52a0\u5feb\u53d1\u5c55\u5de5\u4e1a\u4e92\u8054\u7f51\uff0c\u4fc3\u8fdb\u65b0\u4e00\u4ee3\u4fe1\u606f\u6280\u672f\u4e0e\u5236\u9020\u4e1a\u6df1\u5ea6\u878d\u5408\uff0c\u662f\u987a\u5e94\u6280\u672f\u3001\u4ea7\u4e1a\u53d8\u9769\u8d8b\u52bf\uff0c\u52a0\u5feb\u5236\u9020\u5f3a\u56fd\u3001\u7f51\u7edc\u5f3a\u56fd\u5efa\u8bbe\u7684\u5ba2\u89c2\u8981\u6c42\uff0c\u4e5f\u662f\u6df1\u5316\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\u3001\u4fc3\u8fdb\u5b9e\u4f53\u7ecf\u6d4e\u8f6c\u578b\u5347\u7ea7\u3001\u5efa\u8bbe\u73b0\u4ee3\u5316\u7ecf\u6d4e\u4f53\u7cfb\u7684\u91cd\u8981\u4e3e\u63aa\u3002\u4ece\u5de5\u4e1a\u7ecf\u6d4e\u53d1\u5c55\u89d2\u5ea6\u770b\uff0c\u5de5\u4e1a\u4e92\u8054\u7f51\u4e3a\u5236\u9020\u5f3a\u56fd\u5efa\u8bbe\u63d0\u4f9b\u5173\u952e\u652f\u6491\u3002\u4e00\u662f\u63a8\u52a8\u4f20\u7edf\u5de5\u4e1a\u8f6c\u578b\u5347\u7ea7\uff0c\u901a\u8fc7\u8de8\u8bbe\u5907\u3001\u8de8\u7cfb\u7edf\u3001\u8de8\u4ea7\u533a\u3001\u8de8\u5730\u533a\u7684\u5168\u9762\u4e92\u8054\u4e92\u901a\uff0c\u5b9e\u73b0\u5404\u79cd\u751f\u4ea7\u548c\u670d\u52a1\u8d44\u6e90\u5728\u66f4\u5927\u8303\u56f4\u3001\u66f4\u9ad8\u6548\u7387\u3001\u66f4\u52a0\u7cbe\u51c6\u7684\u4f18\u5316\u914d\u7f6e\uff0c\u63a8\u52a8\u5236\u9020\u4e1a\u4f9b\u7ed9\u4fa7\u7ed3\u6784\u6027\u6539\u9769\uff0c\u5927\u5e45\u63d0\u5347\u5de5\u4e1a\u7ecf\u6d4e\u7684\u53d1\u5c55\u8d28\u91cf\u548c\u6548\u76ca\u3002\u4e8c\u662f\u52a0\u5feb\u65b0\u5174\u4ea7\u4e1a\u7684\u57f9\u80b2\u58ee\u5927\u3002\u5de5\u4e1a\u4e92\u8054\u7f51\u4fc3\u8fdb\u8bbe\u8ba1\u3001\u751f\u4ea7\u3001\u7ba1\u7406\u3001\u670d\u52a1\u7b49\u73af\u8282\uff0c\u7531\u5355\u70b9\u7684\u6570\u5b57\u5316\u5411\u5168\u9762\u7684\u96c6\u6210\u6f14\u8fdb\uff0c\u52a0\u901f\u521b\u65b0\u65b9\u5f0f\u3001\u751f\u4ea7\u6a21\u5f0f\u3001\u7ec4\u7ec7\u5f62\u5f0f\u548c\u5546\u4e1a\u65b9\u5f0f\u7684\u6df1\u523b\u53d8\u9769\uff0c\u50ac\u751f\u667a\u80fd\u5316\u751f\u4ea7\u3001\u7f51\u7edc\u5316\u534f\u540c\u3001\u670d\u52a1\u5316\u5ef6\u4f38\u3001\u4e2a\u6027\u5316\u5b9a\u5236\u7684\u8bf8\u591a\u65b0\u6a21\u5f0f\u3001\u65b0\u4e1a\u6001\u3001\u65b0\u4ea7\u4e1a\u3002\u4ece\u7f51\u7edc\u8bbe\u65bd\u53d1\u5c55\u89d2\u5ea6\u770b\uff0c\u5de5\u4e1a\u4e92\u8054\u7f51\u662f\u7f51\u7edc\u5f3a\u56fd\u5efa\u8bbe\u7684\u91cd\u8981\u5185\u5bb9\u3002\u4e00\u662f\u52a0\u901f\u7f51\u7edc\u6f14\u8fdb\u5347\u7ea7\u3002\u5de5\u4e1a\u4e92\u8054\u7f51\u4fc3\u8fdb\u4eba\u4e0e\u4eba\u76f8\u4e92\u8fde\u63a5\u7684\u516c\u4f17\u4e92\u8054\u7f51\u3001\u7269\u4e0e\u7269\u76f8\u4e92\u8fde\u63a5\u7684\u7269\u8054\u7f51\u5411\u4eba\u3001\u673a\u3001\u7269\u5168\u9762\u4e92\u8054\u62d3\u5c55\uff0c\u5927\u5e45\u63d0\u5347\u7f51\u7edc\u8bbe\u65bd\u7684\u652f\u6491\u670d\u52a1\u80fd\u529b\u3002\u4e8c\u662f\u62d3\u5c55\u7f51\u7edc\u7ecf\u6d4e\u7a7a\u95f4\u3002\u5de5\u4e1a\u4e92\u8054\u7f51\u5177\u6709\u8f83\u5f3a\u7684\u6e17\u900f\u6027\uff0c\u53ef\u4ee5\u4e0e\u4ea4\u901a\u3001\u7269\u6d41\u3001\u80fd\u6e90\u3001\u533b\u7597\u3001\u519c\u4e1a\u7b49\u5b9e\u4f53\u7ecf\u6d4e\u5404\u4e2a\u9886\u57df\u6df1\u5ea6\u878d\u5408\uff0c\u5b9e\u73b0\u4ea7\u4e1a\u4e0a\u4e0b\u6e38\u8de8\u9886\u57df\u7684\u5e7f\u6cdb\u4e92\u8054\u4e92\u901a\uff0c\u63a8\u52a8\u7f51\u7edc\u5e94\u7528\u4ece\u865a\u62df\u5230\u5b9e\u4f53\u3001\u4ece\u751f\u6d3b\u5230\u751f\u4ea7\u7684\u79d1\u5b66\u8de8\u8d8a\uff0c\u6781\u5927\u62d3\u5c55\u4e86\u7f51\u7edc\u7ecf\u6d4e\u7684\u53d1\u5c55\u7a7a\u95f4\u3002"}, {"emotion": "neg", "id": "01d298d1c7972b88d6b45601766302c8e0", "c_t": "\u767d\u9c40\u8c5a\u4ecd\u662f\u201c\u529f\u80fd\u6027\u706d\u7edd\u201d \u4e0d\u6392\u9664\u957f\u6c5f\u4ecd\u6709\u5b58\u6d3b\u4e2a\u4f53\u65b0\u534e\u793e\u6b66\u6c4911\u67086\u65e5\u7535\uff08\u8bb0\u8005\u9ec4\u8273\uff09\u8fd1\u65e5\uff0c\u4e00\u6761\u5173\u4e8e\u767d\u9c40\u8c5a\u7269\u79cd\u706d\u7edd\u7684\u7f51\u4e0a\u6587\u7ae0\uff0c\u5f15\u8d77\u4e86\u8206\u8bba\u5bf9\u767d\u9c40\u8c5a\u7684\u70ed\u8bae\u548c\u5173\u6ce8\u3002\u5bf9\u6b64\uff0c\u8bb0\u8005\u4e13\u8bbf\u4e86\u6211\u56fd\u6de1\u6c34\u9c7c\u7c7b\u7814\u7a76\u4e13\u5bb6\u3001\u957f\u671f\u5173\u6ce8\u957f\u6c5f\u751f\u6001\u7684\u4e2d\u56fd\u79d1\u5b66\u9662\u9662\u58eb\u66f9\u6587\u5ba3\u3002\n\u66f9\u6587\u5ba3\u8868\u793a\uff0c\u5728\u6211\u56fd\u957f\u6c5f\u6d41\u57df\uff0c\u4e0d\u6392\u9664\u8fd8\u6709\u4e2a\u4f53\u5b58\u6d3b\uff0c\u5c24\u5176\u662f\u5728\u957f\u6c5f\u53e3\u5bbd\u9614\u7684\u6c5f\u57df\u91cc\uff0c\u662f\u6709\u53ef\u80fd\u6709\u767d\u9c40\u8c5a\u7684\u3002\u56e0\u6b64\uff0c\u767d\u9c40\u8c5a\u4ecd\u7136\u662f2007\u5e74\u4e2d\u5916\u79d1\u5b66\u5bb6\u5ba3\u5e03\u7684\"\u529f\u80fd\u6027\u706d\u7edd\"\u3002\n\"\u6240\u8c13\u529f\u80fd\u6027\u706d\u7edd\uff0c\u5c31\u662f\u6570\u91cf\u975e\u5e38\u7a00\u5c11\uff0c\u4ee5\u81f4\u4e8e\u81ea\u7136\u6761\u4ef6\u4e0b\u5931\u53bb\u4e86\u7e41\u6b96\u80fd\u529b\u3002\u4f46\u5e76\u4e0d\u662f\u8868\u793a\u8fd9\u4e2a\u7269\u79cd\u5728\u5730\u7403\u4e0a\u6ca1\u6709\u4e86\u3002\"\u66f9\u6587\u5ba3\u8bf4\u3002\n\u767d\u9c40\u8c5a\u662f\u6211\u56fd\u7279\u6709\u7269\u79cd\uff0c\u4ec5\u4ea7\u4e8e\u957f\u6c5f\u4e2d\u4e0b\u6e38\uff0c\u662f\u4e16\u754c\u4e0a\u4ec5\u5b58\u7684\u56db\u79cd\u6de1\u6c34\u8c5a\u4e2d\u6240\u5269\u6570\u91cf\u6700\u7a00\u5c11\u7684\u4e00\u79cd\uff0c\u4e3a\u4e2d\u56fd\u56fd\u5bb6\u4e00\u7ea7\u4fdd\u62a4\u52a8\u7269\u30022006\u5e74\uff0c\u4e2d\u5916\u79d1\u5b66\u5bb6\u5728\u957f\u6c5f\u8fdb\u884c\u8054\u5408\u79d1\u8003\uff0c\u6ca1\u6709\u53d1\u73b0\u767d\u9c40\u8c5a\u7684\u8e2a\u5f71\u30022007\u5e74\u79d1\u5b66\u5bb6\u5ba3\u5e03\u767d\u9c40\u8c5a\u529f\u80fd\u6027\u706d\u7edd\u3002\n\u66f9\u6587\u5ba3\u8868\u793a\uff0c\u5ba3\u5e03\u4e00\u4e2a\u7269\u79cd\u706d\u7edd\u662f\u4e00\u4e2a\u590d\u6742\u7684\u79d1\u5b66\u8fc7\u7a0b\u3002\"\u4e00\u822c\u800c\u8a00\uff0c\u529f\u80fd\u6027\u706d\u7edd\u540e50\u5e74\u91cc\u81ea\u7136\u754c\u6ca1\u6709\u518d\u53d1\u73b0\u5b58\u6d3b\u4e2a\u4f53\uff0c\u624d\u80fd\u9274\u5b9a\u4e3a\u706d\u7edd\u3002\"\n\u6b64\u5916\uff0c\u5728\u4e16\u754c\u81ea\u7136\u4fdd\u62a4\u8054\u76df\u6fd2\u5371\u7269\u79cd\u7ea2\u8272\u540d\u5f55\u4e2d\uff0c\u767d\u9c40\u8c5a\u4ecd\u7136\u88ab\u5217\u4e3a\"\u6781\u5371\"\uff08CR\uff09\u3002\u5728\"\u6781\u5371\"\u4e4b\u4e0a\uff0c\u8fd8\u6709\u91ce\u5916\u706d\u7edd\uff08EW\uff09\u3001\u7edd\u706d\uff08EX\uff09\u3002\n\u8fd1\u5e74\u6765\uff0c\u957f\u6c5f\u4e2d\u4e0b\u6e38\u65f6\u4e0d\u65f6\u4f20\u51fa\u767d\u9c40\u8c5a\u8eab\u5f71\u518d\u73b0\u7684\u4f20\u95fb\u3002\n\u66f9\u6587\u5ba3\u8868\u793a\uff0c\u8fd9\u4e9b\u4f20\u95fb\u6682\u65f6\u4e0d\u80fd\u8bc1\u5b9e\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8bc1\u4f2a\u3002\u5e0c\u671b\u4ee5\u540e\u5b98\u65b9\u548c\u6c11\u95f4\u7684\u8003\u5bdf\uff0c\u5c3d\u53ef\u80fd\u5730\u7559\u4e0b\u5f71\u50cf\u8d44\u6599\u3002\"\u5982\u679c\u53d1\u73b0\u767d\u9c40\u8c5a\uff0c\u5c3d\u91cf\u62cd\u6444\u5230\u7167\u7247\u89c6\u9891\uff0c\u6709\u8fd9\u4e9b\u8d44\u6599\u624d\u80fd\u8fdb\u4e00\u6b65\u6df1\u5165\u7814\u7a76\u548c\u5c55\u5f00\u4fdd\u62a4\u3002\"\n\u539f\u6807\u9898\uff1a\u767d\u9c40\u8c5a\u4ecd\u662f\u201c\u529f\u80fd\u6027\u706d\u7edd\u201d \u4e0d\u6392\u9664\u957f\u6c5f\u4ecd\u6709\u5b58\u6d3b\u4e2a\u4f53"}, {"emotion": "neg", "id": "01c6a8e58da4201e075e5a9653de66c790", "c_t": "\u6700\u5927\u5b97\u8d70\u79c1\u6fd2\u5371\u8774\u8776\u5236\u54c1\u6848\u5728\u6d4e\u5ba3\u5224 \u4e09\u540d\u201c\u53d1\u70e7\u53cb\u201d\u83b7\u5211{IMG:1}\n\u3000\u3000\u4ee5\u7231\u4e4b\u540d\u7684\u4f24\u5bb3\uff0c\u624d\u6700\u53ef\u6015\u3002\u5c71\u4e1c\u4e09\u540d\u8774\u8776\u201c\u53d1\u70e7\u53cb\u201d\uff0c\u56e0\u75f4\u8ff7\u8774\u8776\u8d70\u5230\u4e00\u8d77\uff0c\u4ed6\u4eec\u5728\u660e\u77e5\u6240\u8d2d\u4e70\u7684\u8774\u8776\u5c5e\u4e8e\u76f8\u5173\u56fd\u9645\u516c\u7ea6\u4fdd\u62a4\u7269\u79cd\u7684\u60c5\u51b5\u4e0b\uff0c\u5148\u540e\u4ece\u9a6c\u6765\u897f\u4e9a\u7b49\u56fd\u901a\u8fc7\u90ae\u5bc4\u65b9\u5f0f\uff0c\u8d70\u79c1\u4e24\u5343\u4f59\u53ea\u8774\u8776(\u6b7b\u4f53)\u7528\u4e8e\u51fa\u552e\u725f\u53d6\u66b4\u5229\u3002\u7ecf\u9274\u5b9a\uff0c\u6d89\u6848\u603b\u4ef7\u503c\u9ad8\u8fbe150\u4f59\u4e07\u5143\u3002\n\u3000\u3000\u8fd1\u65e5\uff0c\u8fd9\u8d77\u88ab\u79f0\u4e3a\u201c\u5168\u56fd\u6700\u5927\u5b97\u8d70\u79c1\u6fd2\u5371\u8774\u8776\u5236\u54c1\u6848\u201d\uff0c\u7531\u6d4e\u5357\u5e02\u4e2d\u7ea7\u4eba\u6c11\u6cd5\u9662\u4f5c\u51fa\u4e00\n\u3000\u3000\u5ba1\u5224\u51b3\uff0c\u4e09\u540d\u88ab\u544a\u4eba\u5206\u522b\u4ee5\u8d70\u79c1\u73cd\u8d35\u52a8\u7269\u5236\u54c1\u7f6a\u88ab\u5224\u5904\u6709\u671f\u5f92\u521110\u5e74\u30017\u5e74\u30015\u5e74\u3002\n\u3000\u3000\u4ee5\u7231\u4e4b\u540d\u201c\u60f9\u51fa\u7978\u7aef\u201d\n\u3000\u3000\u4e09\u540d\u201c\u53d1\u70e7\u53cb\u201d\u5f00\u516c\u53f8\u5012\u5356\u8774\u8776\u5236\u54c1\n\u3000\u3000\u636e\u4ecb\u7ecd\uff0c\u81ea2015\u5e74\u4e0a\u534a\u5e74\u4ee5\u6765\uff0c\u5acc\u7591\u4eba\u9b4f\u67d0\u3001\u5f90\u67d0\u3001\u6731\u67d0\u4e09\u4eba\uff0c\u901a\u8fc7\u4e92\u8054\u7f51\u4ece\u5883\u5916\u8ba2\u8d2d\u8774\u8776\u6807\u672c\uff0c\u5229\u7528\u90ae\u9012\u6e20\u9053\u53ca\u4f2a\u62a5\u54c1\u540d\u65b9\u5f0f\u8d70\u79c1\u8fdb\u5883\uff0c\u52a0\u5de5\u88c5\u88f1\u540e\u5728\u6dd8\u5b9d\u7f51\u8fdb\u884c\u5012\u5356\u3002\n\u3000\u3000\u4e09\u540d\u5e74\u8f7b\u4eba\u90fd\u6709\u4f53\u9762\u5de5\u4f5c\u6216\u201c\u9ad8\u5b66\u5386\u201d\uff1a\u9b4f\u67d0\u4f9b\u804c\u4e8e\u4e00\u5bb6\u6295\u8d44\u516c\u53f8\uff0c\u5f90\u67d0\u4f9b\u804c\u4e8e\u6d4e\u5357\u67d0\u8457\u540d\u56fd\u6709\u4f01\u4e1a\uff0c\u6731\u67d0\u662f\u7701\u5185\u67d0\u8457\u540d\u9ad8\u6821\u7684\u5728\u8bfb\u7814\u7a76\u751f\u3002\u201c\u6211\u4eec\u7ecf\u5e38\u53c2\u52a0\u4e0e\u4fdd\u62a4\u8774\u8776\u6709\u5173\u7684\u516c\u76ca\u6d3b\u52a8\uff0c\u5411\u5c0f\u5b66\u751f\u7b49\u4ecb\u7ecd\u8774\u8776\uff0c\u6216\u8005\u5e03\u7f6e\u8774\u8776\u5c55\u89c8\u3002\u63a5\u89e6\u7684\u673a\u4f1a\u591a\u4e86\uff0c\u6211\u4eec\u4fbf\u719f\u6089\u4e86\u3002\u201d\u5f90\u67d0\u8bf4\uff0c\u5bf9\u8774\u8776\u7684\u75f4\u8ff7\uff0c\u8ba9\u4e09\u4eba\u6210\u4e3a\u670b\u53cb\u3002\n\u3000\u30002015\u5e74\uff0c\u9b4f\u67d0\u548c\u5f90\u67d0\u5728\u6d4e\u5357\u6ce8\u518c\u6210\u7acb\u4e86\u67d0\u751f\u7269\u79d1\u6280\u516c\u53f8\uff0c\u4e13\u95e8\u4ece\u4e8b\u8774\u8776\u6807\u672c\u7684\u5236\u4f5c\u3001\u9500\u552e\uff0c\u5e76\u5728\u6dd8\u5b9d\u7f51\u4e0a\u5f00\u8bbe\u4e86\u76f8\u5173\u7f51\u5e97\u62db\u63fd\u751f\u610f\u3002\u56e0\u4e3a\u89c9\u5f97\u201c\u51fa\u552e\u56fd\u5185\u8774\u8776\u6807\u672c\u5229\u6da6\u592a\u4f4e\u201d\uff0c\u9b4f\u67d0\u63d0\u8bae\u201c\u4ece\u56fd\u5916\u8d2d\u4e70\u8774\u8776\u6807\u672c\u201d\u6765\u5356\uff0c\u5f90\u67d0\u6b23\u7136\u540c\u610f\u3002\u6839\u636e\u5206\u5de5\uff0c\u9b4f\u67d0\u8d1f\u8d23\u51fa\u8d44\u4ece\u56fd\u5916\u8d2d\u4e70\u539f\u8776\uff0c\u5f90\u67d0\u8d1f\u8d23\u628a\u8774\u8776\u5236\u6210\u5de5\u827a\u54c1\u51fa\u552e\u3002\u6731\u67d0\u8d1f\u8d23\u4ece\u7f51\u4e0a\u5bfb\u627e\u6d77\u5916\u5356\u5bb6\uff0c\u5b9e\u65bd\u5177\u4f53\u91c7\u8d2d\u3002\u5f53\u5e7411\u6708\uff0c\u6731\u67d0\u5f00\u59cb\u901a\u8fc7Facebook\u8054\u7cfb\u56fd\u5916\u5356\u5bb6\uff0c\u5f88\u5feb\u4fbf\u4e0e\u83f2\u5f8b\u5bbe\u3001\u9a6c\u6765\u897f\u4e9a\u3001\u5370\u5ea6\u5c3c\u897f\u4e9a\u7b49\u56fd\u5bb6\u7684\u5356\u5bb6\u8fbe\u6210\u8d2d\u4e70\u610f\u5411\u3002\u8054\u7cfb\u597d\u5356\u5bb6\u540e\uff0c\u7531\u9b4f\u67d0\u548c\u5f90\u67d0\u51b3\u5b9a\u8ba2\u8d2d\u7684\u6570\u91cf\u3001\u4ef7\u683c\u53ca\u4ea4\u6613\u65b9\u5f0f\uff0c\u6700\u540e\u7531\u9b4f\u67d0\u652f\u4ed8\u8d27\u6b3e\u3002\n\u3000\u3000\u4e00\u6b21\u70ab\u8000\u88ab\u4eba\u4e3e\u62a5\n\u3000\u3000\u88ab\u67e5\u83b7\u7684\u8774\u8776\u4e2d\u6709\u5343\u4f59\u679a\u5c5e\u4fdd\u62a4\u7269\u79cd\n\u3000\u3000\u8fd9\u8d77\u8d70\u79c1\u6848\u7684\u66dd\u5149\uff0c\u6e90\u4e8e\u9b4f\u67d0\u7684\u4e00\u6b21\u70ab\u8000\u30022016\u5e741\u6708\uff0c\u9b4f\u67d0\u628a\u4ece\u6d77\u5916\u7f51\u8d2d\u6765\u7684\u90e8\u5206\u8774\u8776\u7167\u7247\u53d1\u5230\u76f8\u5173QQ\u7fa4\u5185\uff0c\u65e2\u4e3a\u4e86\u5c55\u793a\u81ea\u5df1\u7684\u6536\u85cf\u54c1\uff0c\u4e5f\u60f3\u4ee5\u6b64\u62db\u63fd\u66f4\u591a\u4e70\u5bb6\u3002\u7ed3\u679c\uff0c\u7fa4\u91cc\u7684\u8774\u8776\u7231\u597d\u8005\u5f88\u5feb\u8ba4\u51fa\u8fd9\u4e9b\u8774\u8776\u54c1\u79cd\u5c5e\u4e8e\u6fd2\u5371\u4fdd\u62a4\u7269\u79cd\uff0c\u56fd\u5185\u6839\u672c\u4e0d\u5141\u8bb8\u552e\u5356\uff0c\u4e8e\u662f\u5411\u76f8\u5173\u673a\u5173\u4e3e\u62a5\u4e86\u9b4f\u67d0\u7684\u884c\u4e3a\u3002\n\u3000\u3000\u968f\u540e\uff0c\u6d4e\u5357\u6d77\u5173\u5728\u90ae\u9012\u6e20\u9053\u5c06\u8774\u8776\u5305\u88f9\u622a\u83b7\u30022016\u5e742\u67083\u65e5\uff0c\u6d4e\u5357\u6d77\u5173\u7f09\u79c1\u5c40\u5c06\u5f90\u67d0\u6293\u83b7\u3002\u5f53\u65e5\uff0c\u5728\u5f90\u67d0\u534f\u52a9\u4e0b\u5c06\u9b4f\u67d0\u3001\u6731\u67d0\u6293\u83b7\u3002\u6848\u53d1\u540e\uff0c\u6d89\u6848\u7269\u54c1\u4ece\u5f90\u67d0\u3001\u6731\u67d0\u3001\u9b4f\u67d0\u5904\u88ab\u67e5\u83b7\u3002\u5728\u8d77\u8bc9\u4e66\u4e2d\uff0c\u68c0\u5bdf\u673a\u5173\u8ba4\u4e3a\u4e09\u540d\u88ab\u544a\u4eba\u7684\u4e0a\u8ff0\u884c\u4e3a\u5df2\u4e0d\u662f\u4e00\u822c\u7684\u8fdd\u6cd5\u884c\u4e3a\uff0c\u800c\u662f\u6d89\u5acc\u8d70\u79c1\u73cd\u8d35\u52a8\u7269\u5236\u54c1\u7f6a\u3002\n\u3000\u3000\u636e\u4e86\u89e3\uff0c\u6d4e\u5357\u6d77\u5173\u67e5\u6263\u76842800\u679a\u8774\u8776(\u6b7b\u4f53)\u4e2d\u67091244\u679a\u5c5e\u4e8e\u300a\u6fd2\u5371\u91ce\u751f\u52a8\u690d\u7269\u79cd\u56fd\u9645\u8d38\u6613\u516c\u7ea6\u300b\u9644\u5f55II\u4fdd\u62a4\u7269\u79cd\uff0c\u6db5\u76d6\u7fe0\u53f6\u7ea2\u9888\u51e4\u8776\u3001\u88f3\u51e4\u8776\u3001\u9ec4\u70b9\u9e1f\u7ffc\u51e4\u8776\u3001\u73c2\u88f3\u51e4\u8776\u3001\u9ec4\u7eff\u9e1f\u7ffc\u51e4\u8776\u3001\u77f3\u51a2\u9e1f\u7ffc\u51e4\u8776\u3001\u7eff\u9e1f\u7ffc\u51e4\u8776\u7b49\u591a\u4e2a\u79cd\u5c5e\u3002\u8fd8\u670920\u679a\u5415\u5b8b\u51e4\u8776\u66f4\u662f\u5c5e\u4e8e\u300a\u516c\u7ea6\u300b\u9644\u5f55I\u4e2d\u7684\u4fdd\u62a4\u7269\u79cd\u3002\n\u3000\u3000\u5728\u67e5\u83b7\u7684\u8774\u8776(\u6b7b\u4f53)\u4e2d\uff0c\u5415\u5b8b\u51e4\u8776\u4ef7\u503c\u6700\u9ad8\uff0c\u5236\u6210\u6210\u54c1\u540e\u4ef7\u503c\u53ef\u8fbe\u4e07\u4f59\u5143\u3002\u7fe0\u53f6\u7ea2\u9888\u51e4\u8776\u4ef7\u503c\u4ea6\u4e0d\u83f2\uff0c\u5b83\u7684\u8776\u7fc5\u4e0a\u904d\u5e03\u91d1\u7eff\u8272\u9cde\u7247\uff0c\u5728\u9633\u5149\u7167\u5c04\u4e0b\u53d8\u5e7b\u591a\u5f69\uff0c\u534e\u8d35\u7f8e\u4e3d\uff0c\u662f\u4e16\u754c\u4e0a\u975e\u5e38\u73cd\u7a00\u7684\u8776\u7c7b\u6606\u866b\u4e4b\u4e00\uff0c\u4e5f\u662f\u9a6c\u6765\u897f\u4e9a\u7684\u56fd\u8776\u3002\n\u3000\u3000\u6d89\u6848\u7269\u54c1\u4ef7\u503c\u4e0d\u83f2\n\u3000\u30002\u4e07\u6240\u8d2d\u8774\u8776\u7ecf\u9274\u5b9a\u503c150\u4e07\n\u3000\u3000\u5f53\u516c\u8bc9\u65b9\u8bf4\u51fa\u6d89\u6848\u7269\u54c1\u7684\u4ef7\u503c\u65f6\uff0c\u4e09\u540d\u88ab\u544a\u4eba\u5927\u5403\u4e00\u60ca\uff1a\u5f53\u521d\u4ec5\u82b1\u8d393647.8\u7f8e\u5143(\u7ea62\u4e07\u4f59\u5143\u4eba\u6c11\u5e01)\u8d2d\u4e70\u7684\u539f\u8776\uff0c\u7ecf\u4e91\u5357\u6fd2\u79d1\u59d4\u53f8\u6cd5\u9274\u5b9a\u4e2d\u5fc3\u9274\u5b9a\uff0c\u4ef7\u503c\u9ad8\u8fbe\u4eba\u6c11\u5e01150\u4e07\u4f59\u5143!\u9274\u5b9a\u4eba\u6301\u6709\u4e91\u5357\u7701\u53f8\u6cd5\u5385\u9881\u53d1\u7684\u91ce\u751f\u52a8\u7269\u53f8\u6cd5\u9274\u5b9a\u6267\u4e1a\u8bc1\u4e66\u3002\n\u3000\u3000\u8bb0\u8005\u4e86\u89e3\u5230\uff0c\u6839\u636e\u6d4e\u5357\u6d77\u5173\u7f09\u79c1\u5c40\u7684\u59d4\u6258\uff0c\u4e91\u5357\u6fd2\u79d1\u59d4\u53f8\u6cd5\u9274\u5b9a\u4e2d\u5fc3\u5bf9\u6d89\u6848\u8774\u8776\u8fdb\u884c\u4e86\u79cd\u5c5e\u3001\u4fdd\u62a4\u7ea7\u522b\u548c\u4ef7\u503c\u9274\u5b9a\uff0c\u5148\u540e\u51fa\u5177\u591a\u4efd\u9274\u5b9a\u610f\u89c1\u4e66\uff0c\u4f9d\u6cd5\u9274\u5b9a\u51fa\u5c5e\u4e8e\u300a\u6fd2\u5371\u91ce\u751f\u52a8\u690d\u7269\u79cd\u56fd\u9645\u8d38\u6613\u516c\u7ea6\u300b\u9644\u5f55I\u3001\u9644\u5f55II\u7684\u6fd2\u5371\u8774\u8776\u5171\u8ba11264\u53ea\u3002\u540c\u65f6\uff0c\u4f9d\u636e\u76f8\u5173\u6cd5\u5f8b\u6838\u5b9a\u6d89\u6848\u8774\u8776\u5236\u54c1\u4ef7\u503c\u5171\u8ba1150.9\u4e07\u4f59\u5143\u3002\n\u3000\u3000\u201c\u9274\u5b9a\u673a\u6784\u3001\u9274\u5b9a\u4eba\u5177\u6709\u5408\u6cd5\u8d44\u8d28\uff0c\u9274\u5b9a\u7a0b\u5e8f\u3001\u65b9\u6cd5\u548c\u4ef7\u503c\u8ba1\u7b97\u65b9\u5f0f\u4e8e\u6cd5\u6709\u636e\uff0c\u9274\u5b9a\u7ed3\u679c\u5e94\u4e88\u786e\u8ba4\u3002\u201d\u672c\u6848\u627f\u529e\u6cd5\u5b98\u738b\u56fd\u8f89\u8bf4\uff0c\u4e0d\u8fc7\u7531\u4e8e\u63a7\u8fa9\u53cc\u65b9\u5bf9\u6d89\u6848\u8774\u8776\u7684\u4ef7\u503c\u8ba4\u5b9a\u5b58\u5728\u8f83\u5927\u4e89\u8bae\uff0c\u4e3a\u4e86\u7a33\u59a5\u8d77\u89c1\uff0c\u6d4e\u5357\u4e2d\u9662\u5546\u8bf7\u6797\u4e1a\u5385\u6307\u6d3e\u4e13\u5bb6\u53c2\u52a0\u7531\u5408\u8bae\u5ead\u7ec4\u7ec7\u8bc9\u8bbc\u5404\u65b9\u53c2\u52a0\u7684\u5ea7\u8c08\u4f1a\u3002\n\u3000\u3000\u89e6\u72af\u6cd5\u5f8b\u8b66\u793a\u4f17\u4eba\n\u3000\u3000\u4e09\u4eba\u72af\u8d70\u79c1\u73cd\u8d35\u52a8\u7269\u5236\u54c1\u7f6a\n\u3000\u3000\u738b\u56fd\u8f89\u4ecb\u7ecd\uff0c\u4ece2015\u5e7410\u6708\u81f32016\u5e741\u6708\u95f4\uff0c\u5728\u672a\u7ecf\u76f8\u5173\u90e8\u95e8\u8bb8\u53ef\u548c\u68c0\u9a8c\u68c0\u75ab\u7684\u60c5\u51b5\u4e0b\uff0c\u4e09\u540d\u88ab\u544a\u4eba\u901a\u8fc7\u4e92\u8054\u7f51\u4ece\u83f2\u5f8b\u5bbe\u3001\u5370\u5ea6\u5c3c\u897f\u4e9a\u7b49\u56fd\u8d2d\u4e70\u4e0a\u8ff0\u8774\u8776\u6807\u672c\u603b\u8ba19\u4e2a\u90ae\u5305\uff0c\u5192\u7528\u201c\u88d9\u5b50\u201d\u201c\u827a\u672f\u54c1\u201d\u7b49\u540d\u4e49\u901a\u8fc7\u4e2d\u56fd\u90ae\u653f\u516c\u53f8\u7b49\u6e20\u9053\u90ae\u5bc4\u8d70\u79c1\u8fdb\u53e3\uff0c\u6b32\u7ecf\u52a0\u5de5\u3001\u88c5\u88f1\u540e\u5012\u5356\u725f\u5229\u3002\u96644\u4e2a\u56fd\u9645\u90ae\u5305\u88ab\u6d77\u5173\u4f9d\u6cd5\u67e5\u5904\u5916\uff0c\u5176\u4f59\u5747\u5df2\u59a5\u6295\u3002\n\u3000\u3000\u8fd1\u65e5\uff0c\u6d4e\u5357\u4e2d\u9662\u5bf9\u672c\u6848\u4f5c\u51fa\u4e00\u5ba1\u5224\u51b3\uff1a\u9b4f\u67d0\u72af\u8d70\u79c1\u73cd\u8d35\u52a8\u7269\u5236\u54c1\u7f6a\uff0c\u5224\u5904\u6709\u671f\u5f92\u521110\u5e74\uff0c\u5e76\u5904\u6ca1\u6536\u4e2a\u4eba\u8d22\u4ea75\u4e07\u5143;\u5f90\u67d0\u72af\u8d70\u79c1\u73cd\u8d35\u52a8\u7269\u5236\u54c1\u7f6a\uff0c\u5224\u5904\u6709\u671f\u5f92\u52117\u5e74\uff0c\u5e76\u5904\u7f5a\u91d14\u4e07\u5143;\u6731\u67d0\u72af\u8d70\u79c1\u73cd\u8d35\u52a8\u7269\u5236\u54c1\u7f6a\uff0c\u5224\u5904\u6709\u671f\u5f92\u52115\u5e74\uff0c\u5e76\u5904\u7f5a\u91d12\u4e07\u5143\u3002\u6d89\u6848\u8774\u8776\uff0c\u5219\u7531\u6263\u62bc\u673a\u5173\u6d4e\u5357\u6d77\u5173\u7f09\u79c1\u5c40\u4f9d\u6cd5\u5904\u7f6e\u3002\n\u3000\u3000\u738b\u56fd\u8f89\u4ecb\u7ecd\uff0c\u672c\u6848\u7684\u4e09\u540d\u88ab\u544a\u4eba\u90fd\u662f80\u540e\u300190\u540e\uff0c\u5e0c\u671b\u901a\u8fc7\u8fd9\u8d77\u6848\u4ef6\u80fd\u591f\u7ed9\u4e00\u4e9b\u52a8\u690d\u7269\u7231\u597d\u8005\u4ee5\u8b66\u793a\uff0c\u201c\u541b\u5b50\u7231\u8d22\uff0c\u53d6\u4e4b\u6709\u9053\u3002\u5b88\u4f4f\u6cd5\u5f8b\u7684\u5e95\u7ebf\uff0c\u624d\u80fd\u53d7\u5230\u6cd5\u5f8b\u7684\u4fdd\u62a4\u3002\u201d"}, {"emotion": "neg", "id": "01066774568555a27e282d2c0410ed579c", "c_t": "\u6c99\u7279\u5411\u4e5f\u95e8\u9996\u90fd\u53d1\u5c045\u679a\u5bfc\u5f39 \u7ed3\u679c\u70b8\u5230\u4e86\u81ea\u5df1\u7684\u4f7f\u9986\u636e\u4e5f\u95e8\u80e1\u585e\u6b66\u88c5\u63a7\u5236\u7684\u8428\u5df4\u901a\u8baf\u793e\u62a5\u9053\uff0c\u6c99\u7279\u8054\u519b\u5f53\u5730\u65f6\u95f46\u65e5\u591c\u95f4\u518d\u6b21\u8f70\u70b8\u4e86\u4e5f\u95e8\u9996\u90fd\u8428\u90a3\uff0c\u5171\u6709\u4e94\u679a\u5bfc\u5f39\u51fb\u4e2d\u5e02\u533a\uff0c\u5176\u4e2d\u4e24\u679a\u6216\u4e09\u679a\u5bfc\u5f39\u51fb\u4e2d\u4e86\u6c99\u7279\u9a7b\u4e5f\u95e8\u5927\u4f7f\u9986\u9986\u5740\uff0c\u5269\u4f59\u4e24\u679a\u6216\u4e09\u679a\u5bfc\u5f39\u51fb\u4e2d\u4e86\u76f8\u90bb\u7684\u4e5f\u95e8\u7279\u522b\u5b89\u5168\u90e8\u961f\u603b\u90e8\u3002\n2014\u5e74\u4e5f\u95e8\u518d\u6b21\u9677\u5165\u5185\u4e71\u540e\uff0c\u6c99\u7279\u4f7f\u9986\u4eba\u5458\u4e8e\u5f53\u5e74\u5e74\u5e95\u5168\u90e8\u64a4\u79bb\u4e5f\u95e8\u9996\u90fd\u8428\u90a3\u3002\u968f\u540e\u6c99\u7279\u8054\u519b\u4e8e2015\u5e743\u670826\u65e5\u5c55\u5f00\u4e86\u9488\u5bf9\u4e5f\u95e8\u80e1\u585e\u6b66\u88c5\u548c\u524d\u603b\u7edf\u8428\u5229\u8d6b\u7684\u519b\u4e8b\u884c\u52a8\u3002\u6b64\u540e\u4e0d\u4e45\u80e1\u585e\u6b66\u88c5\u8fdb\u5165\u5e76\u5360\u9886\u4e86\u6c99\u7279\u4f7f\u9986\u5728\u8428\u90a3\u7684\u9a7b\u5730\u3002"}, {"emotion": "neg", "id": "012228b50d28dd08ae09827d3a1b3ee2e2", "c_t": "\u6d77\u5916\u534e\u5a92\u8d70\u8fdb\u65b0\u7586\u5175\u56e2 \u88ab\u62a4\u6797\u5458\u7684\u6545\u4e8b\u6df1\u6df1\u6253\u52a8\u3000\u3000\u4e2d\u65b0\u7f51\u4e4c\u9c81\u6728\u9f5011\u67087\u65e5\u7535(\u8881\u6676 \u8d75\u96c5\u654f) \u201c\u62a4\u6797\u5458\u9a6c\u6653\u534e\u662f\u7ee7\u627f\u7236\u4eb2\u7684\u9057\u613f\uff0c\u4e00\u76f4\u5b88\u62a4\u7740\u8fd9\u7247\u9632\u98ce\u963b\u6c99\u57fa\u5e72\u6797\u3002\u73b0\u5728\uff0c\u4ed6\u53c8\u5e0c\u671b13\u5c81\u7684\u513f\u5b50\u957f\u5927\u4ee5\u540e\uff0c\u80fd\u7ee7\u7eed\u5b88\u62a4\u8fd9\u7247\u9632\u98ce\u963b\u6c99\u57fa\u5e72\u6797\u3002\u5f53\u6211\u542c\u5230\u8fd9\u4e9b\u6545\u4e8b\u65f6\uff0c\u771f\u7684\u662f\u773c\u775b\u90fd\u6e7f\u6da6\u4e86\u3002\u4ed6\u4eec\u7236\u5b50\u4e8c\u4eba\u662f\u2018\u732e\u4e86\u9752\u6625\u3001\u732e\u7ec8\u8eab\uff0c\u732e\u4e86\u7ec8\u8eab\uff0c\u732e\u5b50\u5b59\u2019\u3002\u201d11\u67086\u65e5\uff0c\u65e5\u672c\u300a\u7559\u5b66\u751f\u65b0\u95fb\u7f51\u300b\u526f\u603b\u7f16\u8f91\u9f99\u4e3d\u534e\u5728\u63a5\u53d7\u8bb0\u8005\u91c7\u8bbf\u65f6\uff0c\u611f\u89e6\u9887\u6df1\u3002\n\u3000\u3000\u5f53\u65e5\uff0c\u7531\u56fd\u52a1\u9662\u65b0\u95fb\u529e\u516c\u5ba4\u3001\u65b0\u7586\u751f\u4ea7\u5efa\u8bbe\u5175\u56e2\u3001\u4e2d\u56fd\u65b0\u95fb\u793e\u5171\u540c\u4e3b\u529e\uff0c\u7531\u65b0\u7586\u5175\u56e2\u65b0\u95fb\u529e\u516c\u5ba4\u548c\u4e2d\u56fd\u65b0\u95fb\u793e\u65b0\u7586\u5206\u793e\u627f\u529e\u7684\u201c\u884c\u8d70\u4e2d\u56fd\u00b72017\u6d77\u5916\u534e\u6587\u5a92\u4f53\u8d70\u8fdb\u65b0\u7586\u5175\u56e2\u201d\u91c7\u8bbf\u56e2\u6765\u5230\u7b2c\u516b\u5e08\u4e00\u4e94\u3007\u56e2\uff0c\u8d70\u8fdb\u4e2d\u56fd\u7b2c\u4e8c\u5927\u6c99\u6f20\u2014\u2014\u53e4\u5c14\u73ed\u901a\u53e4\u7279\u6c99\u6f20\u8fb9\u7f18\uff0c\u53c2\u89c2\u3001\u91c7\u8bbf\u8be5\u56e2\u7684\u9632\u98ce\u56fa\u6c99\u6cbb\u6c99\u5de5\u7a0b\u3002\n{IMG:1}\n\u56fe\u4e3a\u7b2c\u516b\u5e08\u4e00\u4e94\u3007\u56e2\u9632\u98ce\u963b\u6c99\u57fa\u5e72\u6797\u3002\u3000\u8881\u6676 \u6444\n\u3000\u3000\u636e\u4ecb\u7ecd\uff0c\u4e00\u4e94\u3007\u56e2\u662f\u7b2c\u516b\u5e08\u6700\u5317\u7aef\u7684\u4e00\u4e2a\u56e2\u573a\uff0c\u6df1\u5165\u53e4\u5c14\u73ed\u901a\u53e4\u7279\u6c99\u6f2070\u4f59\u516c\u91cc\uff0c\u4e1c\u897f\u5317\u4e09\u9762\u73af\u6c99\u3002\u8be5\u56e2\u4ece\u5355\u7eaf\u7684\u690d\u6811\u9020\u6797\uff0c\u8f6c\u5411\u690d\u6811\u548c\u9632\u6c99\u76f8\u7ed3\u5408\u7684\u65b9\u5f0f\uff0c\u5f00\u59cb\u5bf9\u6c99\u6f20\u8fb9\u7f18\u8fdb\u884c\u4e25\u683c\u5c01\u80b2\u4fdd\u62a4\uff0c\u4eba\u5de5\u79cd\u690d\u68ad\u68ad\u3001\u80e1\u6768\uff0c\u5728\u6c99\u6f20\u8179\u5730\u6784\u7b51\u8d77\u4e86\u4ee5\u6797\u4e3a\u4e3b\uff0c\u6797\u704c\u7ed3\u5408\u7684\u56db\u7ea7\u751f\u6001\u9632\u62a4\u201c\u7eff\u8272\u5c4f\u969c\u201d\uff0c\u4ece\u6839\u672c\u4e0a\u963b\u6b62\u4e86\u6d41\u6c99\u7684\u79fb\u52a8\u3002\u591a\u5e74\u96be\u89c1\u7684\u91ce\u751f\u52a8\u7269\u7ecf\u5e38\u5728\u6c99\u6f20\u5468\u56f4\u51fa\u6ca1\uff0c\u82cd\u9e70\u3001\u91ce\u9e21\u3001\u91ce\u5154\u3001\u9ec4\u7f8a\u7b49\u591a\u79cd\u56fd\u5bb6\u7ea7\u4fdd\u62a4\u52a8\u7269\u53ca\u91ce\u72fc\u3001\u72d0\u72f8\u3001\u6591\u9e20\u3001\u571f\u767e\u7075\u7b49\u767e\u4f59\u79cd\u52a8\u7269\uff0c\u65f6\u5e38\u5728\u56e2\u573a\u5468\u8fb9\u7684\u8352\u6f20\u4fdd\u62a4\u533a\u5185\u51fa\u73b0\u3002\n\u3000\u3000\u6765\u81ea\u7f8e\u56fd\u3001\u52a0\u62ff\u5927\u3001\u6fb3\u5927\u5229\u4e9a\u3001\u65e5\u672c\u7b49\u5341\u4f59\u4e2a\u56fd\u5bb6\u7684\u6d77\u5916\u534e\u6587\u5a92\u4f53\u9ad8\u5c42\u4ee3\u8868\u5728\u53c2\u89c2\u7b2c\u516b\u5e08\u4e00\u4e94\u3007\u56e2\u65f6\uff0c\u542c\u5b8c\u62a4\u6797\u5458\u9a6c\u6653\u534e\u7684\u6545\u4e8b\u540e\u90fd\u5f88\u611f\u52a8\u3002\u4eca\u5e7445\u5c81\u7684\u62a4\u6797\u5458\u9a6c\u6653\u534e\uff0c1995\u5e74\u90e8\u961f\u8f6c\u4e1a\u540e\uff0c\u5728\u7236\u4eb2\u7684\u8981\u6c42\u4e0b\uff0c\u653e\u5f03\u4e86\u6bd4\u8f83\u597d\u7684\u5de5\u4f5c\uff0c\u5f00\u59cb\u5b88\u62a4\u8fd9\u4e03\u516b\u767e\u4ea9\u5730\u7684\u9632\u98ce\u963b\u6c99\u57fa\u5e72\u6797\uff0c\u81f3\u4eca\u5df2\u670922\u5e74\u3002\n{IMG:2}\n\u56fe\u4e3a\u6d77\u5916\u534e\u6587\u5a92\u4f53\u4ee3\u8868\u4e0e\u62a4\u6797\u5458\u9a6c\u6653\u534e\u5408\u5f71\u3002\u3000\u8881\u6676 \u6444\n\u3000\u3000\u201c\u6211\u513f\u5b50\u4eca\u5e7413\u5c81\uff0c\u4ece\u5c0f\u5c31\u662f\u5728\u8fd9\u7247\u6797\u5b50\u91cc\u957f\u5927\u7684\uff0c\u4e00\u76f4\u5230\u4e03\u516b\u5c81\uff0c\u90fd\u6ca1\u6709\u89c1\u8fc7\u751f\u4eba\uff0c\u89c1\u5230\u4eba\u5c31\u5f80\u8eab\u540e\u8eb2\uff0c\u6027\u683c\u6709\u4e9b\u817c\u8146\u3002\u201d\u5728\u8c08\u5230\u513f\u5b50\u65f6\uff0c\u9a6c\u6653\u534e\u544a\u8bc9\u8bb0\u8005\uff0c\u5728\u7236\u4eb2\u624b\u91cc\u63a5\u8fc7\u62a4\u6797\u5de5\u4f5c\u7684\u4efb\u52a1\uff0c\u4ed6\u5c31\u8981\u4e16\u4e16\u4ee3\u4ee3\u7684\u4f20\u4e0b\u53bb\uff0c\u5e0c\u671b\u513f\u5b50\u4ee5\u540e\u80fd\u63a5\u8fc7\u624b\u91cc\u7684\u63a5\u529b\u68d2\uff0c\u56de\u5230\u5bb6\u4e61\u5b88\u62a4\u8fd9\u7247\u6797\u5730\uff0c\u5b88\u62a4\u5bb6\u4e61\u3002\n\u3000\u3000\u9f99\u4e3d\u534e\u8bf4\uff1a\u201c\u867d\u7136\u6211\u662f\u7b2c\u4e00\u6b21\u6765\u5230\u65b0\u7586\u5175\u56e2\uff0c\u4f46\u5bf9\u4e8e\u65b0\u7586\u5175\u56e2\u7684\u60c5\u8282\u8d8a\u6765\u8d8a\u6df1\uff0c\u56de\u53bb\u6211\u8981\u901a\u8fc7\u6211\u4eec\u7684\u591a\u5a92\u4f53\u65b9\u5f0f\uff0c\u5c06\u65b0\u7586\u5175\u56e2\u7684\u4eba\u3001\u65b0\u7586\u5175\u56e2\u4eba\u7684\u6545\u4e8b\u548c\u65b0\u7586\u5175\u56e2\u4eba\u7684\u7cbe\u795e\uff0c\u5411\u66f4\u591a\u7684\u4eba\u8fdb\u884c\u5ba3\u4f20\u3002\u201d\n\u3000\u3000\u7f57\u9a6c\u5c3c\u4e9a\u300a\u6b27\u6d32\u4fa8\u62a5\u300b\u526f\u603b\u7f16\u5b59\u96e8\u6885\u544a\u8bc9\u8bb0\u8005\uff1a\u201c\u4eca\u5929\uff0c\u6211\u770b\u5230\u5b88\u62a4\u9632\u98ce\u963b\u6c99\u57fa\u5e72\u6797\u7684\u9a6c\u6653\u534e\uff0c\u4ed6\u4eec\u7236\u5b50\u4e24\u4ee3\u4eba\u7684\u6545\u4e8b\u6df1\u6df1\u5730\u6253\u52a8\u4e86\u6211\uff0c\u6211\u4e00\u5b9a\u8981\u597d\u597d\u5730\u62a5\u9053\u4ed6\u4eec\u7684\u4e8b\u8ff9\uff0c\u62a5\u9053\u65b0\u7586\u5175\u56e2\u4eba\u7684\u6545\u4e8b\u3002\u201d(\u5b8c)"}, {"emotion": "neg", "id": "01853d9a216cb4a965f19a182c1f3b5ae1", "c_t": "\u660c\u6c5f\u9ece\u65cf\u81ea\u6cbb\u53bf\u59d4\u4e66\u8bb0\u9ec4\u91d1\u57ce\u770b\u671b\u773c\u75be\u513f\u7ae5\u97e9\u8bd7\u742a\u4eba\u6c11\u7f51\u6d77\u53e311\u67086\u65e5\u7535 \uff08\u5b5f\u51e1\u76db\u3001\u738b\u6d77\u4e91\uff09\u201c\u4e0b\u4e00\u6b65\uff0c\u660c\u6c5f\u9ece\u65cf\u81ea\u6cbb\u53bf\u53bf\u59d4\u53bf\u653f\u5e9c\u5c06\u6309\u7167\u6d77\u5357\u7701\u59d4\u4e66\u8bb0\u5218\u8d50\u8d35\u7684\u6307\u793a\uff0c\u628a\u97e9\u8bd7\u742a\u7ee7\u7eed\u6cbb\u7597\u597d\uff0c\u8fd8\u8981\u628a\u5168\u53bf\u7684\u5931\u660e\u513f\u7ae5\u8fdb\u884c\u6cbb\u7597\u3002\u201d11\u67086\u65e5\uff0c\u660c\u6c5f\u9ece\u65cf\u81ea\u6cbb\u53bf\u53bf\u59d4\u4e66\u8bb0\u9ec4\u91d1\u57ce\u5728\u4e2d\u5c71\u5927\u5b66\u4e2d\u5c71\u773c\u79d1\u4e2d\u5fc3\u6d77\u5357\u773c\u79d1\u533b\u9662\u770b\u671b\u738b\u4e0b\u4e61\u4e09\u6d3e\u6751\u97e9\u8bd7\u742a\u773c\u75c5\u7684\u5eb7\u590d\u60c5\u51b5\u65f6\u5bf9\u8bb0\u8005\u8bf4\u9053\u3002\u201c\u76ee\u524d\uff0c\u660c\u6c5f\u9ece\u65cf\u81ea\u6cbb\u53bf\u5df2\u7ecf\u542f\u52a8\u4e86\u513f\u7ae5\u773c\u775b\u5065\u5eb7\u7684\u5173\u7231\u884c\u52a8\uff0c\u521d\u6b65\u67e5\u51fa\uff0c\u5168\u53bf\u670932\u540d\u513f\u7ae5\u6709\u773c\u775b\u5065\u5eb7\u95ee\u9898\uff0c\u53bf\u91cc\u6b63\u5728\u7ec4\u7ec7\u7701\u773c\u79d1\u533b\u9662\u4e13\u5bb6\uff0c\u5728\u6d77\u5357\u7701\u536b\u8ba1\u59d4\u7684\u6307\u5bfc\u4e0b\uff0c\u5168\u529b\u4ee5\u8d74\u4f7f\u5931\u660e\u513f\u7ae5\u91cd\u89c1\u5149\u660e\u3002\u201d\n11\u67086\u65e5\u4e0a\u5348\uff0c\u660c\u6c5f\u9ece\u65cf\u81ea\u6cbb\u53bf\u4e3e\u884c\u4e86\u9752\u5c11\u5e74\u513f\u7ae5\u773c\u5065\u5eb7\u7b5b\u67e5\u884c\u52a8\u542f\u52a8\u4eea\u5f0f\u3002\u5f53\u5929\u4e0b\u5348\uff0c\u9ec4\u91d1\u57ce\u8d76\u5230\u4e2d\u5c71\u5927\u5b66\u4e2d\u5c71\u773c\u79d1\u4e2d\u5fc3\u6d77\u5357\u773c\u79d1\u533b\u9662\uff0c\u770b\u671b\u97e9\u8bd7\u742a\u3002\u5728\u533b\u9662\uff0c\u9ec4\u91d1\u57ce\u542c\u53d6\u4e86\u533b\u751f\u5bf9\u97e9\u8bd7\u742a\u6cbb\u7597\u7684\u4ecb\u7ecd\u3002\u5e76\u5631\u5490\u97e9\u8bd7\u742a\u7684\u7236\u4eb2\u97e9\u4e9a\u547d\uff0c\u4e00\u5b9a\u8981\u6309\u7167\u533b\u751f\u8981\u6c42\u5b9a\u671f\u6765\u533b\u9662\u590d\u67e5\uff0c\u79ef\u6781\u914d\u5408\u6cbb\u7597\u65b9\u6848\uff0c\u4e89\u53d6\u8ba9\u97e9\u8bd7\u742a\u7684\u89c6\u529b\u65e9\u65e5\u6062\u590d\u5230\u6b63\u5e38\u6c34\u5e73\u3002\n\u636e\u4e86\u89e3\uff0c\u97e9\u8bd7\u742a\u4eca\u5e744\u5c81\u534a\uff0c\u660c\u6c5f\u9ece\u65cf\u81ea\u6cbb\u53bf\u738b\u4e0b\u4e61\u4e09\u6d3e\u6751\u4eba\uff0c\u73b0\u5c31\u8bfb\u4e8e\u738b\u4e0b\u4e61\u4e2d\u5fc3\u5e7c\u513f\u56ed\u3002\u7ecf\u5e7f\u5dde\u513f\u7ae5\u773c\u79d1\u533b\u9662\u68c0\u67e5\uff0c\u786e\u8bca\u60a3\u6709\u5148\u5929\u6027\u53cc\u773c\u7403\u9707\u98a4\u3002\u5728\u53bf\u536b\u751f\u5c40\u7684\u534f\u8c03\u4e0b\uff0c\u6d77\u5357\u7701\u773c\u79d1\u533b\u9662\u5e38\u52a1\u526f\u9662\u957f\u949f\u5174\u6b66\u5e26\u9886\u4e13\u5bb6\u7ec4\u5bf9\u97e9\u8bd7\u742a\u8fdb\u884c\u4f1a\u8bca\uff0c\u5e76\u6839\u636e\u75c5\u60c5\u7814\u7a76\u51fa\u6cbb\u7597\u65b9\u6848\u3002\u6839\u636e\u7701\u773c\u79d1\u533b\u9662\u4e13\u5bb6\u7ed9\u51fa\u7684\u7ed3\u8bba\u548c\u6cbb\u7597\u65b9\u6848\uff0c\u5bf9\u97e9\u8bd7\u742a\u91c7\u53d6\u914d\u6234\u773c\u955c\u7ea0\u6b63\u548c\u5f31\u89c6\u6cbb\u7597\u4eea\u5eb7\u590d\u8bad\u7ec3\u7684\u6cbb\u7597\u65b9\u6848\uff0c\u8981\u8fdb\u884c\u4e24\u5e74\u7684\u6cbb\u7597\u5468\u671f\uff0c\u6bcf\u4e09\u4e2a\u6708\u4e3a\u4e00\u4e2a\u7597\u7a0b\u5e76\u5b9a\u65f6\u590d\u67e5\uff0c\u73b0\u4eca\u5df2\u6cbb\u7597\u5230\u7b2c\u56db\u4e2a\u7597\u7a0b\uff0c\u97e9\u8bd7\u742a\u7684\u773c\u775b\u4ece\u6700\u65e9\u770b\u4e0d\u5230\u73b0\u5728\u5df2\u6a21\u7cca\u5206\u8fa8\u72d7\u3001\u732b\u8fd8\u6709\u5c0f\u73a9\u5177\u7b49\uff0c\u89c6\u529b\u6062\u590d\u60c5\u51b5\u8fbe\u5230\u9884\u671f\u6548\u679c\u3002\u8be5\u53bf\u6c11\u653f\u5c40\u4ece\u201c\u53cc\u7279\u201d\u8d44\u91d1\u5b89\u63926\u4e07\u5143\u4e13\u6b3e\u7528\u4e8e\u4fdd\u969c\u8be5\u5bb6\u5ead\u7684\u75c5\u60c5\u6cbb\u7597\u5f00\u652f\uff0c\u5e76\u5b89\u6392\u4e13\u4eba\u5168\u7a0b\u8d1f\u8d23\u63a5\u9001\u6d77\u53e3\u6cbb\u7597\u3002\n(\u8d23\u7f16\uff1a\u738b\u5b81\u3001\u848b\u6210\u67f3)"}, {"emotion": "neg", "id": "01eafa503bcf57b4f111e01d52904a8788", "c_t": "\u5468\u9521\u73ae\u56de\u5e94\u662f\u5426\u53c2\u9009\u65b0\u5317\u5e02\u957f\uff1a\u614e\u91cd\u8003\u8651\u00a0\u00a0\u00a0\u00a0\u4eba\u6c11\u7f5111\u67086\u65e5\u7535 \u636e\u53f0\u6e7e\u300a\u8054\u5408\u62a5\u300b\u62a5\u9053\uff0c\u5916\u4f20\u524d\u53f0\u5317\u53bf\u957f\u5468\u9521\u73ae\u5c06\u572811\u670812\u65e5\u5ba3\u5e03\u53c2\u9009\u4e0b\u5c4a\u65b0\u5317\u5e02\u957f\uff0c\u5468\u9521\u73ae\u6628\u5929\u63a5\u53d7\u91c7\u8bbf\u8868\u793a\uff0c\u9009\u4e0d\u9009\u65b0\u5317\u5e02\u957f\uff0c\u4ed6\u73b0\u5728\u5df2\u7ecf\u8d70\u5230\u6700\u540e\u4e00\u82f1\u91cc\uff0c\u4f1a\u7528\u4e0d\u4e00\u6837\u7684\u5fc3\u6001\u4e0e\u4f5c\u6cd5\uff0c\u614e\u91cd\u8003\u8651\u3002\n\u00a0\u00a0\u00a0\u00a0\u5468\u9521\u73ae\u6628\u5929\u8868\u793a\uff0c\u6709\u4e9b\u670b\u53cb\u786e\u5b9e\u975e\u5e38\u6025\uff0c\u8fd8\u6709\u5f88\u591a\u4eba\u8bf4\uff0c\u518d\u4e0d\u8868\u6001\u5c31\u4f1a\u4e00\u76f4\u6301\u88ab\u52a8\uff0c\u60c5\u51b5\u4e0d\u5229\uff0c\u4f46\u76ee\u524d\u62a5\u5bfc\u7684\u6d88\u606f\u90fd\u662f\u88ab\u505a\u51fa\u6765\u7684\u3002\n\u00a0\u00a0\u00a0\u00a0\u5468\u9521\u73ae\u8bf4\uff0c\u5bf9\u4e8e\u53c2\u9009\u65b0\u5317\u4e8b\u957f\uff0c\u4ed6\u8fd8\u5728\u51c6\u5907\uff0c\u4f46\u73b0\u5728\u5df2\u7ecf\u8d70\u5230\u6700\u540e\u4e00\u82f1\u91cc\uff0c\u8fd8\u8981\u5f81\u8be2\u4e00\u4e9b\u672a\u6765\u5bf9\u4ed6\u505a\u4eba\u505a\u4e8b\u6709\u660e\u786e\u63d0\u793a\u7684\u56e2\u4f53\u53ca\u4eba\u58eb\uff0c\u542c\u542c\u5927\u5bb6\u7684\u610f\u89c1\u3002\n\u00a0\u00a0\u00a0\u00a0\u56fd\u6c11\u515a\u7ec4\u53d1\u4f1a\u4e3b\u59d4\u674e\u54f2\u534e\u6628\u5929\u8bf4\uff0c2018\u53bf\u5e02\u957f\u63d0\u540d\u529e\u6cd5\u5df2\u7ecf\u4e2d\u5e38\u4f1a\u901a\u8fc7\uff0c\u515a\u5185\u63d0\u540d\u6709\u4e00\u5b9a\u673a\u5236\uff0c\u65b0\u5317\u5e02\u957f\u4e5f\u662f\u6309\u7167\u673a\u5236\u8d70\uff0c\u7b49\u53c2\u9009\u4eba\u6b63\u5f0f\u8868\u8fbe\u53c2\u9009\u610f\u613f\u540e\uff0c\u5c31\u5148\u8fdb\u884c\u534f\u8c03\uff0c\u534f\u8c03\u4e0d\u6210\u5c31\u529e\u521d\u9009\u3002"}, {"emotion": "neg", "id": "010c74f5fadf709d96ebbcc3a3282b732b", "c_t": "\u672a\u6309\u89c4\u5b9a\u8fdb\u884c\u536b\u751f\u68c0\u6d4b \u5317\u4eac\u65e7\u5bab\u9526\u5c1a\u7f8e\u53d1\u5e97\u88ab\u8b66\u544a\u5343\u9f99\u7f51\u5317\u4eac11\u67087\u65e5\u8baf \u5343\u9f99\u7f51\u8bb0\u8005\u4ece\u5317\u4eac\u5e02\u536b\u751f\u76d1\u7763\u6240\u4e86\u89e3\u5230\uff0c\u5317\u4eac\u5e02\u536b\u751f\u76d1\u7763\u624011\u67086\u65e5\u53d1\u5e03\u76842017\u5e7410\u6708\u4efd\u5317\u4eac\u536b\u751f\u76d1\u7763\u884c\u653f\u5904\u7f5a\u516c\u793a\u663e\u793a\uff0c\u5317\u4eac\u65e7\u5bab\u9526\u5c1a\u7f8e\u53d1\u5e97\u56e0\u8fdd\u53cd\u300a\u516c\u5171\u573a\u6240\u536b\u751f\u7ba1\u7406\u6761\u4f8b\u5b9e\u65bd\u7ec6\u5219\u300b\u76f8\u5173\u6761\u4f8b\u88ab\u8b66\u544a\u3002\n\u636e\u4e86\u89e3\uff0c\u5317\u4eac\u65e7\u5bab\u9526\u5c1a\u7f8e\u53d1\u5e97\u8fdd\u53cd\u4e86\u300a\u516c\u5171\u573a\u6240\u536b\u751f\u7ba1\u7406\u6761\u4f8b\u5b9e\u65bd\u7ec6\u5219\u300b\u7b2c\u4e09\u5341\u516d\u6761\u7b2c\uff08\u4e00\uff09\u9879\uff0c\u8be5\u9879\u89c4\u5b9a\u663e\u793a\uff0c\u516c\u5171\u573a\u6240\u7ecf\u8425\u8005\"\u672a\u6309\u7167\u89c4\u5b9a\u5bf9\u516c\u5171\u573a\u6240\u7684\u7a7a\u6c14\u3001\u5fae\u5c0f\u6c14\u5019\u3001\u6c34\u8d28\u3001\u91c7\u5149\u3001\u7167\u660e\u3001\u566a\u58f0\u3001\u987e\u5ba2\u7528\u54c1\u7528\u5177\u7b49\u8fdb\u884c\u536b\u751f\u68c0\u6d4b\u7684\"\uff0c\u6839\u636e\u6761\u4f8b\uff0c\u7531\u53bf\u7ea7\u4ee5\u4e0a\u5730\u65b9\u4eba\u6c11\u653f\u5e9c\u536b\u751f\u884c\u653f\u90e8\u95e8\u8d23\u4ee4\u9650\u671f\u6539\u6b63\uff0c\u7ed9\u4e88\u8b66\u544a\uff0c\u5e76\u53ef\u5904\u4ee5\u4e8c\u5343\u5143\u4ee5\u4e0b\u7f5a\u6b3e\uff1b\u903e\u671f\u4e0d\u6539\u6b63\uff0c\u9020\u6210\u516c\u5171\u573a\u6240\u536b\u751f\u8d28\u91cf\u4e0d\u7b26\u5408\u536b\u751f\u6807\u51c6\u548c\u8981\u6c42\u7684\uff0c\u5904\u4ee5\u4e8c\u5343\u5143\u4ee5\u4e0a\u4e8c\u4e07\u5143\u4ee5\u4e0b\u7f5a\u6b3e\uff1b\u60c5\u8282\u4e25\u91cd\u7684\uff0c\u53ef\u4ee5\u4f9d\u6cd5\u8d23\u4ee4\u505c\u4e1a\u6574\u987f\uff0c\u76f4\u81f3\u540a\u9500\u536b\u751f\u8bb8\u53ef\u8bc1\u3002  \n\u5317\u4eac\u5e02\u536b\u751f\u76d1\u7763\u6240\u4f9d\u636e\u300a\u516c\u5171\u573a\u6240\u536b\u751f\u7ba1\u7406\u6761\u4f8b\u5b9e\u65bd\u7ec6\u5219\u300b\uff0c\u7ed9\u4e88\u5317\u4eac\u65e7\u5bab\u9526\u5c1a\u7f8e\u53d1\u5e97\u8b66\u544a\u5904\u7f5a\u3002"}, {"emotion": "neg", "id": "019336819d40823cc58d212355ff3fafc3", "c_t": "\u539f\u6cb9\u4ea4\u6613\u63d0\u9192\uff1a\u53cd\u8150\u540e\u6c99\u7279\u51cf\u4ea7\u6216\u66f4\u575a\u5b9a\uff0c\u6cb9\u4ef7\u829d\u9ebb\u5f00\u82b1\u5468\u4e8c(11\u67087\u65e5)\u6b27\u5e02\u76d8\u524d\uff0c\u539f\u6cb9\u4ef7\u683c\u7a84\u5e45\u6574\u7406\uff0c\u65e9\u95f4\u7f8e\u56fd\u539f\u6cb9\u4ef7\u683c\u5fae\u5e45\u4e0a\u6da80.08\u7f8e\u5143\uff0c\u6da8\u5e450.12%\uff0c\u73b0\u62a557.32\u7f8e\u5143/\u6876\u3002\u6c99\u7279\u738b\u50a8\u7684\u53cd\u8d2a\u884c\u52a8\u5de9\u56fa\u4e86\u5176\u6743\u529b\uff0c\u589e\u52a0\u4e86\u5e02\u573a\u5bf9\u6c99\u7279\u51cf\u4ea7\u63d0\u632f\u6cb9\u4ef7\u7684\u4fe1\u5fc3\u3002\n{IMG:1}\n\u5206\u6790\u5e08\u8ba4\u4e3a\uff0c\u6c99\u7279\u738b\u50a8\u7684\u53cd\u8150\u5de9\u56fa\u4e86\u6743\u529b\uff0c\u53cd\u8150\u4e4b\u540e\u6c99\u7279\u7684\u7126\u70b9\u52bf\u5fc5\u4f1a\u8f6c\u79fb\u5230\u51cf\u4ea7\u4e0a\u6765\uff0c\u53cd\u8150\u7684\u6210\u529f\u4e5f\u8ba9\u5e02\u573a\u770b\u5230\u4e86\u6c99\u7279\u738b\u50a8\u575a\u5b9a\u652f\u6491\u6cb9\u4ef7\u7684\u51b3\u5fc3\u3002\u4e2d\u4e1c\u5730\u7f18\u5c40\u52bf\u7ee7\u7eed\u652f\u6491\u6cb9\u4ef7\n\u4e0a\u5468\u672b\u6c99\u7279\u53d1\u751f\u7684\u653f\u6cbb\u6df7\u4e71\u5f62\u52bf\u5bfc\u81f4\u539f\u6cb9\u5e02\u573a\u611f\u5230\u614c\u4e71\uff0c\u8be5\u56fd\u7684\u8bf8\u591a\u738b\u5ba4\u6210\u5458\u3001\u5927\u81e3\u548c\u77e5\u540d\u5546\u4eba\u906d\u5230\u4e86\u902e\u6355\u3002\u653f\u5e9c\u5b98\u5458\u5c06\u5176\u6b64\u6b21\u5927\u89c4\u6a21\u201c\u6e05\u6d17\u201d\u6d3b\u52a8\u79f0\u4f5c\u662f\u4e00\u573a\u53cd\u8150\u8fd0\u52a8\u3002\n\u5916\u90e8\u89c2\u5bdf\u4eba\u58eb\u8ba4\u4e3a\uff0c\u8fd9\u53ef\u80fd\u662f\u6c99\u7279\u738b\u50a8\u7a46\u7f55\u9ed8\u5fb7\u00b7\u672c\u00b7\u8428\u52d2\u66fc\u4e00\u9879\u8986\u76d6\u8303\u56f4\u66f4\u52a0\u5e7f\u6cdb\u7684\u884c\u52a8\u7684\u7ec4\u6210\u90e8\u5206\uff0c\u65e8\u5728\u5de9\u56fa\u4ed6\u5728\u6c99\u7279\u963f\u62c9\u4f2f\u56fd\u5185\u7684\u52bf\u529b\u3002\n\u6c99\u7279\u662f\u5168\u7403\u6700\u5927\u7684\u539f\u6cb9\u751f\u4ea7\u56fd\u4e4b\u4e00\uff0c\u4e5f\u662fOPEC\u7ec4\u7ec7\u5185\u5f71\u54cd\u529b\u6700\u5927\u7684\u6210\u5458\u56fd\u3002\u5206\u6790\u8ba4\u4e3a\uff0c\u6c99\u7279\u738b\u50a8\u5de9\u56fa\u6743\u529b\uff0c\u800c\u4e14\u5168\u7403\u6700\u5927\u7684\u539f\u6cb9\u51fa\u53e3\u56fd--\u6c99\u7279\u76ee\u524d\u4e5f\u6ca1\u6709\u6539\u53d8\u63d0\u632f\u6cb9\u4ef7\u7684\u653f\u7b56\u3002\u8fd9\u5bf9\u6cb9\u4ef7\u6709\u5229\u3002\n\u5206\u6790\u5e08\u8ba4\u4e3a\uff0c\u738b\u50a8\u6210\u529f\u5730\u6574\u56fa\u4e86\u6743\u529b\uff0c\u902e\u6355\u6216\u8bb8\u53ea\u662f\u8fd9\u573a\u6574\u6cbb\u7684\u201c\u7b2c\u4e00\u9636\u6bb5\u201d\uff0c\u63a5\u4e0b\u6765\u738b\u50a8\u5728\u51cf\u5c11\u5168\u7403\u5e93\u5b58\u8fc7\u5269\u65b9\u9762\u7684\u7acb\u573a\uff0c\u5e02\u573a\u90fd\u5f88\u6e05\u695a\uff0c\u738b\u50a8\u5e0c\u671b\u7ed3\u675f\u5f53\u524d\u4f9b\u5e94\u8fc7\u5269\u7684\u72b6\u6001\u3002\u5e02\u573a\u4ecd\u7136\u5bf9OPEC\u51cf\u4ea7\u62b1\u6709\u5e0c\u671b\n\u6700\u8fd1\u4ee5\u6765\uff0c\u539f\u6cb9\u4ef7\u683c\u4e00\u76f4\u90fd\u5448\u73b0\u51fa\u575a\u5b9a\u7684\u4e0a\u6da8\u8d70\u52bf\uff0c\u5176\u4e2d\u6700\u91cd\u8981\u7684\u539f\u56e0\u662f\u5e02\u573a\u5bf9OPEC\u5c06\u4f1a\u5ef6\u957f\u51cf\u4ea7\u534f\u8bae\u7684\u524d\u666f\u62b1\u6709\u5e0c\u671b\uff0c\u9884\u8ba1\u8be5\u7ec4\u7ec7\u5f88\u53ef\u80fd\u5c06\u628a\u8be5\u534f\u8bae\u5ef6\u957f\u5230\u76842018\u5e74\u5e95\u3002\n\u6c99\u7279\u80fd\u6e90\u90e8\u957f\u6cd5\u529b\u8d6b\u8868\u793a\uff0c\u867d\u7136\u77f3\u6cb9\u8f93\u51fa\u56fd\u7ec4\u7ec7(OPEC)\u548c\u4fc4\u7f57\u65af\u7275\u5934\u7684\u5176\u4ed6\u4ea7\u6cb9\u56fd\u5bf9\u51cf\u4ea7\u534f\u8bae\u611f\u5230\u201c\u6ee1\u610f\u201d\uff0c\u4f46\u8fd9\u9879\u201c\u5de5\u4f5c\u8fd8\u6ca1\u6709\u5b8c\u6210\u201d\u3002\n\u5c3c\u65e5\u5229\u4e9a\u77f3\u6cb9\u90e8\u957f\u79f0\uff0c\u53ea\u8981\u5173\u4e8e\u8be5\u56fd\u53c2\u4e0e\u534f\u8bae\u7684\u201c\u6761\u4ef6\u5408\u9002\u201d\uff0c\u8be5\u56fd\u652f\u6301\u5c06\u51cf\u4ea7\u534f\u8bae\u5ef6\u957f\u81f3\u660e\u5e74\u5e95\uff0c\u4f46\u5c3c\u65e5\u5229\u4e9a\u76ee\u524d\u662f\u514d\u4e8e\u6267\u884c\u51cf\u4ea7\u534f\u8bae\u7684\u3002\nOPEC\u5c06\u572811\u6708\u5e95\u53ec\u5f00\